[12035] = {"Tecnología con otro propósito", nil, {"Jeremiah Hawning en Lindeallá quiere que destruyas a 5 recolectores de cosecha y que luego uses las herramientas de Jeremiah para alterar su sistema eléctrico."}},
[12036] = {"Desde las profundidades de Azjol-Nerub", nil, {"Explora El Foso de Narjun y cuéntale lo que hayas descubierto al capataz primero Juktok en Martillo de Agmar."}},
[12037] = {"Búsqueda y rescate", nil, {"Mack, en el Manantial de Granito, quiere que vayas a Drak'Tharon y averigües que le ha pasado a Kurzel."}},
