[12040] = {"Un enemigo en Arthas", nil, {"Kilix el Desenmarañador, en El Foso de Narjun del Cementerio de Dragones, te ha pedido que mates 6 infraseñores Anub'ar.|n"}},
[12041] = {"El imperio perdido", nil, {"Llévale el plan de batalla de Kilix al señor supremo Agmar en Martillo de Agmar al este."}},
[12042] = {"Corazón de los ancestros", nil, {"Drakuru quiere que vayas al punto más alto del Aserradero Cielo Azul y que encuentres el corazón de los ancestros."}},
[12043] = {"Defensa de Boquilla Oxidada", nil, {"Mata a 12 cavadores de la Plaga y a 1 capataz del baldío en las excavaciones que rodean Reposo de Galakrond. Vuelve con Narf en el Puesto Boquilla Oxidada cuando hayas completado esta tarea."}},
[12044] = {"Abastecerse", nil, {"Reúne 8 montones de mena de amalgama de las excavaciones que rodean Reposo de Galakrond y llévaselos a Xink en el Puesto Boquilla Oxidada."}},
[12045] = {"Hielo afilado", nil, {"Recoge 4 montones de fragmentos de hielo de los elementales de hielo al norte y llévaselos a Zivlix en el Puesto Boquilla Oxidada."}},
[12046] = {"Un recubrimiento suave", nil, {"Recoge 12 pellejos de animal finos de los tuneladores jormungar o de los cóndores de hueso de dragón en los aledaños del Puesto Boquilla Oxidada. Cuando hayas encontrado los pellejos, llévaselos a Zivlix."}},
[12047] = {"Algo que no se derrita", nil, {"Busca 12 trozos de hueso astillados entre los huesos de dragón al este del Puesto Boquilla Oxidada."}},
[12048] = {"Armamentos de la Plaga", nil, {"Borus Doblahierro en Martillo de Agmar en el Cementerio de Dragones quiere que le traigas 8 armamentos de la Plaga."}},
[12049] = {"Difícil de tragar", nil, {"Lucha contra un jormungar descomunal al este del Puesto Boquilla Oxidada hasta que abra la boca y lánzale una carga explosiva potente dentro de la garganta. Recoge 6 trozos de carne de jormungar chamuscada del suelo cuando haya explotado y llévaselos a Xink."}},
[12050] = {"¡Más madera!", nil, {"Utiliza el dispositivo de control de la trituradora de Xink para llamar a una trituradora una vez que llegues a los nidales de las arpías al norte. Reúne 50 montones de madera de los árboles de la zona y luego vuelve junto a Xink en el Puesto Boquilla Oxidada.\n\nSi pierdes el dispositivo de control de la trituradora de Xink, habla con Xink en el Puesto Boquilla Oxidada para que te dé uno nuevo."}},
[12051] = {"Machacar algunas plumas", nil, {"Mata a 20 arpías al noroeste del Puesto Boquilla Oxidada para Narf en el Puesto Boquilla Oxidada."}},
[12052] = {"¡Toma arpía!", nil, {"Mata a la maestra del Viento Helado y a 15 arpías Viento Helado en las montañas al noroeste del Puesto Boquilla Oxidada. Vuelve con Narf cuando hayas completado esta tarea.\n\nSi necesitas el dispositivo de control de la trituradora de Xink, habla con Xink en el Puesto Boquilla Oxidada."}},
[12053] = {"El poder de la Horda", nil, {"El capataz primero Juktok en Martillo de Agmar en el Cementerio de Dragones quiere que utilices el confalón de batalla Grito de Guerra en el Poblado Bruma de Hielo y lo defiendas de sus atacantes.\n\nDevuelve el confalón de batalla Grito de Guerra cuando hayas terminado."}},
[12054] = {"Descifrar el diario", nil, {"Llévale una ramita de aliento de espíritus a la sabia Paluna en el Campamento Oneqwah."}},
[12055] = {"Un extraño aparato", nil, {"Lleva el extraño dispositivo de Goramosh a la imagen de la archimaga Modera en Reposo Estelar."}},
[12056] = {"Marcado para morir: Sumo cultor Zangus", nil, {"Mata al Sumo cultor Zangus y lleva la cabeza del Sumo cultor Zangus al capitán Gort en el Martillo de Agmar en el Cementerio de Dragones."}},
[12057] = {"El escrito encuadernado en carne", nil, {"Devuelve el escrito encuadernado en carne al capitán Gort del Martillo de Agmar."}},
