[12841] = {"El trato de la vieja bruja", nil, {"Lok'lira la Vieja Bruja dentro de La Mina Desolada quiere que recuperes las Runas de los Yrkvinn de la sobrestante Syra."}},
[12842] = {"Forja de runas: preparativos para la batalla", nil, {"El instructor Razuvious en El Bastión de Ébano te ha ordenado que uses tu forja de runas para blasonar tu hoja de alma rúnica con una runa [de tu elección].\n\nPuedes cambiar libremente el grabado de tu arma en cualquier momento mientras estés cerca de una forja de runas."}},
[12843] = {"¡Se llevaron a nuestros hombres!", nil, {"Gretchen Chispeante quiere que vayas al Poblado Sifreldar y rescates a 5 prisioneros goblin."}},
[12844] = {"Recuperación de equipo", nil, {"Gretchen Chispeante quiere que obtengas 8 piezas de equipo K3 en el Poblado Sifreldar al norte de K3."}},
[12846] = {"Ningún goblin se queda atrás", nil, {"Encuentra la entrada de la Mina Desolada al norte del Poblado Sifreldar y busca pistas del paradero de Zeev Chispeante."}},
[12847] = {"Segundas oportunidades", nil, {"Mata al arzobispo Landgren y luego invoca la Puerta de Arete en presencia de su cadáver. Habla con el lord comandante Arete dentro de la Catedral Carmesí cuando haya terminado su interrogatorio del espíritu del arzobispo."}},
[12848] = {"El hambre sin fin", nil, {"El instructor Razuvious en El Bastión de Ébano te ha ordenado que liberes a un iniciado indigno y que ¡lo mates a sangre fría!\n\n¡Es tu vida o la suya!"}},
[12849] = {"El poder de la sangre, la escarcha y lo profano", nil, nil},
[12850] = {"Preséntate ante el comandante de la Plaga Thalanor", nil, {"Preséntate ante el comandante de la Plaga Thalanor en el balcón de la primera planta de El Bastión de Ébano."}},
[12851] = {"Una osa poderosa", nil, {"Brijana en el Poblado Brunnhildar quiere que montes a Colmillo de Hielo y dispares a 7 huargos de escarcha y 15 gigantes de escarcha en el Valle de los Viejos Inviernos."}},
[12852] = {"El almirante descubierto", nil, {"El lord comandante Arete quiere que te encargues del gran almirante Viento Oeste. Vuelve al Lord Comandante en Ascenso de la Muerte cuando lo hayas hecho."}},
[12853] = {"¡Lujosa escapada!", nil, {"Habla con Jeer Encajebujía en K3 en las Cumbres Tormentosas."}},
[12854] = {"Tras la pista de Brann", nil, {"Recupera la nota envuelta de arpillera del campamento de Brann y llévasela al Arqueólogo Andorin en Fuerte Escarcha."}},
[12855] = {"Encontrar al culpable", nil, {"Usa la collera de can de escarcha en el Campamento Abandonado para rastrear a quien se llevara las posesiones de Brann. Cuando las recuperes, usa el dispositivo de comunicación y habla con quienquiera que contactes."}},
[12856] = {"Corazón helado", nil, {"Brijana, justo en el este del Poblado Brunnhildar, quiere que vueles hasta Dun Niffelem, que liberes a 3 protodracos cautivas y rescates a 9 prisioneras Brunnhildar."}},
[12857] = {"Se busca: Aleta de Crines de Ira", nil, {"Consigue la aleta de Crines de Ira y llévasela al Jefe Garrafuria en la Brecha de la Luz en Zul'Drak."}},
[12858] = {"Piezas del puzle", nil, {"Reúne 6 fragmentos de disco de inventor y úsalos para ensamblar el disco de inventor."}},
[12859] = {"Noticias frescas: ¡el fuego sigue estando caliente!", nil, {"El anciano chamán Moky en la Brecha de la Luz en Zul'Drak quiere que uses el extintor Garrafuria para apagar 15 fuegos de cabañas en la Guarida Garrafuria.|n"}},
[12860] = {"Extracción de datos", nil, {"Usa el disco de inventor para recuperar 7 juegos de datos ocultos de los bancos de datos."}},
[12861] = {"¡Los trols se han vuelto locos!", nil, {"El jefe Garrafuria en la Brecha de la Luz en Zul'Drak quiere que uses los \"abridores de cerraduras\" Drakuru encontrados en varios trols Drakuru para liberar a 8 Garrafuria capturados."}},
[12862] = {"Cuando todo lo demás falla", nil, {"Habla con Ricket para usar el Artefacto de entrega propulsado D16 para profundizar en Las Cumbres Tormentosas."}},
[12863] = {"Dar las gracias", nil, {"Lleva la caja de suministros médicos a Glorthal Barbatiesa en Fuerte Escarcha."}},
[12864] = {"Exploradores desaparecidos", nil, {"Glorthal en Fuerte Escarcha te ha pedido que busques en el valle del Aliento de Bor y en la Caída de Bor indicios de los exploradores Natoescarcha desaparecidos."}},
[12865] = {"Compañeros leales", nil, {"Fjorlin Cejohelado en Fuerte Escarcha quiere que consigas 8 trozos de carne de rinoceronte de hielo fresca y los uses para alimentar a las águilas de Crestormenta que hay sobre la ciudad."}},
[12866] = {"Frenar a las agresoras", nil, {"Glorthal Barbatiesa en Fuerte Escarcha quiere que mates 8 estridadoras Plumaescarcha y 8 brujas Plumaescarcha."}},
[12867] = {"Ladrones de bebés", nil, {"Fjorlin Cejohelado en Fuerte Escarcha quiere que le traigas 15 huevos de águila de Crestormenta."}},
[12868] = {"Sirana Alarido de Hielo", nil, {"Glorthal Barbatiesa en Fuerte Escarcha quiere que mates a Sirana Alarido de Hielo."}},
[12869] = {"Más allá del límite", nil, {"Fjorling Cejohelado en Fuerte Escarcha quiere que mates a 16 vermis de Las Cumbres Tormentosas en Las Estepas Inferiores."}},
[12870] = {"Reliquias antiguas", nil, {"Llévale 10 reliquias de Ulduar a Rork Barbilla Aguda en Fuerte Escarcha."}},
[12871] = {"Ayuda de la Liga de Expedicionarios", nil, {"Lleva el caparazón de Norgannon a Lagnus en Fuerte Escarcha."}},
[12872] = {"El caparazón de Norgannon", nil, {"Recupera el caparazón de Norgannon del archivista Mechaton."}},
[12873] = {"El Rey Natoescarcha", nil, {"Habla con Yorg Cuoretormenta en Fuerte Escarcha."}},
[12874] = {"Fervor de los Natoescarcha", nil, {"Yorg Cuoretormenta en Fuerte Escarcha te ha pedido que hables con Fjorlin Cejohelado para conseguir un viaje y enfrentarte a tu desafío."}},
[12875] = {"Un guía con experiencia", nil, {"Encuentra a Drom Puño Helado."}},
[12876] = {"Huéspedes no deseados", nil, {"Fjorlin Cejohelado en Fuerte Escarcha quiere que mates a 10 invasores Tronaforjado."}},
[12877] = {"El guardián solitario", nil, {"Recupera el sello Puño Helado de un amonestador enano férreo y llévaselo a Creteus en las Estepas Inferiores."}},
[12878] = {"La reliquia oculta", nil, {"Creteus en Las Estepas Inferiores te ha proporcionado la información necesaria para encontrar el núcleo de Norgannon."}},
[12879] = {"La furia del rey Natoescarcha", nil, {"Llévale el núcleo de Norgannon a Creteus en Las Estepas Inferiores."}},
[12880] = {"El maestro expedicionario", nil, {"Llévale el núcleo de Norgannon a Brann Barbabronce en el Motor de los Creadores."}},
[12881] = {"Los hermanos Barbabronce", nil, nil},
[12882] = {"Reliquias antiguas", nil, {"Estás en esta misión"}},
[12883] = {"Órdenes de Drakuru", nil, {"Lleva las órdenes de Drakuru al señor cruzado Lantinga en la Brecha de la Luz en Zul'Drak."}},
[12884] = {"El Puesto de Vigilancia de Ébano", nil, {"Lleva las órdenes de Drakuru a Stefan Vadu en el Puesto de Vigilancia de Ébano en Zul'Drak."}},
[12885] = {"Los exiliados de Ulduar", nil, {"Habla con Pedruscón el Creador de rocas en el Refugio de Pedruscón."}},
[12886] = {"El Drakkensryd", nil, {"Usa el arpón Hyldnir para derrotar a 10 jinetes de dracos de El Encuentro Hyldnir en el Templo de las Tormentas. Usa el arpón Hyldnir en un adorno de columna para salir del Drakkensryd y habla con Thorim cuando lo hayas conseguido."}},
[12887] = {"Pura diversión", nil, {"Acaba con El Ocular y luego informa al Barón Tajada en La Cámara de las Sombras."}},
[12888] = {"CHATARR-A", nil, {"Llévale la tarjeta de acceso de CHATARR-A junto a 4 puñados de tornillos de cobalto y 5 condensadores sobrecargados a CHATARR-A en la Biblioteca del Inventor."}},
[12889] = {"La consola prototipo", nil, {"Interactúa con la consola prototipo en la Biblioteca del Inventor."}},
[12890] = {"Si el tamaño importara…", nil, nil},
[12891] = {"Tengo una idea, pero primero…", nil, {"El Barón Tajada en La Cámara de las Sombras te ha pedido que consigas una vara de cultor, un gancho de abominación, una cuerda Geist y 5 esencias de la Plaga."}},
[12892] = {"Pura diversión", nil, {"Acaba con El Ocular y luego informa al Barón Tajada en La Cámara de las Sombras."}},
[12893] = {"Libera tu mente", nil, {"El Barón Tajada en La Cámara de las Sombras quiere que uses la vara soberana en los cadáveres de Vil, Lady Bosqueténebro y El Saltador."}},
[12894] = {"El Puesto de Avanzada de los Cruzados", nil, {"Busca al cruzado MacKellar en el Puesto de Avanzada de los Cruzados en Zul'Drak."}},
[12895] = {"El Barbabronce desaparecido", nil, {"Estás en esta misión"}},
[12896] = {"Si no puede ser transformado", nil, {"Derrota al general Aterraluz e informa de tu éxito a Thassarian a bordo de El Rompecielos, la nave de guerra."}},
[12897] = {"Si no puede ser transformado", nil, {"Derrota al general Aterraluz e informa de tu éxito a Koltira Tejemuerte a bordo del Martillo de Orgrim, la nave de guerra."}},
[12898] = {"La Cámara de las Sombras", nil, {"Informa al Barón Tajada en La Cámara de las Sombras."}},
[12899] = {"La Cámara de las Sombras", nil, {"Informa al Barón Tajada en La Cámara de las Sombras."}},
[12900] = {"Hacer un arnés", nil, {"Astrid Bjornrittar en el Poblado Brunnhildar quiere que consigas 3 pellejos de yeti Pelohielo."}},
[12901] = {"Hacer algo a partir de nada", nil, {"El ingeniero Carrizo en el Puesto de Avanzada de los Cruzados en Zul'Drak te ha pedido que consigas 10 trozos de chatarra de la Plaga."}},
[12902] = {"En busca de respuestas", nil, {"Registra la Guarida de Garrafuria para buscar pistas sobre quién o qué inició el ataque contra los wolvar Garrafuria."}},
[12903] = {"Para eso están los amigos…", nil, {"El cruzado MacKellar en el Puesto de Avanzada de los Cruzados te ha pedido que encuentres al cruzado Dargath, a Gerk y a Burr."}},
[12904] = {"La Luz no me dará la venganza", nil, {"Gerk en el Fin de Thrym en Zul'Drak quiere que mates a 15 vargul.|n"}},
[12905] = {"Mildred la Cruel", nil, {"Habla con Mildred la Cruel en la Mina Desolada."}},
[12906] = {"Disciplina", nil, {"Mildred la Cruel en La Mina Desolada quiere que uses la vara disciplinante sobre 6 vrykuls agotados."}},
[12907] = {"Castigos ejemplares", nil, {"Mildred la Cruel en la Mina Desolada quiere que mates a Garhal."}},
[12908] = {"Un prisionero concreto", nil, {"Llévale la llave de Mildred a Lok'lira la Vieja Bruja en la Mina Desolada."}},
[12909] = {"Por el olfato se sabe", nil, {"Habla con Khaliisi a los pies de la montaña al este del lugar del accidente de Grom'arsh."}},
[12910] = {"Encontrar al culpable", nil, {"Monta a Mordisquielo y ve con ella a la fuente del olor de la nota de Brann. Luego, recupera el comunicador de Brann y úsalo para contactar con Brann Barbabronce."}},
[12911] = {"Prueba de crédito por muerte", nil, {"Saluda a la prueba de crédito por muerte escribiendo /wave"}},
[12912] = {"Se acerca una gran tormenta", nil, {"Gymer, rey de los gigantes de tormenta, te ha pedido que encuentres un modo de liberarlo de su celda. Habla con el ingeniero Carrizo en el Puesto de Avanzada de los Cruzados."}},
[12913] = {"¡Habla orco, hombre!", nil, {"Habla con Moteha Natoviento en el Lugar del accidente de Grom'arsh."}},
[12914] = {"La salvación de Gymer", nil, {"El ingeniero Carrizo en el Puesto de Avanzada de los Cruzados en Zul'Drak quiere que le traigas 6 esencias de alma en pena y 6 tierras de diatomeas."}},
[12915] = {"Enmiendas", nil, {"Thorim en el Templo de las Tormentas quiere que mates a Fjorn y a 5 gigantes de hierro Tronaforjado en el Yunque de Fjorn, al este de Dun Niffelem.\n\nUsando el talismán de tierra de Thorim invocarás a sus terráneos para que luchen a tu lado. El talismán requiere una roca de granito; se encuentran cerca del Yunque de Fjorn."}},
[12916] = {"Nuestra única esperanza", nil, {"Habla con Gymer en el Fin de Thrym en Zul'Drak cuando hayas usado la carga de seforio enorme para abrir el recinto de la Plaga donde está prisionero."}},
[12917] = {"Hablar con la voz del viento", nil, {"Llévale 5 voces del viento a Moteha Natoviento en el Lugar del accidente de Grom'arsh."}},
[12918] = {"Gemas perfectas", nil, {"Llévale dos citrinos enormes, dos jades oscuros y dos cristales de Sombras a Ounhulo en Valgarde."}},
[12919] = {"La venganza del Rey de la Tormenta", nil, {"Habla con Gymer para empezar.\n\nCon Gymer como montura, destruye a 100 de la Plaga y mata a Algar el Elegido, a Thrym y al príncipe Navarius. Informa al cruzado MacKellar en el Puesto de Avanzada de los Cruzados si tienes éxito."}},
[12920] = {"Alcanzar a Brann", nil, {"Usa el comunicador de Brann para contactar con Brann Barbabronce y habla con él, y luego habla con Boktar Furia Sangrienta en el lugar del accidente de Grom'arsh.\n\nSi pierdes el comunicador de Brann, habla con Moteha Natoviento en el lugar del accidente de Grom'arsh."}},
[12921] = {"Un cambio de escenario", nil, {"Reúnete con Lok'lira la Vieja Bruja en el Poblado Brunnhildar."}},
[12922] = {"El fuego del refinador", nil, {"Debes reunir 10 chispas furiosas de aparecidos hirvientes en el lago Campo de Escarcha y usarlas en el yunque del Yunque de Fjorn."}},
[12924] = {"Forjando una Alianza", nil, {"Debes ir a Dun Niffelem y pedirle al rey Jokkum que permita que se vuelva a forjar la armadura de Thorim. Después de completar la tarea de Jokkum, habla con Njormeld en Dun Niffelem."}},
[12925] = {"Aberraciones", nil, {"Thyra Kvinnshal en el Poblado Brunnhildar quiere que vayas a Valkyrion y consigas viales de aceite de Escarcha de las aspirantes de Valkyrion. Usa los viales de aceite de Escarcha para destruir 30 huevos de protodraco apestado."}},
[12926] = {"Piezas del rompecabezas", nil, {"Usa el comunicador de Brann para contactar con Brann Barbabronce y que te explique qué hacer a continuación."}},
[12927] = {"Extracción de datos", nil, {"Usa el disco de inventor para recuperar 7 juegos de datos ocultos de los bancos de datos."}},
[12928] = {"El caparazón de Norgannon", nil, {"Recupera el caparazón de Norgannon del archivista Mechaton."}},
[12929] = {"Los terráneos de Ulduar", nil, {"Habla con Pedruscón el Creador de rocas en el Refugio de Pedruscón."}},
[12930] = {"Tierra rara", nil, {"Usa el pico de minería terráneo para minar 7 puñados de tierra encantada. Lleva la tierra y 5 telas de tejido de Escarcha a Pedruscón el Creador de rocas en el Refugio de Pedruscón."}},
[12931] = {"Contraatacando", nil, {"Pedruscón el Creador de rocas quiere que mates a 10 atacantes Tronaforjado y que vuelvas a hablar con él en el Refugio de Pedruscón. Puedes matar a asaltantes Tronaforjado o atracadores Tronaforjado."}},
[12932] = {"El Anfiteatro de la Angustia: ¡Yggdras!", nil, {"¡Entra en la arena del Anfiteatro de la Angustia y derrota a Yggdras! Habla con Wodin el sirviente trol para recoger tu recompensa."}},
[12933] = {"El Anfiteatro de la Angustia: ¡Magnatauro!", nil, {"¡Entra en la arena del Anfiteatro de la Angustia y derrota a Barbahedionda! Habla con Wodin el sirviente trol para recoger tu recompensa."}},
[12934] = {"El Anfiteatro de la Angustia: ¡del más allá!", nil, {"¡Entra en la arena del Anfiteatro de la Angustia y derrota a uno de los señores elementales! Habla con Wodin el sirviente trol para recoger tu recompensa."}},
[12935] = {"El Anfiteatro de la Angustia: ¡Colmillarrmagedón!", nil, {"¡Entra en la arena del Anfiteatro de la Angustia y derrota a Orinoko Rompecolmillos! Habla con Wodin el sirviente trol para recoger tu recompensa."}},
[12936] = {"El Anfiteatro de la Angustia: ¡Korrak el Sanguinario!", nil, {"¡Entra en la arena del Anfiteatro de la Angustia y derrota a Korrak el Sanguinario! Habla con Wodin el sirviente trol para recoger tu recompensa."}},
[12937] = {"Alivio para los caídos", nil, {"Usa la cataplasma telúrica para sanar a 8 defensores terráneos caídos, después habla con Pedruscón el Creador de rocas en el Refugio de Pedruscón."}},
[12938] = {"El Duque", nil, {"Habla con el duque Lankral en La Cámara de las Sombras."}},
[12939] = {"Desafío de honor", nil, {"El duque Lankral en La Cámara de las Sombras te ha pedido que uses la bandera de desafío para que desafíes y derrotes a 6 combatientes Mjordin."}},
