[2159] = {"Livraison à Dolanaar", {"Apportez la Livraison de Dolanaar à l'aubergiste Keldamyr."}},
[2160] = {"Des provisions pour Tannok", {"Livrer la Caisse de ravitaillement à l'auberge de Tannok Martel-de-givre à Kharanos."}},
[2161] = {"Un fardeau de péon", {"Apportez le Fardeau d'Ukor à l'aubergiste Grosk à Tranchecolline."}},
[2178] = {"Brouet de trotteur", {"Apporter 5 morceaux de Viande de trotteur à Alanndarian Chantenuit, à Auberdine."}},
[2198] = {"Le Collier brisé", {"Chercher celui qui a fabriqué le Collier brisé pour découvrir sa valeur potentielle."}},
[2199] = {"Le Prix de la Connaissance", {"Apporter 5 Barres d'Argent à Talvash del Kissel, à Forgefer."}},
[2200] = {"Retour à Uldaman", {"Chercher des indices sur le Collier de Talvash à Uldaman. Le paladin dont il a parlé est la dernière personne à l'avoir porté."}},
[2201] = {"Trouver les gemmes", {"Trouvez le rubis, le saphir et la topaze qui sont éparpillés à travers Uldaman. Une fois que vous les aurez, contactez Talvash del Kissel en utilisant la Fiole de divination qu'il vous a donnée précédemment.","C'est dans le journal, vous savez...\n* Le rubis a été caché dans une zone barricadée des Ombreforge.\n* La topaze a été dissimulée dans une urne, dans l'une des zones trogg, pas très loin de nains de Alliance.\n* Le saphir est en possession du chef trogg, Grimelok."}},
[2202] = {"Rechercher les composants à Uldaman", {"Apporter 12 Champignons magenta à Jarkal Fondemousse à Kargath."}},
[2203] = {"Les réactifs des Terres ingrates II", {"Utiliser les 3 Récipients thaumaturgiques vides sur des dragons gardiens des terres brûlées dans les Terres ingrates. Une fois remplis, les rapporter à Jarkal Fondemousse à Kargath."}},
[2204] = {"Réparer le Collier", {"Obtenir une Source d'énergie de la construction la plus puissante que vous pouvez trouver à Uldaman, puis la remettre à Talvash del Kissel à Forgefer."}},
[2205] = {"Chercher le SI:7", {"Remettre le colis de Keryn Sylvius à maître Mathias Shaw à la Caserne de Hurlevent."}},
[2206] = {"Effraction", {"Trouver le maître des docks défias et récupérer le programme d'embarquement, pour le compte de maître Mathias Shaw."}},
[2218] = {"La voie du salut", {"Parler à Hulfdan Barbe-noire à Forgefer."}},
[2238] = {"Simple subterfuge", {"Trouver Onin MacHammar à Gnomeregan."}},
[2239] = {"Rapport d'Onin", {"Porter le rapport d'Onin à Hulfdan à Forgefer."}},
