[2241] = {"Pomme d'amour", {"Porter la Rose de Jannok à Syurna à Darnassus."}},
[2242] = {"L'appel du destin", {"Trouver Sethir l'Ancien et rapporter tout indice découvert à Syurna."}},
[2258] = {"Les réactifs des Terres ingrates", {"Apporter 5 Gésiers de busard, 10 Crocs de coyote des rochers et 5 Fragments d'élémentaire de roche à Jarkal Fondemousse, à Kargath, dans les Terres ingrates."}},
[2259] = {"Erion Murmombre", {"Contacter Erion Murmombre à Darnassus."}},
[2260] = {"L'ordre d'Erion", {"Voyager vers Hurlevent pour y demander conseil à Renzik \"le Kriss\"."}},
[2278] = {"Les Disques de platine", {"Parlez au Gardien des pierres pour connaître le Savoir qu'il défend. Une fois découvert ce qu'ils peuvent offrir, activez les Disques de Norgannon."}},
[2279] = {"Les Disques de platine", {"Apporter la version miniature des Disques de Norgannon à la Ligue des Explorateurs à Forgefer."}},
[2280] = {"Les Disques de platine", {"Apporter la version miniature des Disques de Norgannon à l'un des Sages des Pitons du Tonnerre."}},
