[2283] = {"Réparer le Collier", {"Rechercher dans l'excavation d'Uldaman un Collier de prix et le rapporter à Dran Droffers à Orgrimmar. Ce Collier peut être endommagé."}},
[2284] = {"Réparer le Collier, deuxième prise", {"Trouver, dans les profondeurs d'Uldaman, des indices sur la localisation des Gemmes."}},
[2298] = {"Extorsion royale", {"Aller à Hurlevent et chercher Renzik « le Kriss »."}},
[2299] = {"Cap sur Hulfdan !", {"Se rendre à Forgefer et parler à Hulfdan Barbe-noire."}},
[2300] = {"SI:7", {"Allez à Hurlevent et cherchez Renzik « le Kriss »."}},
[2318] = {"La Traduction du Journal", {"Trouver quelqu'un qui puisse traduire le journal du paladin. L'endroit le plus proche est Kargath, dans les Terres ingrates."}},
[2338] = {"La traduction du Journal", {"Laisser Jarkal emprunter le Collier en échange de la traduction du Journal."}},
[2339] = {"Trouver les gemmes et la source d'énergie", {"Récupérer, dans Uldaman, les trois gemmes et une source d'énergie pour le collier, puis les rapporter à Jarkal Fondemousse, à Kargath. Jarkal pense que vous pouvez trouver une source d'énergie sur la machine la plus puissante d'Uldaman.","C'est dans le journal, vous savez...\n* Le rubis a été caché dans une zone barricadée des Ombreforge.\n* La topaze a été dissimulée dans une urne, dans l'une des zones trogg, pas très loin de nains de Alliance.\n* Le saphir est possession du chef trogg, Grimelok."}},
[2340] = {"Livrer les Gemmes", {"Apporter les Gemmes et le Collier récupérés à Dran Droffers, à Orgrimmar."}},
[2341] = {"Réparer le collier, troisième prise", {"Rendre visite à Jarkal Fondemousse à Kargath pour constater (ou non) son succès."}},
[2342] = {"Des Trésors à recouvrer", {"Rapporter à Patrick Garrett, à Fossoyeuse, son Trésor de famille, qui se trouve dans le Coffre de famille dans les communs du sud d'Uldaman."}},
[2358] = {"Cornes de Nez'ra", {"Rapporter les Cornes de Nez'ra à Arantir, à Comté-du-lac."}},
[2359] = {"La Tour de Klaven", {"Voler la Clé de la Tour défias, pénétrer dans la Tour et trouver ce que contient le Coffre du bois de la Pénombre. Rapporter les informations à Mathias Shaw à Hurlevent. Lire le journal de Kearnen pour savoir ce qui se passe dans la tour."}},
[2360] = {"Mathias et les Défias", {"Se rendre dans la marche de l'Ouest et trouver l'Agent Kearnen pour un débriefing."}},
[2361] = {"Réparer le Collier", nil},
[2378] = {"Trouver la Main brisée", {"Voyager vers Orgrimmar pour y parler à Shenthul, dans la Faille de l'Ombre."}},
[2379] = {"Zando'zan", {"Parler à Zando'zan à la Faille de l'Ombre."}},
[2380] = {"Cap sur Orgrimmar !", {"S'adresser à Shenthul à la Faille de l'Ombre à Orgrimmar."}},
[2381] = {"Piller les pilleurs", {"Rapporter le Trésor des mers du Sud à Wrenix le Misérable à Cabestan. Ne pas oublier d'obtenir un B.G.A.C. et des Outils de voleur de l'Appareil bidulotronique de Wrenix. Ces deux éléments sont nécessaires pour accomplir la mission.","En cas d'attaque par des perroquets particulièrement hostiles, utiliser le B.G.A.C.!"}},
[2382] = {"Wrenix de Cabestan", {"Aller jusqu'à Cabestan dans les Tarides pour parler à Wrenix le Misérable."}},
[2383] = {"Parchemin simple", {"Lire le Simple parchemin, et parler à Frang, dans la Vallée des épreuves."}},
