[2460] = {"Le Salut brisé", {"Faire le Salut brisé à Shenthul."}},
[2478] = {"Mission : Possible mais non probable", {"Voler la Clé de la tour de Silixiz au Contremaître Silixiz, puis tuer deux Pantins mutants de la KapitalRisk, deux Patrouilleurs de la KapitalRisk, et deux Sentinelles de la KapitalRisk.","Assassiner le Grand contremaître Bisbiwix et prendre sa tête. Ouvrir le coffre de Bisbiwix et y voler la Mixture altérée de Zanzil. Apporter la Mixture et la tête de Bisbiwix à Shenthul à Orgrimmar, et attendre de nouvelles instructions."}},
[2479] = {"L'aide d'Hinott", {"Aller à Moulin-de-Tarren dans les contreforts de Hautebrande et livrer l'Échantillon de la mixture de Zanzil à Serge Hinott.","Pour rejoindre Moulin-de-Tarren, prenez le zeppelin à Fossoyeuse et suivez la route du sud, à travers les Pins argentés et vers Hautebrande. Suivez les panneaux !"}},
[2480] = {"L'aide d'Hinott", {"Attendre Serge Hinott pour achever le remède."}},
[2498] = {"Retour vers Denalan", {"Rellian Vertecime vous demande d'aller parler à Denalan au lac Al'Ameth."}},
[2499] = {"Froncechêne", {"Denalan, au lac Al'Ameth, vous demande d'ôter la Tumeur gargantuesque de Froncechêne."}},
