[3861] = {"COT-COT-CODEC !", {"Trouver de la Nourriture spéciale pour poulet et l'apporter à la poule que vous voulez aider. Lorsque vous l'aurez trouvée, encouragez-la (/acclame) avant d'essayer de lui donner la Nourriture spéciale pour poulet."}},
[3881] = {"Le sauvetage de l'expédition", {"Apporter une Caisse de nourriture et de l'Equipement de recherche à Williden Marshal dans le Cratère d'Un'Goro."}},
