[3883] = {"Écologie étrangère", {"Utiliser la Fiole de raclement pour récolter un Échantillon de paroi de la ruche dans une des nurseries de la ruche Gorishi, à l'intérieur du cratère d'Un'Goro. Chercher les chambres qui contiennent des larves suspendues.","Apporter l'Échantillon de paroi de la ruche à Hol'anyee Marshal dans le cratère d'Un'Goro."}},
[3884] = {"Le journal de Williden", {"Rapporter le Journal à Williden Marshal quelque part dans le Cratère d'Un'Goro."}},
[3885] = {"<NYI> <TXT> The Gadgetzan Run", {"Escort Petra and Dadanga!"}},
[3901] = {"Faites cliqueter les thorax !", {"Tuez 8 Squelettes Cliquethorax, puis retournez voir le Prêtre des ombres Sarvis au Glas."}},
[3902] = {"La chasse aux trésors", {"Fouiller Le Glas et ses alentours pour trouver 6 caisses de Fournitures de récupération, et les ramener au Nécrogarde Saltain."}},
[3903] = {"Milly Osworth", {"Parler à Milly Osworth."}},
[3904] = {"La récolte de Milly", {"Apportez 8 seaux de la récolte de Milly à Milly Osworth, à l'Abbaye de Comté-du-nord."}},
[3905] = {"Manifeste des vendanges", {"Apportez le Manifeste des vendanges à Frère Neals dans l'Abbaye de Comté-du-nord."}},
