[3981] = {"Le commandant Gor'shak", {"Trouver le commandant Gor'shak dans les Profondeurs de Rochenoire.","Vous vous souvenez que le dessin maladroit représentait également des barreaux au-dessus du portrait. Peut-être devriez-vous rechercher un genre de prison."}},
[3982] = {"Que se passe-t-il ?", {"Défendre Gor'shak."}},
[4001] = {"Que se passe-t-il ?", {"Parler à Kharan Force-martel et obtenir autant de renseignements que possible sur l'enlèvement de la princesse Moira Barbe-de-bronze. Apporter ces renseignements à Thrall à Orgrimmar.","Gor'shak a dit que Kharan était détenu dans une cellule proche."}},
[4002] = {"Les Royaumes de l'est", {"Parler à Thrall si vous êtes prêt à vous charger de la mission qu'il a prévue."}},
