[5021] = {"Mieux vaut tard que jamais", {"Le discours incohérent de la femme fantomatique indique qu'elle a besoin qu'on livre un colis pour elle. Elle a dit que le colis se trouvait là où se trouve son cheval. Le fantôme est incompréhensible lorsqu'il s'agit de préciser où se trouve son cheval ou l'endroit où le paquet doit être livré."}},
[5022] = {"Mieux vaut tard que jamais", {"Rendre visite aux Facteurs royaux de Hurlevent pour apprendre l'adresse d'une certaine Emma Gangrepierre. En général, il y a un officier du recensement à l'Hôtel de ville."}},
[5023] = {"Mieux vaut tard que jamais", {"Se renseigner auprès des Surveillants royaux de Fossoyeuse pour apprendre l'adresse d'un certain Jeremiah Gangrepierre. En général, il y a un officier du recensement près du bureau d'inscription des guildes et tabards."}},
