[5929] = {"L'esprit du Grand ours", {"Aller voir l'esprit du Grand ours, au nord-ouest de Reflet-de-Lune et apprendre ce qu'il peut vous enseigner sur la nature de l'ours. Quand cela est fait, retourner voir Dendrite Brasétoile, à Havrenuit, dans Reflet-de-Lune."}},
[5930] = {"Esprit du Grand ours", {"Aller voir l'esprit du Grand ours, au nord-ouest de Reflet-de-Lune et apprendre ce qu'il peut vous enseigner sur la nature de l'ours. Quand cela est fait, retourner voir Dendrite Brasétoile, à Havrenuit, dans Reflet-de-Lune."}},
[5931] = {"Retour à Darnassus", {"Retourner à Darnassus et parler à Mathrengyl Pasdelours, dans l'Enclave cénarienne."}},
[5932] = {"Retour vers les Pitons du Tonnerre", {"Retourner aux Pitons du Tonnerre, et parler à Turak Totem-runique, sur la Cime des Anciens."}},
[5941] = {"Retourner voir Chromie", {"Apporter la Liasse de reliques à Chromie, à Andorhal."}},
[5942] = {"Trésors cachés", {"Utiliser la Clé de Joseph sur le Coffre de Joseph."}},
[5943] = {"La caravane de Gizelton", {"Escorter la caravane de Gizelton à travers le convent de Mannoroc. Parler à Smeed, au camp de Fouillevis, pour toucher la récompense."}},
[5944] = {"En rêve", {"Escortez Taelan Fordring jusqu'à la sortie d'Âtreval."}},
[5961] = {"Le champion de la reine banshee", {"Parler à Nathanos le Flétrisseur, dans les Maleterres de l'est. Il se trouve actuellement à la ferme des Marris."}},
[5981] = {"Géants déchaînés", {"Rivern Givrevent voudrait que vous tuiez 4 Géants Cognegivre et 4 Protecteurs Cognegivre."}},
[6001] = {"Corps et cœur", {"Utiliser la Poussière de lune cénarienne sur la Pierre des séléniens d'Auberdine pour invoquer Lunegriffe. Affronter Lunegriffe, et gagner la force physique et spirituelle qu'il possède.","Quand cela est fait, parler à Mathrengyl Pasdelours, à Darnassus."}},
[6002] = {"Corps et cœur", {"Utiliser la Poussière lunaire cénarienne sur la Pierre des séléniens, entre Mulgore et les Tarides, pour invoquer Lunegriffe. Affronter Lunegriffe, et gagner la force physique et spirituelle qu'il possède.","Quand cela est fait, parler à Turak Totem-runique, aux Pitons du Tonnerre."}},
[6004] = {"Un travail inachevé", {"Tuer deux Médecins écarlates, deux Chasseurs écarlates, deux Magi écarlates et deux Chevaliers écarlates avant de revenir vers Kirsta Fondombre dans les Maleterres de l'ouest."}},
[6021] = {"Zaeldarr le Banni", {"Apportez la Tête de Zaeldarr au Conservateur Alen, à la chapelle de l'Espoir de Lumière."}},
[6022] = {"Tuer utile", {"Allez à l'est à la Croisée de Corin et détruisez les membres du Fléau qui habitent la ville. Rassemblez 7 morceaux de Pourriture vivante en moins de 10 minutes. Utilisez le Mortier et le Pilon pour réduire la Pourriture vivante en Pourriture coagulée."}},
[6023] = {"Un travail inachevé", {"Tuer le veneur Radley et le cavalier Durgen, avant de retourner voir Kirsta Fondombre, dans les Maleterres de l'ouest."}},
[6024] = {"L'appel d'Hameya", {"Tuer l'Infiltrateur Hameya. Utiliser sa clé sur le Monticule de poussière, derrière le caveau de Zaeldarr."}},
[6025] = {"Un travail inachevé", {"Aller à Âtreval, et trouver un poste d'observation élevé d'où voir la ville et jauger les forces de la Croisade écarlate, puis retourner voir Kirsta Fondombre, dans les Maleterres de l'ouest."}},
[6026] = {"Voilà qui est beaucoup demander", {"Smokey LaRue veut que vous lui apportiez 2 Barres de thorium, 1 Bâtonnet doré, 8 Bombes explosives et 8 Déclencheurs instables.","Vous devrez trouver un forgeron et un ingénieur pour obtenir ces objets."}},
[6027] = {"Le Livre des Anciens", {"Obtenir le Livre des Anciens, puis le rapporter à Azore Aldamort, dans la tour d'Ethel Rethor."}},
[6028] = {"Le rapport de Long-guet", {"Apportez le Rapport de Long-guet à l'officier de l'Aube d'argent Purecarde au Camp du Noroît, dans les Maleterres de l'ouest."}},
[6029] = {"Le rapport de Long-guet", {"Livrer le Rapport de Long-guet à l'officier Garush de l'Aube d'argent, à la Barricade, dans les Maleterres de l'ouest."}},
[6030] = {"Le duc Nicholas Zverenhoff", {"Apportez le livre « Études sur la langue des Esprits » au duc Nicholas Zverenhoff, à la chapelle de l'Espoir de Lumière, dans les Maleterres de l'est."}},
[6031] = {"L'étoffe runique", {"Apportez 30 pièces d'étoffe runique à Meilosh au Repaire des Grumegueules."}},
[6032] = {"Le vêtement sacré", {"Apporter 2 pièces d'Etoffe lunaire à Meilosh, au Repaire des Grumegueules."}},
[6041] = {"Quand Smokey chante, je deviens violent", {"Aller à Pestebois, au nord-ouest de la chapelle de l'Espoir de Lumière. Détruire 8 Bâtiments du Fléau en utilisant le Mélange spécial de Smokey sur les Marques de détonation que portent les bâtiments destinés à être détruits. Smokey a fait marquer les ziggourats et les abattoirs.","Une fois la bombe placée, quitter le bâtiment et contempler le résultat de votre opération.","Garder en tête que le Mélange spécial de Smokey est très... instable."}},
[6042] = {"Les petits soucis de la non-vie", {"Tuer 20 Chauves-souris pestiférées nuisibles et 10 Chauves-souris pestiférées monstrueuses. Retourner voir Nathanos le Flétrisseur quand ce sera fait."}},
[6061] = {"Dompter la bête", {"Utiliser le Bâtonnet de domptage pour dompter un trotteur des plaines adulte. S'entraîner à combattre à ses côtés, puis rapporter le Bâtonnet de domptage à Yaw Crin-tranchant, à Sabot-de-sang."}},
[6062] = {"Dompter la bête", {"Utiliser le Bâtonnet de domptage pour dompter un sanglier tacheté redoutable. S'entraîner à combattre à ses côtés, puis rapporter le Bâtonnet de domptage à Thotar de Tranchecolline."}},
[6063] = {"Dompter la bête", {"Utiliser le Bâtonnet de domptage pour dompter une Rôdeuse Tissebois. S'entraîner à combattre à ses côtés, puis rapporter le Bâtonnet de domptage à Dazalar, à Dolanaar."}},
[6064] = {"Dompter la bête", {"Utiliser le Bâtonnet de domptage pour dompter un Grand sanglier des rochers. S'entraîner à combattre à ses côtés, puis rapporter le Bâtonnet de domptage à Grif Cœur-sauvage, à Kharanos."}},
