[6102] = {"Dompter la bête", {"Utiliser le Bâtonnet de domptage pour dompter un Hurleur Strigid. S'entraîner à combattre à ses côtés, puis rapporter le Bâtonnet de domptage à Dazalar, à Dolanaar."}},
[6103] = {"Entraîner la bête", {"Parler à Jocaste, à l'Enclave cénarienne de Darnassus."}},
[6121] = {"Nouvelles leçons", {"Aller à Reflet-de-Lune, et parler à Dendrite Brasétoile, au village de Havrenuit."}},
[6122] = {"Le principe originel", {"Utiliser une Fiole d'échantillon des Chutes de la Bondissante vide pour collecter un échantillon d'eau, à l'entrée de la grotte, à proximité des Chutes.","Apporter la Fiole d'échantillon des Chutes de la Bondissante remplie à Alanndarian Chantenuit, à Auberdine, dans Sombrivage."}},
[6123] = {"Rassembler les ingrédients du remède", {"Apporter 5 Terrestrines et 12 Champignons lunaires à Alanndarian Chantenuit, à Auberdine ; elle pourra ainsi commencer à préparer son remède."}},
[6124] = {"Soigner la maladie", {"Utiliser le Remède pour animaux sur 10 Daims malades qui se trouvent dans Sombrivage ; ceci les soignera. Les Daims malades ont été aperçus d'abord entre le sud de la rivière Bondissante et le nord d'Auberdine ; on en trouve également tout au sud de Sombrivage, à la frontière d'Orneval."}},
[6125] = {"Pouvoir contre le poison", {"Retourner voir Mathrengyl Pasdelours, dans l'Enclave cénarienne, à Darnassus."}},
[6126] = {"Nouvelles leçons", {"Aller à Reflet-de-Lune, et parler à Dendrite Brasétoile, au village de Havrenuit."}},
[6127] = {"Le principe originel", {"Utiliser la Fiole d'échantillon vide du Pic de Brume-funeste pour collecter un échantillon de l'eau d'un bassin, au sommet du Pic de Brume-funeste.","Apporter ensuite la Fiole d'échantillon remplie à Tonga Totem-runique, à la Croisée, dans les Tarides."}},
[6128] = {"Rassembler les ingrédients du remède", {"Apporter 5 Terrestrines et 5 Cornes de kodo à Tonga Totem-runique, à la Croisée, pour qu'il puisse préparer un baume curatif."}},
[6129] = {"Soigner la maladie", {"Utiliser le Remède pour animaux sur 10 Gazelles malades qui se trouvent dans la partie nord des Tarides ; ceci les soignera. Les Gazelles malades ont été vues au nord de la route orientée est-ouest qui traverse la Croisée."}},
[6130] = {"Pouvoir contre le poison", {"Retourner voir Turak Totem-runique, sur la Cime des Anciens, aux Pitons du Tonnerre."}},
[6131] = {"Allié des Grumegueules", {"Grifleur veut que vous prouviez votre valeur en tuant 5 Guerriers Mort-bois, 5 Guides Mort-bois et 5 Jardiniers Mort-bois."}},
[6132] = {"Faites-moi sortir de là !", {"Escorter Melizza Razzkankan, puis parler à Hornizz Razzkankan au cimetière des Kodos en Désolace."}},
[6133] = {"L'ordre du seigneur forestier", {"Aller vers la frontière nord des Maleterres de l'est et récupérer le Registre de Quel'Thalas. L'objet se trouve quelque part dans le gîte de Quel'Lithien.","Tuer également 8 Forestiers, 8 Eclaireurs et 8 Bûcherons."}},
[6134] = {"Chasse à l'ectoplasme", {"Apporter 8 Fantôm-o-plasmes et la Caisse d'aimants fantômes à Hornizz Razzkankan, en Désolace."}},
[6135] = {"Crépuscaile, oh comme je te hais !", {"Trouver Crépuscaile et la tuer. Récupérer une Touffe de fourrure de Crépuscaile sur son cadavre, puis l'apporter à Nathanos le Flétrisseur.","Il paraît que Crépuscaile hante les terres qui se trouvent au nord de la ferme des Marris."}},
[6136] = {"Le corpulent", {"Trouver Creusechair, dans les terres au nord des Maleterres de l'est, et le tuer. Retourner voir Nathanos le Flétrisseur quand la mission sera terminée."}},
[6141] = {"Frère Anton", {"Adressez-vous au frère Anton à la Combe de Nijel, en Désolace."}},
[6142] = {"Des praires comme appât", {"Trouver 10 morceaux de Chair de praire et les apporter à Mai'Lahii, à Proie-de-l'Ombre."}},
[6143] = {"D'autres poissons-chats à fouetter", {"Tuer 7 Myrmidons Ondulame, 7 Nagas Ondulame et 5 Sorcières Ondulame, puis parler à Drulzegar, à Proie-de-l'Ombre, en Désolace."}},
[6144] = {"L'appel du commandement", {"Aller à Fossoyeuse et parler à Bragor Poing-de-sang."}},
[6145] = {"Le messager cramoisi", {"Retourner dans les Maleterres de l'est et traquer le Messager cramoisi, le tuer et récupérer les Ordres du Grand croisé.","Apporter les Ordres à Nathanos le Flétrisseur pour obtenir des instructions supplémentaires."}},
[6146] = {"La ruse de Nathanos", {"Aller à la Main de Tyr, au sud-est de la ferme des Marris. Une fois sur place, mordre dans la Pomme pourrie. Profiter d'avoir revêtu l'apparence d'un membre de la Croisade écarlate pour apporter les Ordres du Grand Croisé au Grand maître Valdelmar."}},
[6147] = {"Retourner voir Nathanos", {"Retournez voir Nathanos le Flétrisseur avec la nouvelle information."}},
[6148] = {"L'Oracle écarlate, Demetria", {"La Croisade écarlate fait mouvement. Quelque part le long de la route, entre la Main de Tyr et Stratholme, vous trouverez l'Oracle Demetria et sa fratrie. La tuer et retourner voir Nathanos le Flétrisseur."}},
[6161] = {"Revendiquer le trésor de Rackmore !", {"Trouver la Clé d'argent de Rackmore. Trouver la Clé d'or de Rackmore. Trouver et ouvrir le Coffre de Rackmore."}},
[6162] = {"La dernière bataille d'un mari", {"Tuer le Seigneur Ror et apporter sa Griffe à Dreka'Sur, au Poste de la Vénéneuse, en Gangrebois."}},
[6163] = {"Ramstein", {"Aller à Stratholme et tuer Ramstein Grandgosier. Prendre sa tête, et l'apporter à Nathanos."}},
[6164] = {"Le livre de compte d'Augustus", {"Trouver le Livre de compte d'Augustus à l'Auberge, au Val-terreur."}},
[6165] = {"<NYI> <TXT> Archmage Timolain's Remains", nil},
[6181] = {"Un message rapide", {"Apportez la Note de Lewis à Thor, le Maître des griffons."}},
[6182] = {"Le premier et le dernier", {"Parler à Mathias Shaw, dans la vieille ville de Hurlevent. Il se trouve dans la caserne."}},
[6183] = {"Honorer les morts", {"Reparlez à Mathias Shaw, quand vous aurez décidé d'accepter son travail."}},
[6184] = {"Flint Ombrelongue", {"Allez au camp du Noroît, dans les Maleterres de l'ouest, et rencontrez votre contact, Flint Ombrelongue."}},
[6185] = {"Les Maleterres de l'Est", {"Fouillez les Maleterres de l'est pour trouver des indices relatifs au \"Flétrisseur\" et aux agents du SI:7 portés disparus. Si vous trouvez un quelconque Insigne du SI:7, rapportez-le à Flint Ombrelongue, au camp du Noroît."}},
[6186] = {"La venue du Flétrisseur", {"Retourner à Hurlevent et informer le roi Varian Wrynn du destin de Nathanos Marris."}},
[6187] = {"L'ordre doit être rétabli", {"Montez une armée et portez-vous vers les Maleterres de l'est. Lancez un assaut total sur Nathanos le Flétrisseur et détruisez tout misérable élément de la Horde qui tenterait de le défendre.","Gardez votre présence d'esprit, Sharmon : la Horde le défendra jusqu'à la mort."}},
[6221] = {"Les Mort-bois du nord", {"Nafien veut que vous tuiez 5 Protecteurs Mort-bois, 5 Vengeurs Mort-bois et 5 Chamans Mort-bois."}},
[6241] = {"Les Tombe-hiver s'agitent", {"Salfa voudrait que vous tuiez 6 Totémiques, 6 Gardes et 6 Guides de la tribu Tombe-hiver."}},
[6261] = {"Dungar Grangodet", {"Apportez la Caisse d'Osric à Dungar Grangodet, le Maître des griffons."}},
[6281] = {"Continuer vers Hurlevent", {"Achetez un voyage en griffon à Thor, Maître des griffons, puis apportez la Note de Lewis à Osric Strang au magasin \"Immunité limitée\", dans la vieille ville de Hurlevent."}},
[6282] = {"La menace des harpies", {"Maggran Lieur-de-terre, de la Retraite de Roche-Soleil, vous demande de tuer sept Harpies, sept Embusquées, sept Tueuses et sept Volplumes Rougefuries."}},
[6283] = {"La lignée Rougefurie", {"Maggran, à la Retraite de Roche-Soleil, vous demande de détruire la Déchiqueteuse Rougefurie et de rapporter son corps comme preuve de votre exploit."}},
[6284] = {"Arachnophobie", {"Tuer Besseleth et apporter le Croc de Besseleth à Maggran, à la Retraite de Roche-Soleil."}},
[6285] = {"Aller voir Lewis", {"Achetez un voyage en griffon pour la Colline des sentinelles à Dungar Grangodet, Maître des griffons, puis apportez la Caisse d'Osric à Lewis à la Colline des sentinelles."}},
[6301] = {"Cycle de la renaissance", {"Tammra Champ-des-vents, de la Retraite de Roche-Soleil, vous demande de récolter 10 Graines de Gaïa."}},
[6321] = {"Approvisionner le Sépulcre", {"Apporter l'Ordre de Podrig à Karos Razok."}},
[6322] = {"Michael Garrett", {"Apporter la Caisse de Gordon à Michael Garret."}},
[6323] = {"Voyage vers Fossoyeuse", {"Acheter un voyage en chauve-souris pour Fossoyeuse, auprès du Maître des chauves-souris, Karos Razok, puis apporter l'Ordre de Podrig à Gordon Wendham, à Fossoyeuse."}},
[6324] = {"Retour vers Podrig", {"Achetez un voyage en chauve-souris pour le Sépulcre, auprès du Maître des chauves-souris Michael Garret, puis apportez la Caisse de Gordon au Nécrogarde Podrig, au Sépulcre."}},
[6341] = {"Le don de Teldrassil", {"Apporter la Collection de Nessa à Vesprystus."}},
[6342] = {"Vol vers Auberdine", {"Prenez un hippogriffe vers Auberdine chez le maître des hippogriffes Vesprystus, puis apportez la Collection de Nessa à Laird."}},
[6343] = {"Retourner voir Nessa", {"Réponse de Laird à Nessa Chantelombre."}},
[6344] = {"Nessa Chantelombre", {"Parler à Nessa Chantelombre."}},
[6361] = {"Un tas de peaux", {"Apportez le Tas de peaux au Maître des coursiers du vent Devrak, à la Croisée."}},
[6362] = {"Voyage vers les Pitons du Tonnerre", {"Achetez un voyage pour les Pitons du Tonnerre, auprès du Maître des coursiers du vent, Devrak, puis apportez le Tas de peaux à Ahanu, aux Pitons du Tonnerre."}},
[6363] = {"Tal, le maître des coursiers du vent", {"Apporter les Marchandises en cuir d'Ahanu à Tal, aux Pitons du Tonnerre."}},
[6364] = {"Retourner vers Jahan", {"Acheter un voyage pour la Croisée auprès du maître des coursiers du vent Tal, puis apporter les Marchandises en cuir d'Ahanu à Jahan Aile-de-faucon."}},
[6365] = {"De la viande pour Orgrimmar", {"Apportez la Chair de Zargh à Devrak, à la Croisée."}},
[6381] = {"Nouvelle vie", {"Planter 10 Graines de Gaïa sur les Monticules de Gaïa, puis rapporter les Graines de Gaïa enchantées restantes à Tammra, à la Retraite de Roche-Soleil."}},
[6382] = {"La Chasse d'Orneval", {"Parler à Senani Cœur-de-tonnerre, au Poste de Bois-brisé, à Orneval."}},
[6383] = {"La Chasse d'Orneval", nil},
[6384] = {"Trajet vers Orgrimmar", {"Achetez un voyage pour Orgrimmar, auprès du Maître des coursiers du vent, Devrak, puis apportez la Chair de Zargh à Gryshka, à Orgrimmar."}},
[6385] = {"Doras, le maître des coursiers du vent", {"Apporter la Lettre de Gryshka à Doras, à Orgrimmar."}},
[6386] = {"Retour à la Croisée", {"Achetez un voyage pour la Croisée auprès du Maître des coursiers du vent Doras, puis apportez la Lettre de Gryshka à Zargh, à la Croisée."}},
[6387] = {"L'honneur des étudiants", {"Apportez la Liste de Brock à Thorgrum Borrelson à Thelsamar."}},
[6388] = {"Gryth Thurden", {"Apportez les Pioches d'honneur à Gryth Thurden à Forgefer."}},
[6389] = {"Une Plaie sur la Croisade", {"Libérez les Termites des Maleterres dans la scierie de la Crête du nord, puis retournez voir Nathaniel Dumah, au camp du Noroît, dans les Maleterres de l'ouest, avec le Tonneau de Termites des Maleterres."}},
[6390] = {"Une Plaie sur la Croisade", {"Libérez les Termites des Maleterres dans la scierie de la Crête du nord, puis retournez voir Mickey Levine, à la Barricade, dans les Clairières de Tirisfal, avec le Tonneau de Termites des Maleterres."}},
[6391] = {"Voyage vers Forgefer", {"Achetez un billet de griffon pour Forgefer, auprès du Maître des griffons, Thorgrum Borrelson, puis apportez la Liste de Brock à Golnir Rochorteil, à Forgefer."}},
[6392] = {"Retourner vers Brock", {"Achetez un billet de griffon pour Thelsamar, auprès du Maître des griffons Gryth Thurden, puis apportez les Pioches d'honneur à Brock Cherchepierre, à Thelsamar."}},
[6393] = {"Guerre élémentaire", {"Apporter 10 Incendrites à Tsunaman à la Retraite de Roche-Soleil."}},
[6394] = {"La pioche de Thazz'ril", {"Apportez la Pioche de Thazz'rill au contremaître Thazz'rill."}},
[6395] = {"La dernière volonté de Marla", {"Apportez le Cadavre de Samuel Fipps sur la Tombe de Marla, puis retourner voir la Novice Elreth."}},
[6401] = {"Kaya est en vie", {"Apprendre la bonne nouvelle à Tammra Champ-des-vents à la Retraite de Roche-Soleil."}},
[6402] = {"Le rendez-vous à Hurlevent", {"Voyagez jusqu'à Hurlevent et rendez-vous aux portes de la ville. Parlez à l'écuyer Rowe, afin qu'il informe le maréchal Windsor de votre arrivée."}},
[6403] = {"La grande mascarade", {"Suivre Reginald Windsor dans Hurlevent. Le protéger contre toute menace !"}},
[6421] = {"Le ravin des Éboulis", {"Explorer la grotte du ravin des Éboulis et apporter 10 Cristaux de résonite à Mor'rogal à la Retraite de Roche-Soleil."}},
[6441] = {"Cornes de satyre", {"Collecter 16 Cornes de satyre pour Pixel, au Poste de Bois-brisé."}},
[6442] = {"Des nagas sur la grève de Zoram", {"Apporter 20 Têtes d'Irequeue à Marukai, le long de la grève de Zoram."}},
[6461] = {"Suceurs de sang", {"Xen'zilla, à Malaka'Jin, voudrait que vous tuiez 10 Rampants des mousses profondes et 7 Venimeux des mousses profondes."}},
[6462] = {"Charme troll", {"Apporter 8 Charmes trolls à Mitsuwa, à l'Avant-poste de Zoram'gar."}},
[6481] = {"Un Terrestre se lève", {"Ouvrir la Gangue de résonite avec le Cristal de résonite enchanté, puis tuer Goggeroc. Retourner voir Mor'rogal pour l'informer et lui rapporter le Cristal de résonite enchanté."}},
[6482] = {"Liberté pour Ruul", {"Escorter Ruul, puis parler à Yama Sabot-de-neige, au Poste de Bois-brisé."}},
[6501] = {"L'Œil de dragon", {"Vous devez écumer le monde pour pouvoir rétablir le pouvoir du Fragment de l'Oeil de dragon. La seule information dont vous disposez à propos de cette chose, c'est qu'elle existe."}},
[6502] = {"Amulette drakefeu", {"Récupérer le Sang du Champion des dragons noirs sur le général Drakkisath. Il se trouve dans sa salle du trône, derrière les Halls d'Ascension, sur le Pic Rochenoire."}},
[6503] = {"Les estafettes d'Orneval", {"Tuer 9 Estafettes d'Orneval, puis retourner voir Kuray'bin, à l'avant-poste de Bois-brisé."}},
[6504] = {"Les pages perdues", {"Trouver les 12 pages manquantes du Manuel d'utilisation du Déchiqueteur, puis les rassembler pour constituer les Chapitres 1, 2, et 3. Ramener les pages à Gurda Ragentaille près du poste de Bois-brisé."}},
[6521] = {"Une alliance impie", {"Apporter la Tête de l'Ambassadeur Malcin à Bragor Poing-de-sang, à Fossoyeuse."}},
[6522] = {"Une alliance impie", {"Apporter le Petit parchemin à Bragor Poing-de-sang, au Quartier royal, à Fossoyeuse."}},
[6523] = {"Protéger Kaya", {"Escorter Kaya Sabot-plat au Camp Aparaje, puis retourner voir Makaba Sabot-plat, à côté de la frontière sud-est des Serres-Rocheuses."}},
[6541] = {"Rapport à Kadrak", {"Faire un rapport à Kadrak, dans la tour de guet, dans les Tarides du nord."}},
[6542] = {"Rapport à Kadrak", {"Faire un rapport à Kadrak, dans la tour de guet, dans les Tarides du nord."}},
[6543] = {"Les rapports Chanteguerre", {"Apporter les Rapports du clan Chanteguerre à l'Eclaireur Chanteguerre, au Messager Chanteguerre et au Voltigeur Chanteguerre. Rapporter leurs comptes rendus mis à jour à Kadrak, dans la tour de guet nord, dans les Tarides."}},
[6544] = {"L'assaut de Torek", {"Escorter Torek lors de son attaque contre l'Avant-poste d'Aile-argent et Duriel Lunéclat, puis faire un rapport à Ertog Ragembroche, au Poste de Bois-brisé."}},
[6545] = {"Mise à jour du Messager Chanteguerre", nil},
[6546] = {"Mise à jour du voltigeur Chanteguerre", nil},
[6547] = {"Mise à jour de l'Eclaireur Chanteguerre", nil},
[6548] = {"Vengez mon village !", {"Tuez 8 Forbans du Totem-sinistre et 6 Mercenaires du Totem-sinistre, puis retournez voir Makaba Sabot-plat, près de la lisière sud-est des Serres-Rocheuses."}},
[6561] = {"L'infamie de Brassenoire", {"Apportez la tête du Seigneur du crépuscule Kelris à Bashana Totem-runique, aux Pitons du Tonnerre."}},
[6562] = {"Problèmes dans les profondeurs", {"Parler à Je'neu Sancrea, à Orneval."}},
[6563] = {"L'Essence d'Aku'Mai", {"Apporter 20 Saphirs d'Aku'Mai à Je'neu Sancrea, à Orneval."}},
[6564] = {"Allégeance aux Dieux très anciens", {"Apportez la Note humide à Je'neu Sancrea à l'Avant-poste de Zoram'gar, à Orneval."}},
[6565] = {"Allégeance aux Dieux très anciens", {"Tuer Lorgus Jett, dans les Profondeurs de Brassenoire et retourner voir Je'neu Sancrea, à Orneval."}},
[6566] = {"Ce que le vent apporte", {"Écouter Thrall."}},
[6567] = {"Le Champion de la Horde", {"Chercher Rokaro au village Proie-de-l'Ombre en Désolace."}},
[6568] = {"Maîtresse en tromperie", {"Apporter la Lettre de Rokaro à Myranda la Mégère, dans les Maleterres de l'ouest."}},
[6569] = {"Illusions d'Oculus", {"Aller au Pic Rochenoire et collecter 20 Yeux de draconide noir. Retourner voir Myranda la Mégère quand la tâche sera terminée."}},
[6570] = {"Brandeguerre", {"Aller à la tourbière du Ver, dans le marécage d'Âprefange, et chercher la tanière de Brandeguerre. Une fois à l'intérieur, porter l'Amulette de subversion draconique, et parler à Brandeguerre."}},
[6571] = {"Fournitures Chanteguerre", {"Collecter les Fournitures Chanteguerre : Lames du clan Chanteguerre, Huile Chanteguerre, et la Cargaison de Haches chanteguerres ayant pour destinataire Locke Okarr, au Poste de Bois-brisé."}},
[6581] = {"Lames du clan Chanteguerre", nil},
[6582] = {"L'épreuve des crânes, Clairvoyant", {"Vous devez trouver Clairvoyant, le drake champion du Vol bleu, et le tuer. Arracher son crâne à son cadavre, et le rapporter à Brandeguerre.","Clairvoyant se trouve au Berceau-de-l'Hiver."}},
[6583] = {"L'épreuve des crânes, Somnus", {"Détruire le Champion drake du Vol vert, Somnus. Arracher son crâne à son cadavre, puis le rapporter à Brandeguerre."}},
[6584] = {"L'épreuve des crânes, Chronalis", {"Chronalis, enfant de Nozdormu, garde les Grottes du temps, dans le Désert de Tanaris. Tuez-le et rapportez son crâne à Brandeguerre."}},
[6585] = {"L'épreuve des crânes, Axtroz", {"Aller à Grim Batol et traquer Axtroz, le Champion drake du Vol rouge. Le tuer et arracher son crâne, puis l'apporter à Brandeguerre."}},
[6601] = {"Ascension...", {"Il semble que la comédie soit finie. Vous savez que l'Amulette de subversion draconique, créée par Myranda la Mégère, ne fonctionnera pas à l'intérieur du pic Rochenoire. Peut-être devriez-vous trouver Rokaro et lui exposer votre fâcheuse situation. Montrez-lui l'Amulette drakefeu terne. Avec un peu de chance, il saura quoi faire."}},
[6602] = {"Le sang du champion des dragons noirs", {"Aller au Pic Rochenoire, et tuer le général Drakkisath. Récupérer son sang et l'apporter à Rokaro."}},
[6603] = {"Troubles au Berceau-de-l'Hiver", {"Trouver Donova Antreneige, à côté des sources chaudes du Berceau-de-l'Hiver."}},
[6604] = {"Les Indomptables enragés", {"Parler à Jaron Sculptepierre, au village de Pluie-d'Étoiles."}},
[6605] = {"Une étrange personne", {"Parler à Winna Hazzard, à Gangrebois."}},
[6606] = {"Un peu de chance", {"Parler à la Mau'ari, à Long-guet."}},
[6607] = {"Nat Pagle, pêcheur de l'extrême", {"Nat Pagle voudrait que vous attrapiez les poissons suivants :","Le Mahi Mahi de Brumejonc, de la grève de Brumejonc, dans le marais des Chagrins.","Un Frappeur de Sar'theris, de la grève de Sar'theris en Désolace.","L'Ahi de Féralas, de la rivière Verdantis de Féralas.","Le Nagevoile bleu de la Côte sauvage, de la Côte sauvage de la vallée de Strangleronce.","Retournez voir Nat Pagle quand la collecte sera terminée."}},
[6608] = {"Vous êtes trop bon", {"Vous pensez que Lumak essaie de vous dire qu'il ne peut plus vous aider à améliorer votre compétence de pêche. Vous devriez essayer de vous adresser maintenant à son ami, Nat Pagle, un pêcheur humain des îles au sud de Theramore (c'est du moins ce que vous croyez comprendre du discours de Lumak)."}},
[6609] = {"Il ne me reste rien !", {"Aller à Kalimdor, dans la ville côtière de Theramore. À partir de là, chercher une île au sud de la ville principale. Nat Pagle vous attend !"}},
[6610] = {"La surprise de palourdes", {"Dirge Hachillico voudrait que vous lui apportiez les objets suivants :","10 Oeufs géants. Vous les trouverez sur les Rocs qui hantent Tanaris ou sur toute autre espèce de grand oiseau.","10 morceaux de Chair de palourde piquante. Vous pouvez trouver la chair de palourde sur... les palourdes, naturellement !","20 Emmentals d'Alterac. Vous les trouverez dans une fromagerie !","Collectez tous ces objets, puis retournez voir Dirge."}},
[6611] = {"Vers Gadgetzan vous allez !", {"Aller à Gadgetzan, dans le Désert de Tanaris, et parler de votre carrière de cuisinier à Dirge Hachillico."}},
[6612] = {"Je connais un gars...", {"Aller à Gadgetzan, dans le Désert de Tanaris, et parler de votre carrière de cuisinier à Dirge Hachillico."}},
[6621] = {"Le roi des Vile-sylve", {"Placer la Bannière de Karang sur le Tertre du totem Vile-sylve. Ne pas laisser les furbolgs détruire la Bannière. Tuer le chef Murgut, et apporter le Totem de Murgut à Karang Amakkar, à Zoram'gar."}},
[6622] = {"Triage", {"Sauver des patients en utilisant des Bandages de Triage, afin de panser leurs blessures. Vous devez sauver 15 patients avant que 6 d'entre eux ne meurent de leurs blessures.","Rappelez-vous l'ordre du triage ! Les Soldats agonisants meurent les premiers, suivis des Soldats grièvement blessés. Les Soldats blessés sont ceux qui meurent les derniers, et ils doivent être soignés APRÈS ceux qui agonisent et ceux qui sont grièvement blessés ; selon le tri que vous avez fait."}},
[6623] = {"Service de traumatologie de la Horde", {"Aller au Trépas-d'Orgrim, dans les hautes-terres Arathies, et évoquer avec le docteur Gregory Victor la possibilité de rejoindre les rangs des médecins de la Horde."}},
[6624] = {"Triage", {"Sauver des patients en utilisant des Bandages de Triage, afin de panser leurs blessures. Vous devez sauver 15 patients avant que 6 d'entre eux ne meurent de leurs blessures.","Rappelez-vous l'ordre du triage ! Les Soldats agonisants meurent les premiers, suivis des Soldats grièvement blessés. Les Soldats blessés sont ceux qui meurent les derniers, et ils doivent être soignés APRES ceux qui agonisent et ceux qui sont grièvement blessés ; selon le tri que vous avez fait."}},
[6625] = {"Service de traumatologie de l'Alliance", {"Aller à Theramore, dans le marécage d'Âprefange, et parler au docteur Gustaf VanHowzen de la possibilité de rejoindre les rangs des médecins de l'Alliance."}},
[6626] = {"L'hôte du mal", {"Tuer 8 Gardes de guerre de Tranchebauge, 8 Tisseurs d'épines Tranchebauge et 8 Sectateur de la Tête de mort, puis retourner voir Myriam Chantelune, à l'entrée des Souilles de Tranchebauge."}},
[6627] = {"L'épreuve de la Connaissance", {"Réussir à répondre à la question de Braug Esprit-troublé, puis lui reparler. Il se trouve dans les Serres-Rocheuses."}},
[6628] = {"L'épreuve de la Connaissance", {"Réussir à répondre à la question de Parqual Fintallas, puis lui reparler. Il se trouve à Fossoyeuse."}},
[6629] = {"Tuer Grundig Noir-nuage", {"Tuer Grundig Noir-nuage et 6 Brutes du Totem-sinistre, puis retourner voir Makaba Sabot-plat, à côté de la frontière sud-est des Serres Rocheuses."}},
[6641] = {"Vorsha la Flagellante", {"Garantir la sécurité de Muglash tandis qu'il voyage vers le brasero. L'aider à éteindre le brasero, puis le protéger, en combattant les nagas.","En cas de réussite, retourner à l'avant-poste de Zoram'gar et informer le messager Chanteguerre de la mort de Vorsha la Flagellante."}},
[6642] = {"Faveur auprès de la Confrérie, Minerai de sombrefer", nil},
[6643] = {"Faveur auprès de la Confrérie, Noyau de feu", nil},
[6644] = {"Faveur auprès de la Confrérie, Noyau de lave", nil},
[6645] = {"Faveur auprès de la Confrérie, Cuir du Magma", nil},
[6646] = {"Faveur auprès de la Confrérie, Sang de la montagne", nil},
[6661] = {"Chasse au rat dans les profondeurs", {"Capturer 5 Rats des profondeurs en utilisant le Pipeau du dératiseur. Conduire les Rats vers Monty. N'oubliez pas de rendre le Pipeau quand vous aurez terminé."}},
[6662] = {"Mon frère, Nipsy", {"Apporter le Carton de viande mystère à Nipsy, au Dépôt du Tram des profondeurs de Hurlevent."}},
[6681] = {"Ravenholdt, le manoir", {"Apporter le Sceau de Ravenholdt à Fahrad, au Manoir de Ravenholdt, dans les collines de Hautebrande.","Votre sagacité sera mise à l'épreuve tout au long de la route. Préparez-vous."}},
[6701] = {"Emblèmes du Syndicat", nil},
[6721] = {"La voie du chasseur", {"Parler à Dazalar, à Dolanaar, en Teldrassil."}},
[6722] = {"La voie du chasseur", {"Parler à Dazalar, à Dolanaar, en Teldrassil."}},
[6741] = {"Plus de butin !", nil},
[6761] = {"La nouvelle frontière", {"Parler à Mathrengyl Pasdelours, dans l'Enclave cénarienne de Darnassus."}},
[6762] = {"Rabine Saturna", {"Parler à Rabine Saturna, au village de Havrenuit, à Reflet-de-Lune. Reflet-de-Lune se trouve entre Gangrebois et le Berceau-de-l'Hiver. Le chemin qui y mène traverse le Repaire des Grumegueules."}},
[6781] = {"Plus de morceaux d'armure !", nil},
[6801] = {"Lokholar le Seigneur des Glaces", nil},
[6804] = {"Eau empoisonnée", {"Utiliser l'Aspect de Neptulon sur les élémentaires empoisonnés des Maleterres de l'est. Apporter 12 Bracelets discordants et l'Aspect de Neptulon au duc Hydraxis, en Azshara."}},
[6805] = {"Tempêtes", {"Tuer 15 Tempétueux de poussière et 15 Grondeurs du désert, puis retourner voir le duc Hydraxis, en Azshara."}},
[6821] = {"Œil du Prophète ardent", {"Apporter l'Oeil du Prophète ardent au duc Hydraxis, en Azshara."}},
[6822] = {"Le Cœur du Magma", {"Tuer 1 Seigneur du feu, 1 Géant de lave, 1 Chien antique du Magma et 1 Surgisseur de lave, puis retourner voir le duc Hydraxis, en Azshara."}},
[6823] = {"Agent de Hydraxis", {"Parvenir au niveau de réputation Honoré auprès des Hydraxiens, puis parler au duc Hydraxis, en Azshara."}},
[6824] = {"Les mains de l'ennemi", {"Apportez les Mains de Lucifron, de Sulfuron, de Gehennas et de Shazzrah au duc Hydraxis, en Azshara."}},
