[6845] = {"Les secrets du passé", {"Parler à Rabine Saturna, au village de Havrenuit, à Reflet-de-Lune."}},
[6846] = {"Que l'attaque commence !", nil},
[6847] = {"L'Œil-qui-voit-tout de maître Ryson", {"Récupérer l'Oeil-qui-voit-tout de Maître Ryson dans les Grottes Hache-d'hiver, puis le rapporter à Maître Ryson.","Sachez que l'on dit de l'Oeil-qui-voit-tout de Maître Ryson qu'il contient une terrible malédiction !"}},
[6848] = {"L'Œil-qui-voit-tout de maître Ryson", {"Récupérez l'Oeil-qui-voit-tout de Maître Ryson dans les Grottes Hache-d'hiver, puis rapportez-le à Maître Ryson.","Sachez que l'on dit de l'Oeil-qui-voit-tout de Maître Ryson qu'il contient une terrible malédiction !"}},
[6861] = {"Le Déchiqueteur portatif de Zinfoirux", {"Le Maître Ingénieur Zinfoirux veut que vous lui apportiez les matériaux suivants :","*30 Barres de thorium.","*50 Barres de Mithril.","*75 Barres de fer.","*1 Scie à vapeur."}},
