[6963] = {"Friandises volées de la fête du Voile d'hiver", {"Trouver l'enquêteur des Gourmandises Fumebois qui est porté disparu dans les montagnes d'Alterac."}},
[6964] = {"L'esprit de la fête", {"Parlez à Sagorne Rôdeur-des-crêtes, dans la Vallée de la Sagesse d'Orgrimmar, de la fête du Voile d'hiver."}},
[6981] = {"L'Éclat luminescent", {"Allez à Cabestan pour trouver quelqu'un qui puisse vous en dire plus sur l'Éclat luminescent.","Puis livrez l'Éclat, selon les instructions."}},
[6982] = {"Fournitures de Froide-dent", {"Apporter 10 Fournitures de Froide-dent à l'intendant de l'Alliance, à Dun Baldar."}},
[6983] = {"La méchanceté vous habite...", {"Localiser les friandises, et les ramener à Béachevé Chipsou, à Orgrimmar. C'est l'abominable Grinche, vivant quelque part dans les régions enneigées des montagnes d'Alterac, qui les détiendrait."}},
[6984] = {"Un grand merci des Gourmandises Fumebois!", nil},
[6985] = {"Fournitures de Gouffrefer", {"Apporter 10 Fournitures de Gouffrefer à l'Intendant de la Horde, au donjon Loup-de-givre."}},
[7001] = {"Ecuries vides", {"Trouvez un Loup des glaces dans la vallée d'Alterac. Servez-vous de la Muselière de loup des glaces pour « dompter » la bête. Lorsque ce sera fait, le loup des glaces vous suivra jusqu'au Maître des écuries Loup-de-givre. Parlez au Maître des écuries Loup-de-givre pour être crédité de cette capture."}},
[7002] = {"Harnais en cuir de bélier", nil},
[7003] = {"Géants rétrécis", {"Utilisez l'Ultra-réducteur de Zorbin sur des géants de n'importe quel type en Féralas afin les rendre plus faciles à gérer. Apportez 15 Résidus de miniaturisation que vous trouverez sur les versions rétrécies des géants à Zorbin Poudrozieu aux docks de la Côte oubliée en Féralas."}},
[7021] = {"Grand-père Hiver est ici !", {"Parler à Grand-père Hiver ; il se trouve près des Gourmandises Fumebois, à Orgrimmar."}},
[7022] = {"Grand-père Hiver est ici !", {"Parler à Grand-père Hiver ; il se trouve près des Gourmandises Fumebois, à Forgefer."}},
[7023] = {"Grand-père Hiver est ici !", {"Parler à Grand-père Hiver ; il se trouve près des Gourmandises Fumebois, à Forgefer."}},
