[7025] = {"Des friandises pour Grand-père Hiver", {"Apporter 5 Biscuits en pain d'épice et un verre de Lait glacé au Grand-père Hiver à Forgefer."}},
[7026] = {"Harnais pour béliers", nil},
[7027] = {"Écuries vides", {"Trouvez un Bélier d'Alterac dans la vallée d'Alterac. Lorsque vous serez à proximité, servez-vous du Collier d'entraînement Foudrepique pour le \"dompter\". Une fois dompté, le Bélier d'Alterac vous suivra jusqu'au Maître des écuries. Parlez au Maître des écuries pour être crédité de cette capture."}},
[7028] = {"Forces maléfiques retorses", {"Collecter 15 Ciselures de Cristaux Théradriques pour Saule, en Désolace."}},
[7029] = {"La corruption de Vylelangue", {"Remplir la Fiole céruléenne renforcée, au Bassin de cristal orange, dans Maraudon.","En faire usage sur les Vignes de Vylevrille pour forcer les Engeances Noxcives corrompues à en sortir.","Soigner 8 plantes en tuant les Engeances Noxcives, puis retourner voir Vark Balafre-glorieuse, à Proie-de-l'Ombre."}},
[7041] = {"La corruption de Vylelangue", {"Remplir la Fiole céruléenne renforcée, au Bassin de cristal orange dans Maraudon.","En faire usage sur les vignes de Vylevrille pour forcer les Engeances Noxcives corrompues à en sortir.","Soigner 8 plantes en tuant les Engeances, puis retourner voir Talendria, à la Combe de Nijel."}},
[7042] = {"Friandises volées de la fête du Voile d'hiver", {"Localiser l'enquêteur des Gourmandises Fumebois qui a été porté disparu dans les montagnes d'Alterac."}},
[7043] = {"La méchanceté vous habite...", {"Trouver les Friandises volées, et les ramener à Karfour Tintinnabourse, à Forgefer. C'est l'Abominable Grinche qui aurait le chargement en sa possession, il résiderait dans les régions neigeuses des montagnes d'Alterac."}},
[7044] = {"Légendes de Maraudon", {"Retrouver les deux parties du Sceptre de Celebras : le Bâtonnet de Celebras et le Diamant de Celebras.","Trouvez un moyen de parler à Celebras."}},
[7045] = {"Un grand merci des Gourmandises Fumebois!", nil},
[7046] = {"Le sceptre de Celebras", {"Aidez Celebras le Racheté pendant qu'il recrée le Sceptre de Celebras.","Parlez-lui après la réussite du rituel."}},
[7061] = {"La fête du Voile d'hiver", {"Prenez le temps de lire « La fête du Voile d'hiver », pour en apprendre plus sur cette fête. Quand vous aurez fini ce livre, apportez-le à Cairne Sabot-de-sang, dans la ville des Pitons du Tonnerre."}},
[7062] = {"L'esprit de cette fête", {"Parlez à l'historien Karnik de la fête du Voile d'hiver, en vous rendant à la Ligue des Explorateurs de Forgefer."}},
[7063] = {"La fête du Voile d'hiver", nil},
[7064] = {"Corruption de la terre et de la graine", {"Anéantir la princesse Theradras et retourner voir Selendra près de Proie-de-l'Ombre, en Désolace."}},
[7065] = {"Corruption de la terre et de la Graine", {"Tuer la princesse Theradras, et retourner voir le Gardien Marandis, à la Combe de Nijel en Désolace."}},
[7066] = {"La graine de la vie", {"Chercher Remulos à Reflet-de-Lune, et lui donner la Graine de la vie."}},
[7067] = {"Les instructions du paria", {"Lire les instructions du paria. Après cela, obtenir l'Amulette d'union de Maraudon et la ramener au paria centaure, dans le sud de Désolace."}},
[7068] = {"Fragments d'Ombréclat", {"Récupérez 10 Fragments d'Ombréclats dans Maraudon, et rapportez-les à Uthel'nay, à Orgrimmar."}},
[7069] = {"<UNUSED>", nil},
[7070] = {"Fragments d'Ombréclat", {"Récupérez 10 Fragments d'Ombréclats à Maraudon, et les apporter à l'archimage Tervosh, dans le marécage d'Âprefange."}},
[7081] = {"Les cimetières de la vallée d'Alterac", {"Attaquez un cimetière, puis présentez-vous au sergent Durgen Foudrepique dans les montagnes d'Alterac."}},
[7082] = {"Les cimetières d'Alterac", {"Attaquez un cimetière, puis présentez-vous au caporal Teeka Gronde-sang dans les montagnes d'Alterac."}},
[7101] = {"Tours et fortins", {"Prenez une tour ennemie, puis présentez-vous au caporal Teeka Gronde-sang dans les montagnes d'Alterac."}},
[7102] = {"Tours et fortins", {"Détruisez la bannière d'une tour ou d'un fortin ennemi, puis présentez-vous au sergent Durgen Foudrepique à Dun Baldar."}},
[7121] = {"L'intendant", {"Parlez à l'Intendant Foudrepique."}},
[7122] = {"Prendre une mine", {"Prenez une mine que les Foudrepiques ne contrôlent pas, puis présentez-vous au sergent Durgen Foudrepique dans les montagnes d'Alterac."}},
[7123] = {"Parlez à notre Intendant", {"Parlez à l'Intendant Loup-de-givre."}},
[7124] = {"Prendre une mine", {"Prenez une mine, puis présentez-vous au caporal Teeka Gronde-sang dans les montagnes d'Alterac."}},
[7141] = {"La bataille d'Alterac", {"Entrez dans la vallée d'Alterac, tuez Drek'Thar, le général de la Horde, puis retournez voir la Prospectrice Taillepierre dans les montagnes d'Alterac."}},
[7142] = {"La bataille pour Alterac", {"Entrez dans la vallée d'Alterac, tuez Vanndar Foudrepique, le général nain, puis retournez voir Voggah Mortepoigne dans les montagnes d'Alterac"}},
[7161] = {"Montrer sa valeur", {"Rendez-vous dans les cavernes des Follepattes, au sud-est de la base principale, et récupérez la Bannière Loup-de-givre qui s'y trouve. Ramenez-la au Maître de guerre Laggrond."}},
[7162] = {"Montrer sa valeur", {"Rendez-vous dans les cavernes de l'Aile de glace, au sud-ouest de Dun Baldar dans la vallée d'Alterac, et récupérez la Bannière Foudrepique. Rapportez-la au lieutenant Haggerdin dans les montagnes d'Alterac."}},
