[7181] = {"La légende de Korrak", {"D'après la légende, le chef des puissants trolls Haches-d'hiver se montre quand il le décide pour semer la destruction chez les habitants de la vallée d'Alterac.","Si Korrak apparaît, détruisez-le et présentez-vous au Maître de guerre Laggrond."}},
[7201] = {"Le dernier élément", {"Partir pour les Profondeurs de Rochenoire et trouver 10 Essences des éléments. Commencer vos recherches par les golems et par les créateurs de golems. Vivian Lagrave a aussi marmonné quelque chose à propos d'élémentaires."}},
[7202] = {"Korrak le Ragesang", {"D'après la légende, le chef des puissants trolls Haches-d'hiver se montre quand il le décide pour semer la destruction chez les habitants de la vallée d'Alterac.","Si Korrak apparaît, détruisez-le et présentez-vous au Lieutenant Haggerdin dans les montagnes d'Alterac."}},
[7221] = {"Discuter avec la prospectrice Taillepierre", {"Discutez avec la Prospectrice Taillepierre à la Garnison de Dun Baldar."}},
[7222] = {"Discuter avec Voggah Mortepoigne", {"Parlez à Voggah au donjon Loup-de-givre."}},
[7223] = {"Morceaux d'armures", {"Apportez 20 Morceaux d'armure à Murgot Forge-profonde à Dun Baldar."}},
[7224] = {"Pris à l'ennemi", {"Apportez 20 Morceaux d'armure au forgeron Regzar au village Loup-de-givre."}},
[7241] = {"La défense des Loups-de-givre", {"Rendez-vous dans la vallée d'Alterac, à l'intérieur des montagnes d'Alterac. Trouvez le maître de guerre Laggrond à l'extérieur du tunnel d'entrée, parlez-lui et commencez votre carrière comme soldat Loup-de-givre. Vous trouverez la vallée d'Alterac au nord de Moulin-de-Tarren à la base des montagnes d'Alterac."}},
[7261] = {"L'impératif souverain", {"Voyagez jusqu'à la vallée d'Alterac, dans les contreforts de Hautebrande. Présentez-vous au lieutenant Haggerdin à l'extérieur du tunnel d'entrée.","Pour la gloire de Barbe-de-bronze !"}},
[7281] = {"Amour fraternel", {"Pénétrez en territoire Foudrepique et tuez le commandant Karl Philips. Lorsque ce sera fait, présentez-vous au commandant Louis Philips."}},
[7282] = {"Amour fraternel", {"Pénétrez en territoire Loup-de-givre et tuez le commandant Louis Philips. Lorsque ce sera fait, présentez-vous au commandant Karl Philips."}},
[7301] = {"Les ailes brisées", {"Enfoncez-vous en territoire Loup-de-givre à la recherche des chefs d'escadrille Slidore, Vipore et Ichman. Si vous les retrouvez, présentez-vous au commandant Duffy."}},
[7302] = {"Les ailes brisées", {"Découvrez ce qui est arrivé aux chefs d'escadrille Guse, Jetzor et Mulverick. Lorsque vous les aurez retrouvés, présentez-vous au commandant Mulfort."}},
[7321] = {"Bisque de tortue apaisante", {"Apporter 10 morceaux de Viande de tortue à Christoph Jeffcoat à Moulin-de-Tarren."}},
