[7504] = {"Sainte Bolognaise : Ce que la lumière ne vous dit pas", {"Rendez ce livre à ses légitimes propriétaires."}},
[7505] = {"Le Horion de givre et vous", {"Rendez ce livre à ses légitimes propriétaires."}},
[7506] = {"Le Rêve d'Emeraude...", {"Rendez ce livre à ses légitimes propriétaires."}},
[7507] = {"Nostro's Compendium", {"Return Nostro's Compendium of Dragon Slaying to the Athenaeum."}},
[7508] = {"Forger Quel'Serrar", nil},
[7509] = {"The Forging of Quel'Serrar", {"You must get Onyxia to breathe fire on the Unfired Ancient Blade. Once this is done, pick up the now Heated Ancient Blade. Be warned, a Heated Ancient Blade will not remain heated forever - time is of the essence.","The last step before returning to me is to slay the beast and drive the Heated Ancient Blade into her corpse.","Do this and Quel'Serrar will be yours."}},
[7521] = {"Tonneraan le Cherchevent", nil},
[7522] = {"Examiner le Calice", nil},
[7541] = {"Un service pour la Horde", nil},
[7562] = {"Mor'zul Porte-sang", {"Parler à Mor'zul Porte-sang à l'Autel des tempêtes, dans les Steppes ardentes."}},
[7563] = {"Rage de sang", {"Apporter 30 bouteilles de Sang de bêtes déchaînées à Mor'zul Porte-sang dans les Steppes ardentes."}},
[7564] = {"Gorzeeki Fouregard", {"Apportez la Caisse de sang à Gorzeeki Fouregard à l'Autel des tempêtes, dans les Steppes ardentes."}},
[7581] = {"Les ancrages de la prison", {"Rendez-vous à Hache-tripes en Féralas et récupérez 15 doses de Sang de satyre sur les satyres Follengeance qui habitent le quartier de Crochebois. Lorsque ce sera fait, retournez voir Daio à la Balafre impure."}},
[7582] = {"Les parois de la prison", {"Rendez-vous à la gorge du Sombre murmure, au Berceau-de-l'Hiver, et récupérez 5 Larmes de l'Hederine sur les démons Hederine qui occupent la gorge. Lorsque ce sera fait, retournez voir Levandre à la Balafre impure."}},
[7583] = {"Suppression", {"Pénétrez dans la Balafre impure et localisez un Commandant de la garde funeste.","Servez-vous de la Prison de cristal luminescente sur lui. Attendez-vous à être sauvagement attaqué, car le démon fera l'impossible pour éviter d'être capturé.","Si vous réussissez, amenez le garde funeste emprisonné à Daio à la Balafre impure."}},
[7601] = {"Les Commandements de Niby", {"Parlez à Démonet à Gangrebois."}},
[7602] = {"La gangressence parfaite", {"Démonet vous a demandé de lui apporter trois Gangressences parfaites, venant de trois régions distinctes.","Les satyres Legashi d'Azshara, les légionnaires de Jaedenar et les gangregardes sentinelles des Terres foudroyées détiennent la Gangressence parfaite de leur région.","Récupérez les Gangressences parfaites et rapportez-les à Démonet, à Gangrebois."}},
[7603] = {"Le noyau infernal de Kroshius", {"Fouillez le Val Grêlé pour trouver les restes de Kroshius. Quand vous les aurez localisés, utilisez le Gangrefeu à proximité et attendez le retour de Kroshius. Quand l'Infernal se sera réveillé, tuez-le et prenez le Noyau infernal sur son cadavre.","Rapportez le Noyau infernal de Kroshius à Niby le Tout-Puissant, à Gangrebois."}},
[7604] = {"Un contrat contraignant", {"Apportez le Contrat de la Confrérie du Thorium à Lokhtos Sombrescompte, si vous voulez recevoir les plans du marteau de sulfuron."}},
[7621] = {"Un avertissement", nil},
[7622] = {"L'équilibre de la Lumière et de l'Ombre", {"Sauvez 50 Paysans avant que 15 d'entre eux aient été tués. Si vous y parvenez, allez parler à Eris Havrefeu.","Consultez le Panneau des morts pour voir combien de paysans ont été tués."}},
[7623] = {"Le seigneur Creuseplaie", {"Achetez des Potions des ténèbres à Gorzeeki à l'Autel des tempêtes, dans les Steppes ardentes.","Servez-vous-en pour vous rendre à Jaedenar, dans Gangrebois et parlez au seigneur Creuseplaie à l'intérieur du Fort des ombres."}},
[7624] = {"Ulathek le traître", {"Affrontez Ulathek, puis apportez le Cœur du traître au seigneur Creuseplaie à Jaedenar."}},
[7625] = {"Poussière d'étoile xorothienne", {"Acheter de la Poussière d’étoile xorothienne à Ur’dan dans le Fort des ombres à Jaedenar, dans Gangrebois. L’apporter à Gorzeeki Fouregard dans les Steppes ardentes."}},
[7626] = {"La Cloche de Dethmoora", {"Apportez 2 Elixirs de puissance de l'ombre à Gorzeeki Fouregard dans les Steppes ardentes."}},
[7627] = {"La Roue de la Marche noire", {"Apportez 6 Grands éclats brillants et 25 charges de Minerai de sombrefer à Gorzeeki dans les Steppes ardentes."}},
[7628] = {"La Chandelle de l'apocalypse", {"Apportez 3 Ecailles de dragon noir à Gorzeeki Fouregard dans les Steppes ardentes."}},
[7629] = {"Livraison de diablotin", {"Apportez le Diablotin en bouteille au laboratoire d'alchimie de la Scholomance. Une fois le parchemin créé, rapportez la bouteille à Gorzeeki Fouregard."}},
[7630] = {"L'arcanite", {"Apporter 1 Barre d'arcanite à Gorzeeki dans les Steppes ardentes."}},
[7631] = {"Le destrier de l'effroi de Xoroth", {"Lisez les Instructions de Mor'zul. Invoquez un Destrier de l'effroi xorothien, vainquez-le, et liez son esprit."}},
[7632] = {"La feuille ancienne", {"Trouvez le propriétaire de la Feuille ancienne pétrifiée. Bonne chance, Sharmon, le monde est vaste."}},
[7633] = {"Une rencontre", nil},
[7634] = {"Feuille d'Ancien cousue de tendons", {"L'Ancien Hastat vous demande de lui ramener un Tendon de dragon bleu adulte. Si vous en trouvez un, ramenez-le-lui à Gangrebois."}},
[7635] = {"La bonne corde", {"L'Ancien Stoma vous demande de lui ramener un Tendon de dragon noir adulte. Si vous en trouvez un, ramenez-le à Stoma à Gangrebois."}},
[7636] = {"Le bâton des Anciens", {"Vous devez trouver et tuer quatre corrupteurs démoniaques :","Simona la Séductrice.","Klinfran l'Enragé.","Solenor le Tueur.","Artorius le Néfaste.","Détruisez ces créatures et retournez voir l'Ancien Vartus à Gangrebois.","Vous trouverez des indices sur leurs faits et gestes sur l'Ecorce pétrifiée qui se trouve dans votre inventaire.","Vous DEVEZ agir seul."}},
[7637] = {"L'accent sur le sacrifice", {"Rendez-vous à Forgefer et procurez-vous l'Encensoir d'exorcisme du grand prêtre Rohan. Pour vous le procurer, il vous faudra verser une donation de 150 pièces d'or."}},
[7638] = {"Le seigneur Grayson Brisombre", {"Parler au seigneur Grayson Brisombre dans le Quartier de la cathédrale de Hurlevent."}},
[7639] = {"Des preuves de jugement", {"Ramener l'Encensoir d'exorcisme au seigneur Grayson Brisombre, dans le Quartier de cathédrale de Hurlevent."}},
[7640] = {"Exorciser Val-Terreur", {"Servez-vous de l'Encensoir d'exorcisme pour chasser les esprits qui tourmentent Val-Terreur. Lorsque vous aurez tué 25 Esprits de Val-Terreur, retournez voir le seigneur Grayson Brisombre dans le Quartier de la cathédrale de Hurlevent."}},
[7641] = {"L'œuvre de Grimand Elmore", {"Parlez à Grimand Elmore dans le Quartier des nains de Hurlevent."}},
[7642] = {"Une ribambelle d'objets", {"Apporter 40 Etoffes runiques, 6 Barres d'arcanite, 10 Larmes d'Arthas, 5 Fioles d'eau sacrée de Stratholme, et 150 pièces d'or à Grimand Elmore, au Quartier des nains de Hurlevent."}},
[7643] = {"L'ancien esprit équin", {"Procurez-vous un aliment pour chevaux capable de nourrir un esprit équin. Il semble que Merideth Carlson, à Austrivage, sache en fabriquer.","Ensuite, rendez-vous au donjon de Hache-tripes en Féralas, et tuez Tendris Crochebois. Sa mort libérera l'Ancien esprit équin. Nourrissez-le avec l'aliment spécial afin de l'apaiser. Enfin, donnez-lui le Caparaçon d'arcanite pour qu'il le bénisse."}},
[7644] = {"Le caparaçon en arcanite béni", {"Retourner dans le quartier de la cathédrale de Hurlevent avec le Caparaçon en arcanite béni pour voir le seigneur Grayson Brisombre."}},
[7645] = {"Fourrage enrichi en manne", {"Récupérer 20 Biscuits de manne enrichis, l'ingrédient vital qui permet de préparer du Fourrage enrichi en manne, pour le compte de Merideth Carlson de Southore dans les contreforts de Hautebrande. L'Aube d'argent est la seule à fabriquer ces biscuits.","Vous devrez également lui donner 50 pièces d'or pour apaiser sa susceptibilité."}},
[7646] = {"Le Clairvoyant", {"Apportez un Diamant d'Azeroth et un Diamant noir en parfait état au seigneur Grayson Brisombre dans le Quartier de la cathédrale de Hurlevent."}},
[7647] = {"Jugement et rédemption", {"Utilisez le Clairvoyant au coeur du sous-sol du Grand ossuaire, dans la Scholomance. Cela attirera des esprits que vous devrez juger. Lorsqu'ils seront vaincus, le Chevalier de la mort Ravassombre apparaîtra. Vainquez-le et purifiez l'âme du destrier déchu.","Ensuite, donnez l'Ame rachetée du destrier et le Caparaçon enchanté et béni au Destrier déchu de Ravassombre."}},
[7648] = {"Le chef d'œuvre de Grimand", {"Présentez le Caparaçon d'arcanite au seigneur Grayson Brisombre dans le Quartier de la cathédrale de Hurlevent, pour inspection."}},
[7649] = {"Les plates en thorium enchanté - Volume I", {"Rendez ce livre à ses légitimes propriétaires."}},
[7650] = {"Les plates en thorium enchanté - Volume II", {"Rendez ce livre à ses légitimes propriétaires."}},
[7651] = {"Les plates en thorium enchanté - Volume III", {"Rendez ce livre à ses légitimes propriétaires."}},
