[7701] = {"ON RECHERCHE : Surveillant Maltorius", {"Loin dans les profondeurs de la Fosse aux scories dans le Chaudron, dans la Gorge des Vents brûlants, vous trouverez le nain traître du clan Sombrefer répondant au nom de surveillant Maltorius.","Tuez le surveillant Maltorius et prenez sa tête. Apportez-la au capitaine de guet Lolo Heurteloin dans la Gorge des Vents brûlants pour la récompense."}},
[7702] = {"Privez-les de sommeil", {"Loin dans les profondeurs de la Fosse aux scories, dans le Chaudron, vous trouverez le dortoir des nains Sombrefer.","Allez dans leur dortoir et volez 20 Oreillers Sombrefer. Revenez voir Evonice Fumesuie dans la gorge des Vents brûlants quand vous aurez accompli cette tâche."}},
[7703] = {"Affaires inachevées chez les Gordok", {"Trouvez le Gantelet de puissance gordok et rendez-le au Capitaine Kromcrush à Hache-tripes.","Selon Kromcrush, les histoires des « vieux » racontent que Tortheldrin - un « vilain » elfe qui se fait appeler prince - l'a volé à l'un des rois gordok."}},
[7704] = {"Regardez un peu la taille !", {"Apportez l'Oreiller de la Femme de chambre Pincetaie à Evonice Fumesuie dans la Gorge des Vents brûlants."}},
[7721] = {"Du carburant pour la zappette", {"Récupérez 10 Noyaux élémentaires d'eau sur les Elémentaires marins et les Embruns le long de la Côte oubliée de Féralas, et rapportez-les à Zorbin Poudrozieu, près du quai là-bas."}},
[7722] = {"Le flux embrasé", {"Quelque part dans la Fosse aux scories du Chaudron, vous trouverez les Plans secrets : Flux embrasé dérobés par le surveillant Maltorius. Trouvez ces plans et rapportez-les au Maître forgeron Brûlotin, dans la Gorge des Vents brûlants."}},
[7723] = {"Maudits gros doigts", {"Hansel Lourdemains veut que vous tuiez 20 Golems de guerre lourds.","Les golems de guerre lourds errent dans les régions qui entourent le Chaudron dans la Gorge des Vents brûlants. Quand vous en aurez tué assez, revenez voir Hansel dans la Gorge des Vents brûlants."}},
[7724] = {"Une menace incandescente", {"Hansel Lourdemains veut que vous tuiez 20 Grandes araignées de lave dans la gorge des Vents brûlants. Vous pouvez trouver les Grandes araignées de lave dans les plateaux et les mares de lave de la gorge des Vents brûlants.","Revenez voir Hansel dans la gorge des Vents brûlants quand vous aurez tué assez de Grandes araignées de lave."}},
[7725] = {"Le retour des géants rétrécis", {"Utilisez l'Ultra-réducteur de Zorbin sur des géants de n'importe quel type en Féralas afin les rendre plus faciles à gérer. Apportez 10 Résidus de miniaturisation que vous trouverez sur les versions rétrécies des géants à Zorbin Poudrozieu aux docks de la Côte oubliée en Féralas."}},
[7726] = {"Encore du carburant pour la zappette", {"Récupérez 6 Noyaux élémentaires d'eau sur les Elémentaires marins et les Embruns le long de la Côte oubliée de Féralas, et rapportez-les à Zorbin Poudrozieu, près du quai."}},
[7727] = {"Incendosaures ? N'importosaures, plutôt", {"Hansel Lourdemains veut que vous tuiez 20 Incendosaures.","On peut les trouver dans les bassins de lave de la Fosse aux scories, qui occupe une partie du Chaudron de la gorge des Vents brûlants.","Retournez voir Hansel Lourdemains dans la gorge des Vents brûlants lorsque vous aurez tué suffisamment d'Incendosaures."}},
[7728] = {"OBJETS VOLES : Tuyère et longue-vue", {"Trouvez la Tuyère de forge et la Longue-vue du guetteur et rapportez-les au sous-chef Scrange dans la gorge des Vents brûlants.","Les seules informations dont vous disposez sur ces objets sont les suivantes : ils ont été dérobés par les nains Sombrefer, la Tuyère de forge est un outil utilisé par les forgerons et la Longue-vue du guetteur est un outil de surveillance inestimable pour les guetteurs."}},
[7729] = {"OFFRE D'EMPLOI : Réduire la concurrence", {"Tuez 15 Sous-chefs Sombrefer et 15 Esclavagistes Sombrefer. Revenez voir le sous-chef Scrange dans la gorge des Vents brûlants quand vous aurez accompli cette tâche."}},
[7730] = {"Infestation de Zukk'ash", {"Rapportez 20 Carapaces de Zukk'ash à Hadoken Rôdeur-agile au Camp Mojache en Féralas."}},
[7731] = {"Fouet-cuisant", {"Battez Fouet-cuisant dans le Gouffre grouillant et rapportez ses glandes à Hadoken Rôdeur-agile au Camp Mojache en Féralas."}},
[7732] = {"Le rapport sur les Zukk'ash", {"Apportez le rapport sur les Zukk'ash du Camp Mojache à Zilzibin Kongadok. Il réside à la Herse d'Orgrimmar."}},
[7733] = {"Qualité améliorée", {"Fournir 10 Peaux de yéti Griffe Féroce à Pratt McGrubben au bastion de Pennelune, en Féralas."}},
[7734] = {"Qualité améliorée", {"Fournir 10 Peaux de yéti Griffe Féroce à Jangdor Rôdeur-agile au Camp Mojache, en Féralas."}},
[7735] = {"Une peau de yéti en parfait état", {"Apportez la Peau de yéti en parfait état à Pratt McGrubben au bastion de Pennelune, en Féralas."}},
