[7761] = {"Les ordres de Main-noire", {"Cet orc est stupide. Il semble que vous deviez trouver un moyen d'obtenir la Marque de Drakkisath pour accéder à l'Orbe de commandement.","D'après la lettre, la Marque est gardée par le général Drakkisath. Vous devriez peut-être enquêter."}},
[7781] = {"Le seigneur de Rochenoire", {"Apportez la Tête de Nefarian au roi Varian Wrynn à Hurlevent."}},
[7782] = {"Le seigneur de Rochenoire", {"Aller dans la vallée des Héros de Hurlevent et parler au grand maréchal Pont-de-Pierre."}},
[7783] = {"Le seigneur de Rochenoire", {"Apportez la Tête de Nefarian à Thrall à Orgrimmar."}},
[7784] = {"Le seigneur de Rochenoire", {"Parlez au Seigneur Runthak à Orgrimmar. Il vous attend dans la vallée de la Force."}},
[7785] = {"Examiner le Calice", {"Examiner le Calice de la renaissance et le rapporter au généralissime Demitrian."}},
[7786] = {"Tonneraan le Cherchevent", {"Pour libérer Tonneraan le Cherchevent de sa prison, vous devez présenter au généralissime Demitrian les moitiés droite et gauche des Liens du Cherchevent, 10 barres d'élémentium, et l'Essence du Seigneur du feu, en Silithus."}},
[7787] = {"Éveille-toi, Lame-tonnerre !", {"Présenter la Lame du baiser du vent dormante au généralissime Demitrian."}},
[7788] = {"Triomphez des envahisseurs !", {"Entrez dans le goulet des Chanteguerres, triomphez de la Horde et gagnez une Marque d'honneur du goulet des Chanteguerres. Puis revenez voir la sentinelle Chanteloin au bosquet d'Aile-argent."}},
[7789] = {"Écrasez les usurpateurs d'Aile-argent", {"Entrez dans le goulet des Chanteguerres, triomphez de l'Alliance et obtenez une Marque d'honneur du goulet des Chanteguerres. Puis revenez voir le capitaine Fracasse-crâne au campement de Mor'shan."}},
