[8283] = {"On recherche : Poigne-de-mort, Terreur des sables", {"Apporter la Pince de Poigne-de-mort à Vish Kozus, capitaine de la garde."}},
[8284] = {"Le mystère du Crépuscule", {"La géologue Fablemort du Fort cénarien en Silithus veut que vous rassembliez 8 Fragments de Tablette du crépuscule."}},
[8285] = {"Le déserteur", {"Apporter la tablette réparée à l'ermite Ortell à Silithus."}},
[8286] = {"Ce que demain apportera", {"Rendez-vous dans les Grottes du temps en Tanaris et trouvez Anachronos, la progéniture de Nozdormu."}},
[8287] = {"Un terrible but", {"Remettez la Transcription décodée de la tablette au commandant Mar'alith."}},
[8288] = {"Il ne peut y en avoir qu'un", {"Ramenez la Tête du seigneur des couvées Lanistaire à Baristolth des Sables changeants, au Fort cénarien en Silithus."}},
