[8319] = {"Les textes du crépuscule cryptés", nil},
[8320] = {"Les géoseigneurs du crépuscule", {"Tuer 10 Géoseigneurs du crépuscule, puis retourner voir Huum Crin-sauvage au Fort cénarien."}},
[8321] = {"Vyral le Vil", {"Remettez la Chevalière de Vyral à Huum Crin-sauvage au Fort cénarien."}},
[8322] = {"Les œufs pourris", {"Placez les Œufs pourris dans le tonneau de bière principal de l'auberge d'Austrivage."}},
