[8325] = {"La reconquête de l'île de Haut-soleil", {"Tuez 8 Wyrms de mana. Ils se trouvent près des Cristaux ardents, non loin de la tour et du belvédère."}},
[8326] = {"Des mesures regrettables", {"Collectez 8 Colliers de lynx sur des Lynx Preste-pattes et des Jeunes Preste-pattes. Lorsque vous aurez terminé, retournez voir la magistrice Erona sur l'île de Haut-soleil."}},
[8327] = {"Se présenter à Lanthan Perilon", {"Parlez à Lanthan Perilon sur l'île de Haut-soleil. Il se tient près du sentier qui part de la Flèche solaire, vers l'ouest."}},
[8328] = {"Le maître des mages", {"Parlez à Julia Heurtesoleil, le maître des mages, à l'intérieur de la Flèche solaire de l'île de Haut-soleil."}},
[8329] = {"La formation de guerrier", nil},
[8330] = {"Les possessions de Solanian", {"Récupérez l'Orbe de divination de Solanian, son Parchemin de magie du Fléau et son Journal. Ils se trouvent sur l'île de Haut-soleil, non loin de l'étang, de la fontaine et de l'un des cristaux ardents. Lorsque vous les aurez tous, restituez-les au Veilleur du puits à la Flèche solaire de l'île de Haut-soleil."}},
[8331] = {"Aurel Feuilledor", {"Parlez à Aurel Feuilledor au Fort cénarien."}},
[8332] = {"Les ducs du Conseil", {"Apportez 1 Grand éclat brillant et 3 Blasons abyssaux à Aurel Feuilledor, au Fort cénarien."}},
[8333] = {"Le médaillon de statut", nil},
[8334] = {"Agression", {"Tuez 7 Sylveniers et 7 Sylveniers farouches, puis retournez voir Lanthan Perilon sur l'île de Haut-soleil."}},
[8335] = {"Felendren le Banni", {"Tuez 8 Âmes en peine arcaniques et 2 Âmes en peine arcaniques contaminées, ainsi que Felendren le Banni. Ils se trouvent à l'académie de Falthrien. Ramenez la Tête de Felendren à Lanthan Perilon sur l'île de Haut-soleil."}},
[8336] = {"Pour une poignée d'esquilles", {"Récupérer 6 Esquilles arcaniques sur n'importe quelle créature qui utilise du mana, puis retourner voir l'arcaniste Ithanas sur l'île de Haut-soleil."}},
[8338] = {"L'esquille arcanique contaminée", {"Remettre l'Esquille arcanique contaminée à l'arcaniste Helion, au belvédère de la Flèche solaire, sur l'île de Haut-soleil."}},
[8339] = {"Royalty of the Council <NYI> <TXT> UNUSED", {"Ramenez les trucs à Aurel Feuilledor au Fort cénarien."}},
