[8354] = {"Caqueter pour un bonbon à la menthe", {"Utilisez l'emote /poulet devant l'aubergiste Norman, et vous recevrez en échange un bonbon à la menthe de Fossoyeuse !"}},
[8355] = {"Boule de gomme en approche", {"Utilisez l'emote /tchou devant Talvash del Kissel, et vous recevrez en échange une boule de gomme de Gnomeregan !"}},
[8356] = {"Montrer ses muscles pour du nougat", {"Utilisez l'emote /frime devant l'aubergiste Allison, et vous recevrez en échange du nougat de Hurlevent !"}},
[8357] = {"Danser pour du massepain", {"Utilisez l'emote /danser devant l'aubergiste Saelienne, et vous recevrez en échange du massepain de Darnassus !"}},
