[8460] = {"Allié des Grumegueules", {"Grifleur veut que vous prouviez votre valeur en tuant 6 Guerriers Mort-bois, 6 Guides Mort-bois et 6 Jardiniers Mort-bois. Lorsque ce sera fait, retournez le voir dans le sud de Gangrebois, près du Sanctuaire d'émeraude."}},
[8461] = {"Les Mort-bois du nord", {"Nafien veut que vous tuiez 6 Protecteurs Mort-bois, 6 Vengeurs Mort-bois et 6 Chamans Mort-bois. Ensuite, retournez le voir dans le nord de Gangrebois, près de l'entrée du repaire des Grumegueules."}},
[8462] = {"Parler à Nafien", {"Suivre la route principale de Gangrebois vers le nord et parler à Nafien le furbolg. Il monte la garde à côté de l'entrée du repaire des Grumegueules."}},
[8463] = {"Les cristaux de mana instables", {"Rapportez 6 Cristaux de mana instables à Aeldon Signesol, sur la Place de l'Épervier."}},
[8464] = {"Les Tombe-hiver s'agitent", {"Salfa voudrait que vous tuiez 8 Chamans Tombe-hiver, 8 Protecteurs Tombe-hiver et 8 Ursas Tombe-hiver. Salfa se trouve juste à l'extérieur de l'entrée du repaire des Grumegueules, au Berceau-de-l'hiver."}},
[8465] = {"Parler à Salfa", {"Traverser le repaire des Grumegueules et sortir au Berceau-de-l'Hiver. Parlez à Salfa, qui monte la garde à l'extérieur du repaire des Grumegueules."}},
[8466] = {"Des plumes pour Grifleur", nil},
[8467] = {"Des plumes pour Nafien", nil},
[8468] = {"On recherche : Thaelis l'Affameur", {"Apportez la Tête de Thaelis au Sergent Kan'ren sur la Place de l'épervier, dans le Bois des chants éternels."}},
[8469] = {"Des perles pour Salfa", nil},
[8470] = {"Le totem rituel mort-bois", {"Amener le Totem rituel mort-bois à l'intérieur du repaire des Grumegueules et voir si l'un des furbolgs qui s'y trouvent est capable d'en faire quelque chose. Les Grumegueules ne vous adresseront pas la parole s'ils ne sont pas Neutres à votre égard, au minimum."}},
[8471] = {"Le totem rituel tombe-hiver", {"Emportez le totem rituel tombe-hiver au Repaire des Grumegueules et voyez si l'un des furbolgs lui trouve une utilisation. Les Grumegueules ne vous parleront pas si vous n'avez pas à leur égard une réputation Neutre au minimum."}},
[8472] = {"Un mauvais fonctionnement", {"Récupérez 6 Noyaux arcaniques sur les Patrouilleurs des arcanes qui circulent dans les ruines de Lune-d'argent et rapportez-les au magistère Jaronis, sur la Place de l'épervier dans le bois des Chants éternels."}},
[8473] = {"Une triste tâche", {"Tuer 10 Gardes verts flétris au Bosquet incendié, puis revenir voir Larianna Ruissevent à l'intérieur de la tour juste au nord-ouest du Bosquet incendié dans le bois des Chants éternels."}},
[8474] = {"Le pendentif du vieux Blanchécorce", {"Trouvez la personne qui a fabriqué le Pendentif du vieux Blanchécorce. Elle pourra peut-être vous parler de cet objet."}},
[8475] = {"La Malebrèche", {"Tuez 8 Pilleurs Ossepeste, puis présentez-vous au forestier Jaela à la Malebrèche dans le bois des Chants éternels."}},
[8476] = {"L'empiètement des Amani", {"Le lieutenant Coursaurore de la retraite des Pérégrins du bois des Chants éternels veut que vous tuiez 5 Berserkers amani et 5 Lanceurs de haches amani."}},
[8477] = {"Le marteau du Taille-épieux", {"Fouillez Tor'Watha au sud-est de la retraite des Pérégrins à la recherche du Taille-épieux Otembe, puis apportez le Marteau d'Otembe à Arathel Solforge, à la retraite des Pérégrins du bois des Chants éternels."}},
[8478] = {"Le choix des armes", nil},
[8479] = {"Zul'Marosh", {"Ven'jashi, le prisonnier troll de Tor'Watha, veut que vous lui remettiez la Tête du chef Zul'Marosh. Le chef Zul'Marosh se trouve à Zeb'Watha, de l'autre côté du lac Elrendar."}},
[8480] = {"Les armes perdues", {"Rapporter 8 Armes sin'dorei à Velendris Blancmatin, dans le bois des Chants éternels."}},
[8481] = {"La racine du mal", {"Plantez la Torche d'invocation de démon à l'entrée de la caverne du grand chef Tombe-hiver, dans le village des furbolgs Tombe-hiver. Triomphez du démon et récupérez l'Essence de Xandivious pour Gorn le Borgne du Repaire des Grumegueules."}},
[8482] = {"Les documents compromettants", {"Remettez les Documents compromettants à Aeldon Signesol sur la Place de l'Épervier, dans le bois des Chants éternels."}},
[8483] = {"L'espion des nains", {"Aeldon Signesol, sur la Place de l'Épervier du Bois des Chants éternels, veut la Tête du prospecteur Garde-enclume."}},
[8484] = {"Négocier la paix", {"Apporter l'Offrande de paix grumegueule au roi Magni Barbe-de-bronze, à Forgefer."}},
[8485] = {"Négocier la paix", {"Apporter l'Offrande de paix grumegueule à Thrall, à Orgrimmar."}},
[8486] = {"L'instabilité des arcanes", {"La telluriste Velania veut que vous tuiez 5 Âmes en peine de mana et 5 Traqueurs de mana."}},
[8487] = {"Le sol contaminé", {"L'apprentie Mirveda au Sanctum oriental dans le bois des Chants éternels veut que vous récupériez 8 Échantillons de terre contaminée."}},
[8488] = {"Des résultats inattendus", {"Protéger l'apprentie Mirveda de l'embuscade du Fléau."}},
[8489] = {"Un convertisseur intact", {"Apportez le Convertisseur arcanique intact à la telluriste Velania."}},
[8490] = {"Renforcer nos défenses", {"Placez le Cristal imprégné contre la Pierre runique de l'est et protégez-le du Fléau pendant 1 minute. Rapportez le Cristal imprégné au garderune Deryan dans le bois des Chants éternels pour recevoir votre récompense."}},
[8491] = {"Récupérer des fourrures", {"Velan Brillechêne à Brise-clémente dans le bois des Chants éternels veut que vous lui apportiez 6 Fourrures de Preste-patte."}},
[8492] = {"L'Alliance a besoin de barres de cuivre !", {"Ramener 20 barres de cuivre au sergent Front-de-pierre dans la Garde militaire de Forgefer."}},
[8493] = {"L'Alliance a besoin de plus de barres de cuivre !", {"Ramener 20 barres de cuivre au sergent Front-de-pierre à l'aérodrome de Dun Morogh."}},
[8494] = {"L'Alliance a besoin de barres de fer !", {"Ramener 20 barres de fer au caporal Carnes dans la Garde militaire de Forgefer."}},
[8495] = {"L'Alliance a besoin de plus de barres de fer !", {"Ramener 20 barres de fer au caporal Carnes à l'aérodrome de Dun Morogh."}},
[8496] = {"Des bandages pour la campagne", {"Apportez 30 Bandages épais en étoffe runique, 30 Bandages épais en soie et 30 Bandages épais en tisse-mage à l'imploratrice céleste Corne-fière. Vous devez aussi apporter le Briefing logistique X pour terminer cette quête."}},
[8497] = {"Survivre dans le désert", {"Ramener 4 Globes d'eau, 4 Anti-venins puissants et 4 Boulettes fumées du désert à Calandrath, à l'auberge du Fort cénarien en Silithus. Vous devez aussi apporter le Briefing logistique I pour terminer cette quête."}},
[8498] = {"Les ordres de bataille du Crépuscule", {"Vous procurer les Ordres de bataille du Crépuscule et les ramener au commandant Mar'alith, au Fort cénarien de Silithus. Pour terminer cette quête, vous devez aussi apporter le Briefing tactique X."}},
[8499] = {"L'Alliance a besoin de barres de thorium !", {"Ramener 20 Barres de thorium à dame Doublenattes dans la Garde militaire de Forgefer."}},
[8500] = {"L'Alliance a besoin de plus de barres de thorium !", {"Apportez 20 Barres de thorium à Dame Doublenattes à l'aérodrome de Dun Morogh."}},
[8501] = {"Objectif : les aiguillonneurs de la Ruche'Ashi", {"Tuez 30 Aiguillonneurs de la Ruche'Ashi, puis présentez-vous au rapport devant le commandant Mar'alith au Fort cénarien en Silithus. Pour terminer cette quête, vous devez aussi apporter le Briefing de combat XII."}},
[8502] = {"Objectif : les ouvrières de la Ruche'Ashi", {"Tuez 30 Ouvrières de la Ruche'Ashi, et revenez faire votre rapport au commandant Mar'alith au Fort cénarien, en Silithus. Vous devez aussi apporter le Briefing de combat III pour terminer cette quête."}},
[8503] = {"L'Alliance a besoin d'étouffante !", {"Ramener 20 Etouffantes au soldat Draunessieu dans la Garde militaire de Forgefer."}},
[8504] = {"L'Alliance a besoin de plus d'étouffante !", {"Ramener 20 étouffantes au soldat Draunessieu à l'aérodrome de Dun Morogh."}},
[8505] = {"L'Alliance a besoin de lotus pourpre !", {"Ramener 20 lotus pourpres à maître Chantenuit, dans la Garde militaire de Forgefer."}},
[8506] = {"L'Alliance a besoin de plus de lotus pourpre !", {"Ramener 20 lotus pourpres à maître Chantenuit, à l'aérodrome de Dun Morogh."}},
[8507] = {"Votre affectation", {"Présentez-vous au poste de la brigade de Forgefer devant la Ruche'Zora. Préparez votre Ordre de mission et obtenez un Ordre de mission signé du capitaine Noirenclume. Ensuite, retournez voir l'Implorateur céleste Kaldon au Fort cénarien, en Silithus.","Note : lancer un sort de soin, ou tout autre sort bénéfique, sur un membre de la brigade de Forgefer vous infligera un marqueur JcJ."}},
[8508] = {"La liste des affectations", nil},
[8509] = {"L'Alliance a besoin de larmes d'Arthas !", {"Ramener 20 larmes d'Arthas au sergent-major Germaine dans la Garde militaire de Forgefer."}},
[8510] = {"L'Alliance a besoin de plus de larmes d'Arthas !", {"Ramener 20 larmes d'Arthas au sergent-major Germaine à l'aérodrome de Dun Morogh."}},
[8511] = {"L'Alliance a besoin de cuir léger !", {"Ramenez 10 Cuirs légers à Bonnie Ecorchepierre dans la Garde militaire de Forgefer."}},
[8512] = {"L'Alliance a besoin de plus de cuir léger !", {"Apportez 10 Cuirs légers à Bonnie Ecorchepierre à l'aérodrome de Dun Morogh."}},
[8513] = {"L'Alliance a besoin de cuir moyen !", {"Ramener 10 Cuirs moyens au soldat Porter dans la Garde militaire de Forgefer."}},
[8514] = {"L'Alliance a besoin de plus de cuir moyen !", {"Ramener 10 cuirs moyens au soldat Porter, à l'aérodrome de Dun Morogh."}},
[8515] = {"L'Alliance a besoin de cuir épais !", {"Ramener 10 Cuirs épais à Marta Finfuseau dans la Garde militaire de Forgefer."}},
[8516] = {"L'Alliance a besoin de plus de cuir épais !", {"Ramener 10 cuirs légers à Marta Finfuseau à l'aérodrome de Dun Morogh."}},
[8517] = {"L'Alliance a besoin de bandages en lin !", {"Ramener 20 bandages en lin à la Sentinelle Argeciel, dans la Garde militaire de Forgefer."}},
[8518] = {"L'Alliance a besoin de plus de bandages en lin !", {"Apportez 20 Bandages en lin à la Sentinelle Argeciel à l'aérodrome de Dun Morogh."}},
[8519] = {"Un pion sur l'Echiquier éternel", {"Apprenez tout ce que vous pouvez au sujet du passé, puis parlez à Anachronos dans les Grottes du temps à Tanaris."}},
[8520] = {"L'Alliance a besoin de bandages en soie !", {"Ramener 20 bandages en soie à l'infirmière Champierreux, dans la Garde militaire de Forgefer."}},
[8521] = {"L'Alliance a besoin de plus de bandages en soie !", {"Apportez 20 Bandages en soie à l'infirmière Champierreux à l'aérodrome de Dun Morogh."}},
[8522] = {"L'Alliance a besoin de bandages en étoffe runique !", {"Ramener 20 Bandages en étoffe runique au Gardien Lunombrage dans la Garde militaire de Forgefer."}},
[8523] = {"L'Alliance a besoin de plus de bandages en étoffe runique !", {"Ramener 20 bandages en étoffe runique au gardien Lunombrage à l'aérodrome de Dun Morogh."}},
[8524] = {"L'Alliance a besoin de thon arc-en-ciel !", {"Ramener 20 Thons arc-en-ciel à Khirry le Gastrognome dans la Garde militaire de Forgefer."}},
[8525] = {"L'Alliance a besoin de plus de thon arc-en-ciel !", {"Ramenez 20 thons arc-en-ciel à Khirry le Gastrognome à l'aéroport de Dun Morogh."}},
[8526] = {"L'Alliance a besoin de rôtis de raptor !", {"Ramener 20 Rôtis de raptor à Sarah Tristesifflet dans la Garde militaire de Forgefer."}},
[8527] = {"L'Alliance a besoin de plus de rôti de raptor !", {"Apportez 20 Rôtis de raptor à Sarah Tristesifflet à l'aérodrome de Dun Morogh."}},
[8528] = {"L'Alliance a besoin de jaune-queue tacheté !", {"Ramener 20 jaunes-queues tachetés à la chasseresse Ondevive dans la Garde militaire de Forgefer."}},
[8529] = {"L'Alliance a besoin de plus de jaune-queue tacheté !", {"Ramener 20 jaunes-queues tachetés à la chasseresse Ondevive à l'aérodrome de Dun Morogh."}},
[8530] = {"L'Alliance a besoin de pierres du Magma roussies !", {"Ramener 50 Pierres du Magma roussies au commandant Fortmartel à l'aérodrome de Dun Morogh."}},
[8532] = {"La Horde a besoin de barres de cuivre !", {"Ramener 20 barres de cuivre au mineur Cromwell dans la Vallée des esprits d'Orgrimmar."}},
[8533] = {"La Horde a besoin de plus de barres de cuivre !", {"Apportez 20 Barres de cuivre au Mineur Cromwell à Durotar."}},
[8534] = {"Rapport de reconnaissance de la Ruche'Zora", {"Trouver l'Eclaireur cénarien Azenel dans la Ruche'Zora et ramener le Rapport de reconnaissance de la Ruche'Zora à l'Imploratrice céleste Corne-fière, au Fort cénarien. Pour terminer cette quête, vous devez aussi apporter le Briefing tactique VI."}},
[8535] = {"Le templier chenu", {"Invoquez et tuez un Templier chenu, puis allez faire votre rapport à Bor Crin-sauvage, au Fort cénarien. Pour terminer cette quête, vous devez aussi apporter le Briefing tactique IV."}},
[8536] = {"Le templier terrestre", {"Invoquez et tuez un Templier terrestre, puis allez faire votre rapport à Bor Crin-sauvage, au Fort cénarien. Pour terminer cette quête, vous devez aussi apporter le Briefing tactique III."}},
[8537] = {"Le templier cramoisi", {"Invoquez et tuez un Templier cramoisi, puis allez faire votre rapport à Bor Crin-sauvage, au Fort cénarien. Pour terminer cette quête, vous devez aussi apporter le Briefing tactique II."}},
[8538] = {"Les quatre ducs", {"Trouver un moyen d'invoquer et de tuer les duc des Cendres, le duc des Profondeurs, le duc des Zéphyrs et le duc des Éclats, puis revenir voir le commandant Mar'alith au Fort cénarien. Pour terminer cette quête, vous devez aussi apporter le Briefing tactique V."}},
[8539] = {"Objectif : les sœurs de Ruche'Zora", {"Tuez 30 Sœurs de la Ruche'Zora et revenez faire votre rapport au commandant Mar'alith au Fort cénarien, en Silithus. Vous devez aussi apporter le Briefing de combat V pour terminer cette quête."}},
[8540] = {"Des bottes pour la Garde", {"Remettre 3 Bottes ornées en mithril à Vish Kozus, capitaine de la garde du Fort cénarien en Silithus. Vous devez aussi apporter le Briefing logistique II pour terminer cette quête."}},
[8541] = {"Des silex à aiguiser pour la Garde", {"Apporter 10 Silex à aiguiser denses, 10 Silex à aiguiser solides et 10 Silex à aiguiser lourds à Vish Kozus, capitaine de la garde du Fort cénarien en Silithus. Vous devez aussi apporter le Briefing logistique III pour terminer cette quête."}},
[8542] = {"La Horde a besoin de barres d'étain !", {"Apportez 20 Barres d'étain au grunt Maug dans la Vallée des esprits d'Orgrimmar."}},
[8543] = {"La Horde a besoin de plus de barres d'étain !", {"Ramener 20 barres d'étain au grunt Maug en Durotar."}},
[8544] = {"Les spallières de conquérant", {"Ramener des Manchettes de commandement qiraji, 2 Idoles de la nuit, 5 Scarabées de pierre et 5 Scarabées d'argile à Andorgos, à Ahn'Qiraj. Pour terminer cette quête, la Progéniture de Nozdormu doit au moins être Neutre à votre égard."}},
