[8733] = {"Eranikus, le tyran du Rêve", {"Rendez-vous sur Teldrassil et trouvez l'agent de Malfurion à l'extérieur des remparts de Darnassus."}},
[8734] = {"Tyrande et Remulos", {"Rendez-vous à Reflet-de-Lune et parlez au gardien Remulos."}},
[8735] = {"La corruption du Cauchemar", {"Rendez-vous aux quatre portails qui ouvrent sur le Rêve d'Emeraude et récupérez un Fragment de la corruption du Cauchemar auprès de chacun d'eux. Lorsque vous aurez terminé, retournez voir le Gardien Remulos à Reflet-de-Lune."}},
[8736] = {"Le Cauchemar se manifeste", {"Défendre Havrenuit d'Eranikus. Le Gardien Remulos ne doit pas mourir. Eranikus non plus. Défendez-vous. Attendez Tyrande."}},
[8737] = {"Le templier d'azur", {"Invoquez et tuez un Templier d'azur, puis allez faire votre rapport à Bor Crin-sauvage, au Fort cénarien. Pour terminer cette quête, vous devez aussi apporter le Briefing tactique I."}},
[8738] = {"Rapport de reconnaissance de la Ruche'Regal", {"Trouver l'Eclaireur cénarien Landion dans la Ruche'Regal et ramener le Rapport de reconnaissance de la Ruche'Regal à l'Imploratrice céleste Corne-fière, au Fort cénarien. Pour terminer cette quête, vous devez aussi apporter le Briefing tactique VII."}},
[8739] = {"Rapport de reconnaissance de la Ruche'Ashi", {"Trouver l'Eclaireur cénarien Jalia dans la Ruche'Ashi et ramener le Rapport de reconnaissance de la Ruche'Ashi à l'Imploratrice céleste Corne-fière, au Fort cénarien. Pour terminer cette quête, vous devez aussi apporter le Briefing tactique VIII."}},
[8740] = {"Les maraudeurs du crépuscule", {"Tuez la Maraudeuse du crépuscule Morna et 2 Maraudeurs du crépuscule. Lorsque ce sera fait, allez faire votre rapport à l'Imploratrice céleste Corne-fière. Pour terminer cette quête, vous devez aussi apporter le Briefing tactique IX."}},
