[9320] = {"Potion de super-mana", nil},
[9321] = {"Potion de super-soins", nil},
[9322] = {"Feux sauvages en Kalimdor", {"Visitez les campements du solstice d'été en Azshara, en Silithus, au cratère d'Un'Goro et à Berceau-de-l'Hiver, puis revenez voir le Gardien des flammes."}},
[9323] = {"Feux sauvages dans les Royaumes de l'est", {"Rendez visite aux campements de la fête du Feu situés dans les Terres foudroyées, les Maleterres de l'est, les Hinterlands, et la gorge des Vents brûlants, puis allez parler au gardien des flammes de la fête."}},
[9324] = {"Voler la flamme d'Orgrimmar", {"Rapportez la Flamme d'Orgrimmar au Maître des traditions."}},
[9325] = {"Voler la flamme des Pitons du Tonnerre", {"Rapportez la flamme des Pitons du Tonnerre au Maître des traditions."}},
[9326] = {"Voler la flamme de Fossoyeuse", {"Rapportez la flamme de Fossoyeuse au Maître des traditions."}},
[9327] = {"Les Réprouvés", {"Allez parler au Grand exécuteur Mavren à Tranquillien, dans les Terres fantômes."}},
[9328] = {"Un héros parmi les Sin'dorei", {"Apportez la Tête de Dar'Khan à Lor'themar Theron, à Lune-d'argent."}},
[9329] = {"Les Réprouvés", {"Allez parler au Grand exécuteur Mavren à Tranquillien, dans les Terres fantômes."}},
[9330] = {"Voler la flamme de Hurlevent", {"Rapportez la flamme de Hurlevent au Conteur de la fête."}},
[9331] = {"Voler la flamme de Forgefer", {"Rapportez la flamme de Forgefer au Conteur de la fête."}},
[9332] = {"Voler la flamme de Darnassus", {"Rapportez la flamme de Darnassus au Conteur de la fête."}},
