[9339] = {"La récompense d'un voleur", nil},
[9340] = {"La Grande fissure", {"Le capitaine des forestiers Venn'ren du Guet de l'épervier veut que vous tuiez 8 Jeunes fauchepierre et 3 Alphas fauchepierre à la Grande fissure, à l'est du Guet de l'épervier."}},
[9341] = {"Tabard de l'Aube d'argent", nil},
[9342] = {"Les perceurs de croûte maraudeurs", nil},
[9343] = {"Tabard de l'Aube d'argent", nil},
[9344] = {"Un départ précipité", nil},
[9345] = {"Préparation du baume", {"Cueillez 12 Atrics des flammes infernales pour un baume et retournez voir Grelag au Poste de Brise-échine."}},
[9346] = {"Quand les sangliers voleront", nil},
[9349] = {"Ramasser les œufs de ravageurs", {"Récupérez 12 Œufs de ravageurs pour le compte de Legassi, de l'épave du zeppelin dans la péninsule des Flammes infernales."}},
[9350] = {"Les péons travaillent sans cesse", {"Récupérez 8 Peaux de sabots-fourchus et 8 tas d'Os de sabots-fourchus sur les Sabots-fourchus déchaînés. Lorsque vous aurez ces matériaux, retournez voir le commandant de la garde Krunk à Thrallmar."}},
[9351] = {"Les marcheurs du Vide sauvages", {"Remettez 10 Essences de marcheur du Vide condensées à Laïus \"Brailleur\" Airbuse à l'épave du zeppelin."}},
[9352] = {"Les intrusions darnassiennes", {"Rendez-vous au Sanctum occidental, au sud-ouest de la Place de l'Épervier, et tuez les intrus s'il y en a. Ensuite, allez faire votre rapport à la telluriste Velania."}},
[9353] = {"Echanger les bon-cadeaux de boîte iCoke", nil},
[9354] = {"À la recherche de nouveaux matériaux", {"Récupérez 8 Peaux de sabots-fourchus et 8 Tendons de sabots-fourchus sur les Sabots-fourchus déchaînés. Lorsque vous les aurez, remettez-les à Humphry, au bastion de l'Honneur."}},
[9355] = {"Un boulot pour un type intelligent", {"Tuer 15 Perceurs de croûte maraudeurs, puis retourner voir le contremaître Biggums au bastion de l'Honneur."}},
[9356] = {"Les doigts dans le nez", {"Apportez 12 Ailes de busard dodues à Legassi à l'épave du zeppelin."}},
[9357] = {"Se présenter à Aeldon Signesol", {"Présentez-vous à Aeldon Signesol à la porte elfique intérieure, dans le bois des Chants éternels."}},
[9358] = {"Le forestier Sareyn", {"Présentez-vous au forestier Sareyn, à l'extérieur du village de Brise-clémente, dans le bois des Chants éternels."}},
[9359] = {"La Retraite des pérégrins", {"Présentez-vous au lieutenant Coursaurore à la Retraite des Pérégrins, dans le bois des Chants éternels."}},
[9360] = {"L'invasion des Amani", {"Remettre les Plans d'invasion amani au lieutenant Coursaurore à la Retraite des pérégrins."}},
[9361] = {"On se régale avec le sanglier infernal !", {"Legassi de l'Épave du zeppelin veut que vous récupériez 8 morceaux de Viande de sanglier infernal purifiée. Pour purifier la viande, utilisez la Mixture de purification sur la Viande de sanglier infernal contaminée que vous prendrez sur des Sangliers infernaux."}},
[9362] = {"Le seigneur de guerre Krellian", {"Récupérez la Coquille prismatique pour l'archimage Xylem. L'archimage réside dans une tour au sommet des falaises d'Azshara."}},
[9363] = {"Avertir le village de Brise-clémente", {"Rendez-vous au village de Brise-clémente dans le bois des Chants éternels et parlez au forestier Degolien."}},
[9364] = {"Magie morcelée", {"Métamorphosez les Fouette-bile d'Azshara, puis tuez les clones qui apparaissent quelques instants plus tard. Quand vous aurez tué 50 clones de métamorphose, retournez voir l'archimage Xylem en Azshara."}},
[9365] = {"La récompense d'un voleur", nil},
[9366] = {"Il nous faut du gangresang", {"Ryathen le Sombre, du Guet de l'épervier dans la péninsule des Flammes infernales, veut que vous lui procuriez 6 Échantillons de grangresang prélevés sur les Fiels de la terreur qui vivent aux bassins d'Aggonar."}},
[9367] = {"La fête du Feu", {"Touchez les feux de joie à l'intérieur de Hurlevent, Forgefer et Darnassus, puis parlez à n'importe quel Maître des traditions de la fête dans une des capitales."}},
[9368] = {"La fête du Feu", {"Touchez les feux de joie à l'intérieur d'Orgrimmar, des Pitons du Tonnerre et de Fossoyeuse, puis parlez à n'importe quel Maître des traditions de la fête dans une des capitales."}},
[9369] = {"Recharger les cristaux de soin", {"Extraire 8 Fioles de sang de phalène sur les Phalènes du val, puis les rapporter à Proenitus, au Point d'impact du val d'Ammen."}},
[9370] = {"Il faut arrêter la purification", {"Placez la Gemme de signalisation près de l'autel d'Aggonar, aux bassins d'Aggonar, et triomphez de tous les anachorètes draeneï qui répondront à vos invocations. Retournez voir Ryathen le Sombre au Guet de l'épervier avec la Gemme de signalisation quand vous en aurez terminé."}},
[9371] = {"La botaniste Taerix", {"Parlez à la botaniste Taerix au Point d'impact dans le val d'Ammen."}},
[9372] = {"Contamination démoniaque", {"Thiah Crin-rouge, du Poste cénarien dans la péninsule des Flammes infernales, vous a demandé de récupérer 6 Échantillons de sang de sanglier infernal sur des Sangliers infernaux massifs."}},
[9373] = {"La missive manquante", {"Remettre l'Etui en cuir rongé à Thiah Crin-rouge, qui campe près des ruines de Sha'naar, dans l'ouest de la péninsule des Flammes infernales."}},
[9374] = {"Le journal d'Arelion", {"La magistrice Carinda, au Guet de l'épervier de la péninsule des Flammes infernales, veut que vous vous rendiez aux bassins d'Aggonar pour y récupérer le Journal d'Arelion."}},
[9375] = {"La route du Guet de l'épervier", {"Protégez le Pèlerin elfe de sang blessé sur la route du Guet de l'épervier, puis parlez à Taleris Mirelaube."}},
[9376] = {"Les souffrances du pèlerin", {"Le Pèlerin elfe de sang blessé a été attaqué par des Ravageurs Plumecroc alors qu'il se trouvait dans le ravin de Plumepoussière. Trouvez son campement et fouillez-le à la recherche de son sac manquant."}},
[9377] = {"Assoiffés de puissance", {"Récupérez une Essence de marcheur du Vide puissante sur l'un des Marcheurs du vide vengeurs, puis retournez voir Taleris Mirelaube au Guet de l'épervier."}},
[9378] = {"DND FLAG Naxxramas, la citadelle de l'effroi", nil},
[9379] = {"Renforcer le chasseur corrompu", {"Récupérez 6 doses de Sang de molosse dimensionnel et remettez-les à Angela \"la Griffe\" Kestrel à Thrallmar."}},
[9380] = {"La multiplication des molosses", {"Récupérer 6 Cadavres de molosses dimensionnels intacts et les remettre au prospecteur Murantus au bastion de l'Honneur."}},
[9381] = {"Les flèches infaillibles", {"Récupérez 8 Plumes de queue de gratte-les-os sur des vautours Gratte-les-os, et remettez-les au fauconnier Drenna Ruissevent du Guet de l'épervier."}},
[9382] = {"Le sort des sabots-fourchus", nil},
[9383] = {"Un plan ambitieux", {"Utilisez le Cristal sanctifié sur un marcheur du Vide incontrôlé blessé, puis retournez voir Elsaana au temple de Telhamat."}},
[9384] = {"<NYI>Harnessing the Light", {"Apportez le Cristal sanctifié luminescent à Rumatu, au temple de Sha'naar, qui invoquera la Lumière pour détruire le démon qui se trouve à l'intérieur."}},
[9385] = {"Les ravageurs déchaînés", {"Tuez 10 Ravageurs Plumecroc et retournez voir Gremni Barbe-longue dans la péninsule des Flammes infernales."}},
[9386] = {"Une Lumière au sein des ombres", nil},
[9387] = {"La source de la corruption", {"Allez aux ruines de Sha'naar, dans la péninsule des Flammes infernales, et obtenez 5 Essences démoniaques sur les Sous-chefs illidari. Retournez voir l'apothicaire Azethen au Guet de l'épervier une fois ce travail terminé."}},
[9388] = {"Flammes vacillantes en Kalimdor", {"Rendez-vous aux feux de joie d'Orneval, des Tarides, de Sombrivage et des Serres-Rocheuses, puis parlez au Gardien des flammes de la fête."}},
[9389] = {"Flammes vacillantes dans les Royaumes de l'est", {"Rendez-vous aux feux de joie des contreforts de Hautebrande, de la forêt des Pins argentés, de la Marche de l'ouest et des Paluns, puis parlez au Gardien des flammes de la fête."}},
[9390] = {"À la recherche de Sedaï", {"L'anachorète Obadeï veut que vous partiez à la recherche de son frère Sedaï dans les alentours, vers l'est, dans la péninsule des Flammes infernales."}},
[9391] = {"Jalonner le chemin", {"Le capitaine des forestiers Venn'ren, du Guet de l'épervier, veut que vous alliez dans la Grande fissure de la péninsule des Flammes infernales et que vous allumiez la Balise du sud, la Balise de l'ouest, et la Balise centrale. Retournez le voir avec la Torche allumée quand vous aurez accompli cette tâche."}},
[9392] = {"La formation de voleur", {"Parler à l’éclaireur Avokor, le maître des voleurs, à l’intérieur de la Flèche solaire de l’île de Haut-soleil."}},
[9393] = {"La formation de chasseur", {"Parlez au forestier Sallina, le maître des chasseurs, à l'intérieur de la Flèche solaire de l'île de Haut-soleil."}},
[9394] = {"Où est Wyllithen ?", {"Retrouver le paysagiste Wyllithen sur les Terres de Ternesoir dans le bois des Chants éternels."}},
[9395] = {"Le havre de Saltheril", {"Parler au seigneur Saltheril au havre de Saltheril dans le bois des Chants éternels."}},
[9396] = {"La magie des arakkoa", {"L'arcaniste Calesthris Aubétoile, du Guet de l'épervier de la péninsule des Flammes infernales, veut que vous lui rameniez 4 Parchemins haal'eshi."}},
[9397] = {"Fermez, fermez la cage aux oiseaux", {"Rendez-vous à la tanière des Haal'esh dans la péninsule des Flammes infernales et fouillez les nids de kaliris à la recherche d'une Jeune femelle kaliri. Utilisez la Cage à oiseaux vide pour la capturer et apportez-la au fauconnier Drenna Ruissevent au Guet de l'épervier."}},
[9398] = {"Prédateurs mortels", {"L'éclaireur Vanura du temple de Telhamat dans la péninsule des Flammes infernales veut que vous tuiez 8 Jeunes fauchepierre et 4 Alphas fauchepierre."}},
[9399] = {"De cruels sous-chefs", {"Tuez 4 Sous-chefs illidari aux ruines de Sha'naar, puis retournez voir Ikan au temple de Telhamat, dans la péninsule des Flammes infernales."}},
[9400] = {"L'assassin", {"Nazgrel veut que vous suiviez la route qui passe au nord de la citadelle des Flammes infernales, à la recherche d'un assassin nommé Krun Brise-échine."}},
[9401] = {"Une arme étrange", {"Apportez la Hache lourde en pierre à Nazgrel, à Thrallmar dans la péninsule des Flammes infernales."}},
[9402] = {"Va chercher !", {"Nagez jusqu'au fond de l'étang des Murmures-sereins et récupérez la Fiole azur pour l'instructeur Antheol."}},
[9403] = {"L'eau pure", {"L'instructeur Antheol veut que vous remplissiez la Fiole azur aux chutes de l'Elrendar."}},
[9404] = {"Fraîchement coupée", {"Tuez les Gardes verts des Chants éternels jusqu'à ce que vous trouviez une Branche vivante pour l'instructeur Antheol."}},
[9405] = {"Le mandat du chef de guerre", {"Apportez la Hache lourde en pierre à la prophétesse Regulkut, à Thrallmar dans la péninsule des Flammes infernales."}},
[9406] = {"Les Mag'har", {"Remettez la lettre des Mag'har à Nazgrel, à Thrallmar dans la péninsule des Flammes infernales."}},
[9407] = {"À travers la Porte des ténèbres", {"Traversez la Porte des ténèbres et allez trouver le général de division Orion. Il attend votre arrivée de l'autre côté, en Outreterre."}},
[9408] = {"Héros oubliés", {"Parler au commandant Danath Trollemort au bastion de l'Honneur."}},
[9409] = {"Livraison urgente", {"Livrer le Paquet de fioles à Zalduun au Point d'impact du val d'Ammen."}},
[9410] = {"Un guide spirituel", {"Utilisez le Totem du loup à l'endroit où vous avez trouvé le corps de Krun Brise-échine et suivez l'Esprit ancestral du loup."}},
[9411] = {"<UNUSED>Infused Bandages", nil},
[9412] = {"<UNUSED>Crystal Flake Throat Lozenge", nil},
[9413] = {"<UNUSED>Crystal Flake Throat Lozenge", nil},
[9414] = {"<UNUSED>Infused Bandages", nil},
[9415] = {"Se présenter au maréchal Murbleu", {"Parlez au maréchal Murbleu au campement de l'Alliance, près de la Ruche'Zora."}},
[9416] = {"Se présenter au général Kirika", {"Parlez au général Kirika au campement de la Horde, près de la Ruche'Regal."}},
[9417] = {"La menace des arakkoa", {"Gremni Barbe-longue veut que vous tuiez 4 Marche-vent haal'eshi et 6 Gardes-serres haal'eshi."}},
[9418] = {"L'orbe d'Avruu", {"Apportez l'Orbe d'Avruu à l'Autel haal'eshi, au sud-est de la tanière des Haal'esh, sur la péninsule des Flammes infernales. Remettez-le à Aeranas lorsqu'il sera libre."}},
[9419] = {"Passer le désert au peigne fin", {"Trouver de la Silithyste et la livrer à l' Extracteur de silithyste, puis parler au maréchal Murbleu au campement de l'Alliance près de la Ruche'Zora."}},
[9420] = {"Les plus belles plumes", {"Mirren Barbe-longue, de la péninsule des Flammes infernales, veut que vous lui rapportiez 8 Plumes de kaliri, prises sur les kaliris de la Tanière des Haal'esh."}},
[9421] = {"La formation de chaman", nil},
[9422] = {"Passer le désert au peigne fin", {"Trouver de la Silithyste et la livrer à un Extracteur de silithyste, puis parler au général Kirika au campement de la Horde près de Ruche'Regal."}},
[9423] = {"Retourner voir Obadeï", {"Retournez voir l'anachorète Obadeï au temple de Telhamat dans la péninsule des Flammes infernales, et apprenez-lui la mort de Sedaï."}},
[9424] = {"La vengeance de Makuru", {"Makuru, au temple de Telhamat dans la péninsule des Flammes infernales, veut que vous lui rameniez 10 Perles ancestrales Mag'har."}},
[9425] = {"Présentez-vous à Moulin-de-Tarren", {"Rendez-vous à Moulin-de-Tarren, dans les contreforts de Hautebrande, et parlez à la conseillère Brunaurore."}},
[9426] = {"Les bassins d'Aggonar", {"Amaan le Sage veut que vous vous rendiez aux bassins d'Aggonar pour y tuer 6 Fiels de la terreur et 6 Pourritures caustiques."}},
[9427] = {"Purifier les eaux", {"Amaan le Sage veut que vous vous rendiez aux bassins d'Aggonar et que vous utilisiez la Fiole de purification sur le cadavre d'Aggonar. Retournez le voir lorsque l'eau aura purifié l'essence d'Aggonar."}},
[9428] = {"Présentez-vous au poste de Bois-brisé", {"Rendez-vous au poste de Bois-brisé en Orneval et parlez au conseiller Ligesol."}},
[9429] = {"En route pour Sombre-comté", {"Rendez-vous à Sombre-comté dans le bois de la Pénombre et parlez à l'anachorète Delan."}},
[9430] = {"Les reliques de Sha'naar", {"Amaan le Sage, au temple de Telhamat dans la péninsule des Flammes infernales, veut que vous vous rendiez aux ruines de Sha'naar pour y récupérer 10 Reliques de Sha'naar."}},
[9431] = {"Une nouvelle approche", {"Récupérez 2 poignées de Terre purificatrice sur les Eboulides fulminants, et remettez-les à Wizlo Braquecoquard au poste de Blanc-relais."}},
[9432] = {"En route pour Astranaar", {"Rendez-vous à Astranaar en Orneval, et parlez au redresseur de torts Palanaar."}},
[9433] = {"Puiser dans le puits de lune", {"Utilisez le Contrôleur du Robotron près du Panneau de contrôle dissimulé, caché près d'un petit bosquet de buissons sur le bord de la pointe la plus proche de Thalanaar, pour prendre le contrôle d'un Robotron 3000. Pilotez le robot jusqu'à l'ouest des Mille pointes où se trouve Thalanaar, l'avant-poste de l'Alliance. Utilisez le robot pour récupérer un échantillon de l'eau du puits de lune, puis ramenez-le à Wizlo Braquecoquard au poste de Blanc-relais."}},
[9434] = {"Tester le tonique", {"Livrez la Caisse de composants à la magistrice Elosai au poste de Librevent."}},
[9435] = {"Les cristaux manquants", {"Reprenez le Chargement de cristaux rares aux Réprouvés et rapportez-le à Huraan à Austrivage."}},
[9436] = {"Étudier les Scalps-rouges", {"Nemeth Œil-de-faucon au campement Grom'gol veut que vous lui rameniez un Totem Scalp-rouge pris sur un Chaman Scalp-rouge."}},
[9437] = {"Le crépuscule du Coureur de l'aube", {"Balandar Brillétoile, à Mur-de-Fougères, veut que vous récupériez la Cargaison du Coureur de l'aube et que vous sauviez son second, Ithania, des troupes de Theramore à la Tour de la halte nord."}},
[9438] = {"Un message pour Thrall", {"Voyagez jusqu'à Orgrimmar et remettez la Lettre de Nazgrel à Thrall."}},
[9439] = {"Des bagages non réclamés", {"Récupérez le Sac du conseiller et la Rapière du conseiller qui se trouvent dans la forteresse d'Angor, et rendez-les au conseiller Sarophas, à Kargath."}},
[9440] = {"De petites bouchées", {"Donnez des Bouchées gangrenées aux animaux capturés par les Perdus. Ramenez les restes à Cersei Chantebrune, à Pierrêche, dans le marais des Chagrins."}},
[9441] = {"Émissaire auprès des Mag'har", {"Thrall veut que vous retourniez voir Gorkan Poing-de-sang au poste Mag'har de la péninsule des Flammes infernales, et que vous lui offriez vos services."}},
[9442] = {"Une maladie débilitante", {"L'imploratrice de la terre Ryga, au poste de Mag'har de la péninsule des Flammes infernales, veut que vous lui rameniez 1 Champignon tête-sèche cueilli dans une caverne de la Grande fissure."}},
[9443] = {"La pseudo-marque du Porteur de Lumière", {"Obtenez la Marque du Porteur de Lumière et ramenez-la à Mehlar Aubelame à la Barricade dans les Maleterres de l'ouest."}},
[9444] = {"Profaner la tombe d'Uther", {"Profaner la Tombe d'Uther puis retourner voir Mehlar Aubelame à la Barricade des Maleterres de l'ouest."}},
[9445] = {"<NYI><TXT>Placeholder: A Worthy Offering", {"Obtenez la Bénédiction d'Uther au sommet de la tour de garde près de l'entrée d'Âtreval et rapportez-la à <PNJ> à la Pointe du Noroît."}},
[9446] = {"La tombe d'Uther", {"Escorter l'Anachorète Truuen à la tombe d'Uther, dans les Maleterres de l'ouest. Ensuite, parler avec la grande prêtresse MacDonnell au camp du Noroît dans les Maleterres de l'ouest."}},
[9447] = {"Administrer le baume de guérison", {"L'Imploratrice de la terre Ryga veut que vous administriez le Baume de guérison à 10 Grunts Mag'har affaiblis proches du poste Mag'har, dans la péninsule des Flammes infernales."}},
[9448] = {"Pitié pour les maudits", {"Tuez 3 Perdus maudits avant de retourner voir l'anachorète Avuun au Havre boueux, dans le marais des Chagrins."}},
[9449] = {"L'appel de la terre", {"Parlez à l'Esprit du val au Bosquet sacré du val d'Ammen."}},
[9450] = {"L'appel de la terre", {"Tuez 4 Esprits de terre sans repos, puis retournez voir l'Esprit du val au Bosquet sacré du val d'Ammen."}},
[9451] = {"L'appel de la terre", {"Donnez le Cristal de terre à Firmanvaar, au Point d'impact du val d'Ammen."}},
[9452] = {"Le lutjan rouge, c'est bon !", {"Utilisez le Filet de pêche draeneï pour attraper 10 Lutjans rouges. Ensuite, retournez voir Diktynna sur l'île de Brume-azur, rendez-lui le filet et donnez-lui les poissons."}},
[9453] = {"Trouvez Actéon !", {"Livrer la Caisse de lutjans rouges à Actéon au Guet d'azur."}},
[9454] = {"La grande chasse aux frôle-lune", {"Apportez 6 Filets de cerfs frôle-lune à Actéon du Guet d'azur."}},
[9455] = {"Une étrange trouvaille", {"Remettez le Cristal luisant faiblement à l'exarque Menelaous, au Guet d'azur."}},
[9456] = {"Nettoyage des traquenuits, île n°2", {"L'exarque Meleanous du Guet d'azur vous a demandé de tuer 8 Traquenuits chétifs infectés."}},
[9457] = {"Un protecteur inhabituel", {"Servez-vous du Don de Naïas près de l'Autel de Naïas pour invoquer l'élémentaire d'eau Naïas. Lorsque vous l'aurez vaincu, retournez voir Nemeth Œil-de-faucon au campement Grom'gol avec le Cœur de Naïas."}},
[9458] = {"<UNUSED>Potion of Heightened Senses", nil},
[9459] = {"<UNUSED>Anti-crit Potion", nil},
[9460] = {"Associer ses forces", {"Keltus Sombrefeuille veut que vous fassiez les poches du Chef des Sentinelles, que vous y récupériez un Mouchoir en dentelle et que vous le lui remettiez."}},
[9461] = {"L'appel du feu", {"Allez voir le prophète Velen, à la Voûte des lumières, à bord de l'Exodar."}},
[9462] = {"L'appel du feu", {"Allez voir Tuluun au Guet d'Azur, sur l'île de Brume-azur."}},
[9463] = {"Usage médical", {"L'anachorète Fateema au Guet d'azur veut que vous lui apportiez 8 Vrilles de piègeracine."}},
[9464] = {"L'appel du feu", {"Parlez à Hargne à la clairière des braises, sur l'île de Brume-d'azur."}},
[9465] = {"L'appel du feu", {"Récupérez la Torche rituelle et rapportez-la à Hargne à la clairière des braises sur l'île de Brume-d'azur."}},
[9466] = {"On recherche : Griffe-noire la Sauvage", {"Apportez les Griffes de Griffe-noire au capitaine des forestiers Venn'ren au Guet de l'épervier dans la péninsule des Flammes infernales."}},
[9467] = {"L'appel du feu", {"Récupérez les Cendres d'Arrogance et rapportez-les avec la Torche rituelle à Hargne, à la clairière des braises sur l'île de Brume-d'azur. N'oubliez pas que vous pouvez utiliser l'Orbe de retour pour vous téléporter auprès de Hargne une fois que vous aurez les cendres."}},
[9468] = {"L'appel du feu", {"Apportez les Cendres d'Arrogance à Tuluun, au Guet d'azur, sur l'île de Brume-azur."}},
[9469] = {"L'appui de Barbe-de-plumes", {"Rendez-vous au Taudis de Barbe-de-plumes et allez voir le nain s'il est chez lui. S'il n'est pas là, cherchez des indices pour savoir où vous pourriez le trouver."}},
[9470] = {"Un geste de bonne volonté", {"L'ambassadeur Rualeth au Nid-de-l'Aigle veut que vous tuiez le féticheur Mai'jin et son araignée familière Tcha'kaz."}},
[9471] = {"Faire sa proie des prédateurs", {"L'ambassadeur Rualeth au Nid-de-l'Aigle veut que vous tuiez 10 Crins-d'argent galeux et 5 Loups Crins-d'argent."}},
[9472] = {"La maîtresse d'Arelion", {"La magistrice Carinda, au Guet de l'épervier, veut que vous attiriez Viera Murmesoleil hors de la ville, puis que vous utilisiez le Parchemin de vindicte de Carinda sur elle."}},
[9473] = {"Une tentative alternative", {"Daedal au Guet d'azur veut que vous lui apportiez 5 Bulbes de gueules-de-loup azurées."}},
[9474] = {"La marque du Porteur de Lumière", {"Récupérez la Marque du Porteur de Lumière et rapportez-la à l'anachorète Truuen au camp du Noroît, dans les Maleterres de l'ouest."}},
[9475] = {"La récupération des œufs", {"Rassemblez 5 Œufs de griffon et rapportez-les à l'ambassadeur Rualeth, au Nid-de-l'Aigle."}},
[9476] = {"À la poursuite de Barbe-de-plumes", {"Rendez-vous à Shadra'Alor et cherchez-y les traces de Barbe-de-plumes."}},
