[9488] = {"Une robe simple", {"L'arcaniste Vandril a besoin de 15 Etoffes de lin et d'une Glande à soie de filetoile."}},
[9489] = {"Purifier la Malebrèche", {"Lancez Mot de pouvoir : Robustesse sur 6 Forestiers des Chants éternels dans la Malebrèche."}},
[9490] = {"La matriarche des écorche-roc", {"Prenez les Griffes de Griffe-noire la Sauvage dans la Grande fissure et remettez-les à Ikan, au temple de Telhamat la Péninsule des flammes infernales."}},
[9491] = {"Cupidité", {"Erelan, à Tranquillien, veut que vous retrouviez la Bague ébréchée en or dans les catacombes des Amani."}},
[9492] = {"L'heure du tournant", {"Apportez le Poing du chef de guerre Kargath au commandant Danath Trollemort, au Bastion de l'Honneur."}},
[9493] = {"L'orgueil de la Gangr'horde", {"Le commandant Romus du Bastion de l'Honneur veut que vous alliez tuer 8 Légionnaires de la Main brisée, 4 Centurions de la Main brisée et 4 Champions de la Main brisée."}},
[9494] = {"Les gangrebraises", {"Le magus Zabraxis du Bastion de l'Honneur veut que vous lui rapportiez une Gangrebraise."}},
[9495] = {"La volonté du Chef de guerre", {"Nazgrel à Thrallmar veut que vous lui rapportiez le Poing du chef de guerre Kargath."}},
[9496] = {"L'orgueil de la Gangr'horde", {"Le chasseur des ombres Ty'jin, de Thrallmar, veut que vous tuiez 8 Légionnaires de la Main brisée, 4 Centurions de la Main brisée et 4 Champions de la Main brisée."}},
[9497] = {"Le symbole de la Gangr'horde", {"Tuez le porteguerre O'mrogg, emparez-vous de la Bannière de la Gangr'horde et remettez-la au suzerain Hun Mutilepoing à Thrallmar."}},
[9498] = {"Le Guet de l'épervier", {"Présentez-vous au capitaine des forestiers Venn'ren au Guet de l'épervier de la péninsule des Flammes infernales."}},
[9499] = {"Le Guet de l'épervier", {"Rendez-vous au Guet de l'épervier au sud-ouest de la péninsule des Flammes infernales et présentez-vous au capitaine des forestiers Venn'ren."}},
[9500] = {"L'appel de l'eau", {"Parlez au long-voyant Nobundo, dans le Hall de Cristal, à l'intérieur de l'Exodar sur l'île de Brume-azur."}},
[9501] = {"L'appel de l'eau", {"Parlez à Aqueuse, à l'intérieur du Récif caché, près de l'île de Brume-sang."}},
[9502] = {"L'appel de l'eau", {"Parlez au long-voyant Nobundo, dans le Hall de Cristal, à l'intérieur de l'Exodar sur l'île de Brume-azur."}},
[9503] = {"L'appel de l'eau", {"Récupérer 6 Essences infectes et les remettre à Aqueuse, au Récif caché, près de l'île de Brume-sang."}},
[9504] = {"L'appel de l'eau", {"Remplir la Gourde en peau vide et retourner voir Aqueuse, au Récif caché, près de l'île de Brume-sang."}},
[9505] = {"La prophétie de Velen", {"Daedal veut que vous alliez au sud, à la recherche des gens mentionnés par la créature blessée."}},
[9506] = {"Il faut bien commencer quelque part", {"L'amiral Odesyus, au Point d'accostage d'Odesyus, veut que vous récupériez le Compas de marine et la Carte nautique volés par la KapitalRisk."}},
[9508] = {"L'appel de l'eau", {"Apportez la Tête de Tel'athion à Aqueuse, du récif caché près de l'île de Brume-sang."}},
[9509] = {"L'appel de l'eau", {"Remettez la Flasque de l'eau la plus pure au long-voyant Nobundo à l'intérieur de l'Exodar sur l'île de Brume-azur."}},
