[9511] = {"Les plans de bataille de Kargath", nil},
[9512] = {"Jumbo gumbo du cuistot", {"« Cuistot » McFadesauce, au Point d'accostage d'Odesyus, veut que vous lui rapportiez 6 Chairs de clampant grouillant."}},
[9513] = {"Reconquérir les ruines", {"La Prêtresse Kyleen Il'dinare, au Point d'accostage d'Odesyus sur l'île de Brume-azur, veut que vous tuiez 5 Myrmidons Irécaille, 5 Nagas Irécaille et 5 Sirènes Irécaille."}},
[9514] = {"Tablette couverte de runes", {"Apportez la Tablette couverte de runes à la Prêtresse Kyleen Il'dinare au Point d'accostage d'Odesyus, sur l'île de Brume-azur."}},
[9515] = {"Le seigneur de guerre Sriss'tiz", {"La Prêtresse Kyleen Il'dinare, au Point d'accostage d'Odesyus sur l'île de Brume-azur, veut que vous tuiez le chef de guerre Sriss'tiz."}},
[9516] = {"Détruire la Légion", {"Tuez 6 Flagellantes de Mannoroc, 6 Gangregardes et 6 Infernaux incendiaires, puis retournez voir le Redresseur de torts Vedaar au Chant des forêts à Orneval."}},
[9517] = {"Un gaspillage honteux", {"Rapportez 15 morceaux de Bois de coupe Chanteguerre et 5 morceaux de Bois gangrené de Satyrnaar à Noueux, au Chant des forêts."}},
[9518] = {"Les agents de la destruction", {"La sentinelle Luciel Murmétoile du Chant des forêts veut que vous tuiez le Surveillant Gorthak, 2 Déchireurs Chanteguerre, 5 Défricheurs de la Horde et 10 Eclaireurs de la Horde."}},
[9519] = {"Le calice perdu", {"Apportez le Calice d'Elune à Kayneth Mortebrise, au Chant des forêts."}},
[9520] = {"Les plans diaboliques", {"Apporter les Plans diaboliques au Redresseur de torts Vedaar au Chant des forêts à Orneval."}},
[9521] = {"Un rapport du front nord", {"Apporter le Rapport de la sentinelle Luciel à la sentinelle Chanteloin au Bosquet d'Aile-argent."}},
[9522] = {"Plus jamais !", {"Tuer Gorgannon et Diathorus le Chercheur, puis retourner voir le redresseur de torts Vedaar au Chant des forêts à Orneval.."}},
[9523] = {"Fragile et précieux - à manipuler avec précaution", {"L'archéologue Adamant Cœur-de-fer, au point d'accostage d'Odesyus sur l'île de Brume-azur, veut que vous lui rameniez 8 Reliques anciennes."}},
[9524] = {"Prisonnière de la citadelle", {"Sauver le capitaine Alina, à l'intérieur de la citadelle des Flammes infernales, avant son exécution.","Cette quête doit être accomplie en mode de difficulté du donjon Héroïque."}},
[9525] = {"Prisonnier de la citadelle", {"Sauver le capitaine Fracasse-os, à l'intérieur de la citadelle des Flammes infernales, avant son exécution.","Cette quête doit être accomplie en mode de difficulté du donjon Héroïque."}},
[9526] = {"Reprendre la colline Gangrefeu", {"Planter 8 Boutures d'arbre dans les Monticules de terre fertile, puis retourner voir Noueux au Chant des forêts, en Orneval."}},
[9527] = {"Tout ce qui reste", {"Cowlen, de l'île de Brume-azur, veut que vous retrouviez les Restes de la famille de Cowlen."}},
[9528] = {"Un appel à l'aide", {"Escorter Magwin à travers l'île, jusqu'à chez elle."}},
[9529] = {"La Pierre", {"Trouvez une Pierre de Vide dans la Malebrèche."}},
[9530] = {"Un plan bourgeonne", {"L'amiral Odesyus, au Point d'accostage d'Odesyus sur l'île de Brume-azur, veut que vous lui rameniez un Arbre creux et 5 Piles de feuilles."}},
[9531] = {"L'art du camouflage ne se perd pas", {"L'amiral Odesyus, au Point d'accostage d'Odesyus de l'île de Brume-azur, veut que vous observiez la rencontre entre Geezle et le traître.","Retournez le voir lorsque vous connaîtrez l'identité du traître."}},
[9532] = {"Trouver Keltus Sombrefeuille", {"Trouver Keltus Sombrefeuille."}},
[9533] = {"La main tendue", {"Rendez-vous au Chant des forêts, et parlez à l'architecte Nemos."}},
[9534] = {"Détruire la Légion", {"Tuez 6 Flagellantes de Mannoroc, 6 Gangregardes et 6 Infernaux incendiaires, puis retournez voir Valusha au poste de Bois-brisé en Orneval."}},
[9535] = {"Les plans diaboliques", {"Apporter les Plans diaboliques à Valusha au poste de Bois-brisé en Orneval."}},
[9536] = {"Plus jamais !", {"Tuer Gorgannon et Diathorus le Chercheur."}},
[9537] = {"Le gnominé est...", {"L'amiral Odesyus, du point d'accostage d'Odesyus, veut que vous récupériez la Communication du traître sur l'ingénieur Finemouture, dit « l'Étincelle »."}},
[9538] = {"Apprendre la langue", {"Lisez le Manuel de langue furbolg calmepin qui vous a été remis par Aurren, puis examinez le Totem d'Akida au Guet d'azur."}},
[9539] = {"Le totem de Coo", {"Grâce aux indices qui vous ont été fournis, trouvez le totem de Coo."}},
[9540] = {"Le totem de Tikti", {"Grâce aux indices qui vous ont été fournis, trouvez le totem de Tikti."}},
[9541] = {"Le totem de Yor", {"Grâce aux indices qui vous ont été fournis, trouvez le totem de Yor."}},
[9542] = {"Le totem de Vark", {"Grâce aux indices qui vous ont été fournis, trouvez le totem de Vark."}},
[9543] = {"Expiation", {"Parler à Amaan le Sage, au temple de Telhamat dans la péninsule des Flammes infernales."}},
[9544] = {"La prophétie d'Akida", {"Libérez 8 Captifs Calmepins des cages du village des Bras-hirsutes. Retournez voir Arugoo des Calmepins au Guet d'azur quand vous aurez accompli cette tâche."}},
[9545] = {"La relique du prophète", {"Amaan le Sage, du temple de Telhamat dans la péninsule des Flammes infernales, veut que vous retourniez auprès du Cadavre de Sedaï, au nord-est du temple de Telhamat, et que vous utilisiez la Relique du prophète à cet endroit. Revenez le voir quand ce sera fait."}},
[9547] = {"L'appel de l'air", {"Parlez au long-voyant Nobundo, dans le Hall de Cristal, à l'intérieur de l'Exodar sur l'île de Brume-azur."}},
[9548] = {"L'équipement chapardé", {"Rapportez l'Equipement de Clopper à Clopper Crackboum, qui se cache dans une carapace de tortue échouée."}},
[9549] = {"Les artéfacts des Vase noire", {"Apportez 3 Idoles murlocs grossières et 6 Couteaux murlocs grossiers à Clopper Crackboum, qui se cache dans une carapace de tortue échouée."}},
[9550] = {"Une carte pour où ?", {"Trouvez le pavillon décrit par la carte, et découvrez ce que celui qui l'a dessinée essayait de représenter."}},
[9551] = {"L'appel de l'air", {"Parlez au long-voyant Nobundo, dans le Hall de Cristal, à l'intérieur de l'Exodar sur l'île de Brume-azur."}},
[9552] = {"L'appel de l'air", {"Parlez à Velaada au pic Vent-sauvage, sur l'île de Brume-azur."}},
[9553] = {"L'appel de l'air", {"Parlez à Susurrus au pic Vent-sauvage, sur l'île de Brume-azur."}},
[9554] = {"L'appel de l'air", {"Retournez voir le long-voyant Nobundo, à l'Exodar, sur l'île de Brume-azur, avec la Volute d'air."}},
[9555] = {"L'appel du feu", {"Parlez au long-voyant Nobundo dans le Hall de Cristal, à bord de l'Exodar."}},
[9556] = {"Au vainqueur...", {"Récupérez une bouteille d'Eau sacrée de Stratholme à l'intérieur de Stratholme, et remettez-la au Mage de Lordaeron, au quartier des mages de Hurlevent ou de Fossoyeuse, lorsque votre camp aura vaincu."}},
[9557] = {"Déchiffrer le livre", {"Apportez l'Ancien journal de bord à l'Anachorète Paetheus au Guet du sang."}},
[9558] = {"Les Barbe-longue", {"Cherchez Gremni Barbe-longue au nord-ouest de la tanière des Haal'esh, loin à l'ouest du bastion de l'Honneur de la péninsule des Flammes infernales."}},
[9559] = {"Le repaire des Calmepins", {"Parlez au Grand chef Calmepin au repaire des Calmepins."}},
[9560] = {"Les bêtes de l'Apocalypse !", {"Moordo, du repaire des Calmepins de l'île de Brume-azur, veut que vous lui rapportiez 8 Peaux de ravageurs."}},
[9561] = {"Les mots de Nolkai", {"Trouvez l'endroit décrit dans le journal de Nolkai et découvrez les biens qu'il a enterrés il y a longtemps."}},
[9562] = {"Pourquoi les murlocs ?", {"Gurf, du repaire des Calmepins sur l'île de Brume-azur, veut que vous retrouviez 5 sacs de Céréales calmepins."}},
[9563] = {"Gagner la confiance de Mirren", {"Mirren Barbe-longue veut que vous lui procuriez 1 Bière amère de Rempart-du-Néant. Pour achever cette quête, les gens du bastion de l'Honneur doivent être Amicaux envers vous."}},
[9564] = {"La dignité de Gurf", {"Rapportez la Dignité de Gurf à Gurf au repaire des Calmepins sur l'île de Brume-azur."}},
[9565] = {"Fouillez le repaire des Calmepins", {"Fouillez le repaire des Calmepins à la recherche d'indices."}},
[9566] = {"Les cristaux de sang", {"Parlez au Grand chef Calmepin au repaire des Calmepins."}},
[9567] = {"Connais ton ennemi", {"Trouvez le Monument de Nazzivus à Nazzivian et récupérez un Glyphe du monument de Nazzivus. Lorsque vous l'aurez, retournez voir le redresseur de torts Aalesia au Guet du sang."}},
[9568] = {"À l'offensive", {"Le redresseur de torts Aalesia du Guet du sang veut que vous tuiez 10 Satyres Nazzivus, 5 Voleurs Nazzivus et 5 Gangreliges Nazzivus."}},
[9569] = {"Contenir la menace", {"Tuez 5 Traqueurs des ombres araxxiens, 5 Invocateurs infernaux axxariens et Zevrax, puis récupérez 5 Cristaux corrompus et retournez auprès du Redresseur de torts Aalesia au Guet du sang."}},
[9570] = {"Le Kurken se cache dans le coin", {"Kurz le Révélateur, du repaire des Calmepins de l'île de Brume-azur, veut que vous tuiez le Kurken et que vous lui rameniez la Peau du Kurken."}},
[9571] = {"La peau du Kurken", {"Donnez la Peau du Kurken à Moordo, au repaire des Calmepins."}},
[9572] = {"Affaiblir les remparts", {"Tuez le gardien des guetteurs Gargolmar, Omor l'Intouché et le drake Nazan. Rapportez la Main de Gargolmar, le Sabot d'Omor et la Tête de Nazan à Caza'rez, à Thrallmar dans la péninsule des Flammes infernales."}},
[9573] = {"Le chef Oomooroo", {"Calmepin le Jeune, du repaire des Calmepins de l'île de Brume-azur, veut que vous tuiez le chef Oomooroo et 9 Indomptables affolés."}},
[9574] = {"Victimes de la corruption", {"Apportez 6 morceaux d'Ecorce cristallisée à Morae, au Guet du sang."}},
[9575] = {"Affaiblir les remparts", {"Tuez le gardien des guetteurs Gargolmar, Omor l'Intouché et le drake Nazan. Rapportez la Main de Gargolmar, le Sabot d'Omor et la Tête de Nazan à Flingot, du bastion de l'Honneur dans la péninsule des Flammes infernales."}},
[9576] = {"Le collier d'Aileron-cruel", {"Apportez le Pendentif de cristal rouge à Morae au Guet du sang."}},
[9577] = {"QUÊTE JOURNALIÈRE DE TEST (JcJ)", {"Parlez à <QUESTGIVER> dans <REGION>."}},
[9578] = {"À la recherche de Galaen", {"Retrouvez Galaen, le mari de Morae."}},
[9579] = {"Le destin de Galaen", {"Remettez l'Amulette de Galaen à Morae au Guet du sang."}},
[9580] = {"Les premiers sec-ours", {"Le pisteur Lyceon, du Guet du sang, veut 8 Flanchets d'anciens ours bruns."}},
[9581] = {"Ce qu'apprennent les cristaux", {"Procurez-vous un Échantillon de cristal du site de l'impact en utilisant la Pioche à cristaux sur le Cristal du site de l'impact et apportez-le au messager Mikolaas au Guet du sang."}},
[9582] = {"La force de l'Un", {"Ouvrez la Cage du ravageur et tuez le Ravageur de la mort qui s'y trouve, puis retournez voir Ruada au Guet d'azur, sur l'île de Brume-azur."}},
[9583] = {"Omar's Test Quest", {"Donner à Omar du tissu"}},
[9584] = {"Le deuxième échantillon", {"Procurez-vous un Échantillon de cristal altéré en utilisant la Pioche à cristaux sur le Cristal de Brume-sang altéré et apportez-le au messager Mikolaas au Guet du sang."}},
[9585] = {"Le dernier échantillon", {"Procurez-vous un Échantillon de cristal axxarien en utilisant la Pioche à cristaux sur le Cristal axxarien et apportez-le au messager Mikolaas au Guet du sang."}},
[9586] = {"Aider Tavara", {"Guvan du Guet d'azur veut que vous trouviez Tavara et que vous guérissiez ses blessures."}},
[9587] = {"De sombres nouvelles", {"Apportez la Lettre inquiétante au commandant Danath Trollemort au bastion de l'Honneur dans la péninsule des Flammes infernales."}},
[9588] = {"De sombres nouvelles", {"Apportez la Lettre inquiétante à Nazgrel, à Thrallmar dans la péninsule des Flammes infernales."}},
[9589] = {"Le sang, c'est la vie", {"Récupérez 10 Fioles de sang de gangr'orc et rapportez-les à Flingot au bastion de l'Honneur dans la péninsule des Flammes infernales."}},
[9590] = {"Le sang, c'est la vie", {"Récupérez 10 Fioles de sang de gangr'orc et rapportez-les au centurion Caza'rez à Thrallmar dans la péninsule des Flammes infernales."}},
[9591] = {"Dompter la bête", {"Utilisez le Totem de domptage pour apprivoiser un Clampant barbelé et revenez voir la chasseresse Kella Arquenuit lorsque vous aurez réussi."}},
[9592] = {"Dompter la bête", {"Utilisez le Totem de domptage pour apprivoiser un Trotteur des bois supérieur et retournez voir la chasseresse Kella Arquenuit."}},
[9593] = {"Dompter la bête", {"Utilisez le Totem de domptage pour apprivoiser un Traquenuit et revenez voir la chasseresse Kella Arquenuit."}},
[9594] = {"Les signes de la Légion", {"Tuez 8 Satyres Nazzivus et 8 Gangreliges Nazzivus, puis apportez la Plaque d'armure de Tzerak au redresseur de torts Aalesia, au Guet du sang."}},
[9595] = {"Contrôle", {"Tuez des Murlocs sur le Rivage envasé pour invoquer une Âme en peine quel'dorei magicienne, puis détruisez-la. Allez ensuite parler à Bati à la Voûte des lumières de l'Exodar."}},
[9598] = {"Rédemption", {"Étudiez le Tome de divinité et parlez à Jol."}},
[9599] = {"<UNUSED>", {"Parlez à Jol, à l'Exodar."}},
[9600] = {"Rédemption", {"Jol veut que vous vous serviez du Symbole de vie pour ressusciter un Jeune chaman furbolg avant de revenir la voir."}},
[9601] = {"En route pour la Barricade", {"Parlez à Mehlar Aubelame à la Barricade."}},
[9602] = {"Délivrez-les du mal...", {"Remettre la Communication du traître à l'exarque Menelaous au Guet d'azur."}},
[9603] = {"Des lits, des bandages, etc.", {"Remettez la Liste de Topher à Laando, le maître des hippogriffes du Guet du sang."}},
[9604] = {"Sur les ailes d'un hippogriffe", {"Acheter à Laando un vol en hippogriffe jusqu'à l'Exodar. À votre arrivée, remettez la Liste de Topher à Nurguni, au Siège du naaru dans l'Exodar."}},
[9605] = {"Le maître des hippogriffes Stephanos", {"Allez trouver Stephanos à l'extérieur de l'Exodar et achetez-lui un vol à dos d'hippogriffe pour rentrer au Guet du sang."}},
[9606] = {"Retourner voir Topher Loaal", {"Achetez à Stephanos un vol en hippogriffe pour rentrer au Guet du sang. Une fois arrivé, livrez les Fournitures de Nurguni à l'Hôte Topher Loaal."}},
[9607] = {"Cœur de rage", {"Faire une enquête exhaustive sur la Fournaise du sang, puis en faire un rapport au commandant de corps Danath Trollemort, au bastion de l'Honneur dans la péninsule des Flammes infernales."}},
[9608] = {"Cœur de rage", {"Faire une enquête exhaustive sur la Fournaise du sang, puis en faire un rapport à Nazgrel, à Thrallmar dans la péninsule des Flammes infernales."}},
[9609] = {"Aidez le gardien Biggs", {"Trouvez le gardien Biggs dans le marais des Chagrins."}},
[9610] = {"Le bassin des larmes", {"Procurez-vous 5 Artéfacts atal'ai et remettez-les à Holaaru, au Havre boueux du marais des Chagrins."}},
[9612] = {"Merci du fond du cœur !", {"Parlez à l'exarque Menelaous au Guet d'azur."}},
[9613] = {"[PÉRIMÉ]La volonté de Keanna", {"La Lame brisée découverte à Karazhan veut que vous demandiez à Koren de la réparer. Pour achever cette quête, il faut aussi que vous soyez Honoré  par l'Œil pourpre"}},
[9616] = {"Bandits !", {"Remettez la Communication des elfes de sang à l'exarque Menelaous au Guet d'azur."}},
[9617] = {"Aller voir les pérégrins", {"Parlez au lieutenant Coursaurore à la retraite des Pérégrins."}},
[9618] = {"Remettre les rapports", {"Transmettez les Rapports de Keltus Sombrefeuille à Zelanis, à Lune-d'argent."}},
[9619] = {"La rune d'invocation", {"Servez-vous de la Pierre de Vide pour invoquer un Marcheur du Vide au village Brume-d'or, vainquez-le puis retournez voir Talionia à Lune-d'argent."}},
[9620] = {"Les explorateurs disparus", {"Le messager Mikolaas veut que vous retrouviez l'équipe d'explorateurs qu'il a envoyé dans les ruines de Loreth'Aran, et que vous les aidiez de votre mieux."}},
[9621] = {"Emissaire auprès de la Horde", {"Remettez la Lettre de Lor'themar Theron à dame Sylvanas Coursevent à Fossoyeuse, la capitale des Réprouvés dans les clairières de Tirisfal."}},
[9622] = {"Avertir votre peuple", {"Parlez à l'exarque Menelaous au Guet d'azur."}},
[9623] = {"Il est temps de grandir", {"L'exarque Menelaous, du Guet d'azur, veut que vous remettiez les Ordres de l'exarque à Torallius le Pâtre, qui se trouve hors de l'Exodar."}},
[9624] = {"La meilleure des gâteries", {"Aonar, à la Croisée de Kessel, veut que vous lui rameniez 10 Poires des sables."}},
[9625] = {"On ne plaisante pas avec les elekk", {"Parlez à Vorkhan le berger des elekk sur l'île de Brume-sang."}},
[9626] = {"Rencontrer le chef de guerre", {"Dame Sylvanas veut que vous remettiez la Lettre scellée par Sylvanas à Thrall, à Orgrimmar, la capitale de Durotar."}},
[9627] = {"L'allégeance à la Horde", {"Retournez à Lune-d'argent et allez faire votre rapport à Lor'themar Theron."}},
[9628] = {"Récupérer des données", {"Récupérez le Cristal de données du relevé sur les nagas Irécailles près des cartographes morts et remettez-le au messager Mikolaas, au Guet du sang."}},
[9629] = {"Attraper... relâcher...", {"Morae, du Guet du sang, veut que vous « marquiez » 6 Eclaireurs Vase noir avec le Marqueur de murlocs."}},
[9630] = {"Le journal de Medivh", {"L’archimage Alturus, du défilé de Deuillevent, veut que vous entriez dans Karazhan et que vous parliez à Wravien."}},
[9631] = {"L'aide d'une collègue", {"Remettez le Fragment d'os carbonisé à Kalynna Rougelatte, dans la Zone 52 de Raz-de-Néant."}},
[9632] = {"De nouveaux alliés", {"Remettez la Lettre d'introduction à la chasseresse Kella Arquenuit, à l'Île de Brume-azur."}},
[9633] = {"En route pour Auberdine", {"Prenez le bateau pour Auberdine. Il part du quai qui se trouve près de la chasseresse Kella Arquenuit. Lorsque vous serez à Auberdine, présentez votre Lettre d'introduction à Thundris Tissevent."}},
[9634] = {"Des prédateurs d'outre-monde", {"Tuez 10 Jeunes Brume-sang, puis retournez voir Vorkhan le berger des elekk à la Croisée de Kessel sur l'île de Brume-sang."}},
[9635] = {"L'extracteur de granules Zappeur-Gazier !", {"Apportez 15 Pièces détachées de la pompe à vapeur à Mack Diver, à Zabra'jin dans le marécage de Zangar."}},
[9636] = {"L'extracteur de granules Zappeur-Gazier !", {"Apportez 15 Pièces détachées de la pompe à vapeur à Manue Fretin, à Telredor dans le marécage de Zangar."}},
[9637] = {"La requête de Kalynna", {"Kalynna Rougelatte veut que vous récupériez le Tome de la pénombre sur le Grand démoniste Néanathème dans la citadelle des Flammes infernales, et le Livre des noms oubliés sur le Tisseur d'ombre Syth dans les salles des Sethekk à Auchindoun.","Cette quête doit être accomplie en mode de difficulté Héroïque."}},
[9638] = {"En de bonnes mains", {"Parler avec Gradav dans la Bibliothèque du gardien, à Karazhan."}},
[9639] = {"Kamsis", {"Parler avec Kamsis dans la Bibliothèque du gardien, à Karazhan."}},
[9640] = {"L'ombre d'Aran", {"Obtenez le Journal de Medivh et retournez voir Kamsis dans la Bibliothèque du Gardien de Karazhan."}},
[9641] = {"Les éclats de cristal irradié", {"Le redresseur de torts Boros du Guet du sang veut que vous lui apportiez 10 Éclats de cristal irradiés."}},
[9642] = {"Encore des éclats de cristal irradié", nil},
[9643] = {"Les lianes étrangleuses", {"Le pisteur Lyceon, du Guet du sang, veut que vous lui rameniez 6 Lianes étrangleuses épineuses."}},
[9644] = {"Plaie-de-nuit", {"Allez sur la Terrasse du Maître à Karazhan et utilisez l'Urne noircie pour invoquer Plaie-de-nuit. Récupérez l'Essence arcanique voilée sur le cadavre de Plaie-de-nuit, et rapportez-la à l'archimage Alturus."}},
[9645] = {"La terrasse du Maître", {"Allez sur la terrasse du Maître à Karazhan et lisez le Journal de Medivh. Retournez auprès de l'archimage Alturus avec le Journal de Medivh lorsque vous aurez accompli cette tâche."}},
[9646] = {"Avis de recherche : Griffe-de-mort", {"Apportez la Patte de Griffe-de-mort au Messager Mikolaas au Guet du sang."}},
[9647] = {"Réduire les bruissements", {"Le pisteur Lyceon au Guet du sang veut que vous tuiez 10 Virevolteurs bleus royaux."}},
[9648] = {"Ménagerie mycologique de Maatparm", {"Maatparm au Guet du sang veut 1 Satyre puant aquatique, 1 Champignon de sang, 1 Polypore des ruines et 1 Collybie gangrenée."}},
[9649] = {"Les larmes d'Ysera", {"Maatparm au Guet du sang veut 2 Larmes d’Ysera."}},
[9663] = {"La tournée de Kessel", {"Kessel, à la croisée de Kessel, veut que vous alliez prévenir de l'invasion imminente des elfes de sang le Grand chef Calmepin au repaire des Calmepins, l'Exarque Menelaous au Guet d'azur et l'Amiral Odesyus au Point d'accostage d'Odesyus. Revenez voir Kessel lorsque vous aurez terminé cette tâche. Vous avez 15 minutes. (Attention à ne pas aller dans l'eau.)"}},
[9664] = {"Fonder de nouveaux avant-postes", {"Capturez la Tour de la garde de la couronne, la Tour du Mur d'est, la Tour du Col du nord et la Tour de Pestebois dans les Maleterres de l'est. Revenez ensuite voir l'émissaire Blanchebarbe à la Chapelle de l'Espoir de Lumière."}},
[9665] = {"Renforcer nos défenses", {"Capturez la Tour de la garde de la couronne, la Tour du Mur d'est, la Tour du Col du nord et la Tour de Pestebois dans les Maleterres de l'est. Revenez ensuite voir l'émissaire Gormok à la Chapelle de l'Espoir de Lumière."}},
[9666] = {"Déclaration de puissance", {"Sur ordre de Kessel, à la Croisée de Kessel sur l'Île de Brume-sang, allez tuer le Seigneur Xiz au Repaire des Irécailles et plantez la Bannière draeneï sur son cadavre."}},
[9667] = {"Il faut sauver la princesse Calmepin", {"Faites sortir la Princesse Calmepin de sa cage, puis allez parler à l’ambassadeur calmepin Olorg au Guet du sang.","Vous devez tuer le Grand chef Bras-hirsute pour obtenir la clé qui ouvre la cage."}},
[9668] = {"Rapport à l'exarque Admetius", {"Parlez à l'Exarque Admetius au Guet du sang."}},
[9669] = {"L'expédition disparue", {"Tuez 8 Tisseuses de brume, 8 Sangsues de brume et leur chef Zarakh. Revenez voir Achelus au Guet du sang quand votre mission sera achevée."}},
[9670] = {"Ils sont vivants ! Peut-être...", {"Le Chercheur Cornelius vous a demandé de libérer 5 Chercheurs de l'expédition."}},
[9671] = {"Une livraison urgente", nil},
[9672] = {"L'héritage du sang maudit", {"L'amiral Odesyus vous a demandé de trouver le capitaine Edward Hanes et de lui parler."}},
[9673] = {"La formation de dresseur", {"Parlez à Halthenis à Lune-d'argent."}},
[9674] = {"Les nagas au sang maudit", {"Le capitaine Edward Hanes, sur l'île Balafre-du-ver, veut que vous tuiez 10 Nagas du sang maudit."}},
[9675] = {"La formation de dresseur", {"Parlez à Ganaar à l'Étage des commerces de l'Exodar."}},
[9676] = {"La formation du paladin", {"Parlez à Jesthenis Heurtesoleil, le maître des paladins, à l'intérieur de la Flèche solaire, sur l'île de Haut-soleil."}},
[9677] = {"Le chevalier-seigneur Vaillessang vous réclame", {"Parlez au chevalier-seigneur Vaillessang, place des Pérégrins, à Lune-d'argent."}},
[9678] = {"La première épreuve", {"Voyagez jusqu'à l'île que le chevalier-seigneur a décrite, trouvez la caverne et allumez le brasero. Lorsque la Première épreuve sera terminée, retournez voir le chevalier-seigneur Vaillessang à Lune-d'argent."}},
[9680] = {"Exhumer le passé", {"L'archimage Alturus veut que vous vous rendiez dans les montagnes au sud de Karazhan dans le défilé de Deuillevent et que vous y récupériez un Fragment d'os carbonisé."}},
[9681] = {"Une étude du pouvoir", {"Parlez au magistère Astalor Ligessang dans la chambre secrète sous les quartiers généraux des chevaliers de sang."}},
[9682] = {"Les sans-espoir...", {"Le capitaine Edward Hanes de l'île Balafre-du-ver veut que vous récupériez 4 Âmes au sang maudit."}},
[9683] = {"Mettre un terme à la malédiction du sang", {"Le capitaine Edward Hanes de l'île Balafre-du-ver veut que vous détruisiez la Statue de la reine Azshara et éliminiez Atoph du Sang maudit."}},
[9684] = {"Revendiquer la Lumière", {"Utilisez le Calice chatoyant sur les Magistères elfes de sang pour le remplir et retournez voir le chevalier-seigneur Vaillessang à Lune-d'argent."}},
[9685] = {"La rédemption des morts", {"Apportez le Calice chatoyant rempli à l'auberge de Lune-d'argent et utilisez ses pouvoirs magiques pour ressusciter Sangrias Mortelame."}},
[9686] = {"La deuxième épreuve", {"Achevez la seconde épreuve en battant le Champion Hargnessang, le Champion Brise-Lumière, le Champion Vivelame et le Champion Heurtesoleil dans le pavillon situé à côté de maître Kelerun Deuillessang."}},
[9687] = {"Rétablir la tranquillité", {"Le prince Toreth de l'île Balafre-du-ver veut que vous rapportiez 8 Os de dragon."}},
[9688] = {"Vers le Rêve", {"Le prince Toreth de l'île Balafre-du-ver vous a demandé de tuer 5 Jeunes virides et 5 Rejetons virides."}},
[9689] = {"Tranchegueule", {"Le prince Toreth de l'île Balafre-du-ver vous a demandé de tuer Tranchegueule."}},
[9690] = {"La deuxième épreuve", {"Présentez-vous au maître Kelerun Deuillessang."}},
[9691] = {"Retour à Lune-d'argent", {"Retournez voir le chevalier-seigneur Vaillessang à Lune-d'argent."}},
[9692] = {"La voie de l'adepte", {"Apportez une Gemme de Kor corrompue, une Caisse de lingots de forge-sang, une bonne mesure de Sang du Courroucé et un Insigne de chevalier de sang au Chevalier-seigneur Vaillessang, à Lune-d'argent."}},
[9693] = {"Ce qu'Argus signifie pour moi", {"L'exarque Admetius du Guet du sang vous a demandé de parler au redresseur de torts Boros."}},
[9694] = {"Le Guet du sang", {"Le redresseur de torts Boros au Guet du sang veut que vous tuiez 10 Espions Epervier du soleil."}},
[9695] = {"Les ordres du roi-soleil", {"Apportez les Ordres du roi-soleil au redresseur de torts Boros au Guet du sang."}},
[9696] = {"Traductions...", {"Apportez la Missive des Eperviers du soleil à l'interrogatrice Elysia au Guet du sang."}},
[9697] = {"La gardienne Leesa'oh", {"Parlez à la gardienne Leesa'oh au Poste de garde cénarien dans le marécage de Zangar."}},
[9698] = {"Une audience avec le Prophète", {"Apportez la Missive des Eperviers du soleil traduite au prophète Velen à l'Exodar."}},
[9699] = {"Vérité ou fiction", {"Apportez les Ordres de Velen au redresseur de torts Boros, au Guet du sang."}},
[9700] = {"Des traits de magie dans l'obscurité", {"Le redresseur de torts Boros du Guet du sang veut que vous trouviez l'emplacement du Portail du soleil et que vous tuiez 5 Anomalies du Vide."}},
[9701] = {"L'observation des sporelins", {"Examiner le Vallon des pontes dans le marécage de Zangar, puis rapporter vos découvertes à la gardienne Leesa'oh au Poste de garde cénarien dans le marécage de Zangar."}},
[9702] = {"Un problème de gloutonnerie", {"Rassembler 10 morceaux d'Aliments abandonnés puis revenir voir la gardienne Leesa'oh au Poste de garde cénarien dans le marécage de Zangar."}},
[9703] = {"Le cryocœur", {"Le redresseur de torts Kuros au Guet du sang veut que vous récupériez 12 boîtes de Fournitures médicales."}},
[9704] = {"Tuée par les Déshérités", {"Allez au sud dans les ruines de Lune-d'argent et cherchez l'Estafette morte dans l'allée du Point-du-jour."}},
[9705] = {"Récupérer un paquet", {"Retournez voir l'estafette Alarion avec le Paquet d'Erona."}},
[9706] = {"Le journal de Galaen - Le destin du redresseur de torts Saruan", {"Apportez le Journal de Galaen au redresseur de torts Kuros au Guet du sang."}},
[9707] = {"Forger l'arme", {"Apportez la Caisse de matériaux à Bemarrin à Lune-d'argent."}},
[9708] = {"Des champignons familiers", {"Collectez 15 Échantillons de champignon et apportez-les à la gardienne Leesa'oh au Poste de garde cénarien dans le marécage de Zangar."}},
[9709] = {"Voler aux voleurs", {"Récupérez 10 Boîtes de champignons et rapportez-les à la gardienne Leesa'oh au Poste de garde cénarien dans le marécage de Zangar."}},
[9710] = {"La corsèque à la trempe de sang", {"Attendez pendant que Bemarrin forge votre arme."}},
[9711] = {"Matis le Cruel", {"Le redresseur de torts Kuros du Guet du sang veut que vous capturiez Matis le Cruel."}},
[9712] = {"Le cheval de guerre thalassien", {"Parlez au chevalier-seigneur Vaillessang pour apprendre comment invoquer votre cheval de guerre thalassien."}},
[9713] = {"Drapeau pour la récolte des chapeluisants", nil},
[9714] = {"Ramenez-moi un autre jardinet !", nil},
[9715] = {"Ramenez-moi un jardinet !", {"Récupérez 5 Hibiscus sanguins et rapportez-les à Gzhun'tt à Sporeggar dans le marécage de Zangar."}},
[9716] = {"Des ennuis au lac Umbretourbe", {"Déterminez la cause de la diminution de l'eau au lac Umbretourbe. Puis retournez voir Ysiel Chantelevent au Refuge cénarien dans le marécage de Zangar."}},
[9717] = {"Oh, alors s'ils veulent la guerre...", {"Cueillez une Palme de sporielle et rapportez-la à T'shu à Sporeggar dans le marécage de Zangar."}},
[9718] = {"À vol d'oiseau", {"Ysiel Chantelevent veut que vous vous serviez de l'Amulette du corbeau des tempêtes pour explorer chacun des principaux lacs du Marécage de Zangar."}},
[9719] = {"Traquer la traqueuse", {"Apportez le Cerveau de la Traqueuse noire à Khn'nix à Sporeggar dans le marécage de Zangar."}},
[9720] = {"L'équilibre doit être préservé", {"Ysiel Chantelevent veut que vous utilisiez les Graines de vignefer sur les Commandes de pompe à vapeur au Lac des serpents, au Lac Umbretourbe, au Lac des furoles et au Lagon. Retournez ensuite lui rendre les graines qu'il vous reste au Refuge cénarien dans le marécage de Zangar."}},
[9721] = {"Le seigneur Solanar vous réclame", {"Parlez au seigneur Solanar Hargnessang à Lune-d'argent."}},
[9722] = {"La voie du maître", {"Allez revoir le seigneur Solanar Hargnessang pour accepter son offre de parrainage."}},
[9723] = {"Un geste d'engagement", {"Apporter 40 Etoffes runiques, 6 Barres d'arcanite, 10 Soleillettes, 5 Runes ténébreuses et 150 pièces d'or au seigneur Solanar Hargnessang, à Lune-d'argent."}},
[9724] = {"Prévenir le Cercle cénarien", {"Parlez à Amythiel Marche-brume au Poste cénarien dans la péninsule des Flammes infernales."}},
[9725] = {"Une preuve de loyauté", {"Le seigneur Solanar Hargnessang, à Lune-d'argent, veut que vous détruisiez 3 Chariots à viande du Fléau et que vous tuiez 15 Ingénieurs de machines de siège du Fléau."}},
[9726] = {"Puisque nous sommes entre amis...", {"Tuez 12 Esclavagistes Écailles-sanglantes et 6 Enchanteresses Écailles-sanglantes, puis retournez voir Gzhun'tt à Sporeggar dans le marécage de Zangar."}},
[9727] = {"Puisque nous sommes toujours entre amis...", {"Tuez 12 Esclavagistes Écailles-sanglantes et 6 Enchanteresses Écailles-sanglantes, puis retournez voir Gzhun'tt à Sporeggar dans le marécage de Zangar."}},
[9728] = {"Un accueil chaleureux", {"Le gardien Hamoot du Refuge cénarien veut que vous lui apportiez 30 Griffes de naga."}},
[9729] = {"Fhwoor écrabouiller !", {"Escortez Fhwoor près de la pompe à vapeur des Furoles pour récupérer l'Arche de Ssslith. Ramenez Fhwoor avec l'arche jusqu'à un endroit sûr, puis allez faire votre rapport à Gzhun'tt, à Sporeggar dans le marécage de Zangar."}},
[9730] = {"Le chef des Sombrecrêtes", {"Tuez Rajah Haghazed au Lagon et retournez voir le gardien Hamoot de l'Expédition cénarienne dans le marécage de Zangar pour chercher votre récompense."}},
[9731] = {"Plan des canalisations", {"Explorez le Lac des Serpents à la recherche d’un drain. Retournez voir Ysiel Chantelevent au Refuge cénarien pour présenter vos découvertes."}},
[9732] = {"Retour au marécage", {"Retournez voir Ysiel Chantelevent au Refuge cénarien dans le marécage de Zangar."}},
[9733] = {"Prévenir le Cercle cénarien", {"Parlez à Mahuram Sabot-robuste à Thrallmar dans la péninsule des Flammes infernales."}},
[9734] = {"Retour au marécage", {"Retournez voir Ysiel Chantelevent au Refuge cénarien dans le marécage de Zangar."}},
[9735] = {"Les vrais maîtres de la Lumière", {"Le seigneur Solanar Hargnessang de Lune-d'argent veut que vous lui apportiez une fiole d'Eau bénite de la Main de Tyr."}},
[9736] = {"Les vrais maîtres de la Lumière", {"Le seigneur Solanar Hargnessang de Lune-d'argent veut que vous lui apportiez 1 Catalyseur arcanique, 1 Poudre crépusculaire, 1 Diamant d'Azeroth et 1 Diamant noir parfait."}},
[9737] = {"Les vrais maîtres de la Lumière", {"Utilisez la Mixture d'extinction près de la flamme éternelle dans la chapelle d'Alonsus pour ôter la protection de la Lumière. Vainquez quiconque tentera de défendre la chapelle."}},
[9738] = {"Disparus au combat", {"Découvrez ce qui est arrivé au lieur de terre Rayge, au naturaliste Morsure, à l'Échardonneur Vertemain et à l'implorateur céleste Griffe. Retournez ensuite voir la guetteuse Jhang au réservoir de Glissecroc dans le marécage de Zangar."}},
[9739] = {"Les malheurs des sporelins", {"Fahssn au vallon des pontes veut que vous collectiez 10 Sacs de spores mûres."}},
[9740] = {"Le portail du soleil", {"Le redresseur de torts Aesom du Guet du sang veut que vous détruisiez le Portail du soleil."}},
[9741] = {"Les bestioles du vide", {"Tuez 12 Bestioles du Vide et faites votre rapport au redresseur de torts Aesom au Guet du sang."}},
[9742] = {"Encore des sacs de spores", nil},
[9743] = {"Ennemis par nature", {"Fahssn, du vallon des pontes du marécage de Zangar, veut que vous lui rameniez 6 Vrilles de seigneurs-tourbe."}},
[9744] = {"Plus de vrilles !", nil},
[9746] = {"Les limites de l'épuisement physique", {"Le redresseur de torts Aesom au Guet du sang veut que vous tuiez 10 Pyromanciens Epervier du soleil et 10 Défenseurs Epervier du soleil."}},
[9747] = {"La tribu Umbretourbe", {"Ikeyen du Refuge cénarien veut que vous vous rendiez au village Umbretourbe pour y tuer Kataru, 8 Prophètes d'Umbretourbe, 6 Féticheurs d'Umbretourbe et 6 Oracles d'Umbretourbe."}},
[9748] = {"Ne buvez pas l'eau", {"Le Redresseur de torts Aesom au Guet du sang veut que vous utilisiez le Flacon d'échantillon d'eau pour obtenir un Échantillon d'eau de Brume-sang."}},
[9749] = {"Ils sont vivants ! Peut-être...", {"Le Chercheur Cornelius a demandé que vous libériez 5 Chercheurs de l'expédition."}},
[9750] = {"UNUSED Urgent Delivery", nil},
[9751] = {"L'héritage du sang maudit", {"L'amiral Odesyus vous a demandé de trouver le capitaine Edward Hanes et de lui parler."}},
[9752] = {"L'évadée d'Umbretourbe", {"Escortez Kayra Long-crin jusqu'au Refuge cénarien dans le marécage de Zangar. Présentez-vous à Ysiel Chantelevent lorsque vous aurez accompli cette tâche."}},
[9753] = {"Ce que nous savons...", {"Parlez à l'exarque Admetius au Guet du sang."}},
[9756] = {"Ce que nous ne savons pas...", {"Parlez à un agent Epervier du soleil capturé au Guet du sang et obtenez des informations secrètes des Eperviers du soleil. Retournez auprès de l'exarque Admetius quand vous aurez terminé cette tâche."}},
[9757] = {"Aller trouver la chasseresse Kella Arquenuit", {"Parlez à la chasseresse Kella Arquenuit près de l'Amarrage de Valaar."}},
[9758] = {"Retourner voir l'arcaniste Vandril", {"Retournez voir l'arcaniste Vandril à Tranquillien dans les Terres fantômes."}},
[9759] = {"La fin de leur monde", {"Suivez et protégez le démolisseur Legoso pendant qu'il essaye de détruire la Bobine vectorielle et tuer Sironas. Retournez voir l'exarque Admetius au Guet du sang si vous réussissez cette mission."}},
[9760] = {"Le Repos du redresseur de torts", {"Parlez au redresseur de torts Corin au Repos du redresseur de torts sur l'île de Brume-sang."}},
[9761] = {"Ouvrir la voie", {"Le redresseur de torts Corin au Repos du redresseur de torts veut que vous tuiez 8 Saboteurs Epervier du soleil et 8 Agents Epervier du soleil."}},
[9762] = {"Une prophétie à écrire", nil},
[9763] = {"Le repaire du seigneur de guerre", {"La guetteuse Jhang veut que vous trouviez et exécutiez le seigneur de guerre Kalithresh à l'intérieur du réservoir de Glissecroc."}},
[9764] = {"Les ordres de dame Vashj", {"Apportez les Ordres de dame Vashj à Ysiel Chantelevent au Refuge cénarien dans le marécage de Zangar."}},
