[10078] = {"Anéantir les indésirables", {"Utilisez la Torche enflammée pour brûler les Lance-lames de la Horde surplombant le sentier de la Gloire. Puis apportez la Torche enflammée à Dumphry au bastion de l'Honneur."}},
[10079] = {"Mineur, tu dors, ton wagon va trop vite", {"Tuez 12 Sapeurs gan'arg puis retournez voir le contremaître Biggums."}},
[10081] = {"Rencontrer Mère Kashur", {"La Grande-mère Geyah veut que vous parliez avec Mère Kashur."}},
[10082] = {"Les ancêtres agités", {"Mère Kashur vous a demandé de donner le repos à 8 Esprits orcs agités."}},
[10084] = {"L'attaque de Mageddon", nil},
[10085] = {"Une visite aux ancêtres", {"Mère Kashur des Terres ancestrales en Nagrand vous a demandé de visiter le Poste de Berceau-de-l'été, les Ruines du Crâne ricanant, Garadar et les Ruines de l'Orbite-sanglante."}},
[10086] = {"Je travaille... Pour la Horde !", {"Apportez 8 morceaux de Métal récupéré et 8 morceaux de Bois récupéré à Megzeg Phalanjette à Thrallmar."}},
[10087] = {"Brûlez-les... Pour la Horde !", {"Utilisez la Torche enflammée pour brûler le Canon est de l'Alliance et le Canon ouest de l'Alliance, puis revenez voir Megzeg Phalanjette à Thrallmar."}},
[10088] = {"Mineur, tu dors, ton wagon va trop vite", nil},
[10089] = {"Les camps de forge de la Légion", nil},
[10090] = {"Les plans de la Légion", nil},
[10091] = {"Les machines à âmes", {"Volez 5 Machines à âmes puis apportez-les à la maître-espionne Mehlisah Hautecouronne sur la terrasse de la Lumière à Shattrath."}},
[10092] = {"L'attaque de Mageddon", nil},
[10093] = {"Le temple de Telhamat", {"Parlez à Amaan le Sage au temple de Telhamat dans l'ouest de la péninsule des Flammes infernales."}},
[10094] = {"Le codex de sang", {"Lisez le Codex de sang dans le Labyrinthe des ombres d'Auchindoun."}},
[10095] = {"Au cœur du labyrinthe", {"Détruisez Marmon, puis allez à la terrasse de la Lumière à Shattrath pour faire un rapport de ce qui s'est passé dans le Labyrinthe des ombres à la maître-espionne Mehlisah Hautecouronne."}},
[10096] = {"Sauver les sporeloks", {"Tuez 10 Drague-marais et 10 Rôdeurs des marais, puis retournez voir Lauranna Thar'well au Refuge cénarien dans le marécage de Zangar."}},
[10097] = {"Frère contre frère", {"Tuez le tisseur d'ombre Syth dans les salles des Sethekk, puis libérez Lakka. Retournez voir Isfar à l'extérieur des salles des Sethekk lorsque vous aurez réussi à la sauver."}},
[10098] = {"L'héritage de Terokk", {"Apportez le Masque de Terokk, la Rémige de Terokk et la Saga de Terokk à Isfar à l'extérieur des salles des Sethekk."}},
[10099] = {"Le cerveau", {"Trouvez et tuez Z'kral dans les mines sous le bastion de l'Honneur, puis faites votre rapport au contremaître Biggums."}},
[10100] = {"Le cerveau", nil},
[10101] = {"Lorsque les esprits parlent", {"Mère Kashur des Terres ancestrales en Nagrand vous a demandé de partir à la recherche de la vision que vous avez eue. Trouvez K'ure à Oshu'gun."}},
[10102] = {"Un secret dévoilé", {"Parlez à A'dal à Shattrath."}},
[10103] = {"Faire son rapport à Zurai", {"Présentez-vous à Zurai au poste du Rat des marais dans le marécage de Zangar."}},
[10104] = {"Des soucis à Tuurem", {"Parlez à Andarl au Bastion allérien dans la forêt de Terokkar."}},
[10105] = {"Des nouvelles pour Rakoria", {"Parlez à Rakoria, au fort des Brise-pierres, dans la forêt de Terokkar."}},
[10106] = {"Les fortifications des Flammes infernales", {"Prendre le Surplomb, le Stade et la Colline brisée, puis retourner voir l'adjudante Tracy Fièrepuits au bastion de l'Honneur dans la péninsule des Flammes infernales."}},
[10107] = {"Mesures diplomatiques", {"Jorin Œil-mort de Garadar en Nagrand vous a demandé de trouver Lantresor de la Lame dans les ruines de la Lame ardente et de lui parler."}},
[10108] = {"Mesures diplomatiques", {"Mo'mor le Briseur, de Telaar en Nagrand, vous a demandé de trouver Lantresor de la Lame dans les ruines de la Lame ardente et de lui parler."}},
[10109] = {"Il me les faut !", {"Koissa, qui se trouve en face du Guet des clans dans le sud de Nagrand, veut que vous lui apportiez 3 Gaz d'élémentaires d'air."}},
[10110] = {"Les fortifications des Flammes infernales", {"Prendre le Surplomb, le Stade et la Colline brisée, puis retourner voir le Crieur-de-guerre Coquard à Thrallmar."}},
[10111] = {"Rapportez-moi cet œuf !", {"Koissa, de Nagrand, veut que vous utilisiez le Bond-o-tron 4000 pour parvenir jusqu'au nid de l'arbre tout proche et voler l'Œuf mystérieux."}},
[10112] = {"Une faveur personnelle", {"Récupérez 5 des Marchandises volées de Lathrai. Rapportez-les au marchand des vents Lathrai près de la Taverne de la Fin du monde dans le quartier de la Ville basse de Shattrath."}},
[10113] = {"Le safari de Nesingwary", {"Jones « Fusil » veut que vous parliez à Hemet Nesingwary au safari de Nesingwary, en périphérie du nord-est de Nagrand."}},
[10114] = {"Le safari de Nesingwary", {"Ohlorn Pérégrin veut que vous parliez à Shado « Fitz » Pérégrin au safari de Nesingwary en Nagrand."}},
[10115] = {"La déviance des Tourbedague", {"Tuez 3 Assassins Tourbedague et 15 Marche-boue Tourbedague."}},
[10116] = {"On recherche : chef Mummaki", {"Apportez le Totem du chef Mummaki à Ikuti au Havre d'Orebor."}},
[10117] = {"On recherche : chef Mummaki", {"Apportez le Totem du chef Mummaki au chasseur des ombres Denjai."}},
[10118] = {"Un message pour les Tourbedague", {"Tuez 3 Assassins Tourbedague et 15 Marche-boue Tourbedague, puis retournez voir le chasseur des ombres Denjai à Zabra'jin."}},
[10119] = {"À travers la Porte des ténèbres", {"Traversez la Porte des ténèbres et allez trouver le commandant Duron. Il attend votre arrivée de l'autre côté - en Outreterre."}},
[10120] = {"Arrivée en Outreterre", {"Le général de division Orion à la Porte des ténèbres dans la péninsule des Flammes infernales veut que vous apportiez le Rapport d'Orion à Vlagga Fileplume."}},
[10121] = {"Éradiquer la Légion ardente", {"Parlez au sergent Fracasse-crâne à la Caravane de ravitaillement, juste à l'extérieur de Thrallmar, à l'est."}},
[10122] = {"À portée de la citadelle", {"Fouillez le camp le plus au nord le long du Rempart du sud à la recherche de traces du soldat Imarion."}},
[10123] = {"Le Ravin de Gangrétincelle", {"Le sergent Fracasse-crâne de la Caravane de ravitaillement à l'est de Thrallmar dans la péninsule des Flammes infernales veut que vous alliez tuer 4 Diablotins Attise-flammes, 6 Porteguerres infernaux et 1 Mande-effroi."}},
[10124] = {"Poste avancé : Trépas du saccageur", {"Présentez-vous au commandant des lignes avancées To'arch au Trépas du saccageur."}},
[10125] = {"Mission : Brouiller les communications", {"Le commandant des lignes avancées To'arch du Trépas du saccageur dans la péninsule des Flammes infernales vous a demandé d'utiliser les Explosifs de To'arch et de détruire l'Antenne de la Légion : Mépris, l'Antenne de la Légion : Haine, et l'Antenne de la Légion : Peur."}},
[10126] = {"Les ordres de Nekrogg le Mène-guerre", nil},
[10127] = {"Mission : Couper le cordon", {"Le commandant des lignes avancées To'arch du Trépas du saccageur dans la péninsule des Flammes infernales vous a demandé de tuer 4 Dresseurs gangrenés, 8 Garde-courroux et le Dominateur Shi'aziv."}},
[10128] = {"Il faut sauver le soldat Imarion", nil},
[10129] = {"Mission : Portes Murketh et Shaadraz", {"Parlez au chef d'escadrille Brack, bombardez la Porte Murketh et la Porte Shaadraz, puis retournez voir le commandant des lignes avancées To'arch au Trépas du saccageur."}},
[10130] = {"Le flanc ouest", {"Tuez 10 Gardes de la Main brisée, 10 Berserkers de la Main brisée et 5 Acolytes de la Main brisée, puis faites votre rapport au lieutenant Amadi dans la tour de garde ouest du bastion de l'Honneur."}},
[10131] = {"Préparation de l'évasion", nil},
[10132] = {"Menace colossale", {"Tuez 5 Colosses déchaînés puis retournez voir Tola'thion au Poste cénarien dans la péninsule des Flammes infernales."}},
[10133] = {"Mission: Tuer le messager", nil},
[10134] = {"Un indice de cristal cramoisi", {"Apportez l'Éclat de cristal cramoisi à Tola'thion au Poste cénarien dans la péninsule des Flammes infernales."}},
[10135] = {"Mission: Être le messager", nil},
[10136] = {"Les Liaisons cruelles", {"Tuez Arazzius le Cruel, puis retournez voir Nazgrel à Thrallmar."}},
[10137] = {"Provoquer le Mène-guerre", nil},
[10138] = {"Sous les ordres de qui?", nil},
[10139] = {"Liquider le commandant", nil},
[10140] = {"En route pour le bastion de l'Honneur", {"Volez jusqu'au bastion de l'Honneur et apportez le Rapport de Duron au maréchal Isildor."}},
[10141] = {"La renaissance de la Légion", {"Parlez au sergent Altumus à la Caravane de ravitaillement de l'est, juste à l'extérieur du bastion de l'Honneur, à l'est."}},
[10142] = {"Le chemin de l'Angoisse", {"Le sergent Altumus de la Caravane de ravitaillement de l'est, qui se trouve à l'est du bastion de l'Honneur dans la péninsule des Flammes infernales, vous a demandé de tuer 4 Diablotins Attise-flammes, 6 Porteguerres infernaux et 1 Mande-effroi."}},
[10143] = {"La halte de l'expédition", {"Présentez-vous au commandant des lignes avancées Kingston à la Halte de l'expédition."}},
[10144] = {"Couper l'ennemi de ses renforts", {"Tuez des ennemis du Portail Grimh et du Portail Kaalez pour collecter des Pierres runiques démoniaques.","Utilisez les Amorces de Kingston à l'intérieur du Portail Grimh et du Portail Kaalez.","Retournez voir le commandant des lignes avancées Kingston avec les explosifs restants."}},
[10145] = {"Mission: Sever the Tie UNUSED", {"Le commandant des lignes avancées Kingston de la Halte de l'expédition dans la péninsule des Flammes infernales vous a demandé de tuer 4 Dresseurs gangrenés, 8 Garde-courroux et le Dominateur Yalqiz."}},
[10146] = {"Mission : Portes Murketh et Shaadraz", {"Parlez au chef d'escadrille Dabir'ee, bombardez la Porte Murketh et la Porte Shaadraz, puis retournez voir le commandant des lignes avancées Kingston à la Halte de l'expédition."}},
