[10159] = {"Nettoyer la colline de Roncecroc", {"Tuez 8 Ravageurs Roncecroc et 8 Crache-venin Roncecroc, puis retournez voir Mahuram Sabot-robuste au Poste cénarien dans la péninsule des Flammes infernales."}},
[10160] = {"Connais ton adversaire", {"Parlez au lieutenant Amadi à la vieille tour de garde juste à l'ouest du bastion de l'Honneur."}},
[10161] = {"En cas d'urgence...", {"Récupérez 30 Débris de zeppelin et rapportez-les à Laïus « Brailleur » Airbuse à l'Épave du zeppelin."}},
[10162] = {"Mission : La saillie abyssale", {"Le commandant des lignes avancées To'arch du Trépas du saccageur dans la péninsule des Flammes infernales vous demande de détruire 20 Péons gan'arg, 5 Surveillants mo'arg et 5 Gangrecanons."}},
[10163] = {"Mission : La saillie abyssale", {"Le chef d'escadrille Griffongar de la Halte de l'expédition dans la péninsule des Flammes infernales vous a demandé de détruire 20 Péons gan'arg, 5 Surveillants mo'arg et 5 Gangrecanons."}},
[10164] = {"Tout va bien se passer", {"Grandpère Aldrimus, à l'extérieur des cryptes Auchenaï d'Auchindoun, vous a demandé d'entrer dans les cryptes Auchenaï et de détruire l'exarque Maladaar."}},
[10165] = {"Casser les prix", {"Le prince-nexus Haramad, qui se trouve à l'extérieur des Tombes-mana, veut que vous alliez tuer le prince-nexus Shaffar et que vous lui rapportiez le Cafetan de Shaffar."}},
[10166] = {"La mémoire de Blanchécorce", {"Larianna Ruissevent veut que vous placiez le Pendentif du vieux Blanchécorce près de la pierre runique au Bosquet incendié."}},
[10167] = {"Auchindoun...", {"Voyagez jusqu'aux cryptes Auchenaï dans le désert des Ossements de la forêt de Terokkar et tuez l'exarque Maladaar pour libérer l'esprit de D'ore."}},
[10168] = {"Par les yeux de l'âme...", {"Trouvez un Miroir des âmes quelque part dans les cryptes Auchenaï et utilisez-le pour invoquer un Esprit assombri issu des Ancêtres d'anciens orcs en Nagrand. Détruisez 15 Esprits assombris afin que les ancêtres puissent reposer en paix.","Retournez voir Mère Kashur dans les Terres ancestrales lorsque vous aurez terminé cette tâche."}},
[10169] = {"Il faut savoir perdre", {"Le magistère Falris de Shattrath veut que vous apportiez la Fiole de sérum sédatif sur la Terrasse de la Lumière et que vous la versiez dans la Marmite du régiment d'Haronem."}},
[10170] = {"Retourner voir la grande-mère", {"Retournez à Garadar et parlez à la grande-mère Geyah."}},
[10171] = {"Le chef inconsolable", {"La grande-mère Geyah à Garadar vous a demandé de parler à Garrosh."}},
[10172] = {"Il n'y a plus d'espoir", {"Parlez à la grande-mère Geyah et rapportez-lui les propos de Garrosh."}},
[10173] = {"Le bâton de l'archimage", {"Reprenez le Bâton de l'archimage Vargoth à Ekkorash l'Inquisitrice et apportez-le à Ravandwyr dans la Zone 52. Pour faire sortir Ekkorash de sa cachette, répandez la Poudre d'abjuration sur le brasero au centre des ruines."}},
[10174] = {"La malédiction de la Tour pourpre", {"Utilisez le Bâton de l'archimage Vargoth pour entrer en contact avec ce dernier.","Si vous perdez le Bâton de l'archimage Vargoth, allez voir Ravandwyr dans la Zone 52 pour obtenir un double."}},
[10175] = {"Thrall, fils de Durotan", {"Rendez-vous à Orgrimmar et apportez le message de Geyah à Thrall."}},
[10176] = {"Ar'kelos le Gardien", {"Entrez dans la Tour pourpre au centre de Kirin'Var et triomphez d'Ar'kelos, puis parlez à l'archimage Vargoth."}},
[10177] = {"Troubles à Auchindoun", {"Présentez-vous au commandant Mahfuun à l'entrée du Labyrinthe des ombres à Auchindoun dans la forêt de Terokkar."}},
[10178] = {"Trouver l'espion To'gun", {"Trouvez l'espion To'gun dans le Labyrinthe des ombres d'Auchindoun."}},
[10179] = {"Le protecteur de Kirin'Var", {"Parlez au Protecteur Valemort à Kirin'Var, au Raz-de-Néant."}},
[10180] = {"Incapable de rester à l'écart", {"Parlez à Isfar, qui se trouve près de l'entrée des salles des Sethekk à Auchindoun."}},
[10182] = {"Le mage de bataille Dathric", {"Trouvez le domicile du mage de bataille Dathric dans l'Allée des sorciers et replacez sa Lame sur le râtelier d'armes. Puis parlez au protecteur Valemort à Kirin’Var."}},
[10183] = {"En route pour la Zone 52", {"Apportez les Notes du néantologue au chef-de-fusée Karlingue dans la Zone 52 au Raz-de-Néant."}},
[10184] = {"Vestiges malveillants", {"Le protecteur Valemort de Kirin'Var veut que vous vainquiez 20 Esprits disjoints de la Place centrale de Kirin'Var."}},
[10185] = {"Un sort pire que la mort", {"Tuez 10 Pourfend-mage et 10 Chercheurs de mana puis retournez voir le protecteur Valemort à Kirin'Var."}},
[10186] = {"On vous engage !", {"Récupérez 10 Cristaux de matrice d'etherlithium et apportez-les au chef-de-fusée Karlingue dans la Zone 52 au Raz-de-Néant."}},
[10187] = {"Un message pour l'archimage", {"Parlez à l'archimage Vargoth en haut de la Tour pourpre, au Raz-de-Néant."}},
[10188] = {"Le cachet de Krasus", {"Reprenez le Cachet de Krasus à la Lieuse de sort Maryana à la Manaforge Coruu, au nord-ouest de la Tour Pourpre."}},
[10189] = {"Manaforge B'naar", {"Le maître-espion Thalodien de la Zone 52 veut que vous vous rendiez dans les environs de la manaforge B'naar et que vous récupériez le Tableau de service du personnel de B'naar sur le capitaine Arathyn.","Accomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10190] = {"Recharger les batteries", {"Utilisez la Pétoire charge-batterie sur assez de chasseurs phasiques pour faire monter le niveau de recharge de la batterie jusqu'à 10. Ensuite, rapportez-la à la robotologue Alley aux ruines d'Enkaat au Raz-de-Néant."}},
[10191] = {"Le Mod. V vit !", {"Escortez le Maxx A. Million Mod. V dans les ruines d'Enkaat jusqu'à son retour en sécurité. Ensuite, allez voir la robotologue Alley à la sortie des ruines d'Enkaat au Raz-de-Néant."}},
[10192] = {"Le compendium de Krasus", {"Récupérez le Compendium de Krasus - Chapitre 1, le Compendium de Krasus - Chapitre 2 et le Compendium de Krasus - Chapitre 3 sur la Place centrale de Kirin'Var."}},
[10193] = {"Cibles majeures", {"Le maître-espion Thalodien de la Zone 52 veut que vous alliez à la manaforge B'naar pour y tuer 2 Maîtres-dimensionnels solfurie, 6 Ingénieurs dimensionnels solfurie et 8 Géologues solfurie.","Accomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10194] = {"Vol furtif", {"Parlez à Veronia à l'extérieur de la Zone 52 et volez jusqu'à la manaforge Coruu.","Accomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10195] = {"Mercenaire voir, mercenaire faire", nil},
[10196] = {"Plus de plumes d'arakkoa", {"Apportez 30 Plumes d'arakkoa à Malukaz au fort des Brise-pierres."}},
[10197] = {"Un déguisement convainquant", {"Procurez-vous une Robe d'arcaniste solfurie, une paire de Gants de chercheur solfurie et un Médaillon de garde solfurie et apportez-les à Caledis Brillaube à la manaforge Coruu au Raz-de-Néant.","Accomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10198] = {"Rassembler des informations", {"Utilisez le déguisement de Solfurie pour vous rendre à la Manaforge Coruu et écouter la conversation entre le Commandant Forgelaube et l'Arcaniste Ardonis. Faites votre rapport à Caledis Brillaube à la Manaforge Coruu lorsque vous aurez accompli cette tâche.","Accomplir des tâches pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10199] = {"Un petit coup de fouet", {"Procurez-vous 5 Aiguillons de raie du Néant pour Bottes dans la Zone 52 du Raz-de-Néant."}},
[10200] = {"Retourner voir Thalodien", {"Retournez voir le maître-espion Thalodien dans la Zone 52 du Raz-de-Néant."}},
[10201] = {"Et maintenant, l'instant de vérité", {"Trouvez Grek et demandez-lui d'essayer l'huile sur son arme. Une fois qu'il l'aura testée, retournez décrire le résultat à Rakoria."}},
[10202] = {"Un déserteur", {"Allez à la manaforge Ara et parlez au magistère Theledorn.","Accomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10203] = {"Zapper des atouts inestimables", {"Utilisez le Zappeur d'ultra-déconsolidation pour renvoyer le Creus-O-Matic ultra-rotatif, la Griffe de drague servopneumatique, l'Analyseur de terrain multispectral et le Gros chariot rempli d'explosifs dans la Zone 52 au Raz-de-Néant. Présentez-vous ensuite au Sapeur en chef Failonfeu à l'extrémité est de la fissure qui parcourt la Zone 52 au Raz-de-Néant."}},
[10204] = {"Cristaux de gemmes de sang", {"La magistrice Larynna veut que vous alliez à la manaforge B'naar pour vous y procurer un Éclat de gemme de sang sur un Magistère solfurie. Utilisez-le près des grands Cristaux de gemme de sang et retournez la voir dans la Zone 52.","Accomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10205] = {"L'écumeur-dimensionnel Nesaad", {"Tuez l'Écumeur-dimensionnel Nesaad, puis retournez voir le traqueur-du-Néant Khay'ji dans la Zone 52 de Raz-de-Néant."}},
[10206] = {"Prendre sa part", {"Récupérez 10 modèles de Technologie éthérienne et rapportez-les à Papa Roulette, dans la Zone 52 au Raz-de-Néant."}},
[10207] = {"Poste avancé: Trépas du saccageur - REUSE", nil},
[10208] = {"Perturber leurs renforts", {"Terrassez les ennemis qui se trouvent autour des portails Xilus et Kruul pour rassembler des Pierres runiques démoniaques.","Utilisez une Amorce de To'arch à l'intérieur des portails Xilus et Kruul.","Rapportez les Amorces restantes à To'arch au Trépas du saccageur dans la péninsule des Flammes infernales."}},
[10209] = {"Le trophée de l'invocateur Kanthin", {"Triomphez de l'invocateur Kanthin à la manaforge Duro, sur l'île au nord du bastion des Solfurie, et reprenez-lui la Pierre de Glacius."}},
[10210] = {"A'dal", {"Parlez à A'dal, le chef des Sha'tar, au centre de la Terrasse de la Lumière."}},
[10211] = {"Ville de Lumière", {"Suivez le serviteur de Khadgar et écoutez son histoire. Retournez ensuite voir Khadgar."}},
[10212] = {"Héros des Mag'har", {"Retournez voir la grande-mère Geyah à Garadar en Nagrand."}},
[10213] = {"Enquêter au sujet de l'accident", {"Cherchez des survivants de l'accident."}},
[10214] = {"Mineur, tu dors, ton wagon va trop vite", {"Tuez 15 Sapeurs gan'arg et retournez voir le contremaître Razelcraz."}},
[10216] = {"La sécurité avant tout", {"L'artificier Morphalius veut que vous alliez tuer 10 Écumeurs de crypte éthériens, 5 Ensorceleurs éthériens, 5 Traqueurs de nexus et 5 Lieurs de sort éthériens.","Lorsque ce sera fait, trouvez le Transporteur éthérien dans la salle avant celle de Shaffar et activez-le."}},
[10218] = {"Le dur labeur des autres", {"Escortez le cryo-ingénieur Sha'heen dans les tombes-mana pour qu'il puisse recueillir l'éther contenu dans les collecteurs d'éther de Shaffar.","Allez voir le prince-nexus Haramad au campement du Consortium à l'extérieur des tombes-mana une fois votre mission accomplie."}},
[10219] = {"Sortir le chien", nil},
[10220] = {"Les forcer à écouter", {"Mordiba veut que vous vainquiez 12 Fantassins inflexibles, 8 Chevaliers inflexibles et 6 Ensorceleurs inflexibles à l'Armurerie de l'expédition à l'ouest du Poste de Brise-échine."}},
[10221] = {"Dr Boum !", {"Tuez Dr Boum puis rapportez la bonne nouvelle au sapeur en chef Failonfeu dans la Zone 52 au Raz-de-Néant."}},
[10222] = {"La garnison Solfurie", {"Le Protecteur Valemort à Kirin'Var veut que vous tuiez 10 Archers solfurie."}},
[10223] = {"Mort à Daellis", {"Le protecteur Valemort de Kirin'Var veut que vous alliez tuer le maître Daellis Heurtelaube."}},
[10224] = {"De l'essence pour les moteurs", {"Collectez 7 Essences d'âmes en peine de mana et rapportez-les à l'ingénieur en chef Trep à la Zone 52 au Raz-de-Néant."}},
[10225] = {"Rapport à l'ingénierie", {"Apportez le Cristal de matrice d'etherlithium à l'Ingénieur en chef Trep dans la Zone 52 au Raz-du-Néant."}},
[10226] = {"Extraction de puissance élémentaire", {"Extrayez 5 Puissances élémentaires et apportez-les à l'Ingénieur en chef Trep, dans la Zone 52 au Raz-de-Néant."}},
[10227] = {"Je vois des draeneï morts", {"Parlez à Ramdor le Fol, qui se trouve sur le tas d'os à l'extérieur des cryptes Auchenaï."}},
[10228] = {"Ezekiel", {"Trouvez Ezekiel dans la ville de Shattrath, et parlez-lui."}},
[10229] = {"Déchiffrer le tome", {"Apportez le Tome mystérieux à Althen l'Historien au Poste de Brise-échine."}},
[10230] = {"Le cor de bataille", {"Prenez le Cor de bataille des Inflexibles au lieutenant-commandant Thalvos et apportez-le à Althen l'Historien au Poste de Brise-échine."}},
[10231] = {"Quel livre ? Je ne vois pas de livre.", {"Ezekiel dit que le livre a été volé. Votre meilleure piste serait de trouver les ruffians et de voir s'ils ont des informations à ce sujet."}},
[10232] = {"Ferrailler contre la Légion", {"Tuez 5 Forgerons funestes mo'arg et 15 Ingénieurs gan'arg. Puis retournez voir Papa Roulette dans la Zone 52 du Raz-de-Néant."}},
[10233] = {"Incendier le bastion des Solfurie", {"À l'aide d'une Torche enflammée, détruisez 4 Balistes Solfurie et 4 Tentes Solfurie, puis retournez voir le lieutenant-ensorceleur Morran à Kirin'Var."}},
[10234] = {"Le malheur d'un démon...", {"Rassemblez 10 Pièces de saccageur gangrené et apportez-les à Mama Roulette au comptoir des Terres-médianes à l'intérieur de l'écodôme Terres-médianes au Raz-de-Néant."}},
[10235] = {"Dégriffer Griffe-funeste", {"Procurez-vous la Main de Griffe-funeste et apportez-la à Mama Roulette au comptoir des Terres-médianes à l'intérieur de l'écodôme Terres-médianes au Raz-de-Néant."}},
[10236] = {"L'Outreterre, c'est nul !", {"Trouvez 6 Pièces de déchiqueteur et retournez voir le contremaître Razelcraz près de la mine au nord-ouest de Thrallmar."}},
[10237] = {"Avertir la Zone 52 !", {"Annoncez la mauvaise nouvelle au chef-de-fusée Karlingue dans la Zone 52 au Raz-de-Néant."}},
[10238] = {"Comment servir le gobelin", {"Sauvez Manni, Moh et Jakk des camps des gangr'orcs et retournez voir le contremaître Razelcraz près de la mine au nord-ouest de Thrallmar."}},
[10239] = {"Une source d'énergie potentielle", {"Le lieutenant-ensorceleur Morran de Kirin'Var veut que vous vous empariez de 10 Cubes d'isolation à énergie dans les exploitations des elfes de sang de la manaforge Coruu."}},
[10240] = {"Établir un périmètre", {"Utilisez le Mécanisme d'activation des runes près de la Rune est, la Rune nord-est et la Rune ouest. Ces runes se trouvent quelque part autour des quartiers de l'Allée des sorciers et de la Place centrale de Kirin'Var. Retournez voir le lieutenant-ensorceleur Morran une fois que vous aurez accompli cette tâche."}},
[10241] = {"Diversion à la manaforge B'naar", {"Tuez 8 Magistères Solfurie et 8 Garde-sang Solfurie puis retournez voir l'exarque Orelis dans la Zone 52.","Accomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10242] = {"Le poste de Brise-échine", {"L'Apothicaire Zelana du Trépas du saccageur vous a demandé de parler au Chef d'escadrille Brack et de prendre une wyverne pour rallier le Poste de Brise-échine, puis de remettre l'Échantillon de sang de l'Orbite-sanglante à l'apothicaire Albreck au Poste de Brise-échine."}},
[10243] = {"Technologie naaru", {"Entrez dans la manaforge B'naar et trouvez la Console de contrôle B'naar.","Accomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10244] = {"R.T.F.M.C.S.", {"Apportez le Manuel de construction du saccageur gangrené à Mama Roulette au comptoir des Terres-médianes à l'intérieur de l'écodôme Terres-médianes au Raz-de-Néant."}},
[10245] = {"La transcription de la console de B'naar", {"Apportez la Transcription de la console de B'naar à l'anachorète Karja dans la Zone 52 au Raz-de-Néant.","Accomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10246] = {"Attaquer la manaforge Coruu", {"Partez pour la manaforge Coruu et tuez 8 Arcanistes solfurie et 5 Chercheurs solfurie. Retournez voir l'exarque Orelis lorsque vous aurez accompli cette tâche.","Accomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10247] = {"Le docteur Vomisa, Ph.T.", {"Allez parler au docteur Vomisa, Ph.T. au Terrain d'essai du Raz-de-Néant."}},
[10248] = {"Toi, le robot", {"Activez les Commandes du saccageur ferrailleur X6000 et testez-en les fonctionnalités. Puis faites part de vos impressions au docteur Vomisa, Ph.T. sur le Terrain d'essai de Raz-de-Néant. Réglez tout problème qui se ferait jour."}},
[10249] = {"On retourne voir le chef !", {"Retournez voir immédiatement le Chef-de-fusée Karlingue, dans la Zone 52 de Raz-de-Néant !"}},
[10250] = {"Vengeance sanglante", {"Sonnez le Cor de bataille des Inflexibles près de la bannière de l'Alliance. Tuez Urtrak puis retournez voir Althen l'Historien au Poste de Brise-échine."}},
[10251] = {"Le Grand Dessein Du Maître ?", {"Trouvez Nitrin l'Instruit à l'Armurerie abandonnée en Nagrand."}},
[10252] = {"Vision des morts", {"Nitrin, à l'Armurerie abandonnée en Nagrand, veut que vous lui rameniez 1 Globe oculaire de Gronn des montagnes, 1 Bec de Venteroc supérieur sans défaut et 1 Poche de graisse de vieux sabot-fourchu."}},
[10253] = {"Levixus le Mandeur d'âme", {"Tuez Levixus et prenez-lui le Livre des morts.","Apportez le Livre des morts à Ramdor le Fol à l'extérieur des Cryptes Auchenaï, à l'entrée d'Auchindoun.","Vous aviez passé un marché, vous vous souvenez ?"}},
[10254] = {"Le commandant Danath", {"Le maréchal Isildor veut que vous vous présentiez au commandant de corps Danath Trollemort au bastion de l'Honneur."}},
[10255] = {"Tester l'antidote", {"Administrez l'Antidote cénarien à un Sanglier infernal massif et observez le résultat. Une fois l'expérience terminée, retournez voir Thiah Crin-rouge au Poste cénarien dans la péninsule des Flammes infernales."}},
[10256] = {"Trouver le Maître des clés", {"Trouvez Apex et procurez-vous un Focalisateur en cristal d'Apex. Retournez à la Tour pourpre et utilisez le Focalisateur à proximité de l'Orbe de l'archimage Vargoth pour découvrir qui détient la clé qui permettra de lever la malédiction pesant sur la tour."}},
[10257] = {"S'emparer de la Pierre cruciale", {"Entrez dans la Botanica du donjon de la Tempête et prenez la Pierre cruciale au commandant Sarannis. Apportez-la à l'archimage Vargoth à la Tour pourpre."}},
[10258] = {"Honorer les morts", {"Présentez-vous au commandant Hogarth à l'Armurerie de l'expédition."}},
[10259] = {"Dans la brèche", {"Tuez 6 Gangregardes de l'invasion et retournez voir l'émissaire de l'Aube d'argent Fièrepuits."}},
[10260] = {"Pokernikus le néantologue", {"Parlez au néantologue Pokernikus sur le pont qui conduit au Raz-de-Néant depuis les Tranchantes."}},
[10261] = {"On recherche : servomoteur d'annihilateur !", {"Procurez-vous un Servomoteur d'annihilateur et apportez-le à Papa Roulette dans la Zone 52 au Raz-de-Néant."}},
[10262] = {"Un monceau d'éthériens", {"Collectez 10 Insignes de Zaxxis et apportez-les au Traqueur-du-Néant Khay'ji dans la Zone 52 de Raz-de-Néant."}},
[10263] = {"Aider le Consortium", {"Parlez au traqueur-du-Néant Khay'ji dans la Zone 52 au Raz-de-Néant."}},
[10264] = {"Aider le Consortium", {"Parlez au Traqueur-du-Néant Khay'ji dans la Zone 52 au Raz-de-Néant."}},
[10265] = {"La collection de cristaux du Consortium", {"Procurez-vous un artéfact cristallin d'Arklon et rapportez-le au traqueur-du-Néant Khay'ji à la Zone 52 au Raz-de-Néant."}},
[10266] = {"Demande d'assistance", {"Trouvez Gahruj et proposez-lui vos services. Il se trouve au comptoir des Terres-médianes dans l'Écodôme Terres-médianes au Raz-de-Néant."}},
[10267] = {"Récupérer ce qui nous revient de droit", {"Récupérez 10 Boîtes d'équipement topographique et rapportez-les à Gahruj au comptoir des Terres-médianes dans l'Écodôme Terres-médianes au Raz-de-Néant."}},
[10268] = {"Une audience avec le prince", {"Remettez l'Équipement topographique à l'image du prince-nexus Haramad à la Foudreflèche au Raz-de-Néant."}},
[10269] = {"Point de triangulation numéro un", {"Utilisez l'Appareil de triangulation pour vous indiquer la direction du premier point de triangulation. Une fois que vous l'aurez trouvé, donnez-en l'emplacement au camelot Hazzin au poste de garde du Protectorat dans l'île de la manaforge Ultris au Raz-de-Néant."}},
[10270] = {"Une proposition pas si modeste", {"Récupérez le Bloc d'alimentation du téléporteur et apportez-le à la Plate-forme de téléportation éthérienne sur la Crête céleste à l'est de la manaforge Ultris. Activez la plate-forme et donnez le Bloc d'alimentation du téléporteur à l'Image du marchand des vents Marid pour qu'il puisse l'installer."}},
[10271] = {"Passer aux choses sérieuses", {"Récoltez 8 Essences de dragon du Néant puis retournez à la Plate-forme de téléportation éthérienne. Ces essences se ramassent sur les drakes du Néant ou les dragons du Néant."}},
[10272] = {"Un départ prometteur", {"Récoltez 15 Oeufs de draconiens du Néant, puis retournez auprès de Tyri et Jorad sur la Crête céleste."}},
[10273] = {"Malencontreuses distractions", {"Parlez avec le marchand des vents Marid et attirez-le à l'écart de son commerce. Débarrassez-vous de lui puis retournez auprès de Tyri et Jorad sur la Crête céleste."}},
[10274] = {"S'emparer de la Crête céleste", {"Utilisez le Défi du Vol bleu pour provoquer Veraku en duel sur le bord sud de la Crête céleste. Triomphez de lui et retournez voir Tyri et Jorad sur la Crête céleste."}},
[10275] = {"Point de triangulation numéro deux", {"Utilisez l'Appareil de triangulation pour vous indiquer la direction du deuxième point de triangulation. Une fois que vous l'aurez trouvé, donnez-en l'emplacement au marchand des vents Tuluman au Point d'ancrage de Tuluman, juste de l'autre côté du pont vers l'île de la manaforge Ara au Raz-de-Néant."}},
[10276] = {"Le triangle est triangulé", {"Récupérez le Cristal d'Ata'mal et rapportez-le à l'image du prince-nexus Haramad à la Foudreflèche au Raz-de-Néant."}},
[10277] = {"Les Grottes du temps", {"Andormu vous demande de suivre la Protectrice du temps dans les Grottes du temps."}},
[10278] = {"Les failles dimensionnelles", {"Utilisez le Générateur de faille dimensionnelle instable dans les Champs de distorsion. Récupérez 3 Néants dimensionnels sur des Marcheurs du Vide instables, puis retournez voir Ogath le Fol au poste de Brise-échine."}},
[10279] = {"Dans le repaire du maître", {"Parlez à Andormu dans les Grottes du temps."}},
[10280] = {"Livraison spéciale à Shattrath", {"Apportez le cristal d'Ata'mal à A'dal sur la Terrasse de la Lumière à Shattrath."}},
[10281] = {"Présentation formelle", {"Retrouvez Tyri à la pointe nord de la Crête céleste et allez lui parler."}},
[10282] = {"Hautebrande d'antan", {"Andormu des Grottes du temps vous demande de vous aventurer dans le Hautebrande d'antan et d'aller voir Erozion."}},
[10283] = {"La diversion de Taretha", {"Rendez-vous au bastion de Fort-de-Durn et placez 5 charges incendiaires dans les tonneaux situés dans chacun des pavillons d'internement grâce au Paquet de bombes incendiaires qui vous a été donné par Erozion.","Parlez à Thrall dans les oubliettes du bastion de Fort-de-Durn une fois les Pavillons d'internement incendiés."}},
[10284] = {"Évasion de Fort-de-Durn", {"Faites signe à Thrall lorsque vous serez prêt à continuer. Suivez-le dans son évasion du bastion de Fort-de-Durn et aidez-le à libérer Taretha et à accomplir son destin.","Allez parler à Erozion au Hautebrande d'antan si vous parvenez à accomplir cette tâche."}},
[10285] = {"Retour vers Andormu", {"Retournez voir le jeune dragon, Andormu, aux Grottes du temps dans le désert de Tanaris."}},
[10286] = {"Le secret d'Arelion", {"La magistrice Carinda veut que vous interrogiez le magistère Aledis, qui a été vu pour la dernière fois quittant le Guet de l'épervier pour se rendre dans le marécage de Zangar."}},
[10287] = {"La maîtresse dévoilée", {"Retournez voir la magistrice Carinda au Guet de l'épervier, à la Péninsule des Flammes infernales."}},
[10288] = {"Arrivée en Outreterre", {"Le commandant Duron à la Porte des ténèbres dans la péninsule des Flammes infernales veut que vous apportiez le Rapport de Duron à Amish Marteau-hardi."}},
[10289] = {"En route pour Thrallmar", {"Voyagez sur le coursier du vent de Vlagga Fileplume jusqu'à Thrallmar. Apportez le Rapport d'Orion au général Krakork."}},
[10290] = {"En quête de farahlite", {"Récoltez 4 morceaux de Farahlite brute et retournez voir Zuben Elgenubi à la Foudreflèche."}},
[10291] = {"Se présenter à Nazgrel", {"Le général Krakork de Thrallmar veut que vous parliez à Nazgrel."}},
[10292] = {"Plus de puissance !", {"Zuben Elgenubi veut que vous preniez 8 Canons de la Légion et que vous les rapportiez à la Foudreflèche."}},
[10293] = {"Toucher le Gros-lot", {"Zuben Elgenubi de la Foudreflèche veut que vous vous procuriez un Noyau de farahlite."}},
[10294] = {"La crête du Vide", {"Allez sur la crête du Vide et tuez les créatures que vous y trouverez. Réunissez 40 Éclats d'âme de la crête du Vide et apportez-les à Ogath le Fol au Poste de Brise-échine."}},
[10295] = {"Du fond des abysses", {"Tuez le baron du Vide Galaxis et récupérez l'Éclat de son âme. Apportez l'éclat à Ogath le Fol au Poste de Brise-échine."}},
[10296] = {"Le Noir Marécage", {"Voyagez à travers les Grottes du temps jusqu'au Noir Marécage pendant l'ouverture de la Porte des ténèbres et parlez à Sa'at."}},
[10297] = {"L'ouverture de la Porte des ténèbres", {"Sa'at, à l'intérieur du Noir Marécage des Grottes du temps, vous a demandé de défendre Medivh jusqu'à ce qu'il parvienne à ouvrir la Porte des ténèbres.","Retournez voir Sa'at si vous parvenez à accomplir cette tâche."}},
[10298] = {"Héros de la progéniture", {"Retournez voir l'enfant, Andormu, aux Grottes du temps dans le désert de Tanaris."}},
[10299] = {"Extinction de la manaforge B'naar", {"Retournez à la manaforge B'naar et prenez le Cristal d'accès de B'naar au Surveillant Theredis. Utilisez-le à la console de contrôle de la manaforge B'naar pour l'éteindre et faites votre rapport à l'anachorète Karja.","Accomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10300] = {"La reconstruction du bâton", {"Obtenez 4 Éclats de cristal sans défaut sur les fantômes des ruines d'Enkaat et rapportez-les à Ravandwyr dans la Zone 52."}},
[10301] = {"Déverrouiller le compendium", {"Prenez l'Oculus héliotrope à la Saccage-sort Marathelle au bastion des Solfurie."}},
[10302] = {"Mutations volatiles", {"Tuez 8 Mutations volatiles, puis retournez voir la botaniste Taerix au Point d'impact dans le val d'Ammen."}},
[10303] = {"Les elfes de sang", {"Tuez 10 Eclaireurs elfes de sang, puis retournez voir Tolaan sur l'Ombrecrête du val d'Ammen."}},
[10304] = {"Le redresseur de torts Aldar", {"Parlez au redresseur de torts Aldar au point d'impact au val d'Ammen."}},
[10305] = {"L'abjuriste Belmara", {"Trouvez la maison de l'abjuriste Belmara dans l'Allée des sorciers et replacez le livre sur l'Étagère de livres. Puis parlez au protecteur Valemort à Kirin'Var."}},
[10306] = {"L'adjurateur Luminrath", {"Trouvez le domicile de l'adjurateur Luminrath dans l'allée des sorciers et replacez le mantelet dans sa Commode. Puis parlez au protecteur Valemort à Kirin'Var."}},
[10307] = {"Cohlien Tissegivre", {"Trouvez le domicile de Cohlien Tissegivre dans l'Allée des sorciers et replacez le chapeau dans sa Cantine. Puis parlez au protecteur Valemort à Kirin'Var."}},
[10308] = {"Un autre monceau d'éthériens", nil},
[10309] = {"C'est un saccageur gangrené, mais avec un cœur", {"Récupérez le Cœur du saccageur gangrené et apportez-le à N. D. Malacamp, au Monceau au Raz-de-Néant."}},
[10310] = {"Sabotez le portail dimensionnel !", {"Escortez Drijya jusqu'au portail dimensionnel au Site d'invasion Destructeur, et faites en sorte qu'il reste en sécurité pendant qu'il tente de le saboter. Puis parlez à Gahruj au comptoir des Terres-médianes à l'intérieur de l'écodôme Terres-médianes au Raz-de-Néant."}},
[10311] = {"Drijya a besoin de votre aide", {"Parlez à Drijya près du Site d'invasion Destructeur au Raz-de-Néant."}},
[10312] = {"Les annales de Kirin'Var", {"Récupérez les Annales de Kirin'Var à l'hôtel de ville de Kirin'Var et rapportez-les au protecteur Valemort."}},
[10313] = {"Mesurer les énergies dimensionnelles", {"L'exarque Orelis de la Zone 52 veut que vous emportiez l'Orbe d'harmonisation interdimensionnelle à la manaforge B'naar et que vous l'utilisiez à l'extrémité de ses quatre pipelines.","Accomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10314] = {"Le doute subsiste...", {"Récupérez 10 Restes répugnants sur les esprits retors de la Cour de la chapelle de Kirin'Var et apportez-les au protecteur Valemort."}},
[10315] = {"Neutraliser les néantomanciens", {"Tuez 15 Néantomanciens solfurie puis retournez voir le traqueur-du-Néant Oazul au Point d'ancrage de Tuluman au Raz-de-Néant."}},
[10316] = {"À la recherche de preuves", {"Fouillez la Cour de la chapelle de Kirin'Var à la recherche d'indices au sujet de l'identité de la personne responsable de la manipulation des esprits."}},
[10317] = {"S'occuper du contremaître", {"Occupez-vous du contremaître Tombesoleil à l'intérieur de la mine de Trelleum, qui se trouve sous la manaforge Ara au Raz-de-Néant."}},
[10318] = {"S'occuper du contremaître", {"Tuez le Grand seigneur Grincegarr. Retournez ensuite auprès du marchand des vents Tuluman au Point d'ancrage de Tuluman de Raz-de-Néant."}},
[10319] = {"La capture du phylactère", {"Récupérez le Phylactère de Naberius dans le quartier de la Cour de la chapelle de Kirin'Var et apportez-le au protecteur Valemort."}},
[10320] = {"Détruisez Naberius !", {"Le protecteur Valemort de Kirin'Var vous demande d'affronter Naberius et de le vaincre dans la Cour de la chapelle du village."}},
[10321] = {"Extinction de la manaforge Coruu", {"Voyagez jusqu'à la manaforge Coruu, à l'est de la Zone 52, et prenez le Cristal d'accès de Coruu à la surveillante Seylanna. Utilisez-le à la console de la manaforge Coruu pour l'éteindre.","Accomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10322] = {"Extinction de la manaforge Duro", {"L'anachorète Karja vous demande de vous rendre à la manaforge Duro et de vous procurer le Cristal d'accès de Duro porté par le surveillant Athanel. Utilisez-le sur la Console de la manaforge Duro pour déclencher l'extinction.","Accomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10323] = {"Extinction de la manaforge Ara", {"Voyagez jusqu'à la manaforge Ara et prenez le Cristal d'accès d'Ara sur le contremaître Azarad. Utilisez-le à la console de la manaforge Ara pour l'éteindre.","Accomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10324] = {"La grande chasse aux frôle-lune", {"Apportez 6 Peaux de chevreuil frôle-lune à Acteon au Guet d'azur."}},
[10325] = {"Les marques de Kil'jaeden", {"Remettre 10 Marques de Kil'jaeden à Adyen le Gardelumière, à Shattrath.","Accomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10326] = {"De nouvelles marques de Kil'jaeden", nil},
[10327] = {"Une marque de Kil'jaeden", nil},
[10328] = {"Briefing des Solfurie", {"Allez à la manaforge Duro et procurez-vous le Briefing militaire solfurie et le Briefing des arcanes solfurie auprès des soldats en poste. Retournez voir l'exarque Orelis une fois cette tâche accomplie.","Accomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10329] = {"Extinction de la manaforge B'naar", {"Retournez à la manaforge B'naar et prenez le Cristal d'accès de B'naar au Surveillant Theredis. Utilisez-le à la console de contrôle de la manaforge B'naar pour l'éteindre et faites votre rapport au maître-espion Thalodien.","Accomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10330] = {"Extinction de la manaforge Coruu", {"Procurez-vous le Cristal d'accès de Coruu sur la surveillante Seylanna. Utilisez-le à la console de contrôle de Coruu pour éteindre la manaforge et retournez voir Caledis Brillaube.","Accomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès des Aldor."}},
[10331] = {"Des outils indispensables", {"Reprenez le Marteau de forge aux apprentis de Kirin'Var et rapportez-le à l'apprenti Andrethan à Kirin'Var."}},
[10332] = {"Le maître forgeron Rhonsus", {"L'apprenti Andrethan de Kirin'Var veut que vous alliez tuer le fantôme de Rhonsus, le maître forgeron de la ville."}},
[10333] = {"Aider Mama Roulette", {"Parlez à Mama Roulette au comptoir des Terres-médianes à l'intérieur de l'écodôme Terres-médianes au Raz-de-Néant."}},
[10334] = {"Plus de clochettes", {"Trouver Marguerite dans les champs près de la colline de la chapelle de Kirin'Var, au Raz-de-Néant."}},
[10335] = {"Cartographier les ruines", {"Placez les trois Repères de géomètre puis revenez voir Zéphyrion à la Foudreflèche au Raz-de-Néant."}},
[10336] = {"Les serviteurs de Culuthas", {"Tuez 10 Molosses de Culuthas et 5 Yeux de Culuthas, puis retournez voir le Traqueur-du-Néant Nauthis à la Foudreflèche au Raz-de-Néant."}},
[10337] = {"Marguerite rentre au pays", {"Escortez Marguerite pour la ramener saine et sauve à son propriétaire, Thadell."}},
[10338] = {"Extinction de la manaforge Duro", {"Retournez à la manaforge Duro et prenez le Cristal d'accès de Duro au surveillant Athanel. Utilisez-le à la console de contrôle de Duro pour l'éteindre et allez faire votre rapport au maître-espion Thalodien.","Accomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10339] = {"L'Ethereum", {"Le commandant Ameer veut que vous tuiez 5 Assassins de l'Ethereum, 5 Troupiers de choc de l'Ethereum, 2 Chercheurs de l'Ethereum et le capitaine Zovax puis que vous activiez le Transpondeur zéta de l'Ethereum."}},
[10340] = {"La halte du Fracas", {"Le commandant des lignes avancées Kingston veut que vous parliez au chef d'escadrille Dabir'ee, que vous preniez un griffon pour la halte du Fracas et que vous vous présentiez à Runetog Marteau-hardi."}},
[10341] = {"Cognez-les maintenant qu'ils sont à terre", {"Tuez 8 Adjurateurs solfurie, 6 Franc-archers solfurie et 4 Centurions solfurie. Retournez voir la magistrice Larynna dans la Zone 52 lorsque vous aurez achevé cette tâche.","Accomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10342] = {"Dégoter de la lauze de peau-de-schiste", {"Collectez 5 Lauzes de peau-de-schiste et apportez-les à Bottes dans la Zone 52 au Raz-de-Néant."}},
[10343] = {"L'invasion sans fin", {"Fouillez la zone de Kirin'Var infestée de créatures de mana à la recherche d'un Fragment de bombe de mana et apportez-le au lieutenant-ensorceleur Morran."}},
[10344] = {"Le chef d'escadrille Griffongar", {"Parlez au chef d'escadrille Griffongar à la halte du Fracas."}},
[10345] = {"La chair ment...", {"Utilisez le Boute-feu du Protectorat sur 12 Cadavres flétris. Lorsque vous aurez fini, rendez le Boute-feu du Protectorat à l'agent Araxes à l'extérieur du Puits d'accès Zéon."}},
[10346] = {"Retour à la saillie abyssale", {"Le griffonnier Soufflevent de la halte du Fracas dans la péninsule des Flammes infernales vous demande de détruire 20 Péons gan'arg, 5 Surveillants mo'arg et 5 Gangrecanons."}},
[10347] = {"Retour à la saillie abyssale", {"Le chef d'escadrille Brack du Trépas du saccageur dans la péninsule des Flammes infernales vous demande de détruire 20 Péons gan'arg, 5 Surveillants mo'arg et 5 Gangrecanons."}},
[10348] = {"De nouvelles opportunités", {"Cueillez 15 Clochettes d'ivoire et retournez voir Mihrdad au comptoir des Terres-médianes."}},
[10349] = {"La lieuse de terre", {"Apportez l'Éclat de cristal cramoisi à la lieuse de terre Galandria Noctebrise au Poste cénarien dans la péninsule des Flammes infernales."}},
[10350] = {"Behomat", {"Allez parler à Behomat, à l'Étage des commerces de l'Exodar."}},
[10351] = {"Remèdes naturels", {"Utilisez la Graine de revitalisation au Cercle de la lieuse de terre pour guérir la terre autour du cristal. Ensuite, retournez voir la lieuse de terre Galandria Noctebrise au Poste cénarien dans la péninsule des Flammes infernales avec toutes les informations que vous aurez."}},
