[10384] = {"Les données de l'Ethereum", {"L'Image du commandant Ameer du lieu de rassemblement de l'Ethereum - Transpondeur zéta de l'Ethereum - veut que vous récupériez la cellule de données de l'Ethereum."}},
[10385] = {"Risque de dommages cérébraux : élevé", {"L'image du commandant Ameer au Transpondeur zéta du lieu de rassemblement de l'Ethereum vous demande de lui rapporter 15 recueils de Données de relais de l'Ethereum."}},
