[10390] = {"Le camp de forge Mageddon", {"Nazgrel de Thrallmar vous demande d'attaquer le Camp de forge Mageddon, de tuer 10 Serviteurs gan'arg et de revenir avec la tête de Tranchescie."}},
[10391] = {"Les canons de la rage", {"Détruisez 3 Gangrecanons Mod.I au Camp de forge Rage et retournez voir Nazgrel à Thrallmar."}},
[10392] = {"Une porte vers l'abîme", {"Tuez Arix'Amal pour vous emparer de la Clé du portail de la Légion ardente. Utilisez cette clé sur la Rune de Mépris, puis retournez voir Nazgrel à Thrallmar."}},
[10393] = {"Plans abjects", {"Apportez la Missive de la Légion ardente au Magistère Faucon-de-sang à Thrallmar."}},
[10394] = {"Une perturbation : le camp de forge Mageddon", {"Tuez 10 Serviteurs gan'arg et Tranchescie au Camp de forge Mageddon puis retournez voir le Commandant des lignes avancées Brock à la halte de l'Honneur."}},
[10395] = {"La sombre missive", {"Apportez la Missive au Scrute-dimensions Kryv dans le donjon principal du bastion de l'Honneur."}},
[10396] = {"Ennemi de mon ennemi...", {"Détruisez 3 Gangrecanons Mod.I au Camp de forge Rage, puis retournez voir le Grand maréchal Brock à la halte de l'Honneur."}},
[10397] = {"Le site d'invasion Annihilateur", {"Tuez Arix'Amal. Prenez la Clé du portail de la Légion ardente et utilisez-la sur la Rune de Mépris."}},
