[10404] = {"Contre la Légion", {"L'exarque Orelis de la Zone 52 veut que vous vous rendiez à la Base de forge Oubli et à la Base de forge Géhenne pour y tuer 12 Assistants-forgerons gan'arg, 6 Seigneurs de la forge mo'arg et 6 Porte-courroux.","Accomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10405] = {"S-A-B-O-T-A-G-E", {"L'image du commandant Ameer au Transpondeur zéta du lieu de rassemblement de l'Ethereum vous demande de lui rapporter un Linceul de l'Ethereum préparé."}},
[10406] = {"Faire passer le message", {"L'Image du commandant Ameer du lieu de rassemblement de l'Ethereum - Transpondeur zéta de l'Ethereum - veut que vous escortiez le démolisseur du Protectorat jusqu'à la canalisation de la manaforge Ultris et que vous le protégiez pendant qu'il sabote le pipeline."}},
[10407] = {"L'ombre de Socrethar", {"L'anachorète Karja de la Zone 52 veut que vous vous procuriez la Première moitié de la pierre de Socrethar sur le maître-forge Morug à la base de forge Oubli et la Seconde moitié de la pierre de Socrethar sur Silroth à la base de forge Géhenne.","Accomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10408] = {"Le roi-nexus Salhadaar", {"L'Image du commandant Ameer du lieu de rassemblement de l'Ethereum - Transpondeur zéta de l'Ethereum - veut que vous utilisiez le Disrupteur du Protectorat pour désactiver les champs de force de Salhadaar puis détruire le roi-nexus Salhadaar."}},
[10409] = {"Un coup fatal à la Légion", {"La grande prêtresse Ishanah veut que vous alliez au Téléporteur de la Légion dans le coin nord-ouest de Raz-de-Néant et que vous l'utilisiez pour vous téléporter au siège de Socrethar. Une fois là-bas, triomphez de Socrethar.","Accomplir des quêtes pour les Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10410] = {"L'aide d'Ishanah", {"Apportez la Pierre de téléportation de Socrethar à la grande prêtresse Ishanah au Sanctuaire de la Lumière perpétuelle sur l'Éminence de l'Aldor à Shattrath.","Accomplir des quêtes pour les Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10411] = {"Le bien-fondé des électrochocs !", {"Le chercheur Navuud du poste de garde du Protectorat au Raz-de-Néant vous a demandé de détruire 30 Globules de rebut du Vide et 30 Globules de vase suintante."}},
[10412] = {"Les chevalières Aile-de-feu", {"La magistrice Fyalenn de Shattrath veut que vous lui apportiez 10 Chevalières Aile-de-feu.","Accomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10413] = {"Les horreurs de la pollution", {"Apportez le Flacon de limon d'horreur du Vide au chercheur Navuud du poste de garde du Protectorat de Raz-de-Néant."}},
