[10415] = {"De nouvelles chevalières Aile-de-feu", nil},
[10416] = {"Synthèse de puissance", {"Voren'thal le Voyant de Shattrath veut que vous vous procuriez un Tome des arcanes.","Accomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10417] = {"Faire un diagnostic !", {"Récupérez les Résultats du diagnostic et apportez-les à Mihrdad au comptoir des Terres-médianes."}},
[10418] = {"S'occuper des saboteurs", {"Mihrdad du comptoir des Terres-médianes veut que vous alliez tuer 8 Crocilisques Ecailles-barbelées."}},
