[10423] = {"À la Foudreflèche", {"Parlez à Ghabar à la Foudreflèche, au Raz-de-Néant."}},
[10424] = {"Diagnostic : critique", {"Utilisez la Machine à diagnostic en vous tenant près du Générateur de l'écodôme Sudron et apportez les Résultats du diagnostic à Ghabar à la Foudreflèche."}},
[10425] = {"L'évasion du lieu de rassemblement", {"Escortez l'Avant-garde du Protectorat capturé jusqu'au Poste de garde du Protectorat au Raz-de-Néant."}},
[10426] = {"La flore des écodômes", {"Utilisez le Modulateur de champ d'énergie sur des Flagellants de Farahlon. Observez les résultats et allez voir Aurine Braselune à la Foudreflèche lorsque vous aurez conduit 10 tests."}},
[10427] = {"Créatures des écodômes", {"Marquez 12 Talbuks pour Aurine Braselune à la Foudreflèche. Vous pouvez marquer soit des Talbuks étalons soit des Talbuks femelles à l'aide du Marqueur de talbuk."}},
