[10445] = {"Les fioles d'éternité", {"Soridormi aux Grottes du temps vous demande de récupérer le Reste de la fiole de Vashj auprès de Dame Vashj au réservoir de Glissecroc et le Reste de la fiole de Kael auprès de Kael'thas Haut-Soleil au donjon de la Tempête."}},
[10446] = {"Le code final", {"Le lieutenant Meridian vous demande d'utiliser le Code final pour désamorcer la Bombe de mana. Faites ensuite votre rapport à Jenai Murmétoile au Bastion allérien dans la forêt de Terokkar."}},
[10447] = {"Le code final", {"Le sergent Chawni vous demande d'utiliser le Code final pour désamorcer la Bombe de mana. Faites ensuite votre rapport à Tooki au fort des Brise-pierres dans la forêt de Terokkar."}},
