[10471] = {"La promesse du défenseur", {"Apportez votre Bague d'éternité à Soridormi aux Grottes du temps après être devenu Révéré par la Balance des sables."}},
[10472] = {"L'engagement du sage", {"Apportez votre Bague d'éternité à Soridormi aux Grottes du temps après être devenu Exalté auprès de la Balance des sables."}},
[10473] = {"L'engagement du guérisseur", {"Apportez votre Bague d'éternité à Soridormi aux Grottes du temps après être devenu Exalté auprès de la Balance des sables."}},
[10474] = {"L'engagement du champion", {"Apportez votre Bague d'éternité à Soridormi aux Grottes du temps après être devenu Exalté auprès de la Balance des sables."}},
[10475] = {"L'engagement du défenseur", {"Apportez votre Bague d'éternité à Soridormi aux Grottes du temps après être devenu Exalté auprès de la Balance des sables."}},
[10476] = {"Ennemis féroces", {"Le gardien Moi'bff Jill de Telaar veut que vous vous procuriez 10 Perles de guerre d'obsidienne sur les différents ogres qui habitent Nagrand."}},
[10477] = {"Plus de perles de guerre", nil},
[10478] = {"Plus de perles de guerre !", nil},
[10479] = {"Prouver votre force", {"Le gardien Bullrok de Garadar veut que vous vous procuriez 10 Perles de guerre d'obsidienne sur les différents ogres qui habitent Nagrand."}},
[10480] = {"Esprits des eaux enragés", {"Le soigneterre Torlok à l'Autel de la damnation dans la vallée d'Ombrelune veut que vous utilisiez le Totem des esprits pour capturer 5 Âmes aquatiques."}},
[10481] = {"Esprits des airs enragés", {"Le soigneterre Torlok à l'Autel de la damnation dans la vallée d'Ombrelune veut que vous utilisiez le Totem des esprits pour capturer 10 Âmes aériennes."}},
[10482] = {"Les charognards gangr'orcs", {"Tuez 20 Orcs Mâche-les-os et retournez voir Amadi, au Bastion de l'honneur."}},
[10483] = {"De mauvais présages", {"Le lieutenant Amadi du bastion de l'Honneur vous a demandé d'apporter 1 Talisman maudit au Caporal Crêtefer, à la Halte de l'expédition."}},
[10484] = {"Talismans maudits", {"Rapportez 12 Talismans maudits au Caporal Crêtefer à la Halte de l'expédition."}},
[10485] = {"Le seigneur de guerre de l'Orbite-sanglante", {"Apportez l'Armure brisée du seigneur de guerre Morkh au Caporal Crêtefer à la Halte de l'expédition."}},
[10486] = {"Une nature envahissante", {"Tuez 12 Saigneurs Aile-rasoir."}},
[10487] = {"La poussière des drakes", {"Procurez-vous 4 Poussières des Drakes-faë."}},
[10488] = {"Pour protéger les nôtres", {"Utilisez la Pommade de Gor'drek sur 5 des Loups redoutables sire-tonnerres."}},
[10489] = {"Abattre un vieil arbre", {"Procurez-vous le Tronc de Fermebranche Basseracines."}},
[10490] = {"L'appel de l'eau", {"Parlez au long-voyant Nobundo, dans le Hall de Cristal, à l'intérieur de l'Exodar sur l'île de Brume-azur."}},
[10491] = {"L'appel de l'air", {"Parlez au long-voyant Nobundo, dans le Hall de Cristal, à l'intérieur de l'Exodar sur l'île de Brume-azur."}},
[10492] = {"Une proposition sérieuse", {"Procurez-vous 15 Échantillons de sang du Berceau-de-l'hiver et 20 pièces d'or, et apportez-les avec une paire de Manchettes des éléments à Deliana à Forgefer."}},
[10493] = {"Une proposition sérieuse", {"Procurez-vous 15 Echantillons de venin de Silithus et 20 pièces d'or, et apportez-les avec une paire de Brassards de Sancteforge à Mokvar, à Orgrimmar."}},
[10494] = {"Une juste compensation", nil},
[10495] = {"Une juste compensation", {"Apportez une Ceinture de Sancteforge et une paire de Gantelets de Sancteforge à Mokvar à Orgrimmar."}},
[10496] = {"Les adieux d'Anthion", nil},
[10497] = {"Les adieux d'Anthion", {"Retournez voir Mokvar à Orgrimmar avec des Bottes de Sancteforge, des Cuissards de Sancteforge et des Spallières de Sancteforge."}},
[10498] = {"Garder le meilleur pour la fin", nil},
[10499] = {"Garder le meilleur pour la fin", {"Donnez votre Casque de Sancteforge et votre Cuirasse de Sancteforge à Mokvar."}},
[10500] = {"La Horde a besoin de votre aide !", {"Parler au seigneur de guerre Gorchuk, dans la vallée des Esprits d'Orgrimmar."}},
[10501] = {"L'Alliance a besoin de votre aide !", {"Parler au grand maréchal Tombeneige dans la Garde militaire de Forgefer."}},
[10502] = {"Les ogres de la Masse-sanglante", {"Tuez 30 Ogres de la Masse-sanglante à l'Avant-poste de la Masse ssanglante."}},
[10503] = {"La menace des Flèchelame", {"Tuez 30 Ogres de Flèchelame et 10 Raptors de Flèchelame au Bastion de Flèchelame et sur les Terres de la Flèchelame."}},
[10504] = {"Les ogres de Flèchelame", {"Tuez 30 Ogres de Flèchelame au Bastion de Flèchelame et sur les Terres de la Flèchelame."}},
[10505] = {"Les ogres de la Masse-sanglante", {"Tuez 30 Ogres de la Masse-sanglante à l'Avant-poste de la Masse ssanglante."}},
[10506] = {"Un redoutable problème", {"Appliquez la Poudre diminutive de Rina pour rapetisser 5 Loups redoutables de la Masse-sanglante, à l'Avant-poste Masse-sanglante et dans le Ravin de Masse-sanglante."}},
[10507] = {"Un moment décisif", {"Utilisez la Pierre de téléportation de Socrethar au site d'invasion Suzerain, au nord de la Base de forge Oubli pour transporter votre groupe jusqu'à l'aire de Socrethar. Une fois là-bas, utilisez la Présence de Voren'thal pour vaincre Socrethar.","Accomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10508] = {"Un cadeau pour Voren'thal", {"Le maître-espion Thalodien de la Zone 52 veut que vous vous procuriez la Première moitié de la pierre de Socrethar auprès du maître-forge Morug à la base de forge Oubli, et la Seconde moitié de la pierre de Socrethar auprès de Silroth à la base de forge Géhenne.","Accomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10509] = {"En route pour la gloire", {"Apportez la Pierre de téléportation de Socrethar à Voren'thal le Voyant sur le Degré des Clairvoyants à Shattrath.","Accomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10510] = {"Dans la mine de draenéthyste", {"Ramassez 5 Cristaux de mine de draenéthyste."}},
[10511] = {"Une étrange bière", {"Récupérez 11 Foudroyante-des-brutes de la Masse-sanglante."}},
[10512] = {"Pinter les Flèchelame", {"Utilisez le Tonneau de foudroyante-des-brutes de la Masse-sanglante pour tenter d'enivrer 5 Ogres de Flèchelame."}},
[10513] = {"Oronok Cœur-fendu", {"Partez à la recherche d'Oronok Cœur-fendu sur la Saillie brisée, au nord de la Citerne de Glissentaille."}},
[10514] = {"J'ai tenu bien des rôles...", {"Oronok Cœur-fendu, à la Ferme d'Oronok dans la vallée d'Ombrelune, veut que vous ramassiez 10 Tubercules d'Ombrelune dans les Plaines brisées.","Il vous demande aussi de rapporter le Sifflet à sanglier d'Oronok lorsque vous aurez terminé."}},
[10515] = {"Une leçon bien apprise", {"Oronok Cœur-fendu, à la Ferme d'Oronok dans la vallée d'Ombrelune, veut que vous détruisiez 10 Œufs d'écorcheurs voraces dans les Plaines brisées."}},
[10516] = {"Les atours d'un redresseur de torts", {"Récupérez la Lame du redresseur de torts Vuuleen et le Bouclier du redresseur de torts Vuuleen."}},
[10517] = {"Gorr'niaud, ton heure est venue...", {"Tuez Gorr'niaud, chef des ogres du bastion de Flèchelame."}},
[10518] = {"Planter la bannière", {"Utilisez la Bannière de Flèchelame au sommet de la Tour Nord-sanglante pour attirer Babin Graillebâfre. Récupérez la Bannière du clan Flèchelame et le Heaume de Babin Graillebâfre."}},
[10519] = {"La Formule de damnation - vérité et histoire", {"Oronok Cœur-fendu à la ferme d'Oronok dans la vallée d'Ombrelune veut que vous écoutiez son histoire. Parlez à Oronok pour commencer à écouter son histoire."}},
[10520] = {"L'aide à l'archidruide Forteramure", {"Parler à l'archidruide Fandral Forteramure à Darnassus."}},
[10521] = {"Grom'tor, fils d'Oronok", {"Trouvez Grom'tor, fils d'Oronok à la Halte de Glissentaille dans la vallée d'Ombrelune."}},
[10522] = {"La Formule de damnation - la charge de Grom'tor", {"Grom'tor, fils d'Oronok, de la Halte de Glissentaille dans la vallée d'Ombrelune, veut que vous récupériez le Premier fragment de la Formule de damnation."}},
[10523] = {"La Formule de damnation - Premier fragment retrouvé", {"Apportez le coffret de Grom'tor à Oronok Cœur-fendu à la Ferme d'Oronok dans la vallée d'Ombrelune."}},
[10524] = {"Artéfacts du clan Sire-tonnerre", {"Récupérez le Tambour du clan Sire-tonnerre, la Flèche du clan Sire-tonnerre et la Tablette du clan Sire-tonnerre."}},
[10525] = {"Guide de vision", {"Utilisez le Fragment d'âme flamboyante pour transformer votre esprit en un guide de vision qui vous dévoilera la position de la dernière relique Sire-tonnerre."}},
[10526] = {"La Pointe du tonnerre", {"Récupérer la Pointe du tonnerre."}},
[10527] = {"Ar'tor, fils d'Oronok", {"Trouvez Ar'tor, fils d'Oronok à la Halte Illidari dans la vallée d'Ombrelune."}},
[10528] = {"Prisons de cristal démoniaque", {"Trouvez la Maîtresse de la douleur Gabrissa à la Halte Illidari et tuez-la, puis rapportez le cadavre d'Ar'tor, fils d'Oronok avec la Clé cristalline."}},
[10529] = {"La voie du chasseur", {"Parlez au lieutenant Coursaurore à la Retraite des Pérégrins, au Bois des Chants éternels."}},
[10530] = {"La voie du chasseur", {"Parlez au lieutenant Coursaurore à la Retraite des Pérégrins, au Bois des Chants éternels."}},
[10531] = {"La bataille pour le bassin Arathi !", {"Attaquez la mine, la scierie, la forge et la ferme, puis revenez voir le grand maréchal Oslight au Refuge de l'Ornière."}},
[10532] = {"Couper les voies de ravitaillement d'Arathor", {"Gagnez la bataille pour le bassin Arathi, obtenez une Caisse de ressources d'Arathi et rapportez-la au nécrotraqueur Mortis au Trépas-d'Orgrim."}},
[10533] = {"Plus de caisses de ressources", nil},
[10534] = {"Retourner chez soi", {"Caedmos de l'Exodar vous demande de venir le voir."}},
[10535] = {"Les ressources du bassin Arathi", {"Gagnez la bataille pour le bassin Arathi, obtenez une Caisse de ressources d'Arathi et rapportez-la au sergent Maclear au Refuge de l'Ornière."}},
[10536] = {"Plus de caisses de ressources", nil},
[10537] = {"Lohn'goron, arc du Cœur-fendu", {"L'Esprit d'Ar'tor à la Halte Illidari dans la vallée d'Ombrelune veut que vous repreniez Lohn'goron, arc du Cœur-fendu aux démons de la région."}},
[10538] = {"Sang bouillonnant", {"Utilisez le Sang de l'Orbite-sanglante au Chaudron maudit pour fabriquer du Sang bouilli. Apportez 12 mesures de Sang bouilli à l'Apothicaire Albreck au Poste de Brise-échine."}},
[10539] = {"Retourner chez soi", {"Caedmos de l'Exodar vous demande de venir le voir."}},
[10540] = {"La Formule de damnation - la charge d'Ar'tor", {"L'Esprit d'Ar'tor à la Halte Illidari dans la vallée d'Ombrelune veut que vous récupériez le Second fragment de la formule de damnation sur Veneratus le Multiple.","Les créatures attaquées par le Chasseur d'esprit ou qui subissent des dégâts infligés par lui ne rapportent ni butin ni expérience."}},
[10541] = {"La Formule de damnation - Second fragment retrouvé", {"Apportez le Coffret d'Ar'tor à Oronok Cœur-fendu à la Ferme d'Oronok dans la vallée d'Ombrelune."}},
[10542] = {"On m'a piqué mon narguilé et mes bibines !", {"Récupérez le Narguilé de T'chali et 10 Foudroyantes-des-brutes de la Masse-sanglante."}},
[10543] = {"Grimnok et Korgaah, je viens vous chercher !", {"Tuez Grimnok Champ-d'honneur et Korgaah."}},
[10544] = {"Malédiction sur vos deux clans !", {"Utilisez le Fétiche salement fortiche pour maudire 5 bâtiments ou tours du Bastion de Flèchelame et 2 de l'avant-poste Masse-sanglante."}},
[10545] = {"Beuverie chez les Flèchelame", {"Utilisez le Tonneau de foudroyante-des-brutes de la Masse-sanglante pour saouler 5 Ogres de Flèchelame."}},
[10546] = {"Borak, fils d'Oronok", {"Trouvez Borak, fils d'Oronok près de la Halte de l'éclipse dans la vallée d'Ombrelune."}},
[10547] = {"Des chardonomanes et des œufs", {"Borak, fils d'Oronok, sur le pont au nord de la Halte de l'éclipse, veut que vous trouviez un Œuf d'arakkoa pourri et que vous l'apportiez à Tobias le Goinfre-crasse à Shattrath, qui se trouve au nord-ouest de la forêt de Terokkar."}},
[10548] = {"La triste vérité", {"Erelan, à Tranquillien, veut que vous retrouviez la Tête d'archéologue réduite dans l'un des coffres de Zeb'Tela ou Zeb'Nowa."}},
[10550] = {"Le fagot de chardons sanglants", {"Rapportez le Fagot de chardons sanglants à Borak, fils d'Oronok sur le pont près de la Halte de l'éclipse dans la vallée d'Ombrelune."}},
[10551] = {"Allégeance à l'Aldor", nil},
[10552] = {"Allégeance aux Clairvoyants", nil},
[10553] = {"Voren'thal le Voyant", {"Parlez à Voren'thal le Voyant à l'intérieur de la Bibliothèque du Voyant sur le Degré des Clairvoyants à Shattrath.","Accomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10554] = {"Ishanah", {"Parlez à Ishanah à l'intérieur du Sanctuaire de la Lumière perpétuelle sur l'Éminence de l'Aldor à Shattrath.","Accomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10555] = {"Malaise", {"Récupérez 18 Plumes lashh'an arrachées."}},
[10556] = {"Égratignures", {"Utilisez la Poignée de plumes dans le Cercle magique lashh'an."}},
[10557] = {"Vol d'essai : le Zephyrium Capacitorium", {"Parlez à Rally Rossecoquin pour utiliser le Zephyrium Capacitorium."}},
[10558] = {"Révéré par le bastion de l'Honneur", {"Le commandant de corps Danath Trollemort du bastion de l'Honneur dans la péninsule des Flammes infernales veut que vous deveniez Révéré par le bastion de l'Honneur en combattant des Gangr'orcs à l'intérieur des Salles brisées de la citadelle des Flammes infernales."}},
[10559] = {"Révéré par Thrallmar", {"Nazgrel, à Thrallmar dans la péninsule des Flammes infernales, veut que vous deveniez Révéré par Thrallmar en combattant les Gangr'orcs à l'intérieur des Salles brisées de la citadelle des Flammes infernales."}},
[10560] = {"Révéré par les Sha'tar", {"A'dal, de Shattrath, veut que vous deveniez Révéré par les Sha'tar en vainquant les forces de Kael'thas à l'intérieur du Donjon de la Tempête."}},
[10561] = {"Révéré par les Gardiens du temps", {"Andormu à l'intérieur des Grottes du temps à Tanaris veut que vous deveniez Révéré par les Gardiens du temps en continuant à défendre les événements de Hautebrande d'antan et du Noir Marécage à l'intérieur des Grottes du temps."}},
[10562] = {"Assiégés !", {"Le chef d'escadrille Nuainn, à l'extérieur du bastion des Marteaux-hardis, vous demande de tuer 10 Attaquants infernaux."}},
[10563] = {"Vers le bastion de la Légion", {"Rendez-vous au Bastion de la Légion et localisez le Système de communication de la Légion. Quand vous serez près de lui, utilisez votre déguisement pour apprendre les plans de la Légion. Retournez voir le chef d'escadrille Nuainn, au bastion des Marteaux-hardis, quand vous aurez accompli votre mission."}},
[10564] = {"Au diable les infernaux !", {"Placez-vous sur le glyphe vert des fortifications du bastion de la Légion et utilisez la Gangrebombe. Ensuite, revenez faire votre rapport au chef d'escadrille Nuainn, au bastion des Marteaux-hardis."}},
[10565] = {"Les pierres de Vekh'nir", {"Récupérez et utilisez un Cristal vekh'nirau Cercle magique vekh'nir pour créer un Cristal wekh'nir chargé."}},
[10566] = {"Par tâtonnements", {"Essayez chacune des baguettes de Dertrok sur un Ogre esclave des arakkoa. Chaque utilisation d'une baguette nécessite un Cristal vekh'nir imprégné."}},
[10567] = {"Créer le pendentif", {"Procurez-vous 6 Griffes de Ruuan'ok et utilisez-les au Cercle d'oracle ruuan'ok pour invoquer un Messager du Corbeau. Tuez-le pour récupérer son Pendentif de messager."}},
[10568] = {"Les tablettes de Baa'ri", {"L'anachorète Ceyla de l'Autel de Sha'tar veut que vous récupériez 12 Tablettes baa'ri sur le sol et sur les Ouvriers cendrelangues aux Ruines de Baa'ri.","Accomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10569] = {"Les débris de Sketh'lon", {"Récupérez le Journal du commandant de Sketh'lon - Page 1, le Journal du commandant de Sketh'lon - Page 2 et le Journal du commandant de Sketh'lon - Page 3 détenues par les arakkoa du sombre Conclave aux Débris sketh'lon, puis rapportez-les au Chevaucheur de griffons Kieran."}},
[10570] = {"Pour une poignée de chardons", {"Borak, fils d'Oronok, du pont qui se trouve près de la halte de l'Éclipse dans la vallée d'Ombrelune, vous demande de récupérer la Missive de Hurlorage."}},
[10571] = {"Oronu l'Ancien", {"L'anachorète Ceyla de l'Autel de Sha'tar veut que vous vous procuriez les Ordres d'Akama sur Oronu l'Ancien aux Ruines de Baa'ri.","Accomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10572] = {"La préparation de la bombe", {"Procurez-vous une Batterie de saccageur gangrené et une Plaque de blindage de saccageur gangrené. Apportez-les au Chef d'escadrille Nuainn au Bastion des Marteaux-hardis."}},
[10573] = {"La Forgemort", {"Présentez-vous à Ewan Aile-sauvage le Tempétueux à la Tour de la Forgemort."}},
[10574] = {"Les corrupteurs cendrelangues", {"Procurez-vous les quatre fragments du médaillon sur Haalum, Eykenen, Lakaan et Uylaru et rapportez-les à l'anachorète Ceyla à l'Autel de Sha'tar dans la vallée d'Ombrelune.","Accomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10575] = {"La Cage de la gardienne", {"L'anachorète Ceyla veut que vous entriez dans la Cage de la gardienne, au sud des ruines de Baa'ri, et que vous interrogiez Sanoru pour apprendre où se trouve Akama.","Accomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10576] = {"Le bonneteau d'Ombrelune", {"Borak, fils d'Oronok, du pont qui se trouve près de la halte de l'Éclipse dans la vallée d'Ombrelune, vous demande de récupérer 6 pièces d'Armure éclipsion."}},
[10577] = {"Ce qu'Illidan veut, Illidan l'obtient...", {"Borak, fils d'Oronok, du pont qui se trouve près de la halte de l'Éclipse dans la vallée d'Ombrelune, vous demande de remettre le message d'Illidan au grand commandant Ruusk à la halte de l'Éclipse."}},
[10578] = {"La Formule de damnation - la charge de Borak", {"Borak, fils d'Oronok, du pont qui se trouve près de la halte de l'Éclipse dans la vallée d'Ombrelune, vous demande de prendre la Troisième partie de la formule de damnation à Ruul l'Assombrisseur.","Cette mission nécessite un groupe."}},
[10579] = {"La Formule de damnation - Troisième fragment retrouvé", {"Apportez le coffret de Borak à Oronok Cœur-fendu à la Ferme d'Oronok dans la vallée d'Ombrelune."}},
[10580] = {"Où sont passés ces fichus gnomes ?", {"Fouillez la zone sud de la Crête dentelée, dans les Tranchantes pour y trouver des indices quant à l'endroit où pourraient se trouver Toshley et les autres gnomes."}},
[10581] = {"Suivre les miettes", {"Suivez la piste de pièces détachées tombées le long de la rampe vers la Tranchecrête. Parlez à Toshley au poste de Toshley, dans les Tranchantes."}},
[10582] = {"Les séides du Conseil des ombres", {"Ewan Aile-sauvage le Tempétueux à la Tour de la Forgemort veut que vous tuiez 10 Gardiens de la Forgemort et 5 Invocateurs de la Forgemort."}},
[10583] = {"Le destin de Flanis", {"Ewan Aile-sauvage le Tempétueux, à la Tour de la Forgemort, veut que vous alliez fouiller la Forgemort à la recherche de Flanis Aile-vive et que vous récupériez le Sac de Flanis."}},
[10584] = {"Récupérer quelques convertisseurs de puissance", {"Utilisez le Collecteur magnétique proto-voltaïque pour récupérer 5 Electromentaires."}},
[10585] = {"La chambre d'invocation", {"Procurez-vous un Déplaceur élémentaire sur un Forgeron de la Forgemort ou un Bricoleur de la Forgemort et utilisez-le pour interrompre le rituel dans la chambre d'invocation. Faites votre rapport à Ewan Aile-sauvage le tempétueux à la Tour de la Forgemort lorsque vous aurez accompli cette tâche."}},
[10586] = {"Abattez le porteguerre !", {"Tuez le porteguerre Razuun, récupérez les Ordres de Razuun et retournez voir le chef d'escadrille Nuainn, au bastion des Marteaux-hardis."}},
[10587] = {"Les terrains d'entraînement de Karabor", {"L'exarque Onaala de l'Autel de Sha'tar veut que vous vous procuriez 8 Glaives solfurie sur les Suppliants chasseurs de démons et les Initiés chasseurs de démons aux Ruines de Karabor, au sud-est de l'Autel de Sha'tar.","Accomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10588] = {"La Formule de damnation", {"Utilisez la Formule de damnation à l'Autel de la damnation pour invoquer Cyrukh le Seigneur du feu.","Détruisez Cyrukh le Seigneur du feu puis parlez au Soigneterre Torlok, qui se trouve aussi à l'Autel de la damnation."}},
[10589] = {"Obtenir un accès", {"Apportez une Commande du téléporteur de la Légion au chef d'escadrille Nuainn au bastion des Marteaux-hardis. On sait que les Fabricants d'armes mo'arg du bastion de la Légion portent ces appareils."}},
[10590] = {"Sachez prouver votre haine", {"Mehlar Aubelame, à la Barricade, veut que vous lui apportiez 20 Pierres du Fléau des serviteurs."}},
[10592] = {"La sagesse de la reine banshee", {"Apportez les Fragments de pierre du Fléau à dame Sylvanas Coursevent à Fossoyeuse."}},
[10593] = {"Un mal ancien", {"Percez les secrets du Temple d'Atal'Hakkar pour libérer Atal'alarion et récupérer dans sa chair la Liane putride. Retournez voir Melhar à la Barricade quand vous aurez terminé cette tâche."}},
[10594] = {"Mesurer la fréquence de résonance", {"Placez les Scanners à fréquence oscillante de telle sorte que, lorsque vous vous tenez en leur centre, vous vous trouvez dans le chevauchement de cinq champs d'oscillation."}},
[10595] = {"Assiégés !", {"Le garde de sang Gulmok, à l'extérieur du village d'Ombrelune, vous demande de tuer 10 Attaquants infernaux."}},
[10596] = {"Vers le bastion de la Légion", {"Rendez-vous au Bastion de la Légion, localisez le Système de communication de la Légion et utilisez votre déguisement pour apprendre les plans de la Légion. Retournez voir le garde de sang Gulmok, au village d'Ombrelune, quand vous aurez accompli votre mission."}},
[10597] = {"La préparation de la bombe", {"Procurez-vous une Batterie de saccageur gangrené et une Plaque de blindage de saccageur gangrené. Apportez-les au garde de sang Gulmok au village d'Ombrelune."}},
[10598] = {"Au diable les infernaux !", {"Utilisez la Gangrebombe en vous tenant dans le glyphe vert dans le Bastion de la Légion afin de détruire les Infernaux, puis venez faire votre rapport au garde de sang Gulmok, au village d'Ombrelune."}},
[10599] = {"La Forgemort", {"Présentez-vous à l'éclaireur Zagran au Guet des scories."}},
[10600] = {"Les séides du Conseil des ombres", {"L' Eclaireur Zagran au Guet des scories veut que vous tuiez 10 Gardiens de la Forgemort et 5 Invocateurs de la Forgemort."}},
[10601] = {"Le destin de Kagrosh", {"L'éclaireur Zagran du Guet des scories veut que vous alliez fouiller la Forgemort à la recherche de Kagrosh et que vous récupériez le Sac de Kagrosh."}},
[10602] = {"La chambre d'invocation", {"Procurez-vous un Déplaceur élémentaire sur un Forgeron de la Forgemort ou un Bricoleur de la Forgemort et utilisez-le pour interrompre le rituel dans la chambre d'invocation. Faites votre rapport à l'éclaireur Zagran au Guet des scories lorsque vous aurez terminé votre mission."}},
[10603] = {"Abattez le porteguerre !", {"Tuez le porteguerre Razuun, récupérez les Ordres de Razuun et retournez voir le garde de sang Gulmok, au village d'Ombrelune."}},
[10604] = {"Obtenir un accès", {"Apportez une Commande du téléporteur de la Légion au garde de sang Gulmok, au village d'Ombrelune. On sait que les Fabricants d'armes mo'arg du bastion de la Légion portent ces appareils."}},
[10605] = {"L'appel de Carendin", {"Parlez à Carendin Halgar à Fossoyeuse."}},
[10606] = {"L'art de l'entretien des saccageurs gangrenés", {"Tuez le docteur Gutrick et procurez-vous la Clé du docteur. Utilisez la clé pour ouvrir le Coffre-fort du docteur, récupérez L'Art de l'entretien des saccageurs gangrenés, et apportez le livre à Plexi au Site d'invasion Cataclysme."}},
[10607] = {"Les murmures du dieu corbeau", {"Tuez un arakkoa Grishna pour obtenir la Compréhension du corvidien pendant 2 minutes. Tant que vous serez sous l’effet de ce sort, vous pourrez trouver et recevoir la prophétie des quatre sanctuaires."}},
[10608] = {"Clair comme le cristal", {"Tuez 10 Ecorcheurs de cristal."}},
[10609] = {"Qui est apparu en premier, l'œuf ou le drake ?", {"Récupérez 3 Essences de proto-drake du Néant, 3 Essences de jeune drake du Néant et 3 Essences de drake du Néant adulte."}},
[10610] = {"La prospection des matières premières", nil},
[10611] = {"L'art de l'entretien des saccageurs gangrenés", {"Tuez le docteur Maleficus et procurez-vous la Clé du docteur. Utilisez la clé pour ouvrir le Coffre-fort du docteur, récupérez L'Art de l'entretien des saccageurs gangrenés, et apportez le livre à Nakansi au Site d'invasion Cataclysme."}},
[10612] = {"La guerre des boutons", {"Utilisez une Console de contrôle de saccageur gangrené pour prendre le contrôle d'une Sentinelle saccageur gangrené. Détruisez 60 Infernaux mortforgés avant que votre saccageur gangrené ne cesse de fonctionner. Quand votre mission sera achevée, faites votre rapport à Plexi au Site d'invasion : Cataclysme."}},
[10613] = {"La guerre des boutons", {"Utilisez une Console de contrôle de saccageur gangrené pour prendre le contrôle d'une Sentinelle saccageur gangrené. Détruisez 60 Infernaux mortforgés avant que votre saccageur gangrené ne cesse de fonctionner. Quand votre mission sera achevée, faites votre rapport à Nakansi au Site d'invasion : Cataclysme."}},
[10614] = {"Des murmures dans le vent", {"Parlez à Leoroxx à Mok’Nathal, dans les Tranchantes."}},
[10615] = {"La sylve Ruuan", {"Parlez à Timeon au Bosquet éternel, dans la Sylve Ruuan des Tranchantes."}},
[10616] = {"<nyi>Breadcrumb", nil},
[10617] = {"Cocons d'aile-de-soie", {"Collectez 8 Cocons d'aile-de-soie sur des Larves d'aile-de-soie.","Tuez rapidement les larves, ou elles se changeront en Ailes-de-soie, ce qui détruirait leurs cocons."}},
[10618] = {"Les ailes les plus douces", {"Collectez 12 Ailes iridescentes."}},
[10619] = {"La tribu cendrelangue", {"Le redresseur de torts Aluumen veut que vous partiez pour les Ruines de Baa'ri afin d'y tuer 3 Dresseurs cendrelangues, 4 Guerriers cendrelangues et 6 Chamans cendrelangues.","Accomplir des quêtes pour l'Aldor fera baisser votre réputation envers les Clairvoyants."}},
[10620] = {"La menace crêtéchine", {"Tuez 10 Traqueuses crêtéchine."}},
[10621] = {"Éclat plaie-des-Illidari", {"Apportez l'Éclat plaie-des-Illidari à Ordinn Tonnepoing au bastion des Marteaux-hardis."}},
[10622] = {"Preuve d'allégeance", {"Tuez Zandras à la Cage de la gardienne dans la vallée d'Ombrelune et retournez voir Sanoru."}},
[10623] = {"Éclat plaie-des-Illidari", {"Apportez l'Éclat plaie-des-Illidari à Grokom Oeil-de-mort au village d'Ombrelune."}},
[10624] = {"L'histoire hantée", {"L'Apothicaire en chef Hildagard du Village d'Ombrelune vous demande de lui rapporter 15 échantillons de Cendre semperardente."}},
[10625] = {"Lunectoplasmes", {"L'Apothicaire en chef Hildagard du village d'Ombrelune dans la vallée d'Ombrelune vous demande de tuer 12 Zélotes ombrelunes."}},
[10626] = {"S'emparer des armes", {"Ordinn Tonnepoing du bastion des Marteaux-hardis veut que vous lui apportiez le Glaive de Makazradon et le Glaive de Morgroron."}},
[10627] = {"S'emparer des armes", {"Grokom Œil-de-mort du village d'Ombrelune veut que vous lui apportiez le Glaive de Makazradon et le Glaive de Morgroron."}},
[10628] = {"Akama", {"Parler à Akama à l'intérieur de la chambre cachée de la Cage de la gardienne."}},
[10629] = {"Un boulot de m...", {"\"Utilisez le Sifflet à gangrechien pour invoquer un Gangrechien. Promenez le gangrechien et tuez des Sangliers infernaux dérangés. Cherchez les Clés du déchiqueteur dans la \"livraison\" du gangrechien. Rapportez les Clés du déchiqueteur au contremaître Razelcraz près de la mine au nord-ouest de Thrallmar."}},
[10630] = {"Sous Thrallmar", {"Tuez Urga'zz puis retournez voir le contremaître Razelcraz près de la mine au nord-ouest de Thrallmar."}},
[10632] = {"Couper quelques crocs...", {"Récupérez 5 Dents de Crocs-lames extrêmement affûtées."}},
[10633] = {"Teron Fielsang - Mythes et légendes", {"L'apothicaire en chef Hildagard du village d'Ombrelune dans la vallée d'Ombrelune vous demande de trouver l'Esprit ancien d'Ombrelune.","Si vous perdez vos Lunectoplasmes, l'apothicaire en chef Hildagard peut vous en faire une nouvelle paire."}},
[10634] = {"Divination : l'armure de Fielsang", {"L'Esprit ancien d'Ombrelune à l'Autel des Ombres dans le sud-est de la vallée d'Ombrelune vous demande de récupérer l'Armure de Fielsang."}},
[10635] = {"Divination : la cape de Fielsang", {"L'Esprit ancien d'Ombrelune à l'Autel des Ombres dans le sud-est de la vallée d'Ombrelune vous demande de récupérer la Cape de Fielsang."}},
[10636] = {"Divination : l'assommoir de Fielsang", {"L'Esprit ancien d'Ombrelune à l'Autel des Ombres dans le sud-est de la vallée d'Ombrelune vous demande de récupérer l'Assommoir de Fielsang."}},
[10637] = {"Une diversion nécessaire", {"L'exarque Onaala de l'Autel de Sha'tar veut que vous retourniez aux Ruines de Karabor et que vous trouviez un moyen de lâcher Azaloth sur ceux qui le tiennent enfermé.","Accomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10638] = {"CECI N'EST PAS UNE QUÊTE", {"Seek <NAME> in the <DISTRICT> of Silvermoon City."}},
[10639] = {"Teron Fielsang, je suis...", {"L'Esprit ancien d'Ombrelune s'est joué de vous ! Maintenant que vous avez les objets dont il avait besoin, il vous a révélé être Teron Fielsang. Pour vous libérer de sa possession, vous devez faire ce qu'il ordonne et tuer Karsius l'Antique Guetteur.","Si vous y parvenez, retournez voir l'Apothicaire en chef Hildagard au village d'Ombrelune."}},
[10640] = {"Altruis", {"Voyagez jusqu'au Nagrand occidental et allez trouver Altruis le souffrant.","Accomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10641] = {"Contre la Légion", {"Altruis le Souffrant veut que vous obteniez du Sang fraîchement versé d'une Prêtresse de colère à la Base de forge Géhenne, à Raz-de-Néant. Versez-le sur le sol et tuez l'Avatar de Sathal. Revenez voir Altruis quand vous aurez accompli votre mission."}},
[10642] = {"Le fantôme dans la machine", {"Zorus l'adjudicateur du bastion des Marteaux-hardis dans la vallée d'Ombrelune veut que vous lui apportiez 15 Échantillons de cendre semperardente."}},
[10643] = {"Messagers d'Ombrelune", {"Zorus l'adjudicateur du bastion des Marteaux-hardis dans la vallée d'Ombrelune veut que vous alliez tuer 12 Messagers ombrelunes."}},
[10644] = {"Teron Fielsang - Mythes et légendes", {"Zorus l'adjudicateur du bastion des Marteaux-hardis dans la vallée d'Ombrelune vous demande de trouver l'Esprit ancien d'Ombrelune.","Si vous perdez vos Lunectoplasmes, Zorus l'adjudicateur peut vous en fabriquer une nouvelle paire."}},
[10645] = {"Teron Fielsang, je suis...", {"L'Esprit ancien d'Ombrelune s'est joué de vous ! Maintenant que vous avez les objets dont il avait besoin, il vous a révélé être Teron Fielsang. Pour vous libérer de sa possession, vous devez faire ce qu'il ordonne et tuer Karsius l'Antique Guetteur.","Si vous y parvenez, retournez voir Zorus l'adjudicateur au Bastion des Marteaux-hardis."}},
[10646] = {"Le disciple d'Illidan", {"Écoutez l'histoire d'Altruis."}},
[10647] = {"Avis de recherche : Uvuros, Fléau d'Ombrelune", {"Tuez Uvuros et récupérez la Crinière flamboyante d'Uvuro. Rapportez-la à Sardon Frantaille le Hurleguerre au village d'Ombrelune dans la vallée d'Ombrelune."}},
[10648] = {"Avis de recherche : Uvuros, Fléau d'Ombrelune", {"Tuez Uvuros et récupérez la Crinière flamboyante d'Uvuro. Rapportez-la à Musebière le Hurleguerre au Bastion des Marteaux-hardis dans la vallée d'Ombrelune."}},
[10649] = {"Le Livre des noms gangrenés", {"Entrez dans le Labyrinthe des ombres d'Auchindoun et procurez-vous le Livre des noms gangrenés sur Cœur-noir le Séditieux. Retournez voir Altruis en Nagrand une fois cette tâche accomplie."}},
[10650] = {"Retour à l'Aldor", {"Parlez à l'exarque Onaala à l'Autel de Sha'tar dans la vallée d'Ombrelune.","Accomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10651] = {"Il faut arrêter Varedis", {"L'exarque Onaala veut que vous vous rendiez aux ruines de Karabor et que vous tuiez Alandien, Theras, Netharel et Varedis. Utilisez le Livre des noms gangrenés quand Varedis utilisera sa métamorphose, pour l'affaiblir. Retournez voir l'exarque Onaala avec le Livre des noms gangrenés lorsque vous aurez terminé cette tâche.","Accomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10652] = {"Derrière les lignes ennemies", {"Parlez à Veronia lorsque vous serez prêt à partir pour la Manaforge Coruu. Une fois là-bas, parlez à Caledis Brillaube.","Accomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10653] = {"Les marques de Sargeras", {"Remettre 10 Marques de Sargeras à Adyen le Gardelumière, à Shattrath.","Accomplir des quêtes pour l'Aldor fera baisser votre réputation auprès des Clairvoyants."}},
[10654] = {"De nouvelles marques de Sargeras", nil},
[10655] = {"Une marque de Sargeras", nil},
[10656] = {"Les chevalières Solfurie", {"La magistrice Fyalenn de Shattrath veut que vous lui apportiez 10 Chevalières Solfurie.","Accomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de la faction Aldor."}},
[10657] = {"Chevauche la foudre", {"Utilisez la Magnéto-sphère repolarisée pour absorber 25 décharges de foudre des Serpents ailécailles. Collectez également 5 Glandes d'éclair ailécailles."}},
[10658] = {"De nouvelles chevalières Solfurie", nil},
[10659] = {"Une chevalière Solfurie", nil},
[10660] = {"Quelles étranges créatures...", {"Le chercheur Tiorus du village d'Ombrelune dans la vallée d'Ombrelune veut que vous vous procuriez 8 Rates de gangrefeu."}},
[10661] = {"Ex-rate-ordinaires !", {"Gnomus du bastion des Marteaux-hardis dans la vallée d'Ombrelune veut que vous récupériez 8 Rates gangrefeu."}},
[10662] = {"L'ermite forgeron", {"Apportez la Boîte de lingots à David Wayne au Refuge de Wayne dans la forêt de Terokkar."}},
[10663] = {"L'ermite forgeron", {"Apportez la Boîte de lingots à David Wayne au Refuge de Wayne, dans la forêt de Terokkar."}},
[10664] = {"Matériel supplémentaire", {"David Wayne du Refuge de Wayne veut que vous lui apportiez une Structure en adamantite, 4 Cuirs granuleux lourds et un Totem de sauvegarde contre les démons."}},
[10665] = {"Tout frais du Méchanar", {"David Wayne du Refuge de Wayne veut que vous lui apportiez une Cellule de mana surchargée."}},
[10666] = {"Le Lexicon Demonica", {"Procurez-vous le Lexicon Demonica sur le Grand Maître Vorpil et apportez-le à David Wayne au Refuge de Wayne."}},
[10667] = {"De la terre du monde souterrain", {"Obtenez une Fiole de terre du monde souterrain de l'hydromancienne Thespia et apportez-la à David Wayne au Refuge de Wayne."}},
[10668] = {"Contre les Illidari", {"Altruis le Souffrant veut que vous alliez tuer Lothros à la Halte Illidari dans la vallée d'Ombrelune."}},
[10669] = {"Contre toute attente", {"Altruis le Souffrant veut que vous preniez la Lance en argent imprégné et que vous l'utilisiez dans la Clairière du portail près du Lac des furoles du marécage de Zangar pour réveiller Xeleth. Retournez voir Altruis lorsque vous aurez tué le démon."}},
[10670] = {"La Larme de la Terre-mère", {"Reprenez la Larme de la Terre-mère au Porteguerre O'mrogg et apportez-la à David Wayne au Refuge de Wayne."}},
[10671] = {"Plus d'une livre de chair", {"Collectez 5 morceaux de Chair éthernergisée."}},
[10672] = {"Franchement, ça n'a aucun sens...", {"Le chercheur Tiorus du village d'Ombrelune dans la vallée d'Ombrelune vous demande de voyager jusqu'aux coulées de lave près des ruines de Baa'ri, d'utiliser l'Unité d'arcano-contrôle pour prendre le contrôle d'un Arcano-Scorp, de nager dans la lave à l'aide de l'Arcano-Scorp et de marquer 8 Grands Dimeurtrodons de gangrefeu."}},
[10673] = {"Gangréchine la Très-Grande", {"Le chercheur Tiorus du village d'Ombrelune dans la vallée d'Ombrelune vous demande de tuer Gangréchine la Très-Grande et de récupérer la Peau de Gangréchine."}},
[10674] = {"Et la lumière fut... piégée", {"Utilisez le Piège à lumière pour attraper 15 Orbes de lumière razaani."}},
[10675] = {"Dégnomez-les tous !", {"Récupérez la Collection d’âmes."}},
[10676] = {"La plaie des Illidari", {"Attendez que David Wayne ait terminé de forger une épée pour vous."}},
[10677] = {"Le deuxième service...", {"Gnomus du bastion des Marteaux-hardis dans la vallée d'Ombrelune veut que vous récupériez 1 Gésier de gangrefeu surdéveloppé."}},
[10678] = {"Le plat principal !", {"Gnomus du bastion des Marteaux-hardis dans la vallée d'Ombrelune veut que vous récupériez la Peau de Gangréchine."}},
[10679] = {"Tremper la lame", {"Voyagez jusqu'aux Bassins Brûlants situés près du sommet de la Main de Gul'dan et utilisez la Lame plaie-des-Illidari forgée pour la tremper dans les bassins de lave. Une fois que vous aurez la Lame plaie-des-Illidari trempée, retournez auprès de David Wayne au Refuge de Wayne."}},
[10680] = {"La main de Gul'dan", {"Parlez au soigneterre Torlok à l'Autel de la damnation dans la vallée d'Ombrelune."}},
[10681] = {"La main de Gul'dan", {"Parlez au soigneterre Torlok à l'Autel de la damnation dans la vallée d'Ombrelune."}},
