[10683] = {"Tablettes de Baa'ri", {"L'arcaniste Thelis du Sanctum des Étoiles veut que vous collectiez 12 Tablettes baa'ri aux Ruines de Baa'ri.","Accomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10684] = {"Oronu l'Ancien", {"L'arcaniste Thelis du Sanctum des Étoiles veut que vous preniez les Ordres d'Akama à Oronu l'Ancien aux Ruines de Baa'ri.","Accomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10685] = {"Les corrupteurs cendrelangues", {"Procurez-vous les quatre fragments du médaillon sur Haalum, Eykenen, Lakaan et Uylaru et rapportez-les à l'arcaniste Thelis au Sanctum des Étoiles dans la vallée d'Ombrelune.","Accomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10686] = {"La Cage de la gardienne", {"L'arcaniste Thelis veut que vous entriez dans la Cage de la gardienne, au sud des ruines de Baa'ri, et que vous interrogiez Sanoru pour apprendre où se trouve Akama.","Accomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10687] = {"Les terrains d'entraînement de Karabor", {"Larissa Heurtesoleil au Sanctum des Étoiles veut que vous vous procuriez 8 Glaives solfurie sur les Suppliants solfurie et les Initiés solfurie aux Ruines de Karabor, au nord-est du Sanctum des étoiles.","Accomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
[10688] = {"Une diversion nécessaire", {"Larissa Heurtesoleil veut que vous retourniez aux Ruines de Karabor et que vous trouviez un moyen de lâcher Azaloth sur ses geôliers.","Accomplir des quêtes pour les Clairvoyants fera baisser votre réputation auprès de l'Aldor."}},
