[10701] = {"Briser Néantroche", {"Tuez Néantroche, puis présentez-vous au Chef-de-fusée Karlingue dans la Zone 52 de Raz-de-Néant."}},
[10702] = {"Un travail de grunt...", {"Le suzerain Or'barokh du village d'Ombrelune dans la vallée d'Ombrelune vous demande de tuer 20 spécimens de la Faune de la vallée d'Ombrelune.","Ces créatures comprennent les Sangliers gangrenés, les Chimères Ailevile et les Pinceurs Brûlecoquille. N'importe quelle combinaison conviendra."}},
[10703] = {"Mettez des bottes...", {"Le thane Yoregar du bastion des Marteaux-hardis dans la vallée d'Ombrelune vous demande de tuer 20 spécimens de la faune de la vallée d'Ombrelune.","Ces créatures comprennent les Sangliers gangrenés, les Chimères Ailevile et les Pinceurs Brûlecoquille. N'importe quelle combinaison fera l'affaire."}},
[10704] = {"Comment pénétrer dans l'Arcatraz", {"A'dal vous a chargé de récupérer les Pièces supérieures et inférieures de la clé de l'Arcatraz. Rapportez-les-lui et il s'en servira pour vous confectionner la Clé de l'Arcatraz."}},
[10705] = {"Le voyant Udalo", {"Trouvez le voyant Udalo à l'intérieur de l'Arcatraz dans le Donjon de la Tempête."}},
[10706] = {"Un mystérieux présage", {"Retournez voir Akama à la Cage de la gardienne dans la vallée d'Ombrelune."}},
[10707] = {"La terrasse Ata'mal", {"Allez au sommet de la Terrasse Ata'mal dans la vallée d’Ombrelune, et procurez-vous le Cœur de fureur. Retournez voir Akama à la Cage de la gardienne dans la vallée d'Ombrelune quand vous aurez accompli cette tâche."}},
