[10709] = {"Réunion", {"Parlez à Rexxar, au bastion des Sire-tonnerre, dans les Tranchantes."}},
[10710] = {"Vol d'essai : la Crête chantante", {"Signez la Décharge de Tally, puis allez parler à Rally Rossecoquin pour utiliser le Zephyrium Capacitorium."}},
[10711] = {"Vol d'essai : le point d'ancrage de Razaan", {"Parlez à Rally Rossecoquin pour utiliser le Zephyrium Capacitorium."}},
[10712] = {"Vol d'essai : la sylve Ruuan", {"Parlez à Rally Rossecoquin pour utiliser le Zephyrium Capacitorium. Lors du vol vers la Sylve Ruuan, faites tourner la Girouette néantine. Remettez la Girouette néantine tournoyante à O'Mally Rossecoquin au Bosquet éternel."}},
[10713] = {"... et un temps pour l'action", {"Tuez 10 Elagueurs du culte du Ver."}},
