[10852] = {"Les amis perdus", {"Ethan veut que vous trouviez 12 de ses amis disparus au Voile Skith et que vous reveniez ensuite le voir à la Caravane des réfugiés."}},
[10853] = {"L'appel des esprits", {"Utilisez les Totems d'appel des esprits pour collecter 8 Esprits de drakes du Néant inférieurs."}},
[10854] = {"La force de Neltharaku", {"Neltharaku, qui vole au-dessus des champs de l'Aile-du-Néant dans la vallée d'Ombrelune, veut que vous libériez 5 Drakes de l'Aile-du-Néant asservis."}},
[10855] = {"Les saccageurs gangrenés ne passeront pas !", {"Récupérez 5 mesures de Gaz de Néant condensé auprès des Mekgénieurs gan'arg de la Base de forge : Oubli, au nord-ouest de la Foudreflèche, et chargez-les dans un Saccageur gangrené inactif proche. Revenez voir le Traqueur-du-Néant Nauthis quand vous aurez terminé."}},
[10856] = {"La meilleure défense", {"Le Traqueur-du-Néant Nauthis veut que vous tuiez 12 Porte-courroux à la Base de forge Géhenne, au nord de la Foudreflèche."}},
[10857] = {"Téléportez-moi ça !", {"Le Traqueur-du-Néant Nauthis veut que utilisiez le Bâtonnet d'interférence mentale sur les mo'arg du Camp de forge Géhenne, et utilisez leur capacité pour Faire exploser le téléporteur sur les trois téléporteurs du camp de forge."}},
[10858] = {"Karynaku", {"Trouvez Karynaku à la Forteresse Gueule-de-dragon."}},
[10859] = {"Réunir les orbes", {"Utilisez le Totem collecteur d'orbe pour collecter 15 Orbes de lumière razaani."}},
[10860] = {"Délices de Mok'Nathal", {"Récupérez 3 Côtelettes de raptor et 3 Chairs de serpent."}},
[10861] = {"Voile Lithic : Frappe préventive", {"Cassez les œufs maudits au Voile Lithic. Rachetez 3 Oisillons et tuez 3 Oisillons maléfiques avant de retourner auprès de Kirrik l'Eveillé à la Caravane des réfugiés."}},
[10862] = {"Se rendre à la Horde", {"Rokag veut que vous trouviez Rilak le Racheté dans le quartier de la Ville basse à Shattrath et que vous exigiez que lui et son peuple se rendent à la Horde."}},
[10863] = {"Les secrets des arakkoa", {"Dame Dena Kennedy veut que vous alliez trouver Rilak le Racheté dans le quartier de la Ville basse à Shattrath pour découvrir quels secrets des arakkoa il est prêt à partager."}},
[10864] = {"Le fardeau des âmes", {"Tuez 20 Orcs de la Main brisée, puis retournez voir l'Apothicaire Antonivitch à Thrallmar."}},
[10865] = {"Informez Leoroxx !", {"Parlez à Leoroxx de ce que préparent les éthériens razaani. Vous le trouverez au village de Mok'Nathal dans les Tranchantes."}},
[10866] = {"Zuluhed le Fourbu", {"Tuez Zuluhed le Fourbu et récupérez la Clé de Zuluhed. Utilisez-la sur les Chaînes de Zuluhed pour libérer Karynaku."}},
[10867] = {"La seule réponse possible", {"Récupérez la Collection d'âmes."}},
[10868] = {"Le sentier de la guerre arakkoa", {"La conseillère Faila vous demande de tuer 14 Serrénites shienor et 6 Ensorceleurs shienor avant de revenir la voir au Fort des Brise-pierres."}},
[10869] = {"Restreindre la nuée", {"Le lieutenant Gravillemartel vous demande de tuer 14 Serrénites shienor et 6 Ensorceleurs shienor avant de revenir le voir au Bastion allérien."}},
[10870] = {"Allié du vol du Néant", {"Permettez à Karynaku de vous ramener auprès de Mordenai dans les Champs de l'Aile-du-Néant."}},
[10871] = {"Allié du vol du Néant", {"Permettez à Karynaku de vous ramener auprès de Mordenai dans les Champs de l'Aile-du-Néant."}},
[10872] = {"Zuluhed le Fourbu", {"Tuez Zuluhed le Fourbu et récupérez la Clé de Zuluhed. Utilisez-la sur les Chaînes de Zuluhed pour libérer Karynaku."}},
[10873] = {"Pris dans la nuit", {"L’Éclaireur Navrin veut que vous vous rendiez sur la Crête de Toile-néant et que vous libériez 6 Guerriers sha'tar entoilés. Revenez ensuite le voir au Camp de base sha'tari."}},
[10874] = {"Voile Shalas : Feux de signal", {"Kirrik l'Eveillé de la Caravane des réfugiés vous demande d'éteindre les quatre feux de signal du Voile Shalas."}},
[10875] = {"Se présenter à Nazgrel", {"Parlez avec Nazgrel à Thrallmar, dans la Péninsule des Flammes infernales."}},
[10876] = {"Le pied de la citadelle", {"Tuez le Commandant de corps Gorax, placez le Défi de la Horde sur son cadavre, tuez la Main de Kargath, puis rapportez la Tête de la Main de Kargath à Nazgrel dans Thrallmar."}},
[10877] = {"La relique de l'effroi", {"Chênuin veut que vous partiez vers l'est pour la Caravane abandonnée afin de récupérer la Relique de l'effroi. Revenez voir Chênuin quand cette tâche sera achevée."}},
[10878] = {"Avant que l'ombre...", {"Mekeda de la Caravane de réfugiés veut que vous tuiez 8 Tirailleurs de la Cabale, 4 Tisse-sorts de la Cabale et 2 Initiés de la Cabale."}},
[10879] = {"L'offensive de Skettis", {"Repoussez les attaquants et parlez à Rilak le Racheté."}},
[10880] = {"Les ordres de la Cabale", {"Remettez les Ordres de la Cabale à Mekeda, à la Caravane de réfugiés, dans le Désert des Ossements."}},
[10881] = {"La tombe des ombres", {"Rendez-vous dans la Tombe des ombres, à l'ouest de la Caravane de réfugiés. Récupérez-y le Drapé d'Arunen, le Marteau de K'alen et le Parchemin d'Atalor. Lorsque vous aurez terminé, retournez voir Mekeda à la Caravane de réfugiés."}},
[10882] = {"Messager funeste", {"Vous avez été chargé d'aller sur le satellite de l'Arcatraz du donjon de la Tempête et de tuer le Messager Cieuriss. Revenez voir A'dal à la Terrasse de la Lumière de Shattrath quand ce sera fait."}},
[10883] = {"La clé de la Tempête", {"Parlez à A'dal à Shattrath."}},
[10884] = {"L'épreuve des naaru : Miséricorde", {"A'dal de Shattrath vous demande de prendre la Hache inutilisée du bourreau dans les Salles brisées de la citadelle des Flammes infernales.","Cette quête doit être accomplie en mode de difficulté du donjon Héroïque."}},
[10885] = {"L'épreuve des naaru : Force", {"A'dal de Shattrath vous demande de récupérer le Trident de Kalithresh et l'Essence de Marmon.","Cette quête doit être accomplie en mode de difficulté du donjon Héroïque."}},
[10886] = {"L'épreuve des naaru : Ténacité", {"A'dal de Shattrath vous demande de sauver Milhouse Tempête-de-mana de l'Arcatraz du donjon de la Tempête.","Cette quête doit être accomplie en mode de difficulté du donjon Héroïque."}},
[10887] = {"Sortir du tombeau", {"Aidez Akuno a trouver le chemin de la Caravane de réfugiés. Lorsque vous aurez terminé cette quête, parlez à Mekeda."}},
[10888] = {"L'épreuve des naaru : Magtheridon", {"A'dal de Shattrath vous demande de tuer Magtheridon."}},
[10889] = {"Retour à Shattrath", {"Rendez-vous à Shattrath et parlez à Rilak le Racheté dans la Ville Basse."}},
[10890] = {"[UNUSED] <NYI> ", nil},
[10891] = {"L'armure impériale en plaques", {"Parlez à Derotain Siroteboue à Gadgetzan, en Tanaris."}},
[10892] = {"L'armure impériale en plaques", {"Parlez à Derotain Siroteboue à Gadgetzan, en Tanaris."}},
[10893] = {"Longue-queue est le pivot", {"Éliminez Draaca Longue-queue, le chef des sectateurs du Ver dans le Tunnel Crâne-du-ver."}},
[10894] = {"Le guetteur du Crâne-du-ver", {"Apportez la Note de rendez-vous au Guetteur Lunombrage à l'entrée du tunnel Crâne-du-ver dans les Tranchantes."}},
[10895] = {"Que Zeth'Gor brûle !", {"Utilisez les Balises fumigènes sur chacune des tours de Zeth'Gor, puis retournez voir le Chef d'escadrille Dabir'ee à la Halte de l'expédition."}},
[10896] = {"Les protecteurs infestés", {"Lakotae veut que vous tuiez 25 Mites des bois puis que vous reveniez le voir à la Caravane de réfugiés. On peut trouver les mites à l'intérieur des corps des Enragés des forêts pourrissants et des Marche-racine infestés."}},
[10897] = {"Maître des potions", {"Lauranna Thar'well veut que vous vous rendiez dans la Botanica du donjon de la Tempête et que vous récupériez le Guide pratique du botaniste sur le Grand botaniste Freywenn. De plus, elle veut que vous lui rameniez 5 Potions de super-soins, 5 Potions de super-mana et 5 Potions de sommeil sans rêve majeures.","*ATTENTION !* Vous ne pouvez choisir qu'une seule spécialité d'alchimie."}},
[10898] = {"Aile-du-ciel", {"Escortez Aile-du-ciel et allez voir Rilak le Racheté à Shattrath."}},
[10899] = {"Maître de la transmutation", {"Zarevhi, de la Foudreflèche du Raz-de-Néant, veut que vous lui rameniez 4 Pouvoirs primordiaux.","*ATTENTION !* Vous ne pouvez choisir qu'une seule spécialité d'alchimie."}},
[10900] = {"La marque de Vashj", nil},
[10901] = {"Le gourdin de Kar'desh", {"Skar'this l'Hérétique, dans les Enclos aux esclaves héroïques du Réservoir de Glissecroc, veut que vous lui apportiez la Chevalière terrestre et la Chevalière flamboyante."}},
[10902] = {"Maître des élixirs", {"Partez pour les Grottes du temps dans le Noir Marécage et procurez-vous 10 Essences d'infinité sur les Seigneurs des failles et Gardiens des failles. Apportez-les avec 5 Élixirs de défense majeure, 5 Élixirs de maîtrise et 5 Élixirs d'agilité majeure à Lorokeem dans la Ville basse de Shattrath.","*ATTENTION !* Vous ne pouvez choisir qu'une spécialisation en alchimie."}},
[10903] = {"Retour au bastion de l'Honneur", {"Parlez à Klatu, à l'auberge du Bastion de l'Honneur."}},
[10904] = {"La récolte des munitions gangrenées", {"Récupérez 5 Boulets de gangrecanon."}},
[10905] = {"Maître des potions", {"Parlez à Lauranna Thar'well au Refuge cénarien dans le Marécage de Zangar.","*ATTENTION !* Vous ne pouvez choisir qu'une spécialisation en alchimie."}},
[10906] = {"Maître des élixirs", {"Parlez à Lorokeem dans la Ville basse de Shattrath.","*ATTENTION !* Vous ne pouvez choisir qu'une spécialisation en alchimie."}},
[10907] = {"Maître de la transmutation", {"Parlez à Zarevhi à la Foudreflèche au Raz-de-Néant.","*ATTENTION !* Vous ne pouvez choisir qu'une spécialisation en alchimie."}},
[10908] = {"Parler à Rilak le Racheté", {"Kirrik l'Éveillé veut que vous parliez à Rilak le Racheté dans la Ville basse de Shattrath."}},
[10909] = {"Les esprits gangrenés", {"Klatu au Bastion de l'Honneur vous a demandé d'utiliser la Relique d'anachorète à l'Avant-poste de Gor'gaz, au sud-ouest du Bastion. Vous devrez ensuite tuer des Orcs de la Main brisée près de la relique, puis détruire les Esprits gangrenés libérés pour venger le Colonel Jules."}},
[10910] = {"La Porte de la mort", {"Utilisez le Signal druidique à la Porte de la mort pour appeler et parler à un Druide du Bosquet éternel."}},
[10911] = {"Feu à volonté !", {"Utilisez les Munitions naturalisées pour prendre le contrôle des Gangrecanons de la Porte de la mort. Utilisez-les pour détruire le Portail dimensionnel sud et le Portail dimensionnel nord."}},
[10912] = {"Le maître-chien", {"Tuez Baelmon le Maître-chien."}},
[10913] = {"Des funérailles inadéquates", {"Le commandant Ra'vaj de l'Avant-poste shat'ari du désert des Ossements, dans la forêt de Terokkar, veut que vous brûliez 8 Guerriers auchenaï tués et 8 Redresseurs de torts sha'tar tués."}},
[10914] = {"Il nous faut un héros", {"Le commandant Ra'vaj de l'Avant-poste shat'ari du désert des Ossements, dans la forêt de Terokkar, veut que vous tuiez 12 Initiés auchenaï et 5 Auspices-funestes auchenaï."}},
[10915] = {"L'exarque déchu", {"Le commandant Ra'vaj de l'Avant-poste shat'ari du désert des Ossements, dans la forêt de Terokkar, veut que vous détruisiez le contenu du Cercueil auchenaï."}},
[10916] = {"À la recherche de chapelets", {"Retrouvez le Chapelet draeneï et rapportez-le à l'Assistant Klatu dans le Bastion de l'Honneur."}},
[10917] = {"La souffrance du paria", {"Apportez 30 Plumes d'arakkoa à Vekax dans la Ville basse de Shattrath."}},
[10918] = {"Plus de plumes", nil},
[10919] = {"Les friandises de Fei Fei", nil},
[10920] = {"Au nom des disparus", {"Le redresseur de torts Haylen veut que vous tuiez 20 Veuves croc-d'effroi, puis que vous reveniez le voir juste à l'ouest de la Crête de Toile-néant."}},
[10921] = {"Terokkarantula", {"Le redresseur de torts Haylen veut que vous alliez tuer Terokkarantula et que vous reveniez le voir à l'ouest de la crête de Toile-néant."}},
[10922] = {"En fouillant dans les os", {"Escortez Leglas et son équipe et protégez-les pendant les fouilles. Si vous réussissez, retournez voir Nanowitz à l'Avant-poste sha'tari."}},
[10923] = {"L'approche du mal", {"Chênuin veut que vous emportiez la Relique de l'effroi au Monticule grouillant. Là-bas, tuez des nécrorateurs et des auspices-funestes auchenaï pour collecter 20 Crânes du destin. Trouvez ensuite le Cercle d'invocation du Monticule grouillant et utilisez la Relique de l'effroi pour invoquer et détruire Teribus le Maudit. Revenez voir Chênuin quand ce sera fait."}},
[10924] = {"La part du diabl-otin !", {"Sab'aoth de la Cosmovrille veut 8 fioles de Sang de poursuivant dimensionnel."}},
[10925] = {"L'approche du mal", nil},
[10926] = {"Retour au camp de base sha'tari", {"Le redresseur de torts Haylen veut que vous retourniez au Camp de base sha'tari, dans le Désert des Ossements et que vous y parliez à l'Éclaireur Navrin."}},
[10927] = {"Tuer les rampantes", {"Tuez 6 Rampantes des cavernes."}},
[10928] = {"Tuer les rampantes", {"Tuez 6 Rampantes des cavernes."}},
[10929] = {"Martelheurt", {"Nanowitz, de l'Avant-poste sha'tari du désert des Ossements, veut que vous lui rameniez 8 Carcasses de cribleurs des ossements adultes."}},
[10930] = {"Le gros ver des ossements", {"Nanowitz, de l'Avant-poste sha'tari du désert des Ossements, veut que vous trouviez des Organes d'énorme ver des ossements.","Il veut aussi qu'on lui rapporte son Martelheurt."}},
[10931] = {"Retour à l'Exodar", {"Parlez à Caedmos à l'Exodar."}},
[10932] = {"Retour à l'Exodar", {"Parlez à Caedmos à l'Exodar."}},
[10933] = {"Retour à Lune-d'Argent", {"Parlez à Aldrae à Lune-d'argent."}},
[10934] = {"Retour à Lune-d'Argent", {"Parlez à Aldrae à Lune-d'argent."}},
[10935] = {"L'exorcisme du colonel Jules", {"Parlez à l'Anachorète Barada. Utilisez le chapelet pour l'aider pendant le rituel, puis parlez au Colonel Jules quand il sera sauvé. Pour finir, retournez voir l'Assistant Klatu."}},
[10936] = {"Trollemort vous cherche !", {"L'Assistant Klatu vous a indiqué que le Commandant de corps Danath Trollemort vous cherchait. Allez lui parler à la caserne du Bastion de l'Honneur dans la Péninsule des Flammes infernales."}},
[10937] = {"Détruisez le maître de manœuvre", {"Le Commandant de corps Danath Trollemort vous a ordonné de tuer au plus vite le Maître de manœuvre Zurok. Retournez voir le commandant de corps au Bastion de l'Honneur dans la Péninsule des Flammes infernales une fois que le maître de manœuvre sera mort."}},
[10938] = {"Suite de Bénédictions de Sombrelune", {"Rapporter la suite de Bénédictions à la foire de Sombrelune quand elle reviendra en ville."}},
[10939] = {"Suite de Tempêtes de Sombrelune", {"Rapporter la suite de Tempêtes à la foire de Sombrelune quand elle reviendra en ville."}},
[10940] = {"Suite de Furies de Sombrelune", {"Rapporter la suite de Furies à la foire de Sombrelune quand elle reviendra en ville."}},
[10941] = {"Suite de Déraison de Sombrelune", {"Rapporter la suite de Déraison à la foire de Sombrelune quand elle reviendra en ville."}},
[10942] = {"La Semaine des enfants", {"Utiliser le Sifflet d'orphelin elfe de sang pour parler à l'enfant que vous surveillerez durant la Semaine des enfants."}},
[10943] = {"La Semaine des enfants", {"Utiliser le Sifflet d'orphelin draeneï pour parler à l'enfant que vous surveillerez durant la Semaine des enfants."}},
[10944] = {"Un secret compromis", {"Allez à la Cage de la gardienne dans la vallée d'Ombrelune et parlez avec Akama."}},
[10945] = {"Hch'uu et le peuple champignon", {"Emmenez votre orpheline, Salandria, à Sporeggar dans le marécage de Zangar. N'oubliez pas de l'appeler si elle n'est pas présente lorsque vous arrivez. Puis aller parler à Hch'uu."}},
[10946] = {"La ruse des Cendrelangues", {"Pénétrez dans le Donjon de la Tempête et tuez Al'ar en portant la Capuche de Cendrelangue. Retournez voir Akama dans la vallée d'Ombrelune une fois que ce sera fait."}},
[10947] = {"Un artéfact du passé", {"Allez aux Grottes du temps en Tanaris, et rendez-vous à la Bataille du mont Hyjal. Quand vous y serez, triomphez de Rage Froidhiver et rapportez le Phylactère chronophasé à Akama dans la vallée d'Ombrelune."}},
[10948] = {"L'âme otage", {"Rendez-vous à Shattrath pour soumettre la requête d'Akama à A'dal."}},
[10949] = {"L'entrée dans le Temple noir", {"Rendez-vous à l'entrée du Temple noir dans la vallée d'Ombrelune et allez parler à Xi'ri."}},
[10950] = {"Auchindoun et le Cercle d'observance", {"Emmenez votre orpheline, Dornaa, à la Pierre de rencontre du Cercle d'observance, au centre d'Auchindoun. Auchindoun est situé au centre du désert des Ossements de la forêt de Terokkar. N'oubliez pas de l'appeler si elle n'est pas présente lorsque vous arrivez."}},
[10951] = {"Une visite à la Porte des ténèbres", {"Emmenez votre orpheline, Salandria, en haut de l'escalier du Destin, pour qu'elle puisse passer devant la Porte des ténèbres dans la péninsule des Flammes infernales. N'oubliez pas de l'appeler si elle n'est pas présente lorsque vous arrivez."}},
[10952] = {"Une visite à la Porte des ténèbres", {"Emmenez votre orpheline, Dornaa, en haut de l'escalier du Destin pour qu'elle passe devant la Porte des ténèbres dans la péninsule des Flammes infernales. N'oubliez pas de l'appeler si elle n'est pas présente lorsque vous arrivez."}},
[10953] = {"Une visite au Trône des éléments", {"Emmenez votre orpheline, Salandria, au Trône des éléments à Nagrand. N'oubliez pas de l'appeler si elle n'est pas présente lorsque vous arrivez. Ensuite, allez parler à l'Élémentaliste Sharvak."}},
[10954] = {"Jheel est au point d'ancrage Aeris !", {"Emmenez votre orpheline, Dornaa, au Point d'ancrage Aeris en Nagrand. N'oubliez pas de l'appeler si elle n'est pas présente lorsque vous arrivez. Ensuite, parlez avec Jheel."}},
[10955] = {"Morthis Murmedaile", {"Allez parler à Morthis Murmedaile au Refuge cénarien dans le marécage de Zangar."}},
[10956] = {"Le Siège du naaru", {"Emmenez votre orpheline, Dornaa, voir O'ros au pied du Siège du naaru dans l'Exodar, sur l'île de Brume-azur. N'oubliez pas de l'appeler si elle n'est pas présente lorsque vous arrivez.","Rappelez-vous que vous pouvez utiliser votre carte à l'intérieur de la ville."}},
[10957] = {"La rédemption du Cendrelangue", {"Aidez Akama à reprendre le contrôle de son âme en vainquant l'Ombre d'Akama dans le Temple noir. Retournez voir le Voyant Kanai quand ce sera fait."}},
[10958] = {"À la recherche des Cendrelangues", {"Trouvez les Ligemorts d'Akama à l'intérieur du Temple noir."}},
[10959] = {"La chute du Traître", {"Le voyant Kanai veut que vous vainquiez Illidan à l'intérieur du Temple noir."}},
[10960] = {"Quand je serai grande…", nil},
[10961] = {"La protection de l'éveil", {"Apportez 10 Fleurs des tourbières, 10 Lichens anciens et 10 Glauriers à Morthis Murmedaile, au Refuge cénarien."}},
[10962] = {"Il est temps de visiter les Grottes", {"Emmenez votre orpheline, Dornaa, devant Zaladormu, le dragon qui se trouve sur l'estrade au milieu des Grottes du temps. N'oubliez pas de l'appeler si elle n'est pas présente lorsque vous arrivez.","Ensuite, achetez-lui une Figurine de dragon auprès de l'Intendant des Gardiens du temps, Alurmi, près du fond du tunnel d'entrée."}},
[10963] = {"Il est temps de visiter les Grottes", {"Emmenez votre orpheline, Salandria, devant Zaladormu, le dragon qui se trouve sur l'estrade au milieu des Grottes du temps. N'oubliez pas de l'appeler si elle n'est pas présente lorsque vous arrivez.","Ensuite, achetez-lui une Figurine de dragon auprès de l'Intendant des Gardiens du temps, Alurmi, près du fond du tunnel d'entrée."}},
[10964] = {"Le réveil du dormeur", {"Utilisez le Gardien de l'éveil pour aider Clintar Marcherêve à sortir du Rêve d'émeraude. Une fois qu'il a repris connaissance, parlez-lui."}},
[10965] = {"Plus qu'un simple rêve", {"Protégez l'Esprit de Clintar Marcherêve pendant qu'il recueille les reliques d'Aviana, puis allez trouver la Garderêve Lurosa."}},
[10966] = {"Retour à l'orphelinat", {"Ramenez votre protégée à l'orphelinat de Shattrath, en rendant le Sifflet d'orphelin draeneï à Miss Hérikorde, directrice de l'orphelinat, dans la Ville basse."}},
[10967] = {"Retour à l'orphelinat", nil},
[10968] = {"En visite chez le long-voyant", nil},
[10969] = {"Rechercher Ameer", {"Trouvez le Commandant Ameer au Poste de garde du Protectorat."}},
[10970] = {"Une mission miséricordieuse", {"Procurez-vous la Clé de la prison de l'Ethereum récupérée pour le Commandant Ameer au Poste de garde du Protectorat."}},
[10971] = {"Secrets de l'Ethereum", {"Le Commandant Ameer, au Poste de garde du Protectorat au Raz-de-Néant, veut que vous lui apportiez une Plaque d'identité de prisonnier de l'Ethereum."}},
[10972] = {"Répertorier les prisonniers de l'Ethereum", nil},
[10973] = {"Un millier de mondes", {"Le Commandant Ameer, au Poste de garde du Protectorat au Raz-de-Néant, veut que vous lui apportiez 5 Clés de la prison de l'Ethereum."}},
[10974] = {"Les chambres de stase de Bash'ir", {"Utilisez la Tablette-clé de l'Ethereum - Alpha pour ouvrir la Chambre de stase alpha au Point d'ancrage de Bash'ir dans les Tranchantes.","Rapportez les Indices de l'alpha à l'Image du commandant Ameer au Point d'ancrage de Bash'ir."}},
[10975] = {"La purge des chambres de Bash'ir", nil},
[10976] = {"La Marque du roi-nexus", {"L'Image du commandant Ameer au Point d'ancrage de Bash'ir dans les Tranchantes vous demande de lui rapporter une Marque du roi-nexus."}},
[10977] = {"Les chambres de stase des Tombes-mana", {"L'Image du commandant Ameer au Point d'ancrage de Bash'ir dans les Tranchantes vous demande d'utiliser la Clé de la chambre de stase des Tombes-mana dans les Tombes-mana d'Auchindoun.","Cette quête ne peut être accomplie qu'au niveau de difficulté Héroïque."}},
[10978] = {"Retourner voir Morthis Murmedaile", {"Apportez les Reliques d'Aviana à Morthis Murmedaile au Refuge cénarien dans le marécage de Zangar."}},
[10979] = {"En route pour le Bosquet éternel", {"Allez parler à Arthorn Chant-du-vent au Bosquet éternel dans les Tranchantes."}},
[10980] = {"Le Livre du Corbeau", {"Utilisez un Œil de raie de l'éther avec votre Pierre de prophète pour voir les fantômes du culte du corbeau. Cherchez l'esprit de Sai'kkal l'Ancien dans la Cime du vortex et interrogez-le au sujet du livre du corbeau, puis retournez auprès d'Arthorn Chant-du-vent."}},
[10981] = {"La chambre personnelle du prince-nexus Shaffar", nil},
[10982] = {"L'œil d'Haramad", nil},
[10983] = {"Mog'dorg le Ratatiné", {"Allez voir Mog'dorg le Ratatiné au sommet de l'une des tours qui se trouvent devant le Cercle de sang dans les Tranchantes."}},
