[10985] = {"Une distraction pour Akama", {"Assurez-vous qu'Akama et Maiev pénètrent bien dans le Temple noir de la Vallée d'Ombrelune une fois que les forces de Xi'ri auront fait diversion."}},
[10986] = {"Des yeux dans le ciel", {"Allez parler à la Gardienne Elaira sur la Crête du Crépuscule à Nagrand."}},
[10987] = {"La chasse à la crécerelle", {"Utilisez le Filet à crécerelles pour capturer une Crécerelle sauvage, et ramenez-la à la Gardienne Elaira sur la Crête du Crépuscule."}},
[10988] = {"Les pierres du Corbeau", {"Rassemblez 8 Pierres du Corbeau à Skettis et apportez-les à Arthorn Chant-du-vent au Bosquet éternel dans les Tranchantes."}},
[10989] = {"Mog'dorg le Ratatiné", {"Parlez à Mog'dorg le Ratatiné au sommet de la tour devant laquelle se tient Chort, juste devant le Cercle de sang, dans les Tranchantes."}},
