[11002] = {"La chute de Magtheridon", {"Rapportez la Tête de Magtheridon au Commandant de corps Danath Trollemort au bastion de l'Honneur dans la péninsule des Flammes infernales."}},
[11003] = {"La chute de Magtheridon", {"Rapportez la Tête de Magtheridon à Nazgrel à Thrallmar dans la péninsule des Flammes infernales."}},
