[11072] = {"Lignées antagonistes", {"Trouvez les Piles de crânes au milieu des cercles d'invocation de Skettis. Invoquez et vainquez chacun des descendants des adversaires de Terokk en utilisant 10 Parchemins perdus dans le temps à la Pile de crâne. Retournez auprès de Hazzik au Raie'odrome de Noirvent avec un gage de chacun d'eux."}},
[11073] = {"La chute de Terokk", {"Emportez l'Offrande perdue dans le temps préparée par Hazzik à la Pile de crânes qui se trouve au milieu de Skettis, puis invoquez et terrassez Terokk. Retournez voir le Commandant Adaris de la Garde-ciel quand vous aurez accompli votre mission."}},
[11074] = {"Les attributs des descendants", {"Rassemblez des Parchemins perdus dans le temps détenus par les arakkoa de Skettis, et emportez-les jusqu'à une Pile de crânes au milieu d'un cercle d'invocation de Skettis. Invoquez et vainquez les descendants des adversaires de Terokk et rapportez les Serres d'Akkarai, l'Échine de Garrok, l'Écaille de Vekkaz et les Griffes de Gezzarak à Hakkiz."}},
[11075] = {"Les mines de l'Aile-du-Néant", {"Allez voir la Maîtresse des mines sur l'Escarpement de l'Aile-du-Néant."}},
[11076] = {"Ramasser les morceaux...", {"La Maîtresse des mines, des Mines de l'escarpement de l'Aile-du-Néant de la vallée d'Ombrelune, veut que vous récupériez 15 caisses de Cargaison de Mine-néant."}},
[11077] = {"Les dragons sont les derniers de nos soucis", {"Le Contremaître Gueule-de-dragon des Mines de l'Aile-du-Néant de la vallée d'Ombrelune veut que vous tuiez 15 Ecorcheurs mine-néant et 5 Ravageurs mine-néant."}},
[11078] = {"Pour régner sur les cieux", {"Récupérez des Dents de dragon."}},
[11079] = {"Un gangrefouet pour Gahk", {"Procurez-vous un Gangrefouet."}},
[11080] = {"Les émanations des reliques", {"Obtenir des Emanations apogides d’une Relique apogide."}},
[11081] = {"La grande révolte des Bourbesang", {"Apportez les Plans d'évasion bourbesang à la Maîtresse des mines."}},
[11082] = {"À la recherche de la vérité", {"Trouvez un Surveillant bourbesang et interrogez-le. Retournez voir la Maîtresse des mines avec les Informations des Bourbesang et la Main du surveillant."}},
[11083] = {"Affolés et perturbés", {"Ronag l'Esclavagiste, des Mines de l'Aile-du-Néant dans la vallée d'Ombrelune, vous demande de tuer le Contremaître bourbesang affolé et 5 Mineurs bourbesang affolés."}},
