[11093] = {"Raies du Néant affamées", {"Utilisez la Cage de raie du Néant dans les bois qui se trouvent au sud du Raie'odrome de Noirvent et tuez des Poursuivants dimensionnels près de la Raie du Néant affamée. Retournez voir le Dresseur Deesak de la Garde-ciel quand vous aurez terminé."}},
[11094] = {"Tuez-les tous !", {"Avertissez l'Arcaniste Thelis du contenu de votre mission et apportez-lui le Lance-fusée gueule-de-dragon au Sanctum des Étoiles dans la vallée d'Ombrelune."}},
[11095] = {"Le commandant Hobb", {"Donnez le Lance-fusée gueule-de-dragon au Commandant Hobb au Sanctum des Étoiles dans la vallée d'Ombrelune."}},
[11096] = {"La menace vient d'en haut", {"Tuez 20 Ogres Gordunni de n'importe quelle variété dans la Barrière, puis retournez voir Yuula à Shattrath."}},
[11097] = {"Le plus mortel des pièges", {"Aidez le Commandant Hobb et les Clairvoyants à défendre le Sanctum des Étoiles. Une fois que l'ensemble des forces Gueule-de-dragon aura été vaincu, retournez voir le suzerain Mor'ghor au Campement des Gueules-de-dragon avec les Médailles de Clairvoyant."}},
[11098] = {"En route pour Skettis !", {"Apportez le Paquet d'explosifs au Sergent Doryn de la Garde-ciel au Raie'odrome de Noirvent à l'extérieur de Skettis."}},
[11099] = {"Tuez-les tous !", {"Avertissez l'Anachorète Ceyla du contenu de votre mission et apportez-lui le Lance-fusée gueule-de-dragon à l'Autel de Sha'tar."}},
