[11119] = {"Haro sur le point d'ancrage de Bash'ir", {"Parlez au Techno-éther apprenti à l’Avant-poste de la Garde-ciel, dans les Tranchantes."}},
[11120] = {"Le défilé des elekk roses", {"Rendez-vous aux guinguettes à l'extérieur de Lune-d'argent, des Pitons du Tonnerre et de Fossoyeuse. Supprimez trois elekk dans chacune d'elles, puis revenez voir Charedor Cassebéchasse. Vous devez vous soûler ou porter des Lunettes Synthébière pour voir les Elekk roses."}},
[11121] = {"QUÊTE JOURNALIÈRE DE TEST (Raid)", {"Parlez à <QUESTGIVER> dans <REGION>."}},
[11122] = {"Histoire d'un aller et retour", {"Allez chercher un tonneau auprès de Flynn Brassefeu à Kharanos et rapportez-le à Pol Distillambre. Rapportez trois tonneaux avant que votre bélier ne s'en aille."}},
[11123] = {"Inspection des ruines", {"Parlez à l'Inspecteur Tarem à l'Auberge du Repos ombragé."}},
[11124] = {"Inspection des ruines", {"Parlez à l'Inspecteur Tarem à l'Auberge du Repos ombragé."}},
[11125] = {"[PH] New Hinterlands Quest", nil},
[11126] = {"Des traîtres parmi nous", {"Démasquez 5 Agitateurs des déserteurs puis retournez voir Calia Hastings près des quais de Theramore."}},
[11127] = {"<NYI>Thunderbrew Secrets", {"Volez la Recette secrète de Tristevin au Camp de Tristevin dans la Marche de l'Ouest, puis revenez voir <NOM> Brasselorge sur le terrain de la fête des Brasseurs."}},
[11128] = {"Guerre de propagande", {"Remettez la Propagande des déserteurs à Calia Hastings près des quais de Theramore."}},
[11129] = {"Kyle a disparu !", {"Ahab Sabot-de-blé, à Sabot-de-sang en Mulgore, veut que vous nourrissiez son précieux chiot, Kyle le Frénétique.","Donnez de la Viande de trotteur tendre à Kyle puis retournez voir Ahab Sabot-de-blé."}},
[11130] = {"Houuuu, ça brille !", {"Eskrokar veut que vous remettiez la Note d'Eskrokar à Budd, dans les Terres fantômes, à l'extérieur de Zul'Aman."}},
[11131] = {"Éteignez les incendies !", {"La Directrice d'orphelinat costumée vous demande d'aider à éteindre tous les foyers d'incendie du village. Une fois qu'ils seront éteints, retournez lui parler."}},
[11132] = {"Des promesses, toujours des promesses...", {"Budd Truducou, dans les collines de la Cognée, veut que vous récupériez sa carte. Elle se trouve sur la terrasse du Grand prêtre Nalorakk à Zul'Aman."}},
[11133] = {"Discréditer les déserteurs", {"Calia Hastings veut que vous distribuiez les Pamphlets trafiqués à 6 Gardes de Theramore. Ensuite, retournez la voir près des quais de Theramore."}},
[11134] = {"La fin des déserteurs", {"Capturez Gavis Targegrise, puis allez faire votre rapport à Calia Hastings près des quais de Theramore."}},
[11135] = {"Le Cavalier sans tête", {"Trouvez le Sanctuaire de la citrouille du Cavalier sans tête."}},
[11136] = {"Un développement inquiétant", {"Présentez-vous au capitaine Wymor à la Halte de la vigie."}},
[11137] = {"Des défias à Âprefange ?", {"Récupérez les Ordres des défias sur Garn Mathers, et remettez-les au Capitaine Wymor à la halte de la Vigie."}},
[11138] = {"Renn McBranchie", {"Apportez les Ordres des défias à Renn McBranchie dans les eaux au large du Rivage de Troubleffroi."}},
[11139] = {"Équipement de plongée d'occasion", {"Apportez le Jeu d'outils et l'Équipement de plongée endommagé à Renn McBranchie dans le marécage d'Âprefange."}},
[11140] = {"Récupérez la cargaison !", {"Remettez 6 Coffres récupérés à Renn McBranchie, dans le marécage d'Âprefange."}},
[11141] = {"Jaina doit être informée", {"Parler à dame Jaina Portvaillant à Theramore."}},
[11142] = {"Survoler l'île d'Alcaz", {"Parlez à Cassa Cramoissaile et utilisez l'un de ses griffons pour survoler l'île d'Alcaz. Lorsque vous aurez achevé vos observations, parlez à dame Jaina Portvaillant à Theramore."}},
[11143] = {"Un lien Sinistre", {"Présentez-vous au Capitaine Darill à la Halte nord."}},
[11144] = {"Confirmer les soupçons", {"Remettez le Plan de bataille du Totem-sinistre au Capitaine Darill, à la Tour de la halte nord."}},
[11145] = {"Prisonniers des Totems-sinistres", {"Le Sergent Lukas, de la Tour de la halte nord, veut que vous libériez 5 Prisonniers de Theramore."}},
[11146] = {"Rapt de raptor", {"Le Sergent Lukas, de la Tour de la halte nord, veut que vous capturiez 6 raptors. Vous pouvez capturer des Raptors Rougefange ou des Hurleurs Rougefange, au choix."}},
[11147] = {"Lâcher les raptors", {"Le Sergent Lukas, de la Tour de la halte nord, veut que vous placiez l'Appât à raptor près du moulin à vent de Sabot-noir."}},
[11148] = {"Les armes des Totems-sinistres", {"Le Capitaine Darill, de la Tour de la halte nord, veut que vous lui rameniez 7 Armes de Sabot-noir."}},
[11149] = {"L'aide de Tabetha", {"Parlez à Tabetha dans sa ferme."}},
[11150] = {"Rasez le poste de Navrecorne !", {"Brûlez la Tente nord, la Tente est et la Tente nord-est du Poste de Navrecorne, puis revenez voir Tabetha dans sa ferme."}},
[11151] = {"Justice pour les Hyal", {"Parlez au Capitaine Garran Vimaire à la citadelle de Theramore."}},
[11152] = {"Enfin la paix", {"Le Capitaine Garran Vimaire, de la citadelle de Theramore, veut que vous déposiez la Couronne au Monument de la famille Hyal."}},
[11153] = {"Forcer le blocus", {"Prenez les Bombes à sous-munitions de Petrov et lâchez-les sur les pirates. Vous devez tuer 25 Pirates du blocus et détruire 10 Canons du blocus. Lorsque vous aurez terminé, retournez voir le Bombardier Petrov au Donjon de la Garde de l’ouest."}},
[11154] = {"Dans le guano jusqu'au cou !", {"Récupérez 10 doses de Guano de sombregriffe et livrez-les à l’Ingénieur Fauboulon, à la Porte d’acier."}},
[11155] = {"Encore de la soupe de brochepelle ?", {"Le chef Bouillenoire vous a demandé de récupérer 6 morceaux de Viande de brochepelle et de les lui remettre au donjon de la Garde de l'ouest."}},
[11156] = {"Les écumeurs navrecorne", {"Tuez 12 Totem-sinistre de Navrecorne puis revenez voir l'Apprenti Morlann à la Ferme de Tabetha."}},
[11157] = {"Les œufs maléfiques", {"Détruisez 15 Œufs de proto-drake et 15 Proto-dragonnets, puis retournez voir le Capitaine Adams au Donjon de la Garde de l'ouest."}},
[11158] = {"Les plumes de Rougefange", {"Brogg de Bourbe-à-brac veut 5 Plumes de Rougefange."}},
[11159] = {"Les esprits du bastion Cognepierre", {"Brogg de Bourbe-à-brac veut que vous apportiez le repos à 10 Esprits Cognepierre."}},
[11160] = {"La bannière des Cognepierres", {"Récupérez la Bannière cognepierre et remettez-la à Brogg de Bourbe-à-brac."}},
[11161] = {"L'essence de l'ennemi", {"Apportez 10 Essences de draconien noir à Brogg à Bourbe-à-brac."}},
[11162] = {"Un défi au Vol noir", {"Plantez la Bannière cognepierre près de l'entrée du Repaire d'Onyxia, attendez la réponse d'Onyxia, puis rendez la bannière à Brogg de Bourbe-à-brac."}},
[11163] = {"Taupe secrète", {"Présentez-vous à Donna Brascoe afin de recevoir une récompense pour votre héroïsme à Zul'Aman. Donna est actuellement postée dans les Terres fantômes, juste à l'extérieur de Zul'Aman."}},
[11164] = {"Illégitimes défenses", {"Dasnob veut que vous récupériez 10 Défenses de trolls des forêts sur les trolls de Zul'Aman. Apportez-les-lui au camp de Budd dans les Terres fantômes."}},
[11165] = {"Un troll parmi les trolls", {"Dasnob vous a chargé de trouver son cousin Zungam pour lui venir en aide, quelque part à l'intérieur de Zul'Aman."}},
[11166] = {"X marque... votre mort !", {"Entrez dans Zul'Aman et visitez la Chambre d'Halazzi, la Plate-forme de Jan'alai et la Plate-forme d'Akil'zon. Rendez compte des détails de ces zones à Budd, dans son camp des Terres fantômes."}},
[11167] = {"La Peste nouvelle", {"L'Apothicaire Lysander, à l'Accostage de la Vengeance, vous demande de récupérer 10 Conteneurs de peste intacts dans le navire échoué au sud-est."}},
[11168] = {"Enrichir le mélange", {"Remettez 3 Glandes à toxine géantes prélevées sur des Clampants des marées géants à l'Apothicaire Lysander, à l'Accostage de la Vengeance, dans le Fjord hurlant."}},
[11169] = {"L'arme des Totems-sinistres", {"L'Apprenti Garion, de la ferme de Tabetha, veut que vous testiez le Totem capturé sur 10 Crocilisques tachetés Sèchefange ou Crocs-lames Sèchefange."}},
[11170] = {"Essai en mer", {"Parlez à l'Éleveuse de chauves-souris Camille et prenez une chauve-souris pour intercepter les renforts de l'Alliance. Lorsque vous serez au-dessus de leur flotte, utilisez les Fioles de peste pour contaminer 16 Réservistes de la flotte du nord."}},
[11171] = {"Seigneur des maléfices ? Ha !", {"Budd Truducou, aux Collines de la Cognée, veut que vous alliez tuer le Seigneur des maléfices Malacrass à Zul'Aman."}},
[11172] = {"L'épave du zeppelin", {"Parler à Moxie Grilledacier à l'Épave de Beezil."}},
[11173] = {"Le voleur de composants", {"Apporter 6 fioles de Venin des marais à l'Apprenti Garion à la Ferme de Tabetha."}},
[11174] = {"Prévention de la corrosion", {"Moxie Grilledacier vous demande de dissoudre 10 limons avant de retourner la voir à l'Épave de Beezil."}},
[11175] = {"Ma fille", {"Le Vieux Mantepierre vous a demandé de trouver sa fille, la Surveillante Irena Mantepierre, à la Porte d'acier du Fjord hurlant."}},
[11176] = {"Allez, au travail !", {"L’exploratrice Irena Mantepierre vous a demandé de parler avec un Chef archéologue de la Porte d’acier, à la Porte d’acier."}},
[11177] = {"L'ermite du manoir des Flammeroles", {"Parler à Jarl \"Œil-des-marais\" au Manoir des Flammeroles."}},
[11178] = {"Le sang du seigneur de guerre", {"Apportez le Sang de Zul'jin à Budd à son campement dans les Terres fantômes, à l'extérieur de Zul'Aman."}},
[11179] = {"[En attente d'un nouveau monstre]Finlay manque de tripes", {"Récupérez 7 Boyaux de sabre-de-brume en parfait état pour Finlay Fléchier, et allez les lui remettre au donjon de la Garde de l’ouest."}},
[11180] = {"Qui hante la Colline des sorcières ?", {"Mordant Grimsby du manoir des Flammeroles vous demande de collecter des informations auprès de 10 Carcasses ressuscitées ou Esprits ressuscités."}},
[11181] = {"La plaie de la sorcière", {"Apporter 9 branches de Sorbier à Mordant Grimsby au Manoir des Flammeroles."}},
[11182] = {"Aux racines du mal", {"Tuez 5 Dresseurs vrykuls et Skeld Fils-de-drake ; Si vous y parvenez, retournez voir un Ancien de l'Étreinte de braise."}},
[11183] = {"Nettoyer la Colline des sorcières", {"Mordant Grimsby au Manoir des Flammeroles veut que vous plantiez la torche au bout du quai et que vous abattiez tout ce qui pourrait apparaître."}},
[11184] = {"ON RECHERCHE : Griffétripe le Vorace", {"Tuer Griffétripe le Vorace et se présenter à Drazzit Suintevalve à Bourbe-à-brac."}},
[11185] = {"La lettre de l'apothicaire", {"Apportez la Lettre scellée à Andello Porter à la Ferme de Tabetha."}},
[11186] = {"Des signes de trahison ?", {"Apportez la Lettre scellée à Nazeer Pique-sanglante à Mur-de-Fougères."}},
[11187] = {"Le lieutenant-mage Malister", {"Le Capitaine Adams vous a demandé de parler à son conseiller, le Lieutenant-mage Malister, au Donjon de la Garde de l'ouest."}},
[11188] = {"Deux torts…", {"Le Lieutenant-mage Malister, du donjon de la Garde de l'ouest, vous a demandé de détruire 3 Proto-drakes."}},
[11189] = {"Une dernière fois", {"Escortez le Fantôme de l'Explorateur Jaren à l'Exploratrice Irena Mantepierre, à la Porte d'acier du Fjord Hurlant."}},
[11190] = {"Ce modèle n'existe pas en taille unique !", {"Le Canonnier Ely veut que vous récupériez 10 Boulets de canons de la Garde de l’ouest et que vous les lui remettiez, au donjon de la Garde de l’ouest."}},
[11191] = {"Le vieux phare", {"Parler à Babs Patranivelle au phare sur l'île à l'est de Theramore."}},
[11192] = {"L'huile de batteur", {"Apportez 4 récipients d'Huile de batteur à Babs Patranivelle au phare près de Theramore."}},
[11193] = {"Créatures ignobles des profondeurs", {"Apporter « Créatures ignobles des profondeurs » à Michael « Crado » Crowe sur les docks de Theramore."}},
[11194] = {"Il existe ?", {"Parlez à Nat Pagle au Point d’accostage de Nat, au Marécage d'Âprefange."}},
[11195] = {"Jouer à la poupée", {"Apportez la Poupée vaudou en lambeaux à Eskrokar à Shattrath."}},
[11196] = {"TEMP X", {"Dasnob vous a chargé de trouver son cousin Zungam pour lui venir en aide, quelque part à l'intérieur de Zul'Aman."}},
[11198] = {"Il faut tuer Tethyr !", {"Le Major Mills aux Docks de Theramore veut que vous abattiez Tethyr."}},
[11199] = {"Se présenter à l'éclaireur Knowles", {"Le capitaine Adams vous a demandé de vous présenter à l'Éclaireur Knowles, à la Tour de la Lance de braise."}},
[11200] = {"Plus qu'une coïncidence", {"Krog, du village de Mur-de-Fougères, veut que vous lui rameniez 7 Armes de Sabot-noir."}},
[11201] = {"Le complot totem-sinistre", {"Remettez le Plan de bataille du Totem-sinistre à Krog, du village de Mur-de-Fougères."}},
[11202] = {"Mission : la flamme éternelle", {"L’Éclaireur Knowles, de la Tour de la Lance de braise, souhaite que vous utilisiez les Torches semperardentes pour détruire les quatre Chars de peste qui entourent Halegrince."}},
[11203] = {"À la recherche de Tabetha", {"Parlez à Tabetha dans sa ferme du Bourbier."}},
[11204] = {"Retourner voir Krog", {"Parler à Krog au village de Mur-de-Fougères."}},
[11205] = {"Rasez le poste de Navrecorne !", {"Brûlez la Tente nord, la Tente est et la Tente nord-est du Poste de Navrecorne, puis revenez voir Tabetha dans sa ferme."}},
[11206] = {"Justice est faite !", {"Retourner voir Krog au village de Mur-de-Fougères."}},
[11207] = {"Sauvez la cargaison !", {"Moxie Grilledacier à l'Épave de Beezil veut que vous retrouviez 8 morceaux de Cargaison de zeppelin."}},
[11208] = {"Une livraison pour Drazzit", {"Remettre la Cargaison du zeppelin à Drazzit Suintevalve à Bourbe-à-brac."}},
[11209] = {"Le marché de Nat", {"Nat Pagle, au Point d'accostage de Nat, vous demande de tester son nouvel appât à poissons."}},
[11210] = {"Il existe !", {"Parler au Major Mills sur les quais de Theramore."}},
[11211] = {"De l'aide pour Bourbe-à-brac", {"Parler à Drazzit Suintevalve à Bourbe-à-brac."}},
[11212] = {"La ferme de Tabetha", {"Allez trouver Tabetha dans sa ferme, dans le Bourbier."}},
[11213] = {"Quelles nouvelles de Tabetha ?", {"Parlez à Tabetha dans sa ferme du Bourbier."}},
[11214] = {"En mission à Bourbe-à-brac", {"Parler à Drazzit Suintevalve à Bourbe-à-brac."}},
[11215] = {"Aider Bourbe-à-brac", {"Parler à Drazzit Suintevalve à Bourbe-à-brac."}},
[11216] = {"L'archimage Alturus", {"Parlez à l'Archimage Alturus à l'extérieur de Karazhan au défilé de Deuillevent."}},
[11217] = {"Attraper un dragon par la queue", {"Gizzix Glougloutache de Bourbe-à-brac vous a demandé de lui apporter 8 tiges de Queue de wyrm."}},
[11218] = {"Danger ! Explosifs !", {"Utilisez la Dynamite sécurisée d’Anneau-d’acier pour récupérer 6 Fragments de minerai du goulet des Murmures et 18 Gemmes du goulet des Murmures. Ensuite, allez remettre tout ça au Sapeur Anneau-d’acier, au donjon de la Garde de l’ouest."}},
[11219] = {"Éteignez les incendies !", {"La Directrice d'orphelinat masquée vous demande d'aider à éteindre tous les foyers d'incendie du village. Une fois qu'ils seront éteints, retournez lui parler en ville."}},
[11220] = {"Le Cavalier sans tête", {"Trouvez le Sanctuaire de la citrouille du Cavalier sans tête."}},
[11221] = {"Rapports de terrain", {"Parlez au Forestier-sombre Lyana et au Nécrotraqueur Razael, sur le champ de bataille du Val Sanglant, au sud de l’Accostage de la Vengeance. Lorsque ce sera fait, retournez voir le Grand exécuteur Anselme."}},
[11222] = {"La preuve de la trahison", {"Parler au roi Varian Wrynn au Donjon de Hurlevent."}},
[11223] = {"Retourner voir Jaina", {"Parler à dame Jaina Portvaillant à Theramore."}},
[11224] = {"Têtes de mule", {"L’Exploratrice Abigail vous a demandé de faire /ohé devant 10 Mules de bât abandonnées, puis de retourner la voir au donjon de la Garde de l’ouest."}},
[11225] = {"L'ermite de la Colline des sorcières", {"Parler à Jarl \"Œil-des-marais\" au Manoir des Flammeroles."}},
[11227] = {"Qu'ils mangent du corbeau", {"Pontius, le maître du chenil de l’Accostage de la Vengeance, veut que vous utilisiez la Cage de chien pestiféré pour invoquer un Chien pestiféré affamé. Vous lui donnerez de la Viande de corbeau, prélevée sur les Corbeaux du Fjord, à l’ouest de l’Accostage de la Vengeance."}},
[11228] = {"L'enfer a gelé…", {"Parlez au vice-amiral Keller au port de Valgarde."}},
[11229] = {"La flotte du Coursevent", {"Parlez à l’Éleveuse de chauve-souris Camille à l’Accostage de la Vengeance, et demandez-lui de vous envoyer sur le Coursevent. Une fois à bord, présentez-vous au capitaine Harker."}},
[11230] = {"Embusqués !", {"Le capitaine Harker, à bord du Coursevent, veut que vous tuiez 15 Soldats de marine de la flotte du nord."}},
[11231] = {"Des clés et des cages", {"Le Père Levariol vous a demandé de récupérer assez de Clés de cage de Gjalerbron pour libérer 10 Prisonniers de Gjalerbron. Lorsque ce sera fait, retournez le voir au Donjon de la Garde de l’ouest."}},
[11232] = {"Guidez nos tirs", {"Utilisez les Fumigènes aux emplacements des Canons de l’Alliance, sur la paroi nord de la Grève des Épaves. Lorsque ce sera fait, présentez-vous au Forestier-sombre Lyana, au Val Sanglant."}},
[11233] = {"Le coup de grâce", {"Le forestier-sombre Lyana, au sud de l’Accostage de la Vengeance, veut que vous tuiez le Capitaine Olster, le Sergent Lorric et le Lieutenant Celeyne à la Grève des Épaves."}},
[11234] = {"Se présenter à Anselme", {"Présentez-vous au Grand exécuteur Anselme à l'Accostage de la Vengeance, dans le Fjord hurlant."}},
[11235] = {"S'occuper de Gjalerbron", {"Le Capitaine Adams vous a demandé de tuer 15 Guerriers de Gjalerbron, 8 Lanceurs de runes de Gjalerbron et 8 Gardiens du sommeil de Gjalerbron. Ensuite, revenez le voir au donjon de la Garde de l’ouest."}},
[11236] = {"Le nécro-suzerain Mezhen", {"Tuez le Nécro-suzerain Mezhen, puis retournez voir le Capitaine Adams au Donjon de la Garde de l’ouest."}},
[11237] = {"Les plans d'attaque de Gjalerbron", {"Remettez les Plans d'attaque de Gjalerbron au Lieutenant-mage Malister, au Donjon de la Garde de l'ouest."}},
[11238] = {"Le wyrm de givre et son maître", {"Tuez le Mande-wyrm Vile et utilisez le Cor mande-wyrm pour appeler et tuer Glacion. Lorsque ce sera fait, retournez avec le cor voir le Lieutenant-mage Malister au Donjon de la Garde de l’ouest."}},
[11239] = {"Au service de la Lumière", {"Tuer 10 Guetteurs impérissables, 4 Horreurs effroyables et 2 Nécrophages putrides. Lorsque ce sera fait, informez de votre réussite le Père Levariol au Donjon de la Garde de l’ouest."}},
[11240] = {"Le chef des dérangés", {"Tuez Squeeg Chassidole, puis présentez-vous au Sapeur Anneau-d'acier, au Donjon de la Garde de l'ouest."}},
[11241] = {"Une piste enflammée", {"Escortez l’Apothicaire Hanes jusqu’à la sortie de la Grève des Épaves. Lorsque ce sera fait, présentez-vous à l’Apothicaire Lysander à l’Accostage de la Vengeance."}},
[11242] = {"Enfin libre !", {"Apportez le Tome de Thomas Thomson à la Directrice d'orphelinat costumée à Comté-de-l'or, à Kharanos ou au Guet d'azur."}},
[11243] = {"Si Valgarde tombe…", {"Le Vice-amiral Keller, du port de Valgarde dans le Fjord hurlant, veut que vous tuiez 12 Envahisseurs écorche-dragon ou 12 Worgs écorche-dragon."}},
[11244] = {"Au secours des secours", {"Le Vice-amiral Keller, du port de Valgarde dans le Fjord hurlant, veut que vous secouriez 8 Éclaireurs de Valgarde qui ont été empalés et laissés pour morts."}},
[11245] = {"Les tours de la fatalité", {"Le Sergent de la Garde de l’ouest vous a expliqué qu’il serait souhaitable de marquer les quatre tours de Mörg en prévision d’une canonnade, à l'aide du Fumigène du sergent."}},
[11246] = {"Salissant, mais nécessaire", {"Le Sergent de la Garde de l’ouest vous a demandé de démembrer 20 Vrykuls de la tribu Mörghiver, à Mörg, à l'aide de la Machette du sergent."}},
[11247] = {"Brûle, Mörg, brûle !", {"Le Sergent de la Garde de l’ouest vous a ordonné de mettre le feu aux maisons longues du nord-est et du nord-ouest et à la caserne de Mörg, à l'aide de la Torche du sergent."}},
[11248] = {"Opération Croque-Mörg", {"Le capitaine Adams vous a demandé d’utiliser l’Insigne de commandement de la Garde de l’ouest à Mörg, puis de parler avec le Sergent de la Garde de l’ouest."}},
[11249] = {"Arrêter l'ascension !", {"Utilisez le Parchemin d’ascension vrykul près du Bûcher du thane pour faire venir ce dernier et le tuer. Lorsque ce sera fait, vous êtes sûr que le Père Levariol, au Donjon de la Garde de l’ouest, sera intéressé par le parchemin, en raison de la nature du rituel."}},
[11250] = {"Gloire au conquérant de Mörg !", {"Allez rendre l’Insigne de commandement de la Garde de l’ouest au Capitaine Adams, au Donjon de la Garde de l’ouest."}},
[11251] = {"Un second souffle", {"L’Éclaireur Valory veut que vous enquêtiez sur les bruits de combat qui viennent du nord-est de Crâne-du-ver."}},
[11252] = {"Dans Utgarde !", {"Le Défenseur Mordun vous a demandé de tuer Ingvar le Pilleur, qui réside dans les profondeurs d’Utgarde.","Lorsque ce sera fait, vous devez présenter sa tête au Vice-amiral Keller."}},
[11253] = {"Renifler l'ennemi", {"Utilisez la Laisse de chien pestiféré près des cages des chiens pestiférés à l’Accostage de la Vengeance, et cherchez les indices là où vous conduira le Chien pestiféré traqueur."}},
[11254] = {"La carte en peau de dragon", {"Apportez la Carte en peau de dragon au Grand exécuteur Anselme à l’Accostage de la Vengeance."}},
[11255] = {"Les prisonniers de Crâne-du-ver", {"Le Vice-amiral Keller, au port de Valgarde dans le Fjord hurlant, veut que vous sauviez 3 Prisonniers de Valgarde capturés."}},
[11256] = {"Mörg doit tomber !", {"Le Chef Totem-de-frêne vous a demandé d’utiliser l’Emblème de Sabot-d’hiver à Mörg, puis de parler avec le Brave Sabot-d’hiver."}},
[11257] = {"Salissant, mais nécessaire", {"Le Brave Sabot-d’hiver vous a demandé de démembrer 20 Vrykuls Mörghiver, à Mörg, à l'aide de la Machette du brave."}},
[11258] = {"Brûle, Mörg, brûle !", {"Le Brave Sabot-d’hiver suggère que vous mettiez le feu aux deux maisons longues les plus basses et à la caserne de Mörg à l'aide de la Torche du brave."}},
[11259] = {"Les tours de la fatalité", {"Le Brave Sabot-d’hiver vous a informé qu’il serait souhaitable de marquer les quatre tours de Mörg en prévision de frappes aériennes à l'aide du Fumigène du brave."}},
[11260] = {"Arrêter l'ascension !", {"Utilisez le Parchemin d’ascension vrykul près du Bûcher du thane pour faire apparaître ce dernier et le tuer. Lorsque ce sera fait, vous êtes sûr que la Grande-mère Ankha, au Camp Sabot-d’hiver, sera intéressée par le parchemin, en raison de la nature du rituel."}},
[11261] = {"Le conquérant de Mörg", {"Allez rendre l’Emblème Sabot-d’hiver au Chef Totem-de-frêne, au Camp Sabot-d’hiver."}},
[11262] = {"Ingvar doit mourir !", {"Le forestier-sombre Marrah vous a demandé de tuer Ingvar le Pilleur, qui réside dans les profondeurs d’Utgarde.","Lorsque ce sera fait, vous devez présenter sa tête au Grand exécuteur Anselme, à l’Accostage de la Vengeance."}},
[11263] = {"S'occuper de Gjalerbron", {"Le Chef Totem-de-frêne vous a demandé de tuer 15 Guerriers de Gjalerbron, 8 Lanceurs de runes de Gjalerbron et 8 Gardiens du sommeil de Gjalerbron. Ensuite, revenez le voir au Camp Sabot-d’hiver."}},
[11264] = {"Le nécro-suzerain Mezhen", {"Tuez le nécro-suzerain Mezhen, puis retournez voir le Chef Totem-de-frêne au camp Sabot-d’hiver."}},
[11265] = {"Des clés et des cages", {"La Grande-mère Ankha vous a demandé de récupérer assez de Clés de cages de Gjalerbron pour libérer 10 Prisonniers de Gjalerbron. Si vous y parvenez, retournez la voir au Camp Sabot-d’hiver."}},
[11266] = {"Les plans d'attaque de Gjalerbron", {"Remettez les Plans d’attaque de Gjalerbron à Celea Crin-gelé, au Camp Sabot-d’hiver."}},
[11267] = {"Le wyrm de givre et son maître", {"Tuez le Mande-wyrm Vile, puis utilisez le Cor mande-wyrm pour appeler Glacion. Tuez-le, et retournez voir Celea Crin-gelé au Camp Sabot-d’hiver avec le cor."}},
[11268] = {"Les morts qui marchent", {"Tuez 10 Guetteurs impérissables, 4 Horreurs effroyables et 2 Nécrophages putrides. Lorsque ce sera fait, allez informer de votre succès la Grande-mère Ankha, au Camp Sabot-d’hiver."}},
[11269] = {"Y laisser des plumes", {"Gil Grisert veut que vous lui rapportiez 10 touffes de Duvet d’hippogriffe tacheté."}},
[11270] = {"La guerre, c'est l'enfer", {"Vous effectuez cette quête"}},
[11271] = {"Des préparatifs hâtifs", {"Ahota Givre-blanc, du Camp Sabot-d’hiver, veut que vous lui rapportiez 10 touffes de Duvet d’hippogriffe tacheté."}},
[11272] = {"Un compte à régler", {"Le Grand exécuteur Anselme veut que vous pénétriez dans Utgarde pour y tuer le prince Keleseth."}},
[11273] = {"La ligue humaine", {"Fouillez Crâne-du-ver à la recherche de Pulroy."}},
[11274] = {"Zedd est probablement mort", {"Fouiller le village de Crâne-du-ver à la recherche de Zedd."}},
[11275] = {"Fabriquer le cor", {"Nokoma Œil-des-neiges, au camp Sabot-d’hiver, veut que vous lui rapportiez 6 Cornes de bélier intactes."}},
[11276] = {"Ils étaient deux", {"Fouillez les Catacombes d'Utgarde à la recherche de Glorenfeld."}},
[11277] = {"Les profondeurs de la dépravation", {"Glorenfeld, dans les Catacombes d'Utgarde, vous demande de réunir 10 Tablettes Crâne-du-ver."}},
[11278] = {"Retour à Valgarde", {"Remettez le Paquet de Glorenfeld à Beltrand McSorf, au port de Valgarde."}},
[11279] = {"Ces proto-dragonnets sont trop verts…", {"Le Porte-peste Tillinghast vous a demandé de récupérer 10 Spécimens de proto-dragonnets pestiférés, et de les lui remettre à la Nouvelle-Agamand."}},
[11280] = {"Draconis Gastritis", {"Le Porte-peste Tillinghast, de la Nouvelle-Agamand, veut que vous utilisiez la Viande pestiférée de Tillinghast pour attirer un Proto-drake. Observez ses réactions lorsqu’il la mangera."}},
[11281] = {"Imiter l'appel de la nature", {"Testez le Cor sculpté, puis allez faire part du résultat à Nokoma Œil-des-neiges, au Camp Sabot-d’hiver."}},
[11282] = {"Une leçon de peur", {"Gorth veut que vous tuiez Ulf le Saigneur, Oric le Torve et Gunnar Thorvardsson, et que vous plantiez la Bannière des Réprouvés sur leurs corps. Tuez des vrykuls tout au long du blocus des Réprouvés jusqu’à ce qu’ils apparaissent."}},
[11283] = {"Le décompte des cadavres de Torvheim", {"Le sergent Gorth veut que vous entriez dans Torvheim pour y tuer 16 Vrykuls de Mörghiver."}},
[11284] = {"Le yéti d'à-côté", {"Le Contremaître Colbey de Fort Hardivar veut que vous battiez Brise-corne."}},
[11285] = {"Torvheim doit brûler !", {"Le sergent Gorth veut que vous entriez dans Torvheim et que vous vous serviez de la Torche de Gorth pour brûler 1 Logis Mörghiver, 1 Tour de guet Mörghiver, 1 Pont Mörghiver et 1 Caserne Mörghiver."}},
[11286] = {"Les artéfacts de la Porte d'acier", {"Le Sage Marche-brume veut que vous récupériez 10 Artéfacts de la Porte d’acier. Lorsque vous les aurez, remettez-les-lui à la Porte d’acier."}},
[11287] = {"Trouver le sage Marche-brume", {"La Grande-mère Ankha vous a demandé d'aller vérifier si le Sage Marche-brume allait bien. Elle pense qu'il se trouve à la Porte d'acier ou bien à proximité."}},
[11288] = {"La Lumière rayonnante", {"Arès le Féal des Catacombes d’Utgarde veut que vous récupériez l’Artéfact sacré.","Cette quête est en temps limité."}},
[11289] = {"Guidé par l'honneur", {"Rapportez l'Artéfact sacré au Seigneur Irulon Lamevraie à Valgarde."}},
[11290] = {"Les plans de bataille des Écorche-dragon", {"Le Vice-amiral Keller, au port de Valgarde dans le Fjord hurlant, vous a demandé de lui apporter les plans de bataille des Écorche-dragon."}},
[11291] = {"Au donjon de la Garde de l'ouest !", {"Remettez les Plans de bataille des Écorche-dragon au Capitaine Adams, au donjon de la Garde de l’ouest, dans le Fjord hurlant."}},
[11292] = {"Faire des faibles sa proie", {"Le Trappeur Jethan, au Fort Hardivar, veut que vous lui rameniez 8 Proies piégées."}},
[11293] = {"Des aboyeurs pour les Brasselorge!", nil},
[11294] = {"Des aboyeurs pour les Tonnebière!", nil},
[11295] = {"L'offensive commence", {"Parlez au Sergent Gorth, qui dirige le blocus, non loin de l’ascenseur, à l’ouest de l’Accostage de la Vengeance, dans le Fjord hurlant."}},
[11296] = {"Les prisonniers de Clivebois", {"Le Long-coureur Nuage-du-ciel vous a demandé de libérer 7 Longs-coureurs Sabot-d’hiver. Lorsque ce sera fait, retournez à la Porte d’acier."}},
[11297] = {"Garder un œil sur les intrus", {"Parlez à l’Apothicaire Malthus, au Camp des Apothicaires."}},
[11298] = {"Mais qu'est-ce qu'il y a dans ce truc ?", {"L’Apothicaire Malthus, du Camp des Apothicaires, veut que vous lui rapportiez 5 Tonneaux des nains."}},
[11299] = {"L'arène du Jugement", {"Trouvez Daegarn dans l’Arène du jugement, à l’intérieur des Catacombes d’Utgarde."}},
[11300] = {"Formidable déconfiture dans l'arène", {"Triomphez des gladiateurs de l’Arène du Jugement et affrontez Oluf le Violent. Battez-le et récupérez le Code antique.","Remettez le Code antique à Glorenfeld à l’entrée des Catacombes d’Utgarde."}},
[11301] = {"Cerveaux ! Cerveaux ! Cerveaux !", {"L’Apothicaire Grick, du Camp des Apothicaires, veut que vous lui rapportiez 12 Cerveaux d’explorateurs dérangés."}},
[11302] = {"Les énigmatiques nymphes de givre", {"Allez parler à Lurielle dans la Clairière Gelée."}},
[11303] = {"L'embuscade", {"Parlez à Lydell à l'Embuscade de Lydell."}},
[11304] = {"La Nouvelle-Agamand", {"Remettez la Souche de Lysander au Porte-peste en chef Harris, à la Nouvelle-Agamand, à l’ouest du Fjord Hurlant."}},
[11305] = {"Une formule sur mesure", {"Procurez-vous le Sang du patriarche écorche-dragon sur Thorvald au Camp de Thorvald, et apportez-le au Porte-peste en chef Harris à la Nouvelle-Agamand."}},
[11306] = {"Réchauffez et remuez", {"Remplissez le Flacon d’apothicaire vide au Chaudron de sang de vrykul, puis mélangez le contenu du Flacon de sang de vrykul avec les Échantillons de peste de Harris jusqu’à ce que vous obteniez une Décoction équilibrée.","Lorsque ce sera fait, retournez voir le Porte-peste en chef Harris."}},
[11307] = {"Essai sur le terrain", {"Pénétrez dans Halegrince et utilisez le Pestorisateur sur 10 Vrykuls Écorche-dragon pestiférés. Ensuite, retournez voir le Porte-peste en chef Harris pour lui présenter les résultats."}},
[11308] = {"On nettoie et on ferme", {"Parlez à Jenny « Scie à métaux » à Nouvelle-Agamand."}},
[11309] = {"Des p'tits bouts partout", {"Jenny « Scie à métaux », à la Nouvelle-Agamand, veut que vous récupériez 6 Ligaments de brochepelle et 1 Livre de chair fraîche."}},
[11310] = {"Avertissement : assemblage nécessaire", {"Servez-vous de la Trousse de confection d’abomination à Halegrince. Attirez des Vrykuls pestiférés avec votre Abomination décérébrée. Tuez-en au moins 20, puis retournez voir Jenny « Scie à métaux » à la Nouvelle-Agamand."}},
[11311] = {"Supprimer les éléments", {"Le Dompteur des vents Kagan, au Camp Sabot-d’hiver, veut que vous tuiez 8 élémentaires des montagnes. Il peut s’agir de Cyclones hurlants ou d’Élémentaires Glacéclat."}},
[11312] = {"La clairière Gelée", {"Parlez à Lurielle à la Clairière Gelée."}},
[11313] = {"Les esprits de la glace", {"Lurielle de la Clairière Gelée veut que vous lui rapportiez 15 Noyaux glacials."}},
[11314] = {"Les sœurs déchues", {"Lurielle de la Clairière Gelée veut que vous utilisiez son pendentif sur 7 Nymphes algides."}},
[11315] = {"Le lierre sauvage", {"Lurielle de la Clairière Gelée veut que vous tuiez 8 Lierres écarlates."}},
[11316] = {"L'engeance de la clairière Tordue", {"Lurielle de la Clairière Gelée veut que vous tuiez 10 Rampants roncevigne."}},
[11317] = {"La purification", {"Le Sage Marche-brume vous a conseillé de vous purifier de votre Trouble intérieur par la méditation au Sanctuaire de Givrelame."}},
[11318] = {"Ça c'est de la course de béliers... enfin, presque.", {"Vous avez quatre minutes pour chevaucher le bélier et maintenir 3 différentes allures pendant 8 secondes chacune."}},
[11319] = {"Les graines de gardiens à l'âme noircie", {"Lurielle de la Clairière Gelée veut que vous geliez 8 Spores."}},
[11320] = {"[NYI] Now this is Ram Racing... Almost.", {"Texte à venir"}},
[11321] = {"Quelqu'un a dit «souvenir»?", nil},
[11322] = {"La purification", {"La guetteuse Feuillelune vous a conseillé de vous purifier de votre Trouble intérieur par la méditation au Sanctuaire de Givrelame."}},
[11323] = {"Déguisé en worg", {"Le Sage Marche-brume vous a demandé de trouver Ulfang et de l’aider. Il doit se trouver dans Clivebois.","Si vous perdez le déguisement de worg, demandez au Sage Marche-brume de vous le remplacer."}},
[11324] = {"L'alpha des worgs", {"Ulfang vous a demandé de tuer Garwal. Ensuite, retournez voir le Sage Marchebrume à la Porte d’acier et rendez-lui le Déguisement de worg."}},
[11325] = {"Déguisé en worg", {"La guetteuse Feuillelune vous a demandé de trouver Ulfang et de l’aider. Il doit se trouver dans Clivebois.","Si vous perdez le Déguisement de worg, demandez à la guetteuse Feuillelune de vous le remplacer."}},
[11326] = {"L'alpha des worgs", {"Ulfang vous a demandé de tuer Garwal. Ensuite, retournez voir la Guetteuse Feuillelune à la Porte d’acier et rendez-lui le Déguisement de worg."}},
[11327] = {"Mission : récupération de paquet", {"L’Éclaireur Knowles vous a demandé de récupérer le Paquet de l’apothicaire, qui est quelque part dans Halegrince. Lorsque vous l’aurez, retournez le voir à la tour de la Lance de braise."}},
[11328] = {"Mission : des infos sur les Réprouvés", {"L’Éclaireur Knowles vous a demandé de livrer le Paquet de l’apothicaire à Peppy Tuyèrronée, au donjon de la Garde de l’ouest."}},
[11329] = {"J'essayerai n'importe quoi !", {"Christopher Sloan, au Fort Hardivar, veut que vous lui rapportiez 5 Rascasses nordiques."}},
[11330] = {"Farpaitement ! Cha va marcher !", {"Peppy Tuyèrronée suggère que vous preniez son Mélange spécial de Peppy et que vous l’administriez au Vrykul écorche-dragon prisonnier, pour voir."}},
[11331] = {"Allez lui dire que... Hips !", {"Peppy Tuyèrronée vous a manipulé afin que ce soit vous qui annonciez au Capitaine Adams les mauvaises nouvelles concernant la peste des Réprouvés. Allez le voir au donjon de la Garde de l’ouest."}},
[11332] = {"Mission : contamination !", {"Le Capitaine Adams vous a ordonné de larguer 5 bombes sur les Chars de peste de la Nouvelle-Agamand. Ne revenez pas le voir au donjon de la Garde de l’ouest tant que ce ne sera pas fait."}},
[11333] = {"Dans le monde des esprits", {"Thoralius le Sage à Valgarde veut que vous retrouviez sa Bourse de réactifs."}},
[11334] = {"Dans le mille !", {"[PH] Rinling wants you to shoot a marked target and then return the Target Rifle to him."}},
[11335] = {"Appel aux armes : bassin Arathi", {"Remportez la bataille dans le Bassin Arathi, puis présentez-vous à un Général de brigade de l'Alliance, à Joug-d'hiver, à Dalaran, à Shattrath ou dans l'une des capitales de l'Alliance."}},
[11336] = {"Appel aux armes : vallée d'Alterac", {"Remportez la bataille dans la Vallée d'Alterac, puis présentez-vous à un Général de brigade de l'Alliance, à Joug-d'hiver, à Dalaran, à Shattrath ou dans l'une des capitales de l'Alliance."}},
[11337] = {"Appel aux armes : Œil du cyclone", {"Gagnez dans l'Œil du cyclone, puis présentez-vous à un Général de brigade de l'Alliance, à Joug-d'hiver, à Dalaran, à Shattrath ou dans l'une des capitales de l'Alliance."}},
[11338] = {"Appel aux armes : goulet des Chanteguerres", {"Remportez la bataille dans le Goulet des Chanteguerres, puis présentez-vous à un Général de brigade de l'Alliance, à Joug-d'hiver, à Dalaran, à Shattrath ou dans l'une des capitales de l'Alliance."}},
[11339] = {"Appel aux armes : bassin Arathi", {"Remportez la bataille dans le Bassin Arathi, puis présentez-vous à un Porteguerre de la Horde, à Joug-d'hiver, à Dalaran, à Shattrath ou dans l'une des capitales de la Horde."}},
[11340] = {"Appel aux armes : vallée d'Alterac", {"Remportez la bataille dans la Vallée d'Alterac, puis présentez-vous à un Porteguerre de la Horde, à Joug-d'hiver, à Dalaran, à Shattrath ou dans l'une des capitales de la Horde."}},
[11341] = {"Appel aux armes : Œil du cyclone", {"Remportez la bataille dans l'Œil du cyclone, puis présentez-vous à un Porteguerre de la Horde, à Joug-d'hiver, à Dalaran, à Shattrath ou dans l'une des capitales de la Horde."}},
[11342] = {"Appel aux armes : goulet des Chanteguerres", {"Remportez la bataille dans le Goulet des Chanteguerre, puis présentez-vous à un Porteguerre de la Horde, à Joug-d'hiver, à Dalaran, à Shattrath ou dans l'une des capitales de la Horde."}},
[11343] = {"L'écho d'Ymiron", {"Thoralius le Sage veut que vous exploriez Crâne-du-ver dans le monde des esprits, à la recherche d’informations sur les Secrets de Crâne-du-ver."}},
[11344] = {"Angoisse à Nifflevar", {"Thoralius le Sage veut que vous exploriez Nifflevar dans le monde des esprits, à la recherche d’informations sur les Secrets de Nifflevar."}},
[11345] = {"Prix et projectiles", {"[PH] Rinling wants you to shoot a marked target and then return the Target Rifle to him."}},
[11346] = {"Le Livre des runes", {"Le Prospecteur Belvar, de Fort Hardivar, veut que vous lui rapportiez le Livre des runes."}},
[11348] = {"La rune de commandement", {"Le Prospecteur Belvar de Fort Hardivar veut que vous testiez la Rune de commandement et que vous battiez le Lieur Murdis."}},
[11349] = {"Maîtriser les runes", {"Le Prospecteur Belvar à Fort Hardivar veut que vous lui rapportiez une série d’Outils de gravure de Runes-de-fer."}},
[11350] = {"Le Livre des runes", {"Le Long-coureur Pembe, au Camp Sabot-d’hiver, veut que vous lui rapportiez le Livre des runes."}},
[11351] = {"Maîtriser les runes", {"Le Long-coureur Pembe au Camp Sabot-d’hiver veut que vous lui rapportiez une série d’Outils de gravure de Runes-de-fer."}},
[11352] = {"La rune de commandement", {"Le Long-coureur Pembe au Camp Sabot-d’hiver veut que vous testiez la Rune de commandement et que vous battiez le Lieur Murdis."}},
[11354] = {"On recherche : La cravache de Nazan", {"Le Marchand des vents Zhareem vous a demandé de vous procurer la Cravache de Nazan. Apportez-la lui à la Ville basse de Shattrath pour recevoir votre récompense.","Cette quête ne peut être accomplie qu'en mode héroïque."}},
[11355] = {"La marche des géants", {"Le Chercheur Aderan à Fort Hardivar veut que vous utilisiez le Piolet cherche-rune sur les cadavres de 4 Géants de pierre runifiés."}},
[11356] = {"La directrice d'orphelinat costumée", {"Parlez à une Directrice d'orphelinat costumée à Comté-de-l'or, à Kharanos ou au Guet d'azur."}},
[11357] = {"La directrice d'orphelinat masquée", {"Parlez à une Directrice d'orphelinat masquée à Tranchecolline, à Brill ou sur la Place de l'Épervier."}},
[11358] = {"L'aimant", {"Le Chercheur Aderan, au Fort Hardivar, veut que vous compariez l’Échantillon de rune avec la Tablette brisée."}},
[11359] = {"Démolir Mégalithe", {"Triomphez de Mégalithe puis revenez voir le Chercheur Aderan au Fort Hardivar."}},
[11360] = {"Un entraînement de pompier", {"Jetez des seaux d'eau sur 5 feux, puis revenez parler à la Directrice d'orphelinat costumée."}},
[11361] = {"L'entraînement au combat du feu", {"Jetez des seaux d'eau sur 5 feux, puis parlez à nouveau à la Directrice d'orphelinat masquée à Tranchecolline."}},
[11362] = {"On recherche : Le bâton à plumes de Keli'dan", {"Le Marchand des vents Zhareem vous a demandé de vous procurer le Bâton à plumes de Keli'dan. Apportez-le lui à la Ville basse de Shattrath pour recevoir votre récompense.","Cette quête ne peut être accomplie qu'en mode héroïque."}},
[11363] = {"On recherche : Le sceau de Lamepoing", {"Le Marchand des vents Zhareem vous a demandé de vous procurer le Sceau de Lamepoing. Apportez-le lui à la Ville basse de Shattrath pour recevoir votre récompense.","Cette quête ne peut être accomplie qu'en mode héroïque."}},
[11364] = {"On recherche : Des centurions de la Main brisée", {"Le Traqueur-du-Néant Mah'duun vous a chargé de tuer 4 Centurions de la Main Brisée. Revenez le voir à la Ville basse de Shattrath quand ils seront tous morts pour recevoir votre prime."}},
[11365] = {"La marche des géants", {"Le Sage Edan, au Camp Sabot-d’hiver, veut que vous utilisiez le Piolet cherche-rune sur les cadavres de 4 Géants de pierre runifiés."}},
[11366] = {"L'aimant", {"Le Sage Edan, au Camp Sabot-d’hiver, veut que vous compariez l’Échantillon de rune avec la Tablette brisée."}},
[11367] = {"Démolir Mégalithe", {"Triomphez de Mégalithe puis revenez voir le Sage Edan au Camp Sabot-d'hiver."}},
[11368] = {"On recherche : Le cœur de Bourbierreux", {"Le Marchand des vents Zhareem vous a demandé de vous procurer le Cœur de Bourbierreux. Apportez-le lui à la Ville basse de Shattrath pour recevoir votre récompense.","Cette quête ne peut être accomplie qu'en mode héroïque."}},
[11369] = {"On recherche : Un œuf de traqueuse noire", {"Le Marchand des vents Zhareem vous a demandé de vous procurer un Œuf de traqueuse noire. Apportez-le lui à la Ville basse de Shattrath pour recevoir votre récompense.","Cette quête ne peut être accomplie qu'en mode héroïque."}},
[11370] = {"On recherche : Le traité du seigneur de guerre", {"Le Marchand des vents Zhareem vous a demandé de vous procurer le Traité du seigneur de guerre. Apportez-le lui à la Ville basse de Shattrath pour recevoir votre récompense.","Cette quête ne peut être accomplie qu'en mode héroïque."}},
[11371] = {"On recherche : Des myrmidons de Glissecroc", {"Le Traqueur-du-Néant Mah'duun vous a chargé de tuer 14 Myrmidons de Glissecroc. Revenez le voir à la Ville basse de Shattrath quand ils seront tous morts pour recevoir votre prime."}},
[11372] = {"On recherche : Le panache d'Ikiss", {"Le Marchand des vents Zhareem vous a demandé de vous procurer le Panache d'Ikiss. Apportez-le-lui à la Ville basse de Shattrath pour recevoir votre récompense.","Cette quête ne peut être accomplie qu'en mode héroïque."}},
[11373] = {"On recherche : L'amulette merveilleuse de Shaffar", {"Le Marchand des vents Zhareem vous a demandé de vous procurer l'Amulette merveilleuse de Shaffar. Apportez-la-lui à la Ville basse de Shattrath pour recevoir votre récompense.","Cette quête ne peut être accomplie qu'en mode héroïque."}},
[11374] = {"On recherche : La gemme d'âme de l'exarque", {"Le Marchand des vents Zhareem vous a demandé de vous procurer la Gemme d'âme de l'exarque. Apportez-la-lui à la Ville basse de Shattrath pour recevoir votre récompense.","Cette quête ne peut être accomplie qu'en mode héroïque."}},
[11375] = {"On recherche : Le murmure de Marmon", {"Le Marchand des vents Zhareem vous a demandé de vous procurer le Murmure de Marmon. Apportez-le-lui à la Ville basse de Shattrath pour recevoir votre récompense.","Cette quête ne peut être accomplie qu'en mode héroïque."}},
[11376] = {"On recherche : Des instructrices malveillantes", {"Le Traqueur-du-Néant Mah'duun vous a chargé de tuer 3 Instructrices malveillantes. Revenez le voir à la Ville basse de Shattrath quand elles seront toutes mortes pour recevoir votre prime."}},
[11377] = {"La vengeance est un plat...", {"Le Rokk, dans la Ville basse, vous a demandé de préparer du Ragoût de kaliri avec sa Marmite. Retournez le voir quand ce sera fait."}},
[11378] = {"On recherche : La tête du chasseur d'époques", {"Le Marchand des vents Zhareem vous a demandé de vous procurer la Tête du chasseur d'époques. Apportez-la-lui à la Ville basse de Shattrath pour recevoir votre récompense.","Cette quête ne peut être accomplie qu'en mode héroïque."}},
[11379] = {"Un ragoût très épicé", {"Le Rokk, dans la Ville basse, vous a demandé de préparer de la Surprise grillée sur démon dans sa Marmite avec deux Côtelettes mok'nathal et un Serpent croustillant. Retournez le voir quand ce sera fait."}},
[11380] = {"Manalicieux", {"Le Rokk, dans la Ville basse, vous a demandé de récupérer 15 Baies de mana dans les Écodômes de Raz-de-Néant."}},
[11381] = {"Une soupe pour l'âme", {"Le Rokk, dans la Ville basse, vous a demandé de préparer de la Soupe spirituelle avec sa Marmite. Retournez le voir quand ce sera fait."}},
[11382] = {"On recherche : Le sablier d'Aeonus", {"Le Marchand des vents Zhareem vous a demandé de vous procurer le Sablier d'Aeonus. Apportez-le-lui à la Ville basse de Shattrath pour recevoir votre récompense.","Cette quête ne peut être accomplie qu'en mode héroïque."}},
[11383] = {"On recherche : Des seigneurs des failles", {"Le Traqueur-du-Néant Mah'duun vous a chargé de tuer 4 Seigneurs des failles. Revenez le voir à la Ville basse de Shattrath quand ils seront tous morts pour recevoir votre prime."}},
[11384] = {"On recherche : Une rognure de Brise-dimension", {"Le Marchand des vents Zhareem vous a demandé de vous procurer une Rognure de Brise-dimension. Apportez-la-lui à la Ville basse de Shattrath pour recevoir votre récompense.","Cette quête ne peut être accomplie qu'en mode héroïque."}},
[11385] = {"On recherche : Des canalistes Cherche-soleil", {"Le Traqueur-du-Néant Mah'duun vous a chargé de tuer 6 Canalistes Cherche-soleil. Revenez le voir à la Ville basse de Shattrath quand ils seront tous morts pour recevoir votre prime."}},
[11386] = {"On recherche : Le projecteur de Pathaleon", {"Le Marchand des vents Zhareem vous a demandé de vous procurer le Projecteur de Pathaleon. Apportez-le-lui à la Ville basse de Shattrath pour recevoir votre récompense.","Cette quête ne peut être accomplie qu'en mode héroïque."}},
[11387] = {"On recherche : Des destructeurs Forge-tempête", {"Le Traqueur-du-Néant Mah'duun vous a chargé de détruire 5 Destructeurs Forge-tempête. Revenez le voir à la Ville basse de Shattrath quand ils seront tous morts pour recevoir votre prime."}},
[11388] = {"On recherche : Le parchemin de Cieuriss", {"Le Marchand des vents Zhareem vous a demandé de vous procurer le Parchemin de Cieuriss. Apportez-le-lui à la Ville basse de Shattrath pour recevoir votre récompense.","Cette quête ne peut être accomplie qu'en mode héroïque."}},
[11389] = {"On recherche : Des sentinelles de l'Arcatraz", {"Le Traqueur-du-Néant Mah'duun vous a chargé de démanteler 5 Sentinelles de l'Arcatraz. Revenez le voir à la Ville basse de Shattrath quand elles seront toutes mortes pour recevoir votre prime."}},
[11390] = {"J'ai une machine volante !", {"Le Chef archéologue de la Porte d’acier veut que vous transportiez 3 Sacs de reliques du bas du site à la plate-forme qui se trouve du côté nord ou sud de la Porte d’acier.","Pour cela, vous devez vous servir du grappin de la machine volante.","Lorsque ce sera fait, retournez le voir."}},
[11391] = {"Patrouille à la Porte d'acier", {"Vous êtes l’as des as de la Porte d’acier, désormais. Le Chef archéologue de la Porte d’acier veut que vous fassiez une patrouille autour du site et que vous abattiez tout ce qui vous attaquera.","Lorsque la patrouille sera terminée, retournez le voir."}},
[11392] = {"L'appel du Cavalier sans tête", {"Posez une Morne chandelle sur la Terre fraîchement retournée."}},
[11393] = {"Où est l'explorateur Jaren ?", {"La Surveillante Irena Mantepierre vous a demandé de retrouver son fiancé, l’Explorateur Jaren."}},
[11394] = {"Et vous pensiez que les murlocs sentaient mauvais !", {"L’Explorateur Jaren veut que vous tuiez 15 créatures du Fléau de n’importe quel type le long de la Côte de Frissonde. Lorsque vous aurez terminé, retournez le voir."}},
[11395] = {"C'est un appareil du Fléau", {"Remettez l’Appareil du Fléau à l’Exporateur Jaren sur la Côte de Frissonde."}},
[11396] = {"Abattez ces boucliers", {"L’Exporateur Jaren, sur la Côte de Frissonde, vous a demandé de détruire 3 Cristaux flaellants."}},
[11397] = {"Et vous pensiez que les murlocs sentaient mauvais !", {"L’Apothicaire Anastasia veut que vous tuiez 15 créatures du Fléau de n’importe quel type le long de la Côte de Frissonde. Lorsque vous aurez terminé, retournez la voir au Camp des Apothicaires."}},
[11398] = {"C'est un appareil du Fléau", {"Remettez l’Appareil du Fléau à l’Apothicaire Anastasia, au Camp des Apothicaires du Fjord Hurlant."}},
[11399] = {"Abattez ces boucliers", {"L’Apothicaire Anastasia, du Camp des Apothicaires, vous a demandé de détruire 3 Cristaux flaellants."}},
[11400] = {"Les béliers de la fête des Brasseurs", {"Apportez le tampon de « Brasseur honoraire » à Pol Distillambre, près de l'entrée de la fête des Brasseurs à Dun Morogh."}},
[11401] = {"L'appel du Cavalier sans tête", {"Posez une Morne chandelle sur la Terre fraîchement retournée."}},
[11402] = {"Quête de Clayton : extrême !", {"Allez donc me tuer ces Écureuils maléfiques !"}},
[11403] = {"Enfin libre !", {"Apportez le Tome de Thomas Thomson à une Directrice d'orphelinat masquée à Durotar, à Brill ou sur la Place de l'Épervier."}},
[11404] = {"L'appel du Cavalier sans tête", {"Posez une Morne chandelle sur la Terre fraîchement retournée."}},
[11405] = {"L'appel du Cavalier sans tête", {"Posez une Morne chandelle sur la Terre fraîchement retournée."}},
[11406] = {"Tout doit être prêt", {"Parlez à Gil Grisert à Fort Hardivar."}},
[11407] = {"Des aboyeurs pour la distillerie Drohn !", {"Faites passer le mot au sujet de la fête des Brasseurs près des drapeaux à l'extérieur de l'hôtel des ventes dans la Vallée de la Force, dans la Vallée de l'Honneur, dans la Vallée de la Sagesse et dans la Vallée des Esprits."}},
[11408] = {"Des aboyeurs pour la brasserie vaudou de T'chali !", {"Faites passer le mot au sujet de la fête des Brasseurs près des drapeaux à l'extérieur de l'hôtel des ventes dans la Vallée de la Force, dans la Vallée de l'Honneur, dans la Vallée de la Sagesse et dans la Vallée des Esprits."}},
[11409] = {"Ça c'est de la course de béliers... enfin, presque.", {"Vous avez quatre minutes pour chevaucher le bélier et maintenir 3 différentes allures pendant 8 secondes chacune."}},
[11410] = {"Celui qui s'échappa", {"Christopher Sloan, du Fort Hardivar, veut que vous triomphiez de Givraileron."}},
[11411] = {"Le camp Sabot-d'hiver", {"Parlez au Chef Totem-de-frêne au camp Sabot-d’hiver."}},
[11412] = {"Histoire d'un aller et retour", {"Obtenez un tonneau auprès du Gobelin coincé sur la route de Tranchecolline, et rapportez-le à l'assistant du Maître des béliers Ray. Faites-le 3 fois avant que votre bélier ne s'en aille."}},
[11413] = {"Quelqu'un a dit «souvenir»?", nil},
[11414] = {"Les frères félons", {"Ulfang vous a demandé de tuer les traîtres worgs, Bjomolf et Varg. Lorsque ce sera fait, retournez le voir, déguisé , à Clivebois.","Si par hasard vous perdiez le Déguisement de worg, retournez voir la Guetteuse Feuillelune et demandez-lui un autre exemplaire."}},
[11415] = {"Les frères félons", {"Ulfang vous a demandé de tuer les traîtres worgs, Bjomolf et Varg. Lorsque ce sera fait, retournez le voir, déguisé , à Clivebois.","Si par hasard vous perdiez le Déguisement de worg, retournez voir le Sage Marche-brume et demandez-lui un autre exemplaire."}},
[11416] = {"Les yeux de l'aigle", {"Ulfang vous a demandé de récupérer les Yeux de l’Aigle. Lorsque ce sera fait, retournez le voir déguisé à Clivebois.","Si vous perdez votre Déguisement de worg, allez en demander un autre à la Guetteuse Feuillelune."}},
[11417] = {"Les yeux de l'aigle", {"Ulfang vous a demandé de récupérer les Yeux de l’Aigle. Lorsque ce sera fait, retournez le voir déguisé à Clivebois.","Si vous perdez votre Déguisement de worg, allez en demander un autre au Sage Marche-brume."}},
[11418] = {"On l'appelle Plume-d'acier", {"Trouvez Plume-d’acier, qui tourne autour de Fort Hardivar, et utilisez le Charme orné de plumes pour découvrir son secret. Ensuite, allez faire part de vos découvertes à Gil Grisert."}},
[11419] = {"Les béliers de la fête des Brasseurs", {"Apportez le tampon de « Brasseur honoraire » à Driz Voltevive, près de l'entrée de la fête des Brasseurs à Durotar."}},
[11420] = {"La voie de la vengeance", {"Le capitaine de la garde Zorek, à Valgarde dans le Fjord Hurlant, vous demande de récupérer le Manuel d'utilisation du harpon."}},
[11421] = {"Ça va jusqu'à 11…", {"Le lieutenant Martel-de-glace, dans le Fjord Hurlant, veut que vous détruisiez la Maison longue écorche-dragon, la Maison des quais écorche-dragon, l’Entrepôt et 8 Défenseurs écorche-dragon.","Rapportez-lui le Mécanisme de commande de harpon quand vous aurez accompli votre mission."}},
[11422] = {"Le trident du fils", {"Le Vieux Nageglace veut que vous récupériez le Trident de Branchie-pourrie. Lorsque ce sera fait, rapportez-le-lui sur sa petite île, sur la côte de Frissonde."}},
[11423] = {"L'héritage de l'ennemi", {"Rendez-vous à Nifflevar, au nord du poste de la Lame-fantôme, et cherchez La saga de la malédiction de l’hiver, La saga des Val'kyrs et La saga des jumeaux."}},
[11424] = {"La colline du Bouclier", {"Jenny « Scie à métaux » vous demande de lui apporter 5 Os de vrykuls anciens des vrykuls réanimés de la Colline du Bouclier, au sud de Nouvelle-Agamand."}},
[11425] = {"Test Quest - Craig", {"La vermine, faut la bousiller !"}},
[11426] = {"Localiser le mécanisme", {"Le capitaine de la garde Zorek, à Valgarde dans le Fjord Hurlant, vous demande de lui apporter un Mécanisme de commande de harpon."}},
[11427] = {"Je vous présente le lieutenant Martel-de-glace…", {"Le capitaine de la garde Zorek vous demande d'apporter le Mécanisme de commande de harpon au Lieutenant Martel-de-glace stationné à l'extérieur de Nifflevar."}},
[11428] = {"Le gardien Fânefeuille", {"Lurielle de la clairière Gelée veut que vous tuiez le Gardien Fânefeuille."}},
[11429] = {"Posez, massacrez !", {"Le lieutenant Martel-de-glace, dans le Fjord Hurlant, veut que vous placiez la Bannière de l’Alliance à Torvheim et que vous la défendiez contre les attaquants.","Revenez ensuite voir le lieutenant Martel-de-glace avec la Bannière de l’Alliance, si vous avez survécu."}},
[11430] = {"Le maître harponneur Yavus", {"Le lieutenant Martel-de-glace, dans le Fjord Hurlant, vous demande de tuer le Maître harponneur Yavus à Nifflevar."}},
[11431] = {"Attraper le Wolpertinger sauvage !", {"Le « nain » Charedor Cassebéchasse vous a demandé de capturer 5 Wolpertingers sonnés avec le Filet à wolpertinger. Il a indiqué qu'il fallait être soûl ou porter des Lunettes Synthébière pour les voir."}},
[11432] = {"Les géants endormis", {"Le Lieutenant-mage Malister veut que vous tuiez 5 Vrykuls dormants. Lorsque ce sera fait, retournez le voir au donjon de la Garde de l’ouest."}},
[11433] = {"Les géants endormis", {"Ahota Givre-blanc veut que vous tuiez 5 Vrykuls dormants. Lorsque ce sera fait, retournez le voir au Camp Sabot-d’hiver."}},
[11434] = {"Trésors oubliés", {"Terry le beau gosse, du Cap du Forban, vous demande de lui rapporter un Vase amani et une Figurine d'aigle de l'épave de bateau au sud."}},
[11435] = {"Gathered Pumpkins NYI", {"Apportez le Tas de citrouilles de la Sanssaint à la Directrice de l'orphelinat costumée."}},
[11436] = {"Et si on faisait du surf", {"Utilisez le grand Lance-harpons vrykul de Nifflevar pour vous lancer dans Valgarde. Informez le Capitaine de la garde Zorek de votre victoire sur les vrykuls."}},
[11437] = {"[PH] Beer Garden A", {"[PH] Speak to a Brewfest Barker in any Beer Garden and receive a free pretzel."}},
[11438] = {"[PH] Beer Garden B", {"[PH] Speak to a Brewfest Barker in any Beer Garden and receive a free pretzel."}},
[11439] = {"Un entraînement de pompier", {"Jetez des seaux d'eau sur 5 feux, puis revenez parler à la Directrice d'orphelinat costumée."}},
[11440] = {"Un entraînement de pompier", {"Jetez des seaux d'eau sur 5 feux, puis revenez parler à la Directrice d'orphelinat costumée."}},
