[11442] = {"Bienvenue à la fête des Brasseurs !", {"[PH] Speak to the Brewfest Organizer and receive a free beer."}},
[11443] = {"Plongée à Coiffedague", {"Harold Lagras, au fond de la Baie de Coiffedague, veut que vous récupériez 10 Caisses de fournitures de Valgarde."}},
[11444] = {"[PH] Beer Garden A", {"[PH] Speak to a Brewfest Barker in any Beer Garden and receive a free pretzel."}},
[11445] = {"[PH] Beer Garden B", {"[PH] Speak to a Brewfest Barker in any Beer Garden and receive a free pretzel."}},
