[11558] = {"Les périls de l'amour", {"Obtenez une Carte de garde moisie et rapportez-la à Fenstad Argyle, à Fossoyeuse."}},
[11559] = {"Commercer avec les Ailerons-d'hiver", {"Ramassez 5 Palourdes aileron-d'hiver et apportez-les à Ahlurglgr à la Retraite des Ailerons-d'hiver."}},
[11560] = {"Jamais sans mes têtards", {"Le Roi Mrgl-Mrgl vous a demandé de sauver 20 Têtards aileron-d'hiver. Retournez le voir à la Retraite des Ailerons-d'hiver quand ce sera fait."}},
[11561] = {"Eux !", {"Brglmurgl, de la Retraite des Ailerons-d'hiver, vous a demandé de tuer 15 Murlocs aileron-d'hiver. N'importe quelle combinaison de guerriers, oracles et guerriers des rivages fera l'affaire."}},
[11562] = {"Mon teinturier est un maître-chanteur", {"Le Roi Mrgl-Mrgl vous a demandé de parler à Mrmrglmr à l'extrémité nord-ouest de la Retraite des Ailerons-d'hiver."}},
[11563] = {"Grmmurggll Mrllggrl Glrggl !!!", {"Mrmrglmr vous demande de revenir le voir à la Retraite des Ailerons-d'hiver avec la Tête de Glrggl."}},
[11564] = {"Un ragoût d'épaulard succulent", {"Le Fendeur Bmurglbrm vous a demandé de récupérer 7 Succulentes graisses d'épaulard. Retournez ensuite le voir à la Retraite des Ailerons-d'hiver."}},
[11565] = {"Le costume de rechange", {"Rapportez son Costume de rechange au Roi Mrgl-Mrgl à la Retraite des Ailerons-d'hiver."}},
[11566] = {"Reddition... ou pas !", {"Le Roi Mrgl-Mrgl, de la Retraite des Ailerons-d’hiver, vous a demandé de lui rapporter une Pince de Claximus."}},
[11567] = {"L'antique armure des kvaldirs", {"Récupérez l'Antique armure des kvaldirs et apportez-la à Orfus de Kamagua dans le Fjord Hurlant."}},
[11568] = {"Le retour à la terre", {"Utilisez le Paquet d'artéfacts vrykuls aux tertres funéraires mis à sac de la Colline du Bouclier pour restituer le Cœur gelé d'Isuldof, le Bouclier des Asirites, le Bâton de la fureur de l'orage et l'Antique armure des kvaldirs.","Retournez voir Orfus de Kamagua à l'Antique élévateur, si vous réussissez."}},
[11569] = {"Le maître des clés Urmgrgl", {"Glrglrglr vous a demandé de récupérer la Clé d’Urmgrgl. Lorsque ce sera fait, retournez le voir dans les Cavernes des Ailerons-d’hiver."}},
[11570] = {"L'évasion des cavernes des Ailerons-d'hiver", {"Escorter Lurgglbr jusqu’à ce qu'il soit en sécurité, puis aller annoncer la bonne nouvelle au Roi Mrgl-Mrgl à la Retraite des Ailerons-d'hiver."}},
[11571] = {"Apprendre à communiquer", {"Le Roi Mrgl-Mrgl vous a donné une conque à utiliser sur les restes de Brûle-gueule. Rapportez-lui la Conque du roi remplie à la Retraite des Ailerons-d'hiver."}},
[11572] = {"Retourner voir Atuik", {"Transmettez les mauvaises nouvelles à l’Ancien Atuik à Kamagua."}},
[11573] = {"Orfus de Kamagua", {"Trouvez Orfus de Kamagua à l'Antique élévateur dans le Fjord Hurlant."}},
[11574] = {"Un peu trop près à mon goût…", {"Endorah veut que vous alliez à l’Escarpement d’Ambre au nord du Bastion Chanteguerre, et que vous remerciiez ceux qui l’ont sauvée."}},
[11575] = {"Juste à temps", {"Moucheronne, au Donjon de la Bravoure, veut que vous alliez à l’Escarpement d’Ambre au nord, et que vous remerciiez ceux qui l’ont sauvée."}},
[11576] = {"Surveiller la faille : l'anomalie de la falaise fendue", {"Garren, de l'Escarpement d'Ambre, vous demande d'utiliser l'Arcanomètre à l'Anomalie de la falaise fendue."}},
